grass.fragmentScript:
varying vec2 vUv;
uniform sampler2D map;
uniform vec3 fogColor;
uniform float fogDensity;
uniform vec3 directionalLightColor; // DirectionalLight color
uniform float directionalLightIntensity; // DirectionalLight intensity
uniform vec3 directionalLightDirection; // DirectionalLight direction (world space)
uniform vec3 ambientLightColor; // AmbientLight color
uniform float ambientLightIntensity; // AmbientLight intensity
uniform vec3 skyColor; // HemisphereLight sky color
uniform vec3 groundColor; // HemisphereLight ground color
uniform float hemisphereIntensity; // HemisphereLight intensity
varying vec3 vWorldPosition;
varying vec3 vNormal;


void main() {
    vec4 texColor = texture2D(map, vUv);
    vec3 color = texColor.rgb;

    // Directional light (Lambertian diffuse)
    vec3 lightDir = normalize(directionalLightDirection); // Ensure direction is normalized
    float diff = max(dot(vNormal, lightDir), 0.0);
    vec3 diffuse = directionalLightColor * directionalLightIntensity * diff * color;

    // Ambient light
    vec3 ambient = ambientLightColor * ambientLightIntensity * color;

    // Hemisphere light
    vec3 hemiDir = vec3(0.0, 1.0, 0.0); // Up direction for hemisphere
    float hemiDot = dot(vNormal, hemiDir) * 0.5 + 0.5; // Remap to 0-1
    vec3 hemi = mix(groundColor, skyColor, hemiDot) * hemisphereIntensity * color;

    // Combine lighting contributions
    vec3 litColor = diffuse + ambient + hemi;

    // Apply fog
    float fogDistance = length(vWorldPosition - cameraPosition);
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695);
    fogFactor = clamp(fogFactor, 0.0, 1.0);

    vec3 finalColor = mix(fogColor, litColor, fogFactor);

    gl_FragColor = vec4(finalColor, texColor.a);
}

grass.vertexScript:
varying vec2 vUv;
uniform float time;
uniform float windSpeed;
uniform float windStrength;
uniform vec2 windDirection;
attribute float instancePhase;
varying vec3 vWorldPosition;
varying vec3 vNormal; // Add for lighting
uniform float maxDistance;

void main() {
    vUv = uv;
    vec2 dir = normalize(windDirection);
    float wave = sin(time * windSpeed + instancePhase) * windStrength;
    wave *= uv.y;
    vec3 displacement = vec3(
        dir.x * wave,
        0.0,
        dir.y * wave
    );

    // Compute world position in world space
    vec4 worldPosition = modelMatrix * instanceMatrix * vec4(position + displacement, 1.0);
    vWorldPosition = worldPosition.xyz; // Store correct world position

    float distance = length(worldPosition.xyz - cameraPosition);
    if (distance > 500.0) {
        //  gl_Position = vec4(1e20, 1e20, 1e20, 1.0); // Move far outside clip space
       // return;
    }
    vec3 normal = normalize(normal); // Assuming grass geometry has normals
    vNormal = normalize((modelMatrix * instanceMatrix * vec4(normal, 0.0)).xyz);

    // Compute view position for gl_Position
    vec4 mvPosition = viewMatrix * worldPosition;
    gl_Position = projectionMatrix * mvPosition;
    vUv = uv;
}

water.fragmentScript:
uniform float time;
uniform float waveHeight;
uniform float liquidAlpha;
uniform vec3 liquidColor;
uniform vec3 foamColor;
uniform sampler2D terrainHeightmap; // Heightmap texture
uniform vec2 terrainSize; // Size of the terrain in world units
uniform vec2 terrainOffset; // Offset of the terrain in world units
uniform float waveFrequency;
uniform float fresnelPower;
uniform vec3 lightDirection;
uniform float ambientIntensity;
uniform float specularIntensity;
uniform float foamThreshold; // How close to terrain foam appears
uniform float foamWidth; // Width of the foam band
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight;
varying vec3 vWorldPosition;
uniform vec3 fogColor;
uniform float fogDensity;

// Function to get terrain height from a heightmap texture
float getTerrainHeightAt(vec2 worldPos) {
    // Convert world position to terrain UV coordinates
    vec2 terrainUV = (worldPos - terrainOffset) / terrainSize;

    // Ensure we're within terrain bounds (0-1)
    if (terrainUV.x < 0.0 || terrainUV.x > 1.0 || terrainUV.y < 0.0 || terrainUV.y > 1.0) {
        return -1000.0; // Outside terrain bounds
    }

    // Sample heightmap texture
    return texture2D(terrainHeightmap, terrainUV).r;
}

void main() {
    vec2 uv = vUv;
    vec3 normal = normalize(vNormal);
    vec3 viewDir = normalize(vViewPosition);
    vec3 lightDir = normalize(lightDirection);

    // Get terrain height at this xz position
    float terrainHeight = getTerrainHeightAt(vWorldPosition.xz);

    // Calculate distance between water surface and terrain
    float waterTerrainDistance = vWorldPosition.y - terrainHeight;

    // Create foam based on proximity to terrain - only if we found terrain nearby
    float terrainFoamFactor = 0.0;
    if(terrainHeight > -999.0) { // If we found valid terrain
        terrainFoamFactor = 1.0 - smoothstep(0.0, foamWidth, waterTerrainDistance);
    }

    // Original wave height-based foam
    float waveFoamFactor = smoothstep(0.8, 1.0, vNormalizedWaveHeight);

    // Combine both foam factors - use max to show foam from either source
    float foamFactor = max(terrainFoamFactor, waveFoamFactor);

    // Fresnel effect for edge transparency
    float fresnel = pow(1.0 - max(dot(normal, viewDir), 0.0), fresnelPower);

    // Diffuse lighting
    float diffuse = max(dot(normal, lightDir), 0.0) * 0.25;

    // Specular (Blinn-Phong)
    vec3 halfwayDir = normalize(lightDir + viewDir);
    float specular = pow(max(dot(normal, halfwayDir), 0.0), 32.0) * specularIntensity;

    // Base color with subtle wave height modulation
    vec3 baseColor = liquidColor * (0.8 + 0.2 * vNormalizedWaveHeight);

    // Apply foam - now blend between base color and foam color
    vec3 color = mix(baseColor, foamColor, foamFactor);

    // Calculate fog
    float fogDistance = length(vWorldPosition - cameraPosition);
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695);
    fogFactor = clamp(fogFactor, 0.0, 1.0);

    // Combine lighting components
    vec3 lightColor = color * (ambientIntensity + diffuse) + vec3(specular);
    vec3 finalColor = mix(fogColor, lightColor, fogFactor);

    // Apply fresnel for transparency at edges and make foam more opaque
    float alpha = mix(0.6, 1.0, fresnel) * liquidAlpha;
    alpha = mix(alpha, 1.0, foamFactor * 0.5); // Make foam more opaque

    gl_FragColor = vec4(finalColor, min(1.0, waterTerrainDistance));
}

water.vertexScript:
uniform float time;
uniform float waveHeight;
uniform float waveFrequency;
uniform float waveSpeed;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight;
varying vec3 vWorldPosition;

// 2D Random function for seeding noise
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// 2D Noise function (simplified Perlin-like noise)
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
           (c - a) * u.y * (1.0 - u.x) +
           (d - b) * u.x * u.y;
}

void main() {
    vUv = uv;

    // Generate smooth noise for subtle variation
    float noiseScale = 0.3; // Larger scale for smoother noise
    float noiseSpeed = 0.05; // Slower noise evolution
    float noiseValue = noise(vUv * noiseScale + time * noiseSpeed);
    float noiseValue2 = noise(vUv * noiseScale * 0.5 + time * noiseSpeed * 0.7 + 10.0);

    // Introduce a dominant wave direction (e.g., along x-axis, simulating wind direction)
    float directionalWave = sin(vUv.x * waveFrequency + time * waveSpeed + noiseValue * 0.5) * waveHeight;

    // Add smaller, secondary waves with slight noise variation
    float secondaryWave = sin(vUv.x * waveFrequency * 1.5 + vUv.y * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * waveHeight * 0.4;
    float detailWave = cos(vUv.x * waveFrequency * 2.0 + vUv.y * waveFrequency * 0.5 + time * waveSpeed * 1.2) * waveHeight * 0.2 * (0.8 + 0.2 * noiseValue);

    // Combine waves with a softer mix
    float displacementY = directionalWave + secondaryWave + detailWave;

    // Store wave height for fragment shader
    vWaveHeight = displacementY;

    // Normalize wave height based on maximum possible displacement
    float maxWaveHeight = waveHeight * (1.0 + 0.4 + 0.2); // Sum of wave amplitudes
    vNormalizedWaveHeight = clamp(displacementY / maxWaveHeight, -1.0, 1.0);

    // Update position with displacement
    vec3 newPosition = vec3(position.x, position.y + displacementY, position.z);

    // Compute normal for lighting
    float offset = 0.01;
    float waveX = (sin((vUv.x + offset) * waveFrequency + time * waveSpeed + noiseValue * 0.5) +
                   sin((vUv.x + offset) * waveFrequency * 1.5 + (vUv.y + offset) * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * 0.4 +
                   cos((vUv.x + offset) * waveFrequency * 2.0 + (vUv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.2 * (0.8 + 0.2 * noiseValue)) * waveHeight;
    float waveZ = (sin(vUv.x * waveFrequency + time * waveSpeed + noiseValue * 0.5) +
                   sin(vUv.x * waveFrequency * 1.5 + (vUv.y + offset) * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * 0.4 +
                   cos(vUv.x * waveFrequency * 2.0 + (vUv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.2 * (0.8 + 0.2 * noiseValue)) * waveHeight;
    vec3 tangent = normalize(vec3(1.0, (waveX - displacementY) / offset, 0.0));
    vec3 bitangent = normalize(vec3(0.0, (waveZ - displacementY) / offset, 1.0));
    vNormal = normalize(cross(tangent, bitangent));

    // Pass view position for fresnel and lighting
    vec4 worldPosition = modelMatrix * vec4(newPosition, 1.0);
    vViewPosition = (cameraPosition - worldPosition.xyz);
    vWorldPosition = worldPosition.xyz;

    // Apply projection and model-view transforms
    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
}

AircraftController.script:
class AircraftController extends engine.Component {
    init({
        acceleration = 1000,
        strafeAcceleration = 15,
        verticalAcceleration = 15,
        pitchSpeed = 1.5,
        yawSpeed = 1,
        rollSpeed = 1,
        mouseSensitivity = 0.00005,
        dampingFactor = 0.15,
        maxSpeed = 6000,
        cameraSmoothing = 0.4,
        collisionRecoveryTime = 2,
        // New parameters for stability
        physicsUpdateRate = 60,      // Hz for physics updates
        positionSmoothingFactor = 0.1, // Reduced from 0.5 for smoother transitions
        velocityThreshold = 0.01     // Minimum velocity threshold to reduce jitter
    }) {
        this.world = this.game.gameEntity.getComponent("InfiniWorld");
        this.scene = this.world.scene;
        this.camera = this.game.camera;
        this.acceleration = acceleration;
        this.strafeAcceleration = strafeAcceleration;
        this.verticalAcceleration = verticalAcceleration;
        this.pitchSpeed = pitchSpeed;
        this.yawSpeed = yawSpeed;
        this.rollSpeed = rollSpeed;
        this.mouseSensitivity = mouseSensitivity;
        this.dampingFactor = dampingFactor;
        this.maxSpeed = maxSpeed;
        this.cameraSmoothing = cameraSmoothing;
        this.parent.transform.quaternion = new THREE.Quaternion();
        
        // New stability parameters
        this.physicsUpdateRate = physicsUpdateRate;
        this.physicsUpdateInterval = 1 / physicsUpdateRate;
        this.physicsAccumulator = 0;
        this.positionSmoothingFactor = positionSmoothingFactor;
        this.velocityThreshold = velocityThreshold;
        this.previousPositions = []; // Store previous positions for smoothing
        this.previousPositionMaxLength = 5; // Number of positions to average

        // Initialize physics properties
        this.physics = this.game.gameEntity.getComponent("Physics");
        this.collisionRecoveryTime = collisionRecoveryTime;
        this.hasCollided = false;
        this.collisionImpulse = new THREE.Vector3();
        
        // Initialize transform properties needed for physics
        this.parent.transform.physicsPosition = new THREE.Vector3().copy(this.parent.transform.position);
        this.parent.grounded = false;
 
        // Store last stable position/rotation for recovery
        this.lastStablePosition = new THREE.Vector3().copy(this.parent.transform.position);
        this.lastStableQuaternion = new THREE.Quaternion().copy(this.parent.transform.quaternion);
        this.lastStableUpdateTime = 0;
        this.stableUpdateInterval = 0.5; // Save stable state every 0.5 seconds

        this.controls = new THREE_.PointerLockControls(this.camera, this.world.renderer.domElement);
        this.controls.pointerSpeed = 0;
        this.scene.add(this.controls.object);

        // Movement properties
        this.thrust = 0;
        this.strafeInput = 0;
        this.verticalInput = 0;
        this.pitchInput = 0;
        this.yawInput = 0;
        this.rollInput = 0;
        this.mouseXInput = 0;
        this.mouseYInput = 0;
        this.velocity = new THREE.Vector3();
        this.targetVelocity = new THREE.Vector3(); // New target velocity for smoothing
        this.collisionTimer = 0;
        // Local axes
        this.forward = new THREE.Vector3(0, 0, 1);
        this.up = new THREE.Vector3(0, 1, 0);
        this.right = new THREE.Vector3(1, 0, 0);

        // World reference vectors
        this.worldUp = new THREE.Vector3(0, 1, 0);
        this.worldForward = new THREE.Vector3(0, 0, 1);

        // Input state
        this.keys = {
            KeyW: false,
            KeyS: false,
            KeyA: false,
            KeyD: false,
            KeyQ: false,
            KeyE: false,
            ShiftLeft: false,
            KeyZ: false,
            Space: false
        };
        this.initialPosition = new THREE.Vector3();
        this.initialPosition.copy(this.parent.transform.position);
        // Camera settings
        this.isThirdPerson = true;
        this.thirdPersonDistance = 50;
        this.thirdPersonHeight = 15;
        this.cameraLookAhead = 5;
        this.lastCameraPosition = new THREE.Vector3();
        this.lastCameraLookAt = new THREE.Vector3();
        this.smoothedAircraftPosition = new THREE.Vector3().copy(this.parent.transform.position);
        this.smoothedAircraftQuaternion = new THREE.Quaternion().copy(this.parent.transform.quaternion);
     
        // Bind event handlers
        this.onKeyDown = this.onKeyDown.bind(this);
        this.onKeyUp = this.onKeyUp.bind(this);
        this.onMouseMove = this.onMouseMove.bind(this);
        this.onMouseDown = this.onMouseDown.bind(this);
        this.onWheel = this.onWheel.bind(this);
        document.addEventListener('keydown', this.onKeyDown);
        document.addEventListener('keyup', this.onKeyUp);
        document.addEventListener('mousemove', this.onMouseMove);
        document.addEventListener('mousedown', this.onMouseDown);
        document.addEventListener('wheel', this.onWheel);

        // Initialize camera
        this.updateCameraPosition();
        
        // Debug mode for development
        this.debugMode = false;
        this.debugInfo = { jitterCount: 0, maxJitter: 0 };
    }

    OnGrounded() {
        if (this.hasCollided) return;
        this.hasCollided = true;
        const restitution = .5;
        this.thrust *= restitution;
        this.parent.transform.position.y += 5;
        
        let reflection = new THREE.Vector3().copy(this.world.getReflectionAt(this.game.deltaTime, this.parent.transform.position, this.velocity, restitution));
        // Add energy loss on bounce (70% energy conservation)
    
        reflection.multiplyScalar(restitution);
        reflection.y *= 2;
        if(reflection.y < 5) reflection.y = 5;

        reflection.y = Math.min(reflection.y, 10);
        // Apply reflected velocity
        this.velocity.copy(reflection);
        this.parent.transform.velocity.copy(reflection);
    }

    onKeyDown(event) {
        if (event.code in this.keys) {
            this.keys[event.code] = true;
        }

        if (event.code === 'KeyV') {
            this.isThirdPerson = !this.isThirdPerson;
            if (this.isThirdPerson) {
                const offset = new THREE.Vector3(0, this.thirdPersonHeight, -this.thirdPersonDistance)
                    .applyQuaternion(this.smoothedAircraftQuaternion);
                this.lastCameraPosition.copy(this.smoothedAircraftPosition).add(offset);
                this.lastCameraLookAt.copy(this.smoothedAircraftPosition);
            }
        }

        if (event.code === 'KeyR') {
            // Reset aircraft orientation and clear collision state
            this.parent.transform.quaternion.set(0, 0, 0, 1);
            this.smoothedAircraftQuaternion.copy(this.parent.transform.quaternion);
            this.hasCollided = false;
            this.collisionTimer = 0;
            this.velocity.set(0, 0, 0);
            this.targetVelocity.set(0, 0, 0);
            this.parent.transform.velocity.set(0, 0, 0);
            this.parent.transform.position.copy(this.initialPosition);
            this.previousPositions = [];
            
            // Reset stable state
            this.lastStablePosition.copy(this.initialPosition);
            this.lastStableQuaternion.copy(this.parent.transform.quaternion);
        }
        
        // Toggle debug mode with F1
        if (event.code === 'F1') {
            this.debugMode = !this.debugMode;
            console.log("Debug mode:", this.debugMode);
        }
    }

    onKeyUp(event) {
        if (event.code in this.keys) {
            this.keys[event.code] = false;
        }
    }

    onMouseMove(event) {
        if (this.controls.isLocked && !this.hasCollided) {
            // Apply more aggressive smoothing to mouse input
            const smoothingFactor = 0.6; // Higher smoothing for mouse movements
            
            // Get new input values
            const newMouseXInput = -(event.movementX || 0) * this.mouseSensitivity;
            const newMouseYInput = -(event.movementY || 0) * this.mouseSensitivity;
            
            // Apply smoothing between old and new values
            this.mouseXInput = this.mouseXInput + newMouseXInput;
            this.mouseYInput = this.mouseYInput + newMouseYInput;
        } else {
            this.mouseXInput = 0;
            this.mouseYInput = 0;
        }
    }
    
    onMouseDown(event) {
        if (!this.controls.isLocked && this.game.deltaTime > 0) {
            this.controls.lock();
            if(!event.bubbles){
                event.bubbles = true;
            }
        }
    }

    onWheel(event) {
        if (this.hasCollided) return;
        
        const delta = event.deltaY * 0.01;
        // Apply smoother thrust changes
        const thrustDelta = this.acceleration * 0.5 * delta;
        this.thrust = Math.max(0, Math.min(this.thrust - thrustDelta, this.maxSpeed));
    }

    updateAxes() {
        this.forward.set(0, 0, 1).applyQuaternion(this.parent.transform.quaternion).normalize();
        this.up.set(0, 1, 0).applyQuaternion(this.parent.transform.quaternion).normalize();
        this.right.set(1, 0, 0).applyQuaternion(this.parent.transform.quaternion).normalize();
    }

    updateCameraPosition() {
        const dt = this.game.deltaTime;
        const smoothingAlpha = 0.9; 
    
    
        // Calculate forward vector from smoothed quaternion
        const forward = new THREE.Vector3(0, 0, 1).applyQuaternion(this.parent.transform.quaternion);
    
        // Calculate look-at target
        const lookTarget = this.parent.transform.position.clone().add(
            forward.clone().multiplyScalar(this.cameraLookAhead)
        );
    
        if (this.isThirdPerson) {
            const offsetDistance = Math.max(1, this.parent.transform.velocity.length() * .001);
            const offset = new THREE.Vector3(0, this.thirdPersonHeight, -this.thirdPersonDistance).applyQuaternion(this.parent.transform.quaternion);
            offset.multiplyScalar(offsetDistance);
            offset.y /= offsetDistance;
            const targetPos = this.parent.transform.position.clone().add(offset);
              
    
            // Add additional camera smoothing to reduce jitter
            this.camera.position.copy(targetPos);
    
            // Extract roll from smoothedAircraftQuaternion
            const up = new THREE.Vector3(0, 1, 0).applyQuaternion(this.parent.transform.quaternion);
            const right = new THREE.Vector3().crossVectors(forward, up).normalize();
            up.crossVectors(right, forward).normalize(); // Recompute up to ensure orthogonality
    
            // Compute quaternion to look at target while preserving roll
            const lookAtMatrix = new THREE.Matrix4().lookAt(
                this.camera.position,
                lookTarget,
                up // Use aircraft's up vector to preserve roll
            );
            
            // Apply additional rotation smoothing
            const newQuaternion = new THREE.Quaternion().setFromRotationMatrix(lookAtMatrix);
            this.camera.quaternion.slerp(newQuaternion, smoothingAlpha);
    
        } else {
            this.camera.position.copy(this.parent.transform.position);
            this.camera.quaternion.copy(this.parent.transform.quaternion);
        }
    }

    updateCollisionState(dt) {
        if (this.hasCollided) {
            this.collisionTimer += dt;

            const fadeRate = Math.max(0, 1 - (this.collisionTimer / this.collisionRecoveryTime));
        
            // Add gradual stabilization as recovery progresses
                // Start adding some auto-stabilization
            const stabilizationForce = 0.3 * (1 - fadeRate);
            
            // Gradually level out

            const levelingQuat = new THREE.Quaternion().setFromUnitVectors(this.up, this.worldUp);
            this.parent.transform.quaternion.slerp(levelingQuat, stabilizationForce * dt * 5);
        
            if(this.parent.transform.position.y < this.parent.transform.groundHeight) this.parent.transform.position.y += 5;
            // Check if recovery time has elapsed
            if (this.collisionTimer >= this.collisionRecoveryTime) {
                this.hasCollided = false;
                this.collisionTimer = 0;
                this.previousPositions = []; // Reset position history
                console.log("Aircraft control restored after collision");
            }
        }
    }

    updatePhysics(dt) {

        // Apply velocity smoothing
        this.targetVelocity.copy(this.velocity);
        
        // If velocity is very small, zero it out to prevent micro-jitters
        if (this.targetVelocity.lengthSq() < this.velocityThreshold * this.velocityThreshold) {
            this.targetVelocity.set(0, 0, 0);
        }
        
        // Smooth velocity transition
        this.parent.transform.velocity.copy(this.targetVelocity); // Smooth velocity transitions
    
        this.parent.transform.position.add(this.targetVelocity.clone().multiplyScalar(this.game.deltaTime)); 
    }
    
    // New method to save stable state
    updateStableState(dt) {
        if (!this.hasCollided) {
            this.lastStableUpdateTime += dt;
            if (this.lastStableUpdateTime >= this.stableUpdateInterval) {
                this.lastStableUpdateTime = 0;
                this.lastStablePosition.copy(this.parent.transform.position);
                this.lastStableQuaternion.copy(this.parent.transform.quaternion);
            }
        }
    }
    
    update() {
        const dt = this.game.deltaTime;
        
        const groundHeight = this.parent.transform.groundHeight;
        if(this.parent.transform.position.y <= groundHeight) this.OnGrounded();
        // Update stable state tracking
        this.updateStableState(dt);

        // Update local axes
        this.updateAxes();

        if (!this.hasCollided) {
            // Normal flight controls when not in collision state
            
            // Smooth inputs
            const inputDamping = 1 - (this.dampingFactor * dt);
            // Cap pitch/yaw inputs to ensure consistent rotation rate
            const maxInput = 1.0; // Maximum input value for pitch/yaw
            this.pitchInput = (this.pitchInput * inputDamping) + Math.max(-maxInput, Math.min(maxInput, this.mouseYInput * this.pitchSpeed));
            this.yawInput = (this.yawInput * inputDamping) + Math.max(-maxInput, Math.min(maxInput, this.mouseXInput * this.yawSpeed));
            this.rollInput = (this.keys.KeyD ? 1 : 0) + (this.keys.KeyA ? -1 : 0);
            this.rollInput *= this.rollSpeed * dt;

            // Thrust control
            const thrustDamping = 1 - (this.dampingFactor * dt);
            this.thrust *= thrustDamping;

            if (this.keys.KeyW) {
                this.thrust += this.acceleration * dt;
            } else if (this.keys.KeyS) {
                this.thrust -= this.acceleration * 2 * dt;
            }
            this.thrust = Math.max(0, Math.min(this.thrust, this.maxSpeed));

            // Calculate speed
            const currentSpeed = Math.min(this.thrust, this.maxSpeed);

            // Apply rotations
            if (this.rollInput !== 0) {
                const rollQuat = new THREE.Quaternion().setFromAxisAngle(this.forward, this.rollInput);
                this.parent.transform.quaternion.premultiply(rollQuat);
            }

            if (this.pitchInput !== 0) {
                // Apply pitch without scaling to ensure consistent rate
                const pitchQuat = new THREE.Quaternion().setFromAxisAngle(this.right, this.pitchInput * dt);
                this.parent.transform.quaternion.premultiply(pitchQuat);
            }

            if (this.yawInput !== 0) {
                const yawQuat = new THREE.Quaternion().setFromAxisAngle(this.worldUp, this.yawInput * dt);
                this.parent.transform.quaternion.premultiply(yawQuat);
            }

            // Normalize quaternion
            this.parent.transform.quaternion.normalize();

            // Update velocity
            this.velocity.copy(this.forward).multiplyScalar(currentSpeed);

            // Apply vertical thrust
            if (this.keys.Space) {
                const verticalVelocity = this.up.clone().multiplyScalar(this.verticalAcceleration * dt);
                this.velocity.add(verticalVelocity);
            }
                
            
        } else {
            // Handle collision state and recovery
            this.updateCollisionState(dt);
        }       

        // Update physics system with latest position and velocity
        this.updatePhysics(dt);
        // Update camera position
        this.updateCameraPosition();

        // Reset mouse input
        this.mouseXInput *= 0.8; // Gradual decay instead of immediate reset
        this.mouseYInput *= 0.8;
        
        // Show debug info if enabled
        if (this.debugMode) {
            const speed = this.parent.transform.velocity.length().toFixed(2);
            const physPos = this.parent.transform.physicsPosition;
            const visualPos = this.parent.transform.position;
            const posDiff = visualPos.distanceTo(physPos).toFixed(4);
            
            console.log(`Speed: ${speed}, Pos diff: ${posDiff}, Jitters: ${this.debugInfo.jitterCount}`);
        }
    }

    onDestroy() {
        this.controls.dispose();
        document.removeEventListener('keydown', this.onKeyDown);
        document.removeEventListener('keyup', this.onKeyUp);
        document.removeEventListener('mousemove', this.onMouseMove);
        document.removeEventListener('wheel', this.onWheel);
        document.removeEventListener('mousedown', this.onMouseDown);
        this.scene.remove(this.parent);
        if (this.parent.geometry) this.parent.geometry.dispose();
        if (this.parent.material) this.parent.material.dispose();
    }
}

Collider.script:
class Collider extends engine.Component {
    init({ debug = false, objectType, spawnType }) {
        this.id = this.parent.id;
        this.prefabData = this.game.getCollections()[objectType][spawnType];
        this.colliderData = this.game.getCollections().colliders[this.prefabData.collider];
        if(!this.colliderData){
            this.colliderData = {
                type: "sphere",
                size: 1,
                offset: new THREE.Vector3(),
                gravity: true,
                mass: 1,
                restitution: 0.25
            }
        }
        this.type = this.colliderData.type; 
        this.size = this.colliderData.size; 
        if(typeof  this.colliderData.offset == "string")  this.colliderData.offset = JSON.parse( this.colliderData.offset);
        this.offset = this.colliderData.offset ? new THREE.Vector3(this.colliderData.offset.x, this.colliderData.offset.y, this.colliderData.offset.z) : new THREE.Vector3(0, 0, 0); // Center offset
        this.gravity = this.colliderData.gravity;
        this.mass = this.colliderData.mass; 
        this.restitution = this.colliderData.restitution;
        this.debug = debug; // Enable debug mode
        this.debugMesh = null; // Store debug mesh
        this.lerpFactor = this.colliderData.lerpFactor || .7; // Adjust this value to control smoothing (0-1)
     
        // Register with physics system
        this.game.gameEntity.getComponent('Physics').registerCollider(this);

        // Create debug visualization if debug mode is enabled
        if (this.debug) {
            this.createDebugMesh();
        }
    }

    createDebugMesh() {
        let geometry, material;

        if (this.type === 'sphere') {
            geometry = new THREE.SphereGeometry(this.size, 16, 16); // Low-poly for performance
            material = new THREE.MeshBasicMaterial({
                color: 0x00ff00, // Green wireframe
                wireframe: true
            });
        } else if (this.type === 'box') {
            geometry = new THREE.BoxGeometry(this.size.x, this.size.y, this.size.z);
            material = new THREE.MeshBasicMaterial({
                color: 0x00ff00, // Green wireframe
                wireframe: true
            });
        } else {
            throw new Error(`Unsupported collider type: ${this.type}`);
        }

        this.debugMesh = new THREE.Mesh(geometry, material);
        this.debugMesh.position.copy(this.parent.transform.physicsPosition).add(this.offset);
        this.game.scene.add(this.debugMesh); // Add to the Three.js scene
    }

    getAABB(position = this.parent.transform.physicsPosition) {
        const pos = position.clone().add(this.offset);
        if (this.type === 'sphere') {
            const radius = this.size;
            return {
                min: {
                    x: pos.x - radius,
                    y: pos.y - radius,
                    z: pos.z - radius
                },
                max: {
                    x: pos.x + radius,
                    y: pos.y + radius,
                    z: pos.z + radius
                }
            };
        } else if (this.type === 'box') {
            return {
                min: {
                    x: pos.x - this.size.x / 2,
                    y: pos.y - this.size.y / 2,
                    z: pos.z - this.size.z / 2
                },
                max: {
                    x: pos.x + this.size.x / 2,
                    y: pos.y + this.size.y / 2,
                    z: pos.z + this.size.z / 2
                }
            };
        }
        throw new Error(`Unsupported collider type: ${this.type}`);
    }

    update() {
 
        this.parent.transform.position.lerp(
            this.parent.transform.physicsPosition,
            this.lerpFactor
        );

        // Update debug mesh position if it exists
        if (this.debug && this.debugMesh) {
            this.debugMesh.position.copy(this.parent.transform.physicsPosition).add(this.offset);
        }
    }

    destroy() {
        // Clean up debug mesh if it exists
        if (this.debug && this.debugMesh) {
            this.game.scene.remove(this.debugMesh);
            this.debugMesh.geometry.dispose();
            this.debugMesh.material.dispose();
            this.debugMesh = null;
        }
    }
}

Physics.script:
class Physics extends engine.Component {
    async init() {
        this.colliders = new Map();
        this.rigidbodies = new Map();
        this.staticColliderIds = new Set(); // Track which ids in rigidbodies are static
        this.staticAABBs = [];  // Store AABB references, not physics objects
        this.collidersToRemove = [];
        this.lastUpdate = 0;
        this.deltaTime = 0;
        this.game.physics = this;
        this.staticAABBs = [];
        this.preloaded = [];
        await RAPIER.init();
        this.RAPIER = RAPIER; // Store reference for easier access
        this.startSimulation(RAPIER);        
        this.eventQueue = new RAPIER.EventQueue(true); // Enable contact events
    }
    
    async startSimulation(r) {
        // Create world with gravity
        let gravity = new r.Vector3(0.0, -98.1, 0.0);
        this.simulation = new r.World(gravity);
        this.simulation.timestep = 1/60;
        if(this.playerCallback){
            this.playerCallback(this.simulation);
        }
        this.preloaded.forEach((c) => {
            if (c.heights) {
                this.createHeightmapCollider(c); // Handle heightmap colliders
            } else {
                this.registerCollider(c); // Handle other colliders
            }
        });        
    }

    createChunkStaticColliders(chunkId, chunkData) {
        chunkData.collisionAABBs.keys().forEach((worldObjectType) => {
            chunkData.collisionAABBs.get(worldObjectType).forEach((aabb) => {
                this.createStaticCollider(chunkId, worldObjectType, aabb);
            });            
        });
        // this.rigidbodies.get(chunkId).push({ type: 'heightmap', rigidBody, collider });
    }

    registerPlayer(callback){
        this.playerCallback = callback;
    }

    removeStaticCollider(staticId) {
        if (!staticId) {
            return false;
        }
        
        if (this.rigidbodies.has(staticId)) {
            const { rigidBody, collider } = this.rigidbodies.get(staticId);
            
            try {
                if (collider) {
                    this.simulation.removeCollider(collider, true);
                }
                
                if (rigidBody) {
                    this.simulation.removeRigidBody(rigidBody);
                }
                
                this.rigidbodies.delete(staticId);  
                this.staticColliderIds.delete(staticId);
                
                return true;
            } catch (error) {
                console.error(`Error removing static collider ${staticId}:`, error);
                // Still remove from our maps even if physics removal failed
                this.rigidbodies.delete(staticId);
                this.staticColliderIds.delete(staticId);
                return false;
            }
        } else {
            return false;
        }
    }
    registerCollider(collider) {
        const r = this.RAPIER;
        if(!r){
            this.preloaded.push(collider);
            return;
        }
        let entity = collider.parent;
        
        if (!collider.id || !entity.transform.position) {
            return;
        }
        
        // Create rigid body
        let rigidBodyDesc;
        if (collider.mass <= 0) {
            rigidBodyDesc = r.RigidBodyDesc.fixed();
        } else {
            rigidBodyDesc = r.RigidBodyDesc.dynamic()
                .setLinearDamping(0.2)
                .setAngularDamping(0.4)
                .setCcdEnabled(true);
        }
        
        // Set initial position
        rigidBodyDesc.setTranslation(
            entity.transform.position.x + collider.offset.x,
            entity.transform.position.y + collider.offset.y,
            entity.transform.position.z + collider.offset.z
        );
        
        // Set initial velocity if available
        if (entity.transform.velocity) {
            rigidBodyDesc.setLinvel(
                entity.transform.velocity.x,
                entity.transform.velocity.y,
                entity.transform.velocity.z
            );
        }
        
        // Create the rigid body
        const rigidBody = this.simulation.createRigidBody(rigidBodyDesc);
        rigidBody.ccd = true;
        // Create collider shape based on type
        let colliderDesc;
        switch (collider.type) {
            case 'sphere':
                colliderDesc = r.ColliderDesc.ball(collider.size);
                break;
            case 'box':
                colliderDesc = r.ColliderDesc.cuboid(
                    collider.size, collider.size, collider.size
                );
                break;
            case 'capsule':
                colliderDesc = r.ColliderDesc.capsule(
                    collider.size, collider.size / 2
                );
                break;
            default:
                colliderDesc = r.ColliderDesc.ball(collider.size);
                break;
        }
        
        // Set restitution (bounciness)
        colliderDesc.setRestitution(collider.restitution).setMass(collider.mass);
        // Create the collider
        const rapierCollider = this.simulation.createCollider(colliderDesc, rigidBody);
        
        // Store data for tracking
        const data = {
            entity: entity,
            type: collider.type,
            size: collider.size,
            gravity: collider.gravity,
            offset: collider.offset,
            mass: collider.mass,
            restitution: collider.restitution,
            rigidBody: rigidBody,
            rapierCollider: rapierCollider,
            reflected: 0,
            wasGrounded: false
        };
        
        this.colliders.set(collider.id, data);
        
        
        return rigidBody;
    }

    unregisterCollider(colliderId) {
        if (this.colliders.has(colliderId)) {
            const data = this.colliders.get(colliderId);
            
            // Remove the Rapier collider and rigid body
            if (data.rapierCollider) {
                this.simulation.removeCollider(data.rapierCollider, true);
            }
            
            if (data.rigidBody) {
                this.simulation.removeRigidBody(data.rigidBody);
            }
            
            this.colliders.delete(colliderId);
            
           
        }
    }


    sendToWorker() {
        // Since we're not using a worker anymore, this method now runs the simulation step
        // and updates all entity positions directly
        
        
        // Step the physics simulation
        this.simulation.step();
        
        // Update all entity positions based on the simulation results
        for (const [colliderId, data] of this.colliders.entries()) {
            const entity = data.entity;
            const rigidBody = data.rigidBody;
            
            if (!rigidBody || !entity) continue;
            
            // Get position from physics
            const position = rigidBody.translation();
            entity.transform.physicsPosition.set(
                position.x,
                position.y,
                position.z
            );
            
            // Get velocity from physics
            const velocity = rigidBody.linvel();
            entity.transform.velocity.set(
                velocity.x,
                velocity.y,
                velocity.z
            );
            
            // Get rotation from physics (quaternion)
            const rotation = rigidBody.rotation();
            entity.transform.quaternion.set(
                rotation.x,
                rotation.y,
                rotation.z,
                rotation.w
            );
            
            // Handle ground detection
               // Update AABB if the method exists
            if (entity.getAABB) {
                data.aabb = entity.getAABB(entity.transform.physicsPosition);
            }
           
            // Handle collision detection
            this.detectCollisions(colliderId, data);
        }
        // Process colliders to remove
        for (const colliderId of this.collidersToRemove) {
            this.unregisterCollider(colliderId);
        }
        this.collidersToRemove = [];
    }
        
    detectCollisions(data) {

        
        // this.eventQueue.drainContactEvents((handle1, handle2, started) => {
        //     const collider1 = this.simulation.getCollider(handle1);
        //     const collider2 = this.simulation.getCollider(handle2);
        //     const data1 = [...this.colliders.entries()].find(([id, data]) => data.rapierCollider === collider1);
        //     const data2 = [...this.colliders.entries()].find(([id, data]) => data.rapierCollider === collider2);

        //     if (data1 && data2 && started && data1[1].entity.OnCollision) {
        //         data1[1].entity.OnCollision(data2[1]);
        //     }
        // });
    }
    addChunkCollider(chunkData) {
        const { cx, cz } = chunkData;
        if (!this.simulation) {
            this.preloaded.push(chunkData);
            return;
        }
        const chunkId = `${cx},${cz}`;
        if(!this.rigidbodies.has(chunkId)){
            this.rigidbodies.set(chunkId, []);            
        }
        this.createHeightmapCollider(chunkId, chunkData);
        this.createChunkStaticColliders(chunkId, chunkData);
    }

    removeChunkColliders(cx, cz) {
        const chunkId = `${cx},${cz}`;
        
        if (this.rigidbodies.has(`${chunkId}`)) {
            let c = 0;
            this.rigidbodies.get(chunkId).forEach((body) => {                
                const { rigidBody, collider } = body;
                if (collider) {
                    this.simulation.removeCollider(collider, true);
                    c++;
                }
                if (rigidBody) {
                    this.simulation.removeRigidBody(rigidBody);
                }
            });
            this.rigidbodies.delete(chunkId);
        }
    }
    createStaticCollider(chunkId, worldObjectType, aabb) {
        if (!aabb || !aabb.id) {
            console.warn("Invalid AABB provided to createStaticCollider");
            return null;
        }

        if (this.rigidbodies.has(aabb.id)) {
            return;
        }
        
        const r = this.RAPIER;
        const halfWidth = (aabb.max.x - aabb.min.x) / 2;
        const halfHeight = (aabb.max.y - aabb.min.y) / 2;
        const halfDepth = (aabb.max.z - aabb.min.z) / 2;
        
        const centerX = (aabb.max.x + aabb.min.x) / 2;
        const centerY = (aabb.max.y + aabb.min.y) / 2;
        const centerZ = (aabb.max.z + aabb.min.z) / 2;
        
        // Create a static rigid body
        const rigidBodyDesc = r.RigidBodyDesc.fixed()
            .setTranslation(centerX, centerY, centerZ);
        const rigidBody = this.simulation.createRigidBody(rigidBodyDesc);
        
        // Create a cuboid collider
        const colliderDesc = r.ColliderDesc.capsule(halfHeight, halfWidth)
                .setCollisionGroups(0x00020004) // Belongs to group 0x0002, interacts with group 0x0004 (dynamic)
                .setSolverGroups(0x00020004); // Same for solver groups
        const collider = this.simulation.createCollider(colliderDesc, rigidBody);
        
        // Store reference to the static collider with a unique ID
        this.rigidbodies.get(chunkId).push({ type: worldObjectType, rigidBody, collider });
        this.staticColliderIds.add(aabb.id); // Mark this as a static collider

    }
    createHeightmapCollider(chunkId, chunkData) {
        const r = this.RAPIER;
        const { cx, cz } = chunkData;
       // const { heights, nx, ny, scale } = heightmap;
        // Validate inputs

        // Create a static rigid body for the terrain chunk
        const rigidBodyDesc = r.RigidBodyDesc.fixed()
                .setSoftCcdPrediction(5)
                .setTranslation(
                    cx * this.game.terrain.chunkSize,
                    0,
                    cz * this.game.terrain.chunkSize
                );
        const rigidBody = this.simulation.createRigidBody(rigidBodyDesc);
        // Create heightfield collider
       // const heightfield = new Float32Array(heights);
        // const colliderDesc = r.ColliderDesc.heightfield(nx - 1, ny - 1, heightfield, scale)
        //     .setSensor(false);
        const colliderDesc = r.ColliderDesc.trimesh(chunkData.geometry.positions, chunkData.geometry.indices)    
            .setCollisionGroups(0x00010004) // Belongs to group 0x0001, interacts with group 0x0004 (dynamic)
            .setSolverGroups(0x00010004)
            .setRestitution(chunkData.restitution)
            .setFriction(chunkData.friction); // Same for solver groups;
        const collider = this.simulation.createCollider(colliderDesc, rigidBody);    
        // Store collider with a unique ID
      //  this.createHeightmapMesh(cx, cz, chunkData);
        this.rigidbodies.get(chunkId).push({ type: 'heightmap', rigidBody, collider });
    }
    createHeightmapMesh(cx, cz, chunkData) {
        const { geometry } = chunkData;       

        // Create geometry for the heightmap
        const plane = new THREE.BufferGeometry();
        plane.setAttribute('position', new THREE.Float32BufferAttribute(geometry.positions, 3));
        plane.setAttribute('normal', new THREE.Float32BufferAttribute(geometry.normals, 3));
        plane.setIndex(geometry.indices);
        plane.attributes.position.needsUpdate = true; // Mark for update
        plane.attributes.normal.needsUpdate = true; // Mark for update
        plane.computeVertexNormals(); // Recalculate normals for lighting

        // Create material
        const material = new THREE.MeshStandardMaterial({
            color: 0x00ff00, // Green for visibility, adjust as needed
            wireframe: true   // Wireframe to visualize mesh structure
        });

        // Create mesh
        const mesh = new THREE.Mesh(plane, material);

        // Position mesh to match collider
        mesh.position.set(
            cx * this.game.terrain.chunkSize, // Center x
            1,                                                           // y (heights are in geometry)
            cz * this.game.terrain.chunkSize  // Center z
        );

        // // Rotate plane to lie flat (x, z plane), as PlaneGeometry is initially in x, y plane
        // mesh.rotation.x = -Math.PI / 2;
        // Add to scene
        this.game.scene.add(mesh);

        // Store mesh for debugging (optional)
        const chunkId = `heightmap_${cx}_${cz}`;
        this.meshes = this.meshes || new Map(); // Assuming a meshes Map to store
        this.meshes.set(chunkId, mesh);

    }
    onDestroy() {
        // Clean up Rapier resources
        for (const [colliderId, data] of this.colliders.entries()) {
            if (data.rapierCollider) {
                this.simulation.removeCollider(data.rapierCollider, true);
            }
            if (data.rigidBody) {
                this.simulation.removeRigidBody(data.rigidBody);
            }
        }
        
        this.colliders.clear();
        this.rigidbodies.clear();
        this.simulation = null;
    }
    
}

PlayerAttacker.script:
class PlayerAttacker extends engine.Component {
    init() {
        this.stats = this.parent.getComponent("stats").stats;
        this.camera = this.game.camera;
        // Bind mouse click event
        this.cooldown = .25;
        this.onMouseDown = this.handleMouseDown.bind(this);
        document.addEventListener('mousedown', this.onMouseDown);
        
        // Throw state tracking
        this.isThrowAnimationPlaying = false;
        this.throwRequested = false;
        this.throwTimer = 0;
        this.animationLength = 2;
        this.throwDelay = this.animationLength * 0.6; // Keep your original delay
        this.throwSpeed = this.cooldown;
    }
    
    update() {
        // Handle cooldown timer
        if (this.cooldown > 0) {
            this.cooldown -= this.game.deltaTime;
        }
        
        // Handle throw animation timing
        if (this.throwRequested) {
            this.throwTimer += this.game.deltaTime;
            
            // When we reach the throw delay, launch the projectile
            if (this.throwTimer >= this.throwDelay * this.throwSpeed) {
                this.throwRequested = false;
                this.throwTimer = 0;
                this.launchProjectile();
                
                // Animation is still playing but projectile is launched
                this.isThrowAnimationPlaying = false;
            }
        }
        
        return true;
    }
    handleMouseDown(event) {
        if (event.button !== 0) return;
        if (this.cooldown > 0 || this.isThrowAnimationPlaying) return;
        
        this.cooldown = this.throwSpeed;
        
        const modelRenderer = this.parent.getComponent("modelRenderer");
        if (modelRenderer) {            
            modelRenderer.throw(this.animationLength / this.throwSpeed, this.animationLength);
            this.isThrowAnimationPlaying = true;
            this.throwRequested = true;
            this.throwTimer = 0;
        }
    }
    launchProjectile() {
        const projectileType = this.stats.projectile;
        if (!projectileType) return;
        let projectileDef = this.game.getCollections().projectilePrefabs[projectileType];
       
        let projStats = { ...projectileDef };
        delete projStats.render;
        projStats.baseDamage = this.stats.damage || 1;  
        projStats.piercing = this.stats.piercing || 0;
        projStats.splashRadius = this.stats.splashRadius || 0;
        projStats.critChance = this.stats.critChance || 0.05;
        projStats.critMultiplier = this.stats.critMultiplier || 2;
        
        // Calculate firing direction from camera
        const direction = new THREE.Vector3();
        this.camera.getWorldDirection(direction); // Camera's forward vector
        direction.normalize();
        
        // Spawn position: slightly offset from player
        let spawnPos = this.parent.transform.position.clone().add(direction.clone().multiplyScalar(5));
        let projectileParams = this.game.getCollections().projectiles[projectileType];
           
        if (projectileParams.offset) {
            const offset = JSON.parse(projectileParams.offset);
            // Create offset vector
            const offsetVector = new THREE.Vector3(offset.x, offset.y, offset.z);
            // Apply player's rotation to the offset
            offsetVector.applyQuaternion(this.parent.transform.quaternion);
            // Add rotated offset to spawn position
            spawnPos.add(offsetVector);
        }
        
        // Spawn projectile entity
        const projectile = this.game.spawn(
            'projectile',
            {
                objectType: 'projectilePrefabs',
                spawnType: projectileType,
                direction: direction, // No specific target, uses direction
                owner: this.parent,
                stats: projStats
            },
            spawnPos
        );          
    }
    
    destroy() {
        document.removeEventListener('mousedown', this.onMouseDown);
    }
}

PlayerController.script:
class PlayerController extends engine.Component {
    init({
        walkSpeed = 100, // Adjusted for Rapier (meters/second)
        runSpeed = 200,
        jumpForce = 350, // Adjusted for Rapier (meters/second)
        gravity = 981, // Standard gravity (m/s)
        mouseSensitivity = 0.002,
        characterHeight = 18,
        characterRadius = 3,
        cameraHeight = 16,
        cameraSmoothing = 0.2
    }) {
        let gameComponent = this.game.gameEntity.getComponent('game');
        this.world = gameComponent.world;
        this.physics = gameComponent.physics;
        this.physics.registerPlayer(this.setupPhysics.bind(this));
        this.scene = this.world.scene;
        this.camera = this.game.camera;

        // Movement parameters
        this.walkSpeed = walkSpeed;
        this.runSpeed = runSpeed;
        this.jumpForce = jumpForce;
        this.gravity = gravity;
        this.mouseSensitivity = mouseSensitivity;
        this.cameraSmoothing = cameraSmoothing;

        // Character dimensions
        this.characterHeight = characterHeight;
        this.characterRadius = characterRadius;
        this.cameraHeight = cameraHeight;


        // Initialize movement state
        this.velocity = new THREE.Vector3(); // For tracking vertical velocity (e.g., jumping, gravity)
        this.isGrounded = false;
        this.jumpRequested = false;
        this.isRunning = false;

        // Camera properties
        this.isFirstPerson = false; // Default to first-person
        this.thirdPersonDistance = 50;
        this.thirdPersonHeight = 25;
        this.cameraTargetPosition = new THREE.Vector3();
        this.cameraLookAt = new THREE.Vector3();
        this.cameraPitch = 0;
        this.cameraYaw = 0;

        // Local axes
        this.forward = new THREE.Vector3(0, 0, 1);
        this.up = new THREE.Vector3(0, 1, 0);
        this.right = new THREE.Vector3(1, 0, 0);

        // Movement inputs
        this.moveForward = 0;
        this.moveRight = 0;

        // Input state
        this.keys = {
            KeyW: false,
            KeyS: false,
            KeyA: false,
            KeyD: false,
            ShiftLeft: false,
            Space: false
        };

        // Pointer lock controls
        this.controls = { isLocked: false };
        document.addEventListener('click', () => {
            if (!this.controls.isLocked) {
                this.world.renderer.domElement.requestPointerLock();
            }
        });
        document.addEventListener('pointerlockchange', () => {
            this.controls.isLocked = document.pointerLockElement === this.world.renderer.domElement;
        });

        // Bind event handlers
        this.onKeyDown = this.onKeyDown.bind(this);
        this.onKeyUp = this.onKeyUp.bind(this);
        this.onMouseMove = this.onMouseMove.bind(this);
        document.addEventListener('keydown', this.onKeyDown);
        document.addEventListener('keyup', this.onKeyUp);
        document.addEventListener('mousemove', this.onMouseMove);

        // Debug helpers
        this.debug = {
            showRaycasts: false,
            raycastHelpers: []
        };

        // Sync initial camera position
        this.updateCameraPosition();
    }

    setupPhysics(simulation) {
        
        // Initialize Rapier physics
        this.rapierWorld = simulation; // Assume InfiniWorld initializes a Rapier World
        this.characterController = this.rapierWorld.createCharacterController(0.01); // Offset of 0.01m
        this.setupCharacterController();

        // Create player rigid-body and collider
        this.setupPlayerBody();
    }

    setupCharacterController() {
        // Configure Rapier character controller
        this.characterController.enableAutostep(0.3, 0.2, true); // Auto-step for stairs/obstacles
        this.characterController.enableSnapToGround(0.5); // Stick to ground when going downhill
        this.characterController.setMaxSlopeClimbAngle(45 * Math.PI / 180); // Max 45 climb
        this.characterController.setMinSlopeSlideAngle(30 * Math.PI / 180); // Slide on steep slopes
        this.characterController.setApplyImpulsesToDynamicBodies(true); // Push dynamic objects
        this.characterController.setUp({ x: 0, y: 1, z: 0 }); // Up vector is +Y
    }

    setupPlayerBody() {
        // Create a kinematic position-based rigid-body
        let rigidBodyDesc = RAPIER.RigidBodyDesc.kinematicPositionBased()
            .setTranslation(0, 50, 0); // Start above ground
        this.rigidBody = this.rapierWorld.createRigidBody(rigidBodyDesc);

        // Create a capsule collider for the player
        let colliderDesc = RAPIER.ColliderDesc.capsule(
            this.characterHeight / 2 - this.characterRadius, // Half-height (excluding caps)
            this.characterRadius
        )
            .setTranslation(0, this.characterHeight / 2, 0) // Center capsule vertically
            .setCollisionGroups(0x0001) // Optional: set collision groups
            .setSolverGroups(0x0001); // Optional: set solver groups
        this.collider = this.rapierWorld.createCollider(colliderDesc, this.rigidBody);

        // Store collision radius for camera calculations
        this.parent.collisionRadius = this.characterRadius;

        // Sync Three.js transform with Rapier
        this.syncTransformToRapier();
    }

    syncTransformToRapier() {
        // Update Rapier rigid-body position from Three.js transform
        const pos = this.parent.transform.position;
        this.rigidBody.setNextKinematicTranslation({ x: pos.x, y: pos.y, z: pos.z });
    }

    syncRapierToTransform() {
        // Update Three.js transform from Rapier rigid-body position
        const pos = this.rigidBody.translation();
        const smoothingFactor = 0.5;
        this.parent.transform.position.lerp(
            new THREE.Vector3(pos.x, pos.y, pos.z),
            Math.min(1, this.game.deltaTime * 60 * smoothingFactor)
        );
    }

    onKeyDown(event) {
        if (event.code in this.keys) {
            this.keys[event.code] = true;
            if (event.code === 'Space' && !event.repeat && this.isGrounded) {
                this.jumpRequested = true;
                this.parent.getComponent("modelRenderer").jump(500 / this.jumpForce);
            }
        }
        if (event.code === 'KeyV') {
            this.isFirstPerson = !this.isFirstPerson;
            if (!this.isFirstPerson) {
                const offset = new THREE.Vector3(0, this.thirdPersonHeight, this.thirdPersonDistance)
                    .applyQuaternion(this.parent.transform.quaternion);
                this.cameraTargetPosition.copy(this.parent.transform.position).add(offset);
                this.cameraLookAt.copy(this.parent.transform.position);
            }
        }
        if (event.code === 'KeyB') {
            this.debug.showRaycasts = !this.debug.showRaycasts;
            if (!this.debug.showRaycasts) {
                this.debug.raycastHelpers.forEach(helper => {
                    if (helper && this.scene.children.includes(helper)) {
                        this.scene.remove(helper);
                    }
                });
                this.debug.raycastHelpers = [];
            }
        }
    }

    onKeyUp(event) {
        if (event.code in this.keys) {
            this.keys[event.code] = false;
        }
    }

    onMouseMove(event) {
        if (this.controls.isLocked) {
            this.cameraYaw -= event.movementX * this.mouseSensitivity;
            this.cameraPitch += event.movementY * this.mouseSensitivity;
            this.cameraPitch = Math.max(-Math.PI / 2 + 0.1, Math.min(Math.PI / 2 - 0.1, this.cameraPitch));
            this.parent.transform.quaternion.setFromEuler(new THREE.Euler(0, this.cameraYaw, 0));
        }
    }

    updateAxes() {
        const rotation = new THREE.Euler(0, this.cameraYaw, 0, 'YXZ');
        const quaternion = new THREE.Quaternion().setFromEuler(rotation);
        this.forward.set(0, 0, 1).applyQuaternion(quaternion).normalize();
        this.forward.y = 0;
        this.forward.normalize();
        this.right.set(-1, 0, 0).applyQuaternion(quaternion).normalize();
        this.right.y = 0;
        this.right.normalize();
        this.up.set(0, 1, 0);
    }

    updateCameraPosition() {
        const dt = Math.min(this.game.deltaTime, 0.033);
        const smoothingAlpha = this.cameraSmoothing * dt * 60;

        const pitchQuat = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), this.cameraPitch);
        const yawQuat = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 1, 0), this.cameraYaw);
        const combinedQuat = new THREE.Quaternion().multiplyQuaternions(yawQuat, pitchQuat);

        if (this.isFirstPerson) {
            const eyePosition = this.parent.transform.position.clone().add(new THREE.Vector3(0, this.cameraHeight, 0));
            const forwardDir = new THREE.Vector3(0, 0, 1).applyQuaternion(combinedQuat);
            const forwardOffset = 0.2; // Small offset to avoid clipping
            eyePosition.add(forwardDir.multiplyScalar(forwardOffset));
            this.camera.position.copy(eyePosition);
            const yawOffsetQuat = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(0, 1, 0), Math.PI);
            const invertedPitchQuat = new THREE.Quaternion().setFromAxisAngle(new THREE.Vector3(1, 0, 0), -this.cameraPitch);
            const finalQuat = new THREE.Quaternion().multiplyQuaternions(yawQuat, yawOffsetQuat).multiply(invertedPitchQuat);
            this.camera.quaternion.copy(finalQuat);
        } else {
            const forwardDir = new THREE.Vector3(0, 0, 1).applyQuaternion(combinedQuat);
            const offsetDistance = this.thirdPersonDistance;
            const offsetHeight = this.thirdPersonHeight;
            const targetPosition = this.parent.transform.position.clone()
                .add(new THREE.Vector3(0, offsetHeight, 0))
                .sub(forwardDir.clone().multiplyScalar(offsetDistance));
            this.camera.position.lerp(targetPosition, smoothingAlpha);
            const lookAtPos = this.parent.transform.position.clone().add(new THREE.Vector3(0, this.cameraHeight, 0));
            this.cameraLookAt.lerp(lookAtPos, smoothingAlpha);
            this.camera.lookAt(this.cameraLookAt);
        }
    }

    update() {
        if (!this.controls.isLocked) return;
        const dt = Math.min(this.game.deltaTime, 0.1);

        // Check if grounded
        this.isGrounded = this.characterController.computedGrounded();

        // Process inputs
        if(this.isGrounded){
            // Update local axes
            this.updateAxes();
            this.moveForward = 0;
            this.moveRight = 0;
            if (this.keys.KeyW) this.moveForward += 1;
            if (this.keys.KeyS) this.moveForward -= 1;
            if (this.keys.KeyA) this.moveRight -= 1;
            if (this.keys.KeyD) this.moveRight += 1;
            this.isRunning = this.keys.ShiftLeft;
        }
        this.parent.getComponent("modelRenderer").isRunning = this.isRunning;

        // Compute desired movement
        const speed = this.isRunning ? this.runSpeed : this.walkSpeed;
        const moveDir = new THREE.Vector3();
        if (this.moveForward !== 0) moveDir.add(this.forward.clone().multiplyScalar(this.moveForward));
        if (this.moveRight !== 0) moveDir.add(this.right.clone().multiplyScalar(this.moveRight));
        moveDir.normalize();

        // Apply gravity and jumping
        this.velocity.y -= this.gravity * dt;
        if (this.jumpRequested && this.isGrounded) {
            this.velocity.y = this.jumpForce;
            this.jumpRequested = false;
            this.isGrounded = false;
        }

        // Compute desired translation
        const desiredTranslation = {
            x: moveDir.x * speed * dt,
            y: this.velocity.y * dt,
            z: moveDir.z * speed * dt
        };

        // Use Rapier character controller to compute corrected movement
        this.characterController.computeColliderMovement(this.collider, desiredTranslation);


        // Apply corrected movement
        const correctedMovement = this.characterController.computedMovement();
        const currentPos = this.rigidBody.translation();
        const newPos = {
            x: currentPos.x + correctedMovement.x,
            y: currentPos.y + correctedMovement.y,
            z: currentPos.z + correctedMovement.z
        };
        this.rigidBody.setNextKinematicTranslation(newPos);

        // Sync Three.js transform
        this.syncRapierToTransform();

        // Handle collision events (e.g., for sound effects or pushing objects)
        for (let i = 0; i < this.characterController.numComputedCollisions(); i++) {
            const collision = this.characterController.computedCollision(i);
            // Example: Log collision or trigger effects
            console.log('Collision with collider handle:', collision.handle);
        }

        // Reset vertical velocity if grounded
        if (this.isGrounded) {
            this.velocity.y = 0;
        }

        // Update camera
        this.updateCameraPosition();

        // Clean up debug visuals
        if (this.debug.showRaycasts) {
            this.debug.raycastHelpers.forEach(helper => {
                if (helper && this.scene.children.includes(helper)) {
                    this.scene.remove(helper);
                }
            });
            this.debug.raycastHelpers = [];
        }
    }

    onDestroy() {
        document.removeEventListener('keydown', this.onKeyDown);
        document.removeEventListener('keyup', this.onKeyUp);
        document.removeEventListener('mousemove', this.onMouseMove);

        // Clean up Rapier objects
        this.rapierWorld.removeCollider(this.collider);
        this.rapierWorld.removeRigidBody(this.rigidBody);
        this.rapierWorld.removeCharacterController(this.characterController);

        // Clean up debug helpers
        this.debug.raycastHelpers.forEach(helper => {
            if (helper && this.scene.children.includes(helper)) {
                this.scene.remove(helper);
            }
        });
    }
}

Transform.script:
class Transform extends engine.Component {
    init({x, y, z, scaleX, scaleY, scaleZ, rotationX, rotationY, rotationZ}) {

        this.position = new THREE.Vector3(x, y, z);
        this.velocity = new THREE.Vector3();
        this.physicsVelocity = new THREE.Vector3();
        this.lastPosition = new THREE.Vector3(x, y, z);
        this.physicsPosition = new THREE.Vector3(x, y, z);
        this.gridPosition = new THREE.Vector2(x, y);
        this.drawPosition = new THREE.Vector2(x, y);
        this.scale = new THREE.Vector3(scaleX || 1, scaleY || 1, scaleZ || 1);
        this.quaternion = new THREE.Quaternion();
        this.quaternion.setFromAxisAngle( new THREE.Vector3( rotationX, rotationY, rotationZ ), Math.PI / 2 );        
        this.parent.transform = this;         

    }
  

    postUpdate() {
        
        // Update grid and draw positions
        this.lastPosition.x = this.position.x;
        this.lastPosition.y = this.position.y;
        this.lastPosition.z = this.position.z;

    }
    
}

Animator.script:
class Animator extends engine.Component {


    init({ objectType, spawnType}) {
        if(!this.animations) this.animations = {};
        this.currentAnimation = "idle";
        this.currentFrame = 0;
        this.frameDuration = .166; // 10 frames per animation frame (~0.166s at 60 FPS)
        this.frameTimer = 0;

        this.baseSpeed = this.parent.getComponent("stats")?.stats.speed || 1;
    }

    update() {

        this.frameTimer += this.game.deltaTime;
        let currentSpeedPercent = this.parent.getComponent("stats")?.stats.speed / this.baseSpeed || 1;

        if (this.frameTimer >= this.frameDuration / currentSpeedPercent && this.animations[this.currentAnimation]) {
            this.frameTimer = 0;
            const animFrames = this.animations[this.currentAnimation];
            this.currentFrame = (this.currentFrame + 1) % animFrames.length;
        }
        // Sync direction with Renderer (if separate)
        const renderer = this.parent.getComponent("Renderer");
        if (renderer && this.animations[this.currentAnimation] && this.animations[this.currentAnimation][this.currentFrame]) {
            renderer.images = this.animations[this.currentAnimation][this.currentFrame];
        }
    }

    setAnimation(animationType) {
        if (this.animations[animationType] && this.currentAnimation !== animationType) {
            this.currentAnimation = animationType;
            this.currentFrame = 0;
            this.frameTimer = 0;
        }
    }

}

ArrayTracker.script:
class ArrayTracker extends engine.Component {
    
    init( {objectType}) {
        this.arr = objectType;
        if(!this.game.state[this.arr]){
            this.game.state[this.arr] = [];
        }
        this.game.state[this.arr].push(this.parent);
    }

    destroy(){
        let index = this.game.state[this.arr].indexOf(this.parent);
        this.game.state[this.arr].splice(index, 1);        
    }
}

AudioManager.script:
class AudioManager extends engine.Component {

    init() {
        this.isInitialized = false;
        this.bindInitialization();
    }

    bindInitialization() {
        const initHandler = () => {            
            if (!this.isInitialized) {
                this.initialize();
            }
        };

        document.addEventListener('click', initHandler, { once: true });
        document.addEventListener('keydown', initHandler, { once: true });
    }

    async initialize() {
        class SoundPool {
            constructor(audioContext, destination, poolSize = 8) {
                this.audioContext = audioContext;
                this.destination = destination;
                this.poolSize = poolSize;
                this.sources = [];
                this.initializePool();
            }

            initializePool() {
                for (let i = 0; i < this.poolSize; i++) {
                    this.sources.push(this.createSource());
                }
            }

            createSource() {
                const source = {
                    active: false,
                    id: null,
                    envelopeGain: this.audioContext.createGain(),
                    gainNode: this.audioContext.createGain(),
                    filter: this.audioContext.createBiquadFilter(),
                    distortion: this.audioContext.createWaveShaper(),
                    compressor: this.audioContext.createDynamicsCompressor(),
                    pannerNode: this.audioContext.createStereoPanner(),
                    delay: this.audioContext.createDelay(5.0),
                    delayGain: this.audioContext.createGain(),
                    convolver: this.audioContext.createConvolver(),
                    reverbGain: this.audioContext.createGain(),
                    noiseFilter: this.audioContext.createBiquadFilter(),
                    noiseGain: this.audioContext.createGain(),
                    destination: this.destination
                };

                source.filter.type = 'lowpass';
                source.filter.frequency.setValueAtTime(20000, this.audioContext.currentTime);
                source.filter.Q.setValueAtTime(0.5, this.audioContext.currentTime);

                source.noiseFilter.type = 'lowpass';
                source.noiseFilter.frequency.setValueAtTime(2000, this.audioContext.currentTime);
                source.noiseFilter.Q.setValueAtTime(1, this.audioContext.currentTime);

                source.noiseGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                source.distortion.curve = null;
                source.distortion.oversample = '4x';

                source.compressor.threshold.setValueAtTime(-24, this.audioContext.currentTime);
                source.compressor.knee.setValueAtTime(30, this.audioContext.currentTime);
                source.compressor.ratio.setValueAtTime(12, this.audioContext.currentTime);
                source.compressor.attack.setValueAtTime(0.003, this.audioContext.currentTime);
                source.compressor.release.setValueAtTime(0.25, this.audioContext.currentTime);

                source.pannerNode.pan.setValueAtTime(0, this.audioContext.currentTime);

                source.delay.delayTime.setValueAtTime(0.3, this.audioContext.currentTime);
                source.delayGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                source.reverbGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                const chain = [
                    source.envelopeGain,
                    source.gainNode,
                    source.filter,
                    source.distortion,
                    source.compressor,
                    source.pannerNode
                ];

                for (let i = 0; i < chain.length - 1; i++) {
                    chain[i].connect(chain[i + 1]);
                }

                // Route delay and reverb after filter
                source.filter.connect(source.delay);
                source.delay.connect(source.delayGain);
                source.delayGain.connect(source.delay);
                source.delayGain.connect(source.pannerNode);

                source.filter.connect(source.convolver);
                source.convolver.connect(source.reverbGain);
                source.reverbGain.connect(source.pannerNode);

                source.pannerNode.connect(source.destination);

                return source;
            }

            getSource() {
                let source = this.sources.find(src => !src.active && (!src.lastUsed || this.audioContext.currentTime - src.lastUsed > 1));
                if (!source && this.sources.length < this.poolSize * 2) {
                    source = this.createSource();
                    this.sources.push(source);
                }
                if (source) {
                    source.active = true;
                    source.gainNode.gain.setValueAtTime(1, this.audioContext.currentTime);
                    source.envelopeGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.delayGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.reverbGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.noiseGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                }
                return source;
            }

            releaseSource(source) {
                if (source) {
                    source.active = false;
                    source.id = null;
                    const now = this.audioContext.currentTime;

                    // Reset all nodes
                    source.filter.frequency.cancelScheduledValues(now);
                    source.filter.frequency.setValueAtTime(20000, now);
                    source.distortion.curve = null;
                    source.pannerNode.pan.setValueAtTime(0, now);
                    source.envelopeGain.gain.setValueAtTime(0, now);
                    source.gainNode.gain.setValueAtTime(1, now);
                    source.delayGain.gain.setValueAtTime(0, now);
                    source.reverbGain.gain.setValueAtTime(0, now);
                    source.noiseGain.gain.setValueAtTime(0, now);

                    // Disconnect and clear sources if they exist
                    if (source.source) {
                        try {
                            source.source.stop(now);
                            source.source.disconnect();
                        } catch (e) {
                            console.warn('Error stopping source:', e);
                        }
                        source.source = null;
                    }

                    if (source.noiseSource) {
                        try {
                            source.noiseSource.stop(now);
                            source.noiseSource.disconnect();
                        } catch (e) {
                            console.warn('Error stopping noise source:', e);
                        }
                        source.noiseSource = null;
                    }

                    source.lastUsed = now;
                }
            }

            destroy() {
                this.sources.forEach(source => {
                    source.envelopeGain.disconnect();
                    source.gainNode.disconnect();
                    source.filter.disconnect();
                    source.distortion.disconnect();
                    source.compressor.disconnect();
                    source.pannerNode.disconnect();
                    source.delay.disconnect();
                    source.delayGain.disconnect();
                    source.convolver.disconnect();
                    source.reverbGain.disconnect();
                    source.noiseFilter.disconnect();
                    source.noiseGain.disconnect();
                });
                this.sources = [];
            }
        }
        this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
        this.masterBus = this.createAudioBus('master');
        this.musicBus = this.createAudioBus('music');
        this.sfxBus = this.createAudioBus('sfx');
        this.uiBus = this.createAudioBus('ui');
        this.musicBus.connect(this.masterBus);
        this.sfxBus.connect(this.masterBus);
        this.uiBus.connect(this.masterBus);
        this.masterBus.connect(this.audioContext.destination);
        this.soundPools = {
            sfx: new SoundPool(this.audioContext, this.sfxBus.input, 6),
            music: new SoundPool(this.audioContext, this.musicBus.input, 2),
            ui: new SoundPool(this.audioContext, this.uiBus.input, 3)
        };
        this.buffers = {};
        this.activeSounds = new Set();
        if (this.audioContext.state === 'suspended') {
            await this.audioContext.resume();
        }
        this.isInitialized = true;
    }

    createAudioBus(name) {
        const bus = {
            name,
            input: this.audioContext.createGain(),
            compressor: this.audioContext.createDynamicsCompressor(),
            output: this.audioContext.createGain()
        };

        bus.input.connect(bus.compressor);
        bus.compressor.connect(bus.output);

        bus.connect = (destination) => {
            bus.output.connect(destination.input || destination);
        };

        bus.setVolume = (value) => {
            const now = this.audioContext.currentTime;
            bus.output.gain.cancelScheduledValues(now);
            bus.output.gain.setTargetAtTime(
                value <= 0 ? 0.000001 : value,
                now,
                0.02
            );
        };

        return bus;
    }

    getSynthSound(soundCollectionName, soundName) {
        if (this.game.getCollections()[soundCollectionName]?.[soundName]) {
            return this.game.getCollections()[soundCollectionName][soundName].audio;
        }
        console.warn('sound not found', soundCollectionName, soundName);
        return null;
    }

    playSound(soundCollectionName, soundName) {
        let data = this.getSynthSound(soundCollectionName, soundName);
        if (data) {
            this.playSynthSound(`${soundCollectionName}_${soundName}`, data);
        }
    }

    playSynthSound(soundId, soundConfig, options = {}) {
        if (!this.isInitialized) {
            this.initialize();
            return;
        }        
        if (this.audioContext.state === 'suspended') {
            return;
        }
        const category = options.category || 'sfx';
        const pool = this.soundPools[category];

        if (!pool) {
            console.warn(`No sound pool for category ${category}`);
            return null;
        }

        let instanceCount = 0;
        this.activeSounds.forEach(sound => {
            if (sound.id === soundId) instanceCount++;
        });

        if (instanceCount >= (options.maxInstances || 3)) {
            console.warn(`Max instances reached for sound ${soundId}`);
            return null;
        }

        const sound = pool.getSource();
        if (!sound) {
            console.warn(`No available source for sound ${soundId}`);
            return null;
        }

        sound.id = soundId;
        sound.category = category;

        // Create main oscillator
        const oscillator = this.createSynthSource(soundConfig);
        sound.source = oscillator;
        oscillator.connect(sound.envelopeGain);

        // Add noise generator if configured
        if (soundConfig.noise && soundConfig.noise.amount > 0) {
            this.applyNoiseToSound(sound, soundConfig.noise);
        }

        this.createEnvelopeFromConfig(soundConfig.envelope, sound.envelopeGain, soundConfig.duration);

        if (soundConfig.effects) {
            this.applySynthEffects(sound, soundConfig.effects, soundConfig);
        } 

        this.configureSoundInstance(sound, options);

        const now = this.audioContext.currentTime;
        oscillator.start(now);

        // Start noise generator if it exists
        if (sound.noiseSource) {
            sound.noiseSource.start(now);
        }

        const duration = soundConfig.duration || 1;
        const release = (soundConfig.envelope?.release) || 0.3;
        let totalDuration = duration + release;

        if (soundConfig.effects?.delay?.time) {
            const delayTail = soundConfig.effects.delay.time * 3;
            if (totalDuration < delayTail) {
                totalDuration = delayTail;
            }
        }

        oscillator.stop(now + totalDuration + 0.02);
        if (sound.noiseSource) {
            sound.noiseSource.stop(now + totalDuration + 0.02);
        }

        this.activeSounds.add(sound);

        oscillator.onended = () => {
            setTimeout(() => {
                sound.active = false;
                this.activeSounds.delete(sound);
                pool.releaseSource(sound);
            }, (soundConfig.effects?.delay?.time || 0) * 1000 * 3);
        };

        return sound;
    }

    createSynthSource(config) {
        // Default to oscillator unless waveform is 'noise'
        const oscillator = config.waveform === 'noise'
            ? this.createNoiseSource('white') 
            : this.audioContext.createOscillator();

        if (config.waveform !== 'noise') {
            oscillator.type = config.waveform || 'sine';
            const baseFreq = config.frequency || 440;
            const now = this.audioContext.currentTime;

            // Initialize frequency
            oscillator.frequency.cancelScheduledValues(now);
            oscillator.frequency.setValueAtTime(baseFreq, now);

            // Apply pitch envelope if defined
            if (config.pitchEnvelope) {
                const startMultiplier = config.pitchEnvelope.start ?? 1;
                const endMultiplier = config.pitchEnvelope.end ?? 1;
                const envelopeTime = config.pitchEnvelope.time ?? config.duration ?? 1;

                if (startMultiplier !== 1 || endMultiplier !== 1) {
                    const startFreq = baseFreq * startMultiplier;
                    const endFreq = baseFreq * endMultiplier;

                    oscillator.frequency.setValueAtTime(startFreq, now);
                    oscillator.frequency.exponentialRampToValueAtTime(
                        Math.max(endFreq, 0.01),
                        now + envelopeTime
                    );
                }
            }
        }

        return oscillator;
    }

    applyNoiseToSound(sound, noiseConfig) {
        if (!noiseConfig || noiseConfig.amount <= 0) return;

        const noiseType = noiseConfig.type || 'white';
        const noiseAmount = Math.min(1, Math.max(0, noiseConfig.amount));

        // Create the noise source
        sound.noiseSource = this.createNoiseSource(noiseType);

        // Configure noise filter if specified
        if (noiseConfig.filter && noiseConfig.filter.type !== 'none') {
            sound.noiseFilter.type = noiseConfig.filter.type || 'lowpass';
            sound.noiseFilter.frequency.setValueAtTime(
                noiseConfig.filter.frequency || 2000, 
                this.audioContext.currentTime
            );
        }

        // Set noise gain based on the amount
        sound.noiseGain.gain.setValueAtTime(noiseAmount, this.audioContext.currentTime);

        // Connect noise through the filter and envelope
        sound.noiseSource.connect(sound.noiseFilter);
        sound.noiseFilter.connect(sound.noiseGain);
        sound.noiseGain.connect(sound.envelopeGain);
    }

    createNoiseSource(noiseType = 'white') {
        const bufferSize = this.audioContext.sampleRate * 2;
        const noiseBuffer = this.audioContext.createBuffer(1, bufferSize, this.audioContext.sampleRate);
        const output = noiseBuffer.getChannelData(0);

        // Generate different types of noise
        switch (noiseType) {
            case 'pink':
                this.generatePinkNoise(output);
                break;
            case 'brown':
                this.generateBrownNoise(output);
                break;
            case 'white':
            default:
                this.generateWhiteNoise(output);
                break;
        }

        const source = this.audioContext.createBufferSource();
        source.buffer = noiseBuffer;
        source.loop = true;

        return source;
    }

    generateWhiteNoise(output) {
        for (let i = 0; i < output.length; i++) {
            output[i] = Math.random() * 2 - 1;
        }
    }

    generatePinkNoise(output) {
        // Pink noise algorithm (approximation using Paul Kellet's method)
        let b0 = 0, b1 = 0, b2 = 0, b3 = 0, b4 = 0, b5 = 0, b6 = 0;

        for (let i = 0; i < output.length; i++) {
            const white = Math.random() * 2 - 1;

            // Filter white noise to create pink noise
            b0 = 0.99886 * b0 + white * 0.0555179;
            b1 = 0.99332 * b1 + white * 0.0750759;
            b2 = 0.96900 * b2 + white * 0.1538520;
            b3 = 0.86650 * b3 + white * 0.3104856;
            b4 = 0.55000 * b4 + white * 0.5329522;
            b5 = -0.7616 * b5 - white * 0.0168980;

            output[i] = (b0 + b1 + b2 + b3 + b4 + b5 + b6 + white * 0.5362) * 0.11;
            b6 = white * 0.115926;
        }
    }

    generateBrownNoise(output) {
        // Brown noise algorithm (random walk)
        let lastValue = 0;

        for (let i = 0; i < output.length; i++) {
            // Small random change from previous value
            const white = Math.random() * 2 - 1;
            lastValue = (lastValue + (0.02 * white)) / 1.02;

            // Keep within range
            output[i] = lastValue * 3.5; // Amplify to make it audible
        }

        // Normalize to prevent clipping
        const max = Math.max(...Array.from(output).map(Math.abs));
        if (max > 0) {
            for (let i = 0; i < output.length; i++) {
                output[i] /= max;
            }
        }
    }

    createEnvelopeFromConfig(envelope, gainNode, duration = 1) {
        if (!envelope) {
            console.warn('No envelope provided, using default');
            envelope = { attack: 0.01, decay: 0.1, sustain: 0.7, release: 0.3 };
        }

        const now = this.audioContext.currentTime;

        const attack = Math.max(0.01, envelope.attack || 0.01);
        const decay = Math.max(0, envelope.decay || 0.1);
        const sustain = Math.max(0, Math.min(1, envelope.sustain || 0.7));
        const release = Math.max(0.02, envelope.release || 0.3);

        gainNode.gain.cancelScheduledValues(now);
        gainNode.gain.setValueAtTime(0, now);
        gainNode.gain.linearRampToValueAtTime(1, now + attack);
        gainNode.gain.linearRampToValueAtTime(sustain, now + attack + decay);
        gainNode.gain.setValueAtTime(sustain, now + duration);
        gainNode.gain.setTargetAtTime(0.0001, now + duration, release / 3);
        gainNode.gain.setValueAtTime(0, now + duration + release); // Absolute zero
    }

    applySynthEffects(sound, effectsConfig, soundConfig) {
        const now = this.audioContext.currentTime;

        // Filter
        if (effectsConfig.filter) {
            sound.filter.type = effectsConfig.filter.type || 'lowpass';
            sound.filter.frequency.setValueAtTime(effectsConfig.filter.frequency || 20000, now);
            sound.filter.Q.setValueAtTime(effectsConfig.filter.Q || 0.5, now);

            // Apply pitch envelope to filter for noise waveforms
            if (sound.source.buffer && soundConfig.pitchEnvelope && (soundConfig.pitchEnvelope.start !== 1 || soundConfig.pitchEnvelope.end !== 1)) {
                const baseFreq = soundConfig.frequency || 100;
                const startFreq = baseFreq * (soundConfig.pitchEnvelope.start + 1);
                const endFreq = baseFreq * soundConfig.pitchEnvelope.end;
                const duration = soundConfig.pitchEnvelope.time || soundConfig.duration || 1;

                sound.filter.frequency.cancelScheduledValues(now);
                sound.filter.frequency.setValueAtTime(startFreq, now);
                sound.filter.frequency.exponentialRampToValueAtTime(
                    Math.max(endFreq, 0.01),
                    now + duration
                );
                sound.filter.frequency.exponentialRampToValueAtTime(
                    Math.max(endFreq * 0.5, 0.01),
                    now + soundConfig.duration + (soundConfig.envelope?.release || 0.3)
                );
            }
        }

        // Distortion
        if (effectsConfig.distortion && effectsConfig.distortion > 0) {
            sound.distortion.curve = this.makeDistortionCurve(effectsConfig.distortion);
        } else {
            sound.distortion.curve = null;
        }

        // Delay
        if (effectsConfig.delay && effectsConfig.delay.feedback > 0) {
            sound.delay.delayTime.setValueAtTime(effectsConfig.delay.time || 0.3, now);
            sound.delayGain.gain.setValueAtTime(Math.min(effectsConfig.delay.feedback || 0, 0.8), now);
            sound.delayGain.gain.setTargetAtTime(0, now + soundConfig.duration + (soundConfig.envelope?.release || 0.3), 0.1);
        } else {
            sound.delayGain.gain.setValueAtTime(0, now);
        }

        // Reverb
        if (effectsConfig.reverb && effectsConfig.reverb > 0) {
            this.generateImpulseResponse(sound.convolver);
            sound.reverbGain.gain.setValueAtTime(Math.min(effectsConfig.reverb, 1), now);
            sound.reverbGain.gain.setTargetAtTime(0, now + soundConfig.duration + (soundConfig.envelope?.release || 0.3), 0.2);
        } else {
            sound.reverbGain.gain.setValueAtTime(0, now);
        }

        // Panning
        if (effectsConfig.pan !== undefined) {
            sound.pannerNode.pan.setValueAtTime(Math.max(-1, Math.min(1, effectsConfig.pan)), now);
        }
    }

    configureSoundInstance(sound, options) {
        const now = this.audioContext.currentTime;

        if (options.volume !== undefined) {
            sound.gainNode.gain.cancelScheduledValues(now);
            sound.gainNode.gain.setTargetAtTime(
                options.volume <= 0 ? 0.000001 : Math.max(0, Math.min(options.volume, 1)),
                now,
                0
            );
        } else {
            sound.gainNode.gain.cancelScheduledValues(now);
            sound.gainNode.gain.setTargetAtTime(
                0.005,
                now,
                0
            );
        }
        if (options.position && sound.pannerNode.positionX) {
            const pos = options.position;
            sound.pannerNode.positionX.setValueAtTime(pos.x || 0, now);
            sound.pannerNode.positionY.setValueAtTime(pos.y || 0, now);
            sound.pannerNode.positionZ.setValueAtTime(pos.z || 0, now);
        }

        if (options.pitch && sound.source && sound.source.playbackRate) {
            sound.source.playbackRate.setValueAtTime(options.pitch, now);
        }
    }

    generateImpulseResponse(convolver) {
        const length = this.audioContext.sampleRate * 1.5; // Shorter impulse
        const impulse = this.audioContext.createBuffer(2, length, this.audioContext.sampleRate);

        for (let channel = 0; channel < 2; channel++) {
            const impulseData = impulse.getChannelData(channel);
            for (let i = 0; i < length; i++) {
                const decay = Math.pow(1 - i / length, 2);
                impulseData[i] = (Math.random() * 2 - 1) * decay;
            }
        }

        convolver.buffer = impulse;
    }

    makeDistortionCurve(amount) {
        const k = Math.max(amount, 0) * 10;
        const n_samples = 44100;
        const curve = new Float32Array(n_samples);
        const deg = Math.PI / 180;
        for (let i = 0; i < n_samples; i++) {
            const x = (i * 2) / n_samples - 1;
            curve[i] = (3 + k) * x * 20 * deg / (Math.PI + k * Math.abs(x));
        }
        return curve;
    }

    stopSound(sound) {
        if (!sound) return;

        const now = this.audioContext.currentTime;

        if (sound.gainNode) {
            sound.gainNode.gain.cancelScheduledValues(now);
            sound.gainNode.gain.setValueAtTime(sound.gainNode.gain.value || 0, now);
            sound.gainNode.gain.linearRampToValueAtTime(0, now + 0.05);
        }

        if (sound.source) {
            try {
                sound.source.stop(now + 0.06);
            } catch (e) {
                console.warn('Error stopping source:', e);
            }
        }

        if (sound.noiseSource) {
            try {
                sound.noiseSource.stop(now + 0.06);
            } catch (e) {
                console.warn('Error stopping noise source:', e);
            }
        }

        sound.active = false;
        this.activeSounds.delete(sound);
    }

    stopAllSounds() {
        this.activeSounds.forEach(sound => {
            this.stopSound(sound);
        });
        this.activeSounds.clear();
    }
}

Attacker.script:
class Attacker extends engine.Component {
    
 init() {        
        this.stats = this.getComponent('stats').stats;    
        this.level = 1;
        this.target = null;
        this.projectiles = [];  
        this.cooldown = 0;
    }

    update() {
        if (this.cooldown > 0) this.cooldown -= this.game.deltaTime;

        // Validate current target
        if (this.target) {
            const distance = Math.hypot(
                this.target.transform.position.x - this.parent.transform.position.x,
                this.target.transform.position.y - this.parent.transform.position.y
            );
            if (distance > this.stats.range || this.target.getComponent('health').hp <= 0) {
                this.target = null;
            }
        }

        // Find target if none
        if (!this.target) {
            this.findTarget();
        }

        // Attack if ready and has target
        if (this.cooldown <= 0 && this.target) {
            this.attack();
        }

        if (this.cooldown <= 0 && this.stats.mineAmt > 0) {
            this.gather();
        }
        return true;
    }
    
    findTarget() {
        this.target = null;
        let furthestEnemy = null;
        let furthestDistance = -1;
        const nearbyEntities = this.game.spatialGrid.getNearbyEntities(
            this.parent.transform.gridPosition.x, 
            this.parent.transform.gridPosition.y, 
            this.stats.range,
            "enemy"
        );
        for (let enemy of nearbyEntities) {
            let enemyHP = enemy.getComponent("health").hp;
            let followPath = enemy.getComponent('followPath');
            if (enemyHP <= 0) continue;          

            let distanceToEnd = this.game.state.paths[followPath.pathIndex].length - followPath.indexInPath;
            if (distanceToEnd > furthestDistance) {
                furthestDistance = followPath.indexInPath;
                furthestEnemy = enemy;
            }  
        }
        
        this.target = furthestEnemy;
    }

    gather() {
        this.game.state.bloodShards += this.stats.mineAmt;
        this.cooldown = this.stats.attackSpeed;
    }

    attack() {
        if (!this.target) return; 
        this.launchProjectile();
        this.cooldown = this.stats.attackSpeed;
    }
    
    launchProjectile() {
        this.stats = this.getComponent('stats').stats;    
        let projectileType = this.stats.projectile;
        let projectileDef = this.game.getCollections().projectilePrefabs[projectileType];
        

        let projStats = { ...projectileDef };
        delete projStats.render;
        projStats.baseDamage = this.stats.damage || 1; 
        projStats.speed = this.stats.speed || 5;     
        projStats.piercing = this.stats.piercing || 0;
        projStats.splashRadius = this.stats.splashRadius || 0;
        projStats.critChance = this.stats.critChance || 0.05;
        projStats.critMultiplier = this.stats.critMultiplier || 2;
    
       	if(projectileDef.customRenderer == "lightning") {
         	this.game.spawn('lightningProjectile', { objectType: "projectilePrefabs", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else if(projectileDef.isBallistic) {
         	this.game.spawn('ballisticProjectile', { objectType: "projectilePrefabs", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else if( this.stats.projectileCount > 0 ) {
          this.game.spawn('multiShotProjectile', { objectType: "projectilePrefabs", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else {
          this.game.spawn('projectile', { objectType: "projectilePrefabs", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        }
    }
}

BallisticProjectile.script:
class BallisticProjectile extends engine.Component {
    
    
init({ spawnType, owner, target, stats }) {
    this.type = spawnType;
    this.def = this.game.getCollections().projectilePrefabs[this.type];
    this.owner = owner;
    this.target = target;
    this.stats = stats;
    this.piercedEnemies = [];
    this.ownerStats = this.owner.getComponent("stats").stats;
    this.distanceTraveled = 0;
    this.distanceToSpawnParticle = 1;
    
    // Ballistic trajectory variables
    this.startPosition = { ...this.parent.transform.position, z: 10 }; // Start 10 units above ground
    this.targetPosition = { ...this.target.transform.position, z: 0 };
    this.time = 0;
    
    this.totalDist = Math.sqrt(
        (this.targetPosition.x - this.startPosition.x) ** 2 + 
        (this.targetPosition.y - this.startPosition.y) ** 2
    );
    this.maxHeight = this.totalDist / Math.PI;
    
    // Initialize position with Z component
    this.parent.transform.position.z = this.startPosition.z;
    this.positionZ = this.startPosition.z;
    
    // Animation state variables
    this.lastZPosition = this.positionZ;

    
    this.peakThreshold = this.maxHeight * 0.1; // 10% threshold for idle at peak
    this.currentAnimState = 'ascend';
    if(this.stats.attackSound){
        this.game.audioManager.playSound('attackSounds', this.stats.attackSound);
    }
}

setAnimation(anim) {

    if(this.game.getCollections().configs.game.is3D) {
        this.modelRenderer = this.parent.getComponent('modelRenderer');
        this.modelRenderer?.setAnimation(anim);
    } else {
        this.animator = this.parent.getComponent('animator');
        this.animator?.setAnimation(anim);
    }
}

update() {
    this.parent.transform.position.z = this.positionZ;
    
    // Save previous z position to detect direction change
    this.lastZPosition = this.positionZ;

    // Calculate progress (0 to 1)
    this.time += this.game.deltaTime;
    const dx = this.targetPosition.x - this.parent.transform.position.x;
    const dy = this.targetPosition.y - this.parent.transform.position.y;
    const distSq = dx * dx + dy * dy;
    let dist = Math.sqrt(distSq);
    const speed = this.stats.speed;
    this.parent.transform.position.x += (dx / dist) * speed / (Math.PI);
    this.parent.transform.position.y += (dy / dist) * speed / (Math.PI);


    const currentDist = Math.sqrt(
        (this.parent.transform.position.x - this.startPosition.x) ** 2 + 
        (this.parent.transform.position.y - this.startPosition.y) ** 2
    );
    const xyprogressToTarget = Math.min(1, currentDist / this.totalDist);

    // Parabolic trajectory calculation (2:1 isometric adjusted)
    this.parent.transform.position.z = this.maxHeight * (1 - Math.pow(2 * xyprogressToTarget - 1, 2));

    this.positionZ = this.parent.transform.position.z;
    
    // Calculate distance from peak height to determine if we're near the top
    const distanceFromPeak = Math.abs(this.positionZ - this.maxHeight);
    
    // Check if animation state needs to change based on z movement and proximity to peak
    if( xyprogressToTarget < .075 ) {
        if (this.currentAnimState !== 'launch') {
            this.setAnimation('launch');
            this.currentAnimState = 'launch';
        }
    // } else if( xyprogressToTarget > .95 ) {
    //     if (this.currentAnimState !== 'land') {
    //         this.animator.setAnimation('land');
    //         this.currentAnimState = 'land';
    //     }
    } else if (distanceFromPeak <= this.peakThreshold) {
        // We're near the peak of the trajectory
        if (this.currentAnimState !== 'idle') {
            this.setAnimation('idle');
            this.currentAnimState = 'idle';
        }
    } else if (this.positionZ < this.lastZPosition) {
        // We're descending and not near the peak
        if (this.currentAnimState !== 'descend') {
            this.setAnimation('descend');
            this.currentAnimState = 'descend';
        }
    } else if (this.positionZ > this.lastZPosition) {
        // We're ascending and not near the peak
        if (this.currentAnimState !== 'ascend') {
            this.setAnimation('ascend');
            this.currentAnimState = 'ascend';
        }
    }
    
    // Check if we've hit the ground (Z <= 0)
    if (this.parent.transform.position.z <= 0) {
        this.parent.transform.position.z = 0; // Snap to ground

        // Hit detection - same as before but at current position
        const targetDistSq = (this.parent.transform.position.x - this.target.transform.position.x) ** 2 + 
                            (this.parent.transform.position.y - this.target.transform.position.y) ** 2;



            // We missed the target but hit the ground - maybe still do splash damage
        if (this.stats.splashRadius > 0) {
            this.processSplashDamage();
        }
        this.parent.destroy();
        if(this.stats.hitSound){
            this.game.audioManager.playSound('hitSounds', this.stats.hitSound);
        }

        return;
    }
    
    // Update distance traveled for particles
    const tDx = this.parent.lastPosition.x - this.parent.transform.position.x;
    const tDy = this.parent.lastPosition.y - this.parent.transform.position.y;
    const tDist = Math.sqrt(tDx * tDx + tDy * tDy);
    this.distanceTraveled += tDist;
    
    if (this.def.particle && this.distanceTraveled > this.distanceToSpawnParticle) {
        let position = new THREE.Vector3(this.parent.lastPosition.x + Math.random() * 4 - 2, this.parent.lastPosition.y + Math.random() * 4 - 2, this.parent.transform.position.z + Math.random() * 4 - 2)
        this.game.spawn("particle", { objectType: "particlePrefabs", spawnType: this.def.particle }, position);

        this.distanceTraveled = 0;
        this.distanceToSpawnParticle += Math.random() * 2;
    }
}


processSplashDamage() {
    const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
        this.parent.transform.gridPosition.x, 
        this.parent.transform.gridPosition.y, 
        this.stats.splashRadius,
        "enemy"
    );
    this.game.spawn("explosion", { radius: this.stats.splashRadius },this.parent.transform.position);
    for (const enemy of nearbyEnemies) {
        if (enemy.isDead) continue;
        
        const dx = enemy.transform.position.x - this.parent.transform.position.x;
        const dy = enemy.transform.position.y - this.parent.transform.position.y;
        const distSq = dx * dx + dy * dy;
        
        let gridSize = this.game.getCollections().configs.game.gridSize;
        const splashRadiusSq = this.stats.splashRadius * this.stats.splashRadius * gridSize * gridSize;
        
        if (distSq <= splashRadiusSq) {
            let enemyHealth = enemy.getComponent("health");
            let enemyEnergyShield = enemy.getComponent("energyshield");
            let enemyStats = enemy.getComponent("stats");
            let enemyStatClone = { ...enemyStats.stats };
            enemyStatClone.energyShield = enemyEnergyShield.energyShield;
            
            let damageResult = engine.getFunction("calculateDamage")(this.stats, enemyStatClone);
            if (!damageResult.wasEvaded) {
                enemyHealth.hp -= damageResult.damageDealt;
                enemyEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                this.game.spawn("hitEffect", { damageType: this.stats.damageType , lifeSpan: .3},enemy.transform.position);
                if (this.ownerStats.slowAmount) {
                    enemyStats.addEffect(this.game.getCollections().effects.slow, this.game.effects.slow, this.ownerStats.slowAmount);
                }
            }
        }
    }
}
}

ChainProjectile.script:
class ChainProjectile extends engine.Component {
    
    init( { spawnType, owner, target, stats }) {        
        this.type = spawnType;
        this.owner = owner;
        this.target = target;
        this.stats = stats;
        this.piercedEnemies = [];
        this.ownerStats = this.owner.getComponent("stats").stats;
        this.chainTargets = []; // Store all targets hit in one frame
        this.hasStruck = false; // Flag to strike only once
    }

    update() {
        if (this.hasStruck) {
            this.parent.destroy(); // Destroy after one frame of striking
            return;
        }

        if (!this.target || this.target.destroyed) {
            this.parent.destroy();
            return;
        }

        // Strike the initial target
        let targetHealth = this.target.getComponent("health");
        let targetEnergyShield = this.target.getComponent("energyshield");
        let targetStats = {...this.target.getComponent("stats").stats};
        targetStats.energyShield = targetEnergyShield.energyShield;
        let damageResult = engine.getFunction("calculateDamage")(this.stats, targetStats);                    
        if( damageResult.wasEvaded ) { return; }         
        targetHealth.hp -= damageResult.damageDealt;
        targetEnergyShield.absorbDamage(damageResult.damageAbsorbed);
        this.piercedEnemies.push(this.target);
        this.chainTargets.push(this.target);
        this.game.spawn("hitEffect", { damageType: this.stats.damageType, lifeSpan: 1},this.target.transform.position);
        // Chain to nearby enemies
        if (this.stats.piercing > 0 && this.piercedEnemies.length <= this.stats.piercing) {
            const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
                this.target.transform.gridPosition.x, 
                this.target.transform.gridPosition.y, 
                this.ownerStats.range,
                "enemy"
            );

            for (let enemy of nearbyEnemies) {
                if (enemy.destroyed || this.piercedEnemies.includes(enemy)) continue;
                const dx = enemy.transform.position.x - this.target.transform.position.x;
                const dy = enemy.transform.position.y - this.target.transform.position.y;
                const distSq = dx * dx + dy * dy;
                let gridSize = this.game.getCollections().configs.game.gridSize;
                if (distSq <= this.ownerStats.range * this.ownerStats.range * gridSize * gridSize) {
                    let enemyHealth = enemy.getComponent("health");
                    let enemyEnergyShield = enemy.getComponent("energyshield");
                    let enemyStats = {...enemy.getComponent("stats").stats};
                    enemyStats.energyShield = targetEnergyShield.energyShield;
                    let damageResult = engine.getFunction("calculateDamage")(this.stats, enemyStats); 
                    if(!damageResult.wasEvaded) {
                        enemyHealth.hp -= damageResult.damageDealt;
                        enemyEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                        this.piercedEnemies.push(enemy);
                        this.chainTargets.push(enemy);
                        this.game.spawn("hitEffect", { damageType: this.stats.damageType, lifeSpan: 1 },enemy.transform.position);
                        if (this.piercedEnemies.length > this.stats.piercing) break;
                    } else {
                        break;
                    }
                }
            }
        }

        // Apply additional effects (e.g., slow, leech) to all hit targets
        for (let enemy of this.chainTargets) {
            if (this.ownerStats.slowAmount) {
                enemy.getComponent("stats").addEffect(this.game.getCollections().effects.slow, this.game.effects.slow, this.ownerStats.slowAmount);
            }
            if (this.ownerStats.leech > 0) {
                const healing = this.stats.damage * this.ownerStats.leech * this.game.state.stats.healingMultiplier;
                this.game.state.bloodCoreHP = Math.min(this.game.state.stats.maxBloodCoreHP, this.game.state.bloodCoreHP + healing);
            }
            if (this.ownerStats.thief && this.ownerStats.thief != 0) {
                const stealAmt = this.stats.damage * this.ownerStats.thief * this.game.state.stats.bloodShardMultiplier;
                this.game.state.bloodShards += stealAmt;
            }
        }

        this.hasStruck = true; // Mark as struck, render lightning this frame
    }
}

