UpgradeManager.script:
class UpgradeManager extends engine.Component {
        

    init() {
        this.game.state.activeUpgrades = {};
    }
    
    update() {
       this.applyActiveUpgrades();
        // Level Up check
        if (this.game.state.essence >= this.game.state.essenceToNextLevel && !this.game.state.isLevelingUp) {
            this.showUpgradeMenu();
        }
    }

    applyActiveUpgrades() {
        engine.getFunction("calculateStats")(this.game.state.stats, this.game.state.activeUpgrades['global']);    
    }

    
    // Upgrade system
    showUpgradeMenu() {
        if (this.game.state.isLevelingUp) return; // Prevent re-triggering
        
        this.game.state.isLevelingUp = true;
        this.game.state.isPaused = true;
        
        this.game.uiManager.upgradeMenu.style.display = 'block';
        this.game.uiManager.overlay.style.display = 'block';
        this.game.uiManager.upgradeOptionsDiv.innerHTML = '';
        
        // Filter upgrades based on conditions
        const availableUpgrades = this.game.upgrades.filter(upgrade => upgrade.canApply(this.game.state));
        
        // Choose 3 random upgrades
        const options = [];
        while (options.length < 3 && availableUpgrades.length > 0) {
            const index = Math.floor(Math.random() * availableUpgrades.length);
            options.push(availableUpgrades[index]);
            availableUpgrades.splice(index, 1);
        }
        
        // Create upgrade options
        options.forEach(upgrade => {
            const div = document.createElement('div');
            div.className = 'upgrade-option';
            div.innerHTML = `
                <div class="upgrade-icon">${upgrade.icon}</div>
                <div class="upgrade-desc">
                    <div class="upgrade-title">${upgrade.title}</div>
                    ${upgrade.desc}
                </div>
            `;
            div.onclick = () => this.selectUpgrade(upgrade);
            this.game.uiManager.upgradeOptionsDiv.appendChild(div);
        });
    }

    selectUpgrade(upgrade) {       
        // Add to active upgrades list if not already
        if (!this.game.state.activeUpgrades[upgrade.appliesTo]) {
            this.game.state.activeUpgrades[upgrade.appliesTo] = [upgrade];
        } else {
            this.game.state.activeUpgrades[upgrade.appliesTo].push(upgrade);
        }

        this.applyActiveUpgrades();
        if(upgrade.onAcquire) {
            upgrade.onAcquire(this.game.state);
        }
        
        upgradeMenu.style.display = 'none';
        overlay.style.display = 'none';
        
        this.game.state.essence -= this.game.state.essenceToNextLevel;
        this.game.state.level++;
        this.game.state.essenceToNextLevel = Math.floor(this.game.state.essenceToNextLevel * 1.4);        
        
        this.game.state.isLevelingUp = false;
        this.game.state.isPaused = false;
    }
}

WaveManager.script:
class WaveManager extends engine.Component {
    
    init() {   
        
        this.resetWaveState();
    }

    resetWaveState() {
        this.game.state.waveSets = this.game.getCollections().levels[this.game.state.level].wavesets;
        this.game.state.currentWaveIds = [];
        this.game.state.currentWaveEnemies = [];
        this.game.state.enemiesSpawned = [];
        this.game.state.spawnTimers = []; // Array for individual spawn timers
        this.game.state.spawnRate = 1;    // Base spawn rate (can be modified per waveset if needed)
        this.game.state.waveTimer = 0;
        this.game.state.startDelayTimer = 0;
        this.game.state.round = 0;
    }

    update() {
        // Skip wave updates if game is over or in certain states
        if (this.game.state.gameOver || this.game.state.victory || this.game.state.isLevelingUp) return;

        this.game.state.startDelayTimer += this.game.deltaTime;

        // Process all wavesets in parallel
        for (let i = 0; i < this.game.state.currentWaveIds.length; i++) {
            let waveSet = this.game.getCollections().wavesets[this.game.state.waveSets[i]];
            
            // Skip if still in start delay
            if (waveSet.startDelay && this.game.state.startDelayTimer < waveSet.startDelay) continue;
            
            if(this.game.deltaTime){
            // Update individual spawn timer for this waveset
                this.game.state.spawnTimers[i] += this.game.deltaTime;
            }
            // If this waveset still has enemies to spawn
            if (this.game.state.enemiesSpawned[i] < this.game.state.currentWaveEnemies[i].length) {
                if (this.game.state.spawnTimers[i] >= this.game.state.spawnRate) {
                    // Create enemy from the appropriate waveset using the enemy type and start point index
                    const enemyType = this.game.state.currentWaveEnemies[i][this.game.state.enemiesSpawned[i]];
                    const startPointIndex = i;
                    
                    
                    this.game.spawn('enemy', { objectType: "enemyPrefabs", spawnType: enemyType, pathIndex: startPointIndex, friction: 1000, density: 1000}, new THREE.Vector3());
                    this.game.state.enemiesSpawned[i]++;
                    this.game.state.spawnTimers[i] = 0; // Reset this waveset's timer
                    
                    // Calculate total progress across all wavesets
                    const totalEnemies = this.game.state.currentWaveEnemies.reduce((sum, wave) => sum + wave.length, 0);
                    const totalSpawned = this.game.state.enemiesSpawned.reduce((sum, count) => sum + count, 0);
                    
                    // Update wave progress bar
                    document.getElementById('waveProgress').style.width = (totalSpawned / totalEnemies * 100) + '%';
                }
            }
        }
        
        // Check if all wavesets have completed spawning
        const allWavesetsComplete = this.game.state.enemiesSpawned.every((spawned, index) => 
            spawned >= this.game.state.currentWaveEnemies[index].length
        );
        
        // Move to next wave if all enemies defeated and all wavesets have completed spawning
        if (this.game.state.enemies.length === 0 && allWavesetsComplete) {
            this.game.state.waveTimer++;
            
            if (this.game.state.waveTimer >= this.game.state.waveDelay) {
                this.startNextWave();
            }
        }
    }

    startNextWave() {
        document.getElementById('waveDisplay').textContent = this.game.state.round + 1;
        
        this.game.state.currentWaveIds = [];
        this.game.state.currentWaveEnemies = [];
        this.game.state.enemiesSpawned = [];
        this.game.state.spawnTimers = [];
        
        let totalWaves = 0;
        for (let i = 0; i < this.game.state.waveSets.length; i++) {
            const waveSetId = this.game.state.waveSets[i];
            const waveSet = this.game.getCollections().wavesets[waveSetId];
            
            // Check if this waveset has enough waves for the current round
            if (this.game.state.round < waveSet.waves.length) {
                const currentWaveId = waveSet.waves[this.game.state.round];
                
                // Add this wave to the current active waves
                this.game.state.currentWaveIds.push(currentWaveId);
                this.game.state.currentWaveEnemies.push(this.game.getCollections().waves[currentWaveId].enemies);
                this.game.state.enemiesSpawned.push(0);
                this.game.state.spawnTimers.push(0); // Initialize spawn timer for this waveset
            }
            
            totalWaves = Math.max(totalWaves, waveSet.waves.length);
        }
        
        // If all wavesets are exhausted, end the game
        if (this.game.state.currentWaveIds.length === 0) {
            this.game.state.victory = true;
            this.game.state.isPaused = true;
            document.getElementById('victoryMenu').style.display = 'block';
            document.getElementById('overlay').style.display = 'block';
            return;
        }
        
        this.game.state.maxWaves = totalWaves;
        this.game.state.spawnRate = 5;
        this.game.state.waveTimer = 0;
        this.game.state.startDelayTimer = 0;
        
        // Reset wave progress bar
        document.getElementById('waveProgress').style.width = '0%';
        this.game.state.round++;
    }
}

calculateDamage.script:
function calculateDamage(attack, target) {
    // Set default values if not provided
    
    attack.speed = attack.speed || 5;     
    attack.piercing = attack.piercing || 0,
    attack.splashRadius = attack.splashRadius || 0;
    attack.critChance = attack.critChance || .05;
    attack.critMultiplier = attack.critMultiplier || 2;
    attack.penetration = {
      "fire": attack.firePenetration || 0,
      "electric": attack.electricPenetration || 0,
      "cold": attack.coldPenetration || 0,
      "physical": attack.physicalPenetration || 0,
      "toxic": attack.toxicPenetration || 0,
    }
    target.resistances = {
      "fire": target.fireResistance || 0,
      "electric": target.electricResistance || 0,
      "cold": target.coldResistance || 0,
      "physical": target.physicalResistance || 0,
      "toxic": target.toxicResistance || 0,
    }
    
    // Initialize result object
    const result = {
      damageBlocked: 0,
      damageAbsorbed: 0,
      damageDealt: 0,
      energyShieldRemaining: target.energyShield || 0,
      wasEvaded: false,
      wasCritical: Math.random() <= attack.critChance,
      hitDetails: {}
    };
    
    // Check for evasion
    if (target.evasion && target.evasion > 0) {
      const evasionRoll = Math.random() * 100;
      if (evasionRoll < target.evasion) {
        result.wasEvaded = true;
        result.hitDetails.evasion = "Attack evaded";
        return result;
      }
    }
    
    // Calculate initial damage
    let damage = attack.baseDamage;
    
    // Apply critical hit
    if (result.wasCritical) {
      damage *= attack.critMultiplier;
      result.hitDetails.critical = `Critical hit (${attack.critMultiplier}x damage)`;
    }
    
    // Apply resistances based on damage type
  
    if(attack.damageType != "physical") {
      const resistance = target.resistances[attack.damageType] || 0;
      const penetration = attack.penetration[attack.damageType] || 0;
      const finalResist = resistance - penetration;
      let resistanceMultiplier = 1 - (finalResist / 100);
      damage *= resistanceMultiplier;
  
      
      if (finalResist !== 0) {
        if (finalResist > 0) {
          result.hitDetails.resistance = `${attack.damageType} resisted (${finalResist}%)`;
        } else {
          result.hitDetails.resistance = `${attack.damageType} vulnerability (${-finalResist}%)`;
        }
      }  
    }
    
    // Apply armor (only affects physical damage)
    if (attack.damageType === "physical" && target.armor > 0) {
      let effectiveArmor = target.armor;
      if (attack.penetration.physical > 0 ) {
        // Piercing ignores 75% of armor
        effectiveArmor = target.armor * (1 - attack.penetration.physical);
      }
      // Armor formula: damage reduction percentage = armor / (armor + 100)
      const armorReduction = effectiveArmor / (effectiveArmor + 100);
      const blockedDamage = damage * armorReduction;
      
      damage -= blockedDamage;
      result.damageBlocked = blockedDamage;
      result.hitDetails.armor = `Armor blocked ${blockedDamage.toFixed(1)} damage`;
    }
    
    // Round damage to 1 decimal place for cleaner numbers
    damage = Math.round(damage * 10) / 10;
    
    // Apply shield absorption
    if (target.energyShield > 0) {
      if (damage <= target.energyShield) {
        // Shield absorbs all damage
        result.damageAbsorbed = damage;
        result.energyShieldRemaining = target.energyShield - damage;
        result.hitDetails.energyShield = `Shield absorbed all damage, ${result.energyShieldRemaining.toFixed(1)} shield remaining`;
        damage = 0;
      } else {
        // Shield is depleted, remaining damage goes through
        result.damageAbsorbed = target.energyShield;
        result.damageDealt = damage - target.energyShield;
        result.energyShieldRemaining = 0;
        result.hitDetails.energyShield = `Shield depleted, ${result.damageDealt.toFixed(1)} damage dealt to health`;
        damage -= target.energyShield;
      }
    } else {
      // No shield, all damage goes to health
      result.damageDealt = damage;
    }
    
    // Make sure we don't return negative damage
    result.damageDealt = Math.max(0, result.damageDealt);
    
    return result;
  }

calculateStats.script:
function calculateStats(stats, calcArray) {

    if( calcArray && calcArray.length > 0 ) {
        let additiveStats = {};
        let multiplicitiveStats = {};
        for(let key in stats) {
            additiveStats[key] = [];
            multiplicitiveStats[key] = [];
        }
        for(let effect of calcArray) {
            effect.apply(stats, additiveStats, multiplicitiveStats);
        }
        let addedEffects = {};
        for(let key in additiveStats){
            for(let val of additiveStats[key]){ 
                if(addedEffects[key]){
                    addedEffects[key] += val - 1;
                } else {
                    addedEffects[key] = val - 1;
                }
            }
        }

        for(let key in addedEffects) {
            if( stats[key] ) {
                stats[key] *= ( 1 + addedEffects[key] );
            }
        }

        let multipliedUpgrades = {};        
        for(let key in multiplicitiveStats){
            for(let val of multiplicitiveStats[key]){ 
                if(stats[key]){
                    stats[key] *= val;
                }
            }
        }
    }
}

main.css:
:root {
	/* Solid hex colors */
	--primary-dark: #0f2418;
	/* Deep forest green */
	--primary-bg: #1a3526;
	/* Dark moss green */
	--secondary-bg: #224031;
	/* Muted pine green */
	--accent-color: #4a7c59;
	/* Fresh green (main accent) - matches grass */
	--bright-accent: #5a9e6f;
	/* Lighter, vibrant green */
	--highlight-color: #6abe54;
	/* Soft green highlight - matches necroOrb */
	--shadow-color: #3a443c;
	/* Darker green for shadows */
	--text-color: #f7f7f0;
	/* Off-white for readability - matches our palette */
	--gold-color: #e3d163;
	/* Warm gold - matches goldMine inner */
	--essence-color: #7047a3;
	/* Purple for essence - matches necromancer color */
	--health-color: #4a7c59;
	/* Forest green - matches grass */
	/* Opacity variables for use with solid colors */
	--semi-transparent: 0.6;
	--high-transparent: 0.8;
	--very-high-transparent: 0.95;
	--low-transparent: 0.3;
	--overlay-transparent: 0.75;
	--hover-transparent: 0.9;
	--full-transparent: 0.98;
	--near-full-transparent: 0.99;
}

* {
	margin: 0;
	padding: 0;
	box-sizing: border-box;
}

body {
	margin: 0;
	padding: 0;
	background-color: var(--primary-dark);
	background-image: radial-gradient(circle at center, var(--primary-bg) 0%, var(--primary-dark) 100%);
	color: var(--text-color);
	font-family: 'Crimson Pro', serif;
	overflow: hidden;
	line-height: 1.6;
}

#gameContainer {
    position: relative;
    width: 100vw;
    height: 100vh;
}

canvas { 
    background-color: var(--secondary-bg);
    transition: transform 0.3s ease;
    width: 100vw;
    height: 100vh;
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    margin: 0;
    z-index: 0;
}

/* ========== Tower Menu Container ========== */
#towerMenu {
	display: none;
    position: absolute;
    bottom: 30px;
    border-radius: 14px;
    padding: .5em;
    max-width: 90%;
    width: 820px;
    box-shadow: 0 12px 24px rgba(0, 0, 0, 0.4);
    border: 1px solid var(--accent-color);
	background-color: var(--primary-dark);
    grid-template-columns: repeat(auto-fit, minmax(160px, 1fr));
    gap: 5px;
    z-index: 100;
	left: 0;
	right: 0;
	margin: 0 auto;
	overflow-x: auto;
	overflow-y: hidden;
	padding: 10px;
	scrollbar-width: thin;
	scrollbar-color: var(--accent-color) transparent;
}

/* ========== Tower Options ========== */
.tower-option {
	background-color: var(--secondary-bg);
	border-radius: 8px;
	border: 1px solid var(--accent-color);
	color: var(--text-color);
	cursor: pointer;
	padding: .1em .5em;
}
.tower-option:hover {
	background-color: var(--primary-bg);
	transition: background-color 0.3s ease;
}	

#statsContainer {
	display: none;
	width: 800px;
	max-width: 90%;
	padding: 12px;
	background: var(--primary-dark);
	border-radius: 10px;
	border: 1px solid var(--accent-color);
	border-opacity: var(--low-transparent);
	/* Note: This won't work directly; see below */
	backdrop-filter: blur(5px);
	position: absolute;
	z-index: 1;
	margin: 1em auto;
	left: 0;
	right: 0;
}

#stats {
	display: flex;
	justify-content: space-around;
	gap: 20px;
	flex-wrap: wrap;
}

.stat {
	display: flex;
	align-items: center;
	gap: 8px;
	font-size: 1.1rem;
}

.stat-icon {
	width: 24px;
	height: 24px;
	border-radius: 50%;
	box-shadow: 0 0 8px #000;
	display: flex;
	justify-content: center;
	align-items: center;
}

#upgradeMenu {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	background: linear-gradient(to bottom, var(--secondary-bg), var(--primary-dark));
	opacity: var(--very-high-transparent);
	color: var(--text-color);
	padding: 25px;
	border-radius: 12px;
	border: 2px solid var(--accent-color);
	display: none;
	z-index: 10;
	width: 450px;
	box-shadow: 0 0 40px var(--shadow-color);
	backdrop-filter: blur(10px);
}

#upgradeMenu h2 {
	text-align: center;
	color: var(--highlight-color);
	margin-top: 0;
	border-bottom: 1px solid var(--accent-color);
	padding-bottom: 15px;
	font-family: 'Cinzel', serif;
	letter-spacing: 1px;
	font-weight: 700;
}

.upgrade-option {
	margin: 18px 0;
	padding: 18px;
	background: linear-gradient(to right, var(--secondary-bg), var(--primary-bg));
	opacity: var(--high-transparent);
	cursor: pointer;
	border-radius: 8px;
	transition: all 0.3s;
	display: flex;
	align-items: center;
	border: 1px solid var(--accent-color);
	border-opacity: var(--low-transparent);
	/* Note: This won't work directly; see below */
	box-shadow: 0 4px 8px #000;
}

.upgrade-option:hover {
	background: linear-gradient(to right, var(--accent-color), var(--bright-accent));
	opacity: var(--hover-transparent);
	transform: translateX(8px);
	box-shadow: 0 6px 12px #000;
}

.upgrade-icon {
	width: 48px;
	height: 48px;
	margin-right: 18px;
	background: linear-gradient(135deg, var(--accent-color), var(--secondary-bg));
	border-radius: 8px;
	display: flex;
	align-items: center;
	justify-content: center;
	font-size: 22px;
	box-shadow: 0 3px 6px #000;
}

.upgrade-desc {
	flex-grow: 1;
}

.upgrade-title {
	font-weight: bold;
	margin-bottom: 6px;
	color: var(--highlight-color);
	font-size: 1.2rem;
	font-family: 'Cinzel', serif;
}

#overlay {
	position: absolute;
	top: 0;
	left: 0;
	width: 100%;
	height: 100%;
	background: #000;
	opacity: var(--overlay-transparent);
	display: none;
	z-index: 5;
	backdrop-filter: blur(3px);
}

#waveInfo {
	position: absolute;
	top: 15px;
	left: 50%;
	transform: translateX(-50%);
	background: linear-gradient(to right, var(--secondary-bg), var(--primary-bg));
	opacity: var(--high-transparent);
	padding: 8px 20px;
	border-radius: 20px;
	display: flex;
	align-items: center;
	gap: 15px;
	box-shadow: 0 4px 12px #000;
	border: 1px solid var(--accent-color);
	border-opacity: var(--low-transparent);
	/* Note: This won't work directly; see below */
	backdrop-filter: blur(5px);
}

#waveDisplay {
	font-weight: bold;
	color: var(--highlight-color);
}

.progress-bar {
	width: 220px;
	height: 12px;
	background: var(--primary-dark);
	border-radius: 6px;
	overflow: hidden;
	box-shadow: inset 0 1px 3px #000;
	border: 1px solid var(--accent-color);
	border-opacity: var(--low-transparent);
	/* Note: This won't work directly; see below */
}

.progress-fill {
	height: 100%;
	background: linear-gradient(to right, var(--accent-color), var(--highlight-color));
	width: 0%;
	box-shadow: 0 0 8px var(--shadow-color);
	transition: width 0.3s ease;
}

#startGameBtn {
	padding: 12px 35px;
	background: linear-gradient(to bottom, var(--bright-accent), var(--accent-color));
	color: white;
	border: none;
	border-radius: 30px;
	cursor: pointer;
	font-family: 'Cinzel', serif;
	font-weight: 500;
	font-size: 1.2rem;
	transition: all 0.3s;
	box-shadow: 0 6px 12px #000;
	text-shadow: 0 1px 2px #000;
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	z-index: 20;
}

#startGameBtn:hover {
	background: linear-gradient(to bottom, var(--highlight-color), var(--bright-accent));
	transform: translate(-50%, -50%) scale(1.05);
	box-shadow: 0 8px 16px #000;
}

#startGameBtn:active {
	transform: translate(-50%, -50%) scale(1);
	box-shadow: 0 4px 8px #000;
}

#tooltip {
	position: absolute;
	background: linear-gradient(to bottom, var(--primary-dark), var(--primary-dark));
	opacity: var(--very-high-transparent);
	color: var(--text-color);
	padding: 12px 15px;
	border-radius: 8px;
	display: none;
	z-index: 100;
	border: 1px solid var(--accent-color);
	pointer-events: none;
	max-width: 280px;
	box-shadow: 0 0 20px var(--shadow-color);
	font-size: 0.95rem;
	backdrop-filter: blur(5px);
}

#gameOverMenu,
#victoryMenu {
	position: absolute;
	top: 50%;
	left: 50%;
	transform: translate(-50%, -50%);
	background: linear-gradient(to bottom, var(--secondary-bg), var(--primary-dark));
	opacity: var(--full-transparent);
	color: white;
	padding: 40px;
	text-align: center;
	border-radius: 15px;
	border: 3px solid var(--accent-color);
	z-index: 20;
	display: none;
	box-shadow: 0 0 60px var(--shadow-color);
	backdrop-filter: blur(10px);
	min-width: 350px;
}

#gameOverMenu h2,
#victoryMenu h2 {
	color: var(--highlight-color);
	font-size: 32px;
	margin-bottom: 20px;
	font-family: 'Cinzel', serif;
	text-shadow: 0 2px 4px #000;
}

#gameOverMenu p,
#victoryMenu p {
	font-size: 18px;
	margin-bottom: 25px;
	line-height: 1.6;
}

.menu-button {
	margin-top: 25px;
	padding: 12px 25px;
	background: linear-gradient(to bottom, var(--bright-accent), var(--accent-color));
	border: none;
	color: white;
	cursor: pointer;
	border-radius: 8px;
	transition: all 0.3s;
	font-family: 'Cinzel', serif;
	font-size: 16px;
	letter-spacing: 1px;
	box-shadow: 0 4px 8px #000;
	text-shadow: 0 1px 2px #000;
}

.menu-button:hover {
	background: linear-gradient(to bottom, var(--highlight-color), var(--bright-accent));
	transform: scale(1.05) translateY(-3px);
	box-shadow: 0 6px 15px #000;
}

.menu-button:active {
	transform: scale(1) translateY(0);
	box-shadow: 0 3px 6px #000;
}

/* Stat icons with custom styling */
#shardsDisplay {
	color: var(--gold-color);
	font-weight: 600;
}

#essenceDisplay,
#essenceNeededDisplay {
	color: var(--essence-color);
	font-weight: 600;
}

#populationDisplay,
#maxPopulationDisplay {
	color: var(--essence-color);
	font-weight: 600;
}

#hpDisplay {
	color: var(--health-color);
	font-weight: 600;
}

#waveDisplay {
	display: inline-block;
}


/* Mobile-first adjustments (applies below 768px) */
@media screen and (max-width: 768px) {
    /* Base layout adjustments */
    #gameContainer {
        overflow: hidden;
    }
    #towerMenu {
        position: fixed;
		bottom: 0;
        left: 0;
        right: 0;
        width: 95vw;
		max-height: 20vh;
		overflow-y:auto;
        max-width: unset;
        margin: 0 auto;
        grid-template-columns: repeat(2, 1fr);
    }

    .tower-option {
        min-width: unset;
        font-size: 0.95rem;
    }

    /* Add a close button for mobile */
    #menuCloseButton {
        display: block;
        position: absolute;
        top: 8px;
        right: 8px;
        background: var(--accent-color);
        border: none;
        color: var(--text-color);
        width: 32px;
        height: 32px;
        border-radius: 50%;
        cursor: pointer;
        z-index: 100;
    }

    /* Adjust stats container position */
    #statsContainer {
        top: 10px;
        width: 95%;
        padding: 8px;
    }
    #stats {
        gap: 8px;
    }

    .stat {
        font-size: 0.9rem;
        gap: 4px;
    }

    /* Upgrade menu adjustments */
    #upgradeMenu {
        width: 90%;
        padding: 15px;
    }

    .upgrade-option {
        margin: 12px 0;
        padding: 12px;
        flex-direction: column;
        align-items: flex-start;
    }

    .upgrade-icon {
        width: 36px;
        height: 36px;
        margin-right: 12px;
        font-size: 18px;
    }

    .upgrade-title {
        font-size: 1rem;
    }

    /* Wave info adjustments */
    #waveInfo {
        top: 80px;
        width: 90%;
        padding: 6px 15px;
    }

    .progress-bar {
        width: 150px;
    }

    /* Game over/victory menus */
    #gameOverMenu, #victoryMenu {
        width: 90%;
        padding: 20px;
    }

    #gameOverMenu h2, #victoryMenu h2 {
        font-size: 1.5rem;
    }

    /* Tooltip adjustments */
    #tooltip {
        max-width: 200px;
        font-size: 0.85rem;
        padding: 8px 10px;
    }

    /* Button sizes */
    #startGameBtn {
        padding: 10px 25px;
        font-size: 1rem;
    }

    .menu-button {
        padding: 10px 20px;
        font-size: 0.9rem;
    }
}

/* Additional adjustments for very small screens */
@media screen and (max-width: 480px) {


    #stats {
        justify-content: center;
    }

    .stat {
        font-size: 0.8rem;
    }

    .progress-bar {
        width: 120px;
        height: 10px;
    }

    #waveDisplay {
        font-size: 0.9rem;
    }

    .upgrade-option {
        padding: 10px;
        margin: 8px 0;
    }

    .upgrade-title {
        font-size: 0.9rem;
    }
}

/* Orientation-specific adjustments */
@media screen and (max-height: 500px) and (orientation: landscape) {

    #statsContainer {
        top: 5px;
        padding: 5px;
    }

  
}
@media screen and (max-width: 480px) {


    #menuCloseButton {
        top: 4px;
        right: 4px;
        width: 28px;
        height: 28px;
    }
}

