  }
  
  /* Common styles for items */
  #graphics-editor-container  .shape-item, .group-item {
    padding: 8px;
    margin: 5px 0;
    border-radius: 4px;
    cursor: grab;
    border: 1px solid transparent;
    transition: border-color 0.2s;
  }
  
  /* Shapes styling */
  #graphics-editor-container  .shape-item {
    border-left: 1px solid #555;
  }
  
  #graphics-editor-container  .shape-item:active {
    cursor: grabbing;
  }
  
  /* Group styling */
  #graphics-editor-container  .group-item {
    border-left: 4px solid #007bff; /* Visual distinction for groups */
    font-weight: 500;
  }
  
  /* Selected states - use border instead of background */
  #graphics-editor-container  .shape-item.selected, 
  #graphics-editor-container  .group-item.selected {
    border-color: #4080ff;
  }
  
  /* Drag-over states */
  #graphics-editor-container .group-item.drag-over,
  #graphics-editor-container  #shape-list.drag-over {
    border-color: #40a040;
    box-shadow: 0 0 3px rgba(0, 100, 0, 0.5);
  }
  
  /* Animation for when dragging */
  #graphics-editor-container .shape-item.dragging {
    opacity: 0.7;
  }
  
  /* Group shapes container */
  #graphics-editor-container .group-shapes {
    margin-left: 15px;
    padding: 5px;
    border-left: 2px dashed #666;
  }
  
  /* Ungrouped shapes section */
  #graphics-editor-container #shape-list .ungrouped-shapes {
    margin-top: 10px;
    padding: 10px;
    border: 1px dashed #666;
  }

  #graphics-editor-container .transform-buttons {
    display: flex;
    gap: 10px;
    margin-bottom: 10px;
}

#graphics-editor-container .transform-buttons button {
    flex: 1;
    padding: 8px;
    background: #444;
    border: 1px solid #666;
    color: white;
    cursor: pointer;
}

#graphics-editor-container .transform-buttons button.active {
    background: #0066cc;
    border-color: #0088ff;
}

TextureEditor.css:
#texture-editor-wrapper {
    display: flex;
    gap: 20px;
    padding: 10px;
    border-radius: 5px;
}

.texture-editor-tools {
    width: 200px;
    flex-shrink: 0;
}

.texture-editor-canvas-container {
    flex-grow: 1;
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
    border-radius: 5px;
}

#texture-canvas {
    background-color: transparent;
    box-shadow: 0 0 10px rgba(0,0,0,0.1);
    image-rendering: pixelated;
    border: 1px solid #000;
}

#noTextureMessage {
    position: absolute;
    color: #666;
}

.tool-section {
    margin-bottom: 15px;
    padding-bottom: 15px;
}

.tool-section h3 {
    margin-top: 0;
    margin-bottom: 10px;
