}

.tool-btn {
    margin-right: 5px;
    margin-bottom: 5px;
    padding: 5px 10px;
    cursor: pointer;
}

.tool-btn.active {
    background-color: #007bff;
    color: white;
}

.color-palette {
    display: flex;
    flex-wrap: wrap;
    gap: 5px;
    margin-bottom: 10px;
}

.color-btn {
