}

#scene-editor-container .property input, #scene-editor-container .property select {
    width: 120px;
    padding: 4px;
    border-radius: 3px;
}

#scene-editor-container .property-group {
    margin-bottom: 8px;
}

#scene-editor-container .component-header {
    padding: 8px;
    font-weight: bold;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

#scene-editor-container .btn {
    padding: 4px 8px;
    border-radius: 3px;
    cursor: pointer;
}


#scene-editor-container .add-entity-btn,#scene-editor-container .add-component-btn {
    display: flex;
    align-items: center;
    gap: 4px;
}

#scene-editor-container .no-selection {
    padding: 16px;
    text-align: center;
}

#scene-editor-container .vector3-input {
    display: flex;
    width: 200px;
    gap: 2px;
}

#scene-editor-container .vector3-input input {
    width: 64px;
    text-align: center;
}

#scene-editor-container .dropdown-content {
    display: none;
    position: absolute;
    min-width: 160px;
    box-shadow: 0px 8px 16px 0px rgba(0,0,0,0.2);
    z-index: 1;
    border-radius: 3px;
