fragmentScript:
uniform float time;
uniform float waveHeight;
uniform float liquidAlpha;
uniform vec3 liquidColor;
uniform vec3 foamColor;
uniform sampler2D terrainHeightmap; // Heightmap texture
uniform vec2 terrainSize; // Size of the terrain in world units
uniform vec2 terrainOffset; // Offset of the terrain in world units
uniform float waveFrequency;
uniform float fresnelPower;
uniform vec3 lightDirection;
uniform float ambientIntensity;
uniform float specularIntensity;
uniform float foamThreshold; // How close to terrain foam appears
uniform float foamWidth; // Width of the foam band
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight;
varying vec3 vWorldPosition;
uniform vec3 fogColor;
uniform float fogDensity;

// Function to get terrain height from a heightmap texture
float getTerrainHeightAt(vec2 worldPos) {
    // Convert world position to terrain UV coordinates
    vec2 terrainUV = (worldPos - terrainOffset) / terrainSize;

    // Ensure we're within terrain bounds (0-1)
    if (terrainUV.x < 0.0 || terrainUV.x > 1.0 || terrainUV.y < 0.0 || terrainUV.y > 1.0) {
        return -1000.0; // Outside terrain bounds
    }

    // Sample heightmap texture
    return texture2D(terrainHeightmap, terrainUV).r;
}

void main() {
    vec2 uv = vUv;
    vec3 normal = normalize(vNormal);
    vec3 viewDir = normalize(vViewPosition);
    vec3 lightDir = normalize(lightDirection);

    // Get terrain height at this xz position
    float terrainHeight = getTerrainHeightAt(vWorldPosition.xz);

    // Calculate distance between water surface and terrain
    float waterTerrainDistance = vWorldPosition.y - terrainHeight;

    // Create foam based on proximity to terrain - only if we found terrain nearby
    float terrainFoamFactor = 0.0;
    if(terrainHeight > -999.0) { // If we found valid terrain
        terrainFoamFactor = 1.0 - smoothstep(0.0, foamWidth, waterTerrainDistance);
    }

    // Original wave height-based foam
    float waveFoamFactor = smoothstep(0.8, 1.0, vNormalizedWaveHeight);

    // Combine both foam factors - use max to show foam from either source
    float foamFactor = max(terrainFoamFactor, waveFoamFactor);

    // Fresnel effect for edge transparency
    float fresnel = pow(1.0 - max(dot(normal, viewDir), 0.0), fresnelPower);

    // Diffuse lighting
    float diffuse = max(dot(normal, lightDir), 0.0) * 0.25;

    // Specular (Blinn-Phong)
    vec3 halfwayDir = normalize(lightDir + viewDir);
    float specular = pow(max(dot(normal, halfwayDir), 0.0), 32.0) * specularIntensity;

    // Base color with subtle wave height modulation
    vec3 baseColor = liquidColor * (0.8 + 0.2 * vNormalizedWaveHeight);

    // Apply foam - now blend between base color and foam color
    vec3 color = mix(baseColor, foamColor, foamFactor);

    // Calculate fog
    float fogDistance = length(vWorldPosition - cameraPosition);
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695);
    fogFactor = clamp(fogFactor, 0.0, 1.0);

    // Combine lighting components
    vec3 lightColor = color * (ambientIntensity + diffuse) + vec3(specular);
    vec3 finalColor = mix(fogColor, lightColor, fogFactor);

    // Apply fresnel for transparency at edges and make foam more opaque
    float alpha = mix(0.6, 1.0, fresnel) * liquidAlpha;
    alpha = mix(alpha, 1.0, foamFactor * 0.5); // Make foam more opaque

    gl_FragColor = vec4(finalColor, min(1.0, waterTerrainDistance));
}

vertexScript:
uniform float time;
uniform float waveHeight;
uniform float waveFrequency;
uniform float waveSpeed;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight;
varying vec3 vWorldPosition;

// 2D Random function for seeding noise
float random(vec2 st) {
    return fract(sin(dot(st.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

// 2D Noise function (simplified Perlin-like noise)
float noise(vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    vec2 u = f * f * (3.0 - 2.0 * f);

    return mix(a, b, u.x) +
           (c - a) * u.y * (1.0 - u.x) +
           (d - b) * u.x * u.y;
}

void main() {
    vUv = uv;

    // Generate smooth noise for subtle variation
    float noiseScale = 0.3; // Larger scale for smoother noise
    float noiseSpeed = 0.05; // Slower noise evolution
    float noiseValue = noise(vUv * noiseScale + time * noiseSpeed);
    float noiseValue2 = noise(vUv * noiseScale * 0.5 + time * noiseSpeed * 0.7 + 10.0);

    // Introduce a dominant wave direction (e.g., along x-axis, simulating wind direction)
    float directionalWave = sin(vUv.x * waveFrequency + time * waveSpeed + noiseValue * 0.5) * waveHeight;

    // Add smaller, secondary waves with slight noise variation
    float secondaryWave = sin(vUv.x * waveFrequency * 1.5 + vUv.y * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * waveHeight * 0.4;
    float detailWave = cos(vUv.x * waveFrequency * 2.0 + vUv.y * waveFrequency * 0.5 + time * waveSpeed * 1.2) * waveHeight * 0.2 * (0.8 + 0.2 * noiseValue);

    // Combine waves with a softer mix
    float displacementY = directionalWave + secondaryWave + detailWave;

    // Store wave height for fragment shader
    vWaveHeight = displacementY;

    // Normalize wave height based on maximum possible displacement
    float maxWaveHeight = waveHeight * (1.0 + 0.4 + 0.2); // Sum of wave amplitudes
    vNormalizedWaveHeight = clamp(displacementY / maxWaveHeight, -1.0, 1.0);

    // Update position with displacement
    vec3 newPosition = vec3(position.x, position.y + displacementY, position.z);

    // Compute normal for lighting
    float offset = 0.01;
    float waveX = (sin((vUv.x + offset) * waveFrequency + time * waveSpeed + noiseValue * 0.5) +
                   sin((vUv.x + offset) * waveFrequency * 1.5 + (vUv.y + offset) * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * 0.4 +
                   cos((vUv.x + offset) * waveFrequency * 2.0 + (vUv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.2 * (0.8 + 0.2 * noiseValue)) * waveHeight;
    float waveZ = (sin(vUv.x * waveFrequency + time * waveSpeed + noiseValue * 0.5) +
                   sin(vUv.x * waveFrequency * 1.5 + (vUv.y + offset) * waveFrequency * 0.3 + time * waveSpeed * 0.8 + noiseValue2 * 0.3) * 0.4 +
                   cos(vUv.x * waveFrequency * 2.0 + (vUv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.2 * (0.8 + 0.2 * noiseValue)) * waveHeight;
    vec3 tangent = normalize(vec3(1.0, (waveX - displacementY) / offset, 0.0));
    vec3 bitangent = normalize(vec3(0.0, (waveZ - displacementY) / offset, 1.0));
    vNormal = normalize(cross(tangent, bitangent));

    // Pass view position for fresnel and lighting
    vec4 worldPosition = modelMatrix * vec4(newPosition, 1.0);
    vViewPosition = (cameraPosition - worldPosition.xyz);
    vWorldPosition = worldPosition.xyz;

    // Apply projection and model-view transforms
    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
}
