AttackEnemyBehaviorAction.script:
/**
 * AttackEnemyBehaviorAction - Combat action
 * Attacks the target enemy stored in shared state
 *
 * Parameters:
 *   targetKey: string (default: 'target') - Key in shared state for target entity ID
 *
 * Returns RUNNING while attacking (continuous action)
 */
class AttackEnemyBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        const combat = game.getComponent(entityId, 'combat');
        if (!combat) {
            return this.failure();
        }

        // Anchor unit while attacking - stand still
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Perform attack
        this.performAttack(entityId, targetId, game, combat);

        // Return running - attacking is a continuous action
        return this.running({
            target: targetId,
            attacking: true
        });
    }

    onEnd(entityId, game) {
        // Unanchor when combat action ends
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = false;
        }
    }

    performAttack(attackerId, targetId, game, combat) {
        if (!combat.lastAttack) combat.lastAttack = 0;

        const effectiveAttackSpeed = this.getEffectiveAttackSpeed(attackerId, game, combat.attackSpeed);
        const timeSinceLastAttack = game.state.now - combat.lastAttack;

        if (timeSinceLastAttack < 1 / effectiveAttackSpeed) {
            // Attack on cooldown
            return;
        }

        combat.lastAttack = game.state.now;

        // Face the target
        const attackerPos = game.getComponent(attackerId, 'position');
        const targetPos = game.getComponent(targetId, 'position');
        const facing = game.getComponent(attackerId, 'facing');

        if (attackerPos && targetPos && facing) {
            const dx = targetPos.x - attackerPos.x;
            const dz = targetPos.z - attackerPos.z;
            facing.angle = Math.atan2(dz, dx);
        }

        // Trigger attack animation
        if (game.gameManager && game.gameManager.has('triggerSinglePlayAnimation')) {
            const animationSpeed = this.calculateAnimationSpeed(attackerId, game, combat.attackSpeed);
            const minAnimationTime = 1 / combat.attackSpeed * 0.8;
            game.gameManager.call('triggerSinglePlayAnimation', attackerId, 'attack', animationSpeed, minAnimationTime);
        }

        // Handle projectile or melee damage
        if (combat.projectile) {
            this.fireProjectile(attackerId, targetId, game, combat);
        } else if (combat.damage > 0) {
            // Melee attack - schedule damage
            const damageDelay = (1 / combat.attackSpeed) * 0.5;
            const element = this.getDamageElement(attackerId, game, combat);

            game.gameManager.call('scheduleDamage',
                attackerId,
                targetId,
                combat.damage,
                element,
                damageDelay,
                {
                    isMelee: true,
                    weaponRange: (combat.range || 50) + 10
                }
            );
        } else if (game.abilitySystem) {
            // Ability-only units (damage = 0) - use abilities for combat
            this.useOffensiveAbility(attackerId, targetId, game);
        }
    }

    useOffensiveAbility(attackerId, targetId, game) {
        const abilities = game.gameManager.call('getEntityAbilities', attackerId);
        if (!abilities || abilities.length === 0) return;

        // Find available offensive abilities
        const availableAbilities = abilities
            .filter(ability => game.abilitySystem.isAbilityOffCooldown(attackerId, ability.id))
            .filter(ability => !ability.canExecute || ability.canExecute(attackerId))
            .sort((a, b) => (b.priority || 0) - (a.priority || 0) || a.id.localeCompare(b.id));

        if (availableAbilities.length > 0) {
            game.abilitySystem.useAbility(attackerId, availableAbilities[0].id, { target: targetId });
        }
    }

    fireProjectile(attackerId, targetId, game, combat) {
        const targetHealth = game.getComponent(targetId, 'health');
        if (!targetHealth || targetHealth.current <= 0) return;

        const projectileData = game.getCollections().projectiles[combat.projectile];
        if (projectileData) {
            game.gameManager.call('fireProjectile', attackerId, targetId, {
                id: combat.projectile,
                ...projectileData
            });
        }
    }

    getEffectiveAttackSpeed(entityId, game, baseAttackSpeed) {
        if (game.equipmentSystem) {
            const equipment = game.getComponent(entityId, 'equipment');
            if (equipment && equipment.attackSpeed) {
                return baseAttackSpeed * equipment.attackSpeed;
            }
        }
        return baseAttackSpeed;
    }

    calculateAnimationSpeed(attackerId, game, baseAttackSpeed) {
        let animSpeed = baseAttackSpeed;
        if (game.equipmentSystem) {
            const equipment = game.getComponent(attackerId, 'equipment');
            if (equipment && equipment.attackSpeed) {
                animSpeed *= equipment.attackSpeed;
            }
        }
        return animSpeed;
    }

    getDamageElement(entityId, game, combat) {
        if (combat.element) {
            return combat.element;
        }

        const weaponElement = this.getWeaponElement(entityId, game);
        if (weaponElement) {
            return weaponElement;
        }

        return game.damageSystem?.ELEMENT_TYPES?.PHYSICAL || 'physical';
    }

    getWeaponElement(entityId, game) {
        if (!game.equipmentSystem) return null;

        const equipment = game.getComponent(entityId, 'equipment');
        if (!equipment) return null;

        const mainHandItem = equipment.slots?.mainHand;
        if (mainHandItem) {
            const itemData = game.gameManager.call('getItemData', mainHandItem);
            if (itemData && itemData.stats && itemData.stats.element) {
                return itemData.stats.element;
            }
        }

        const offHandItem = equipment.slots?.offHand;
        if (offHandItem) {
            const itemData = game.gameManager.call('getItemData', offHandItem);
            if (itemData && itemData.stats && itemData.stats.element) {
                return itemData.stats.element;
            }
        }

        return null;
    }
}

BaseBehaviorAction.script:
class BaseBehaviorAction extends GUTS.BaseBehaviorNode {
    static STATUS = {
        SUCCESS: 'success',
        FAILURE: 'failure',
        RUNNING: 'running'
    };

    constructor(game, config) {
        super(game, config);
    }

    /**
     * Check if this action can execute for the given entity
     * @param {string} entityId - Entity ID
     * @param {object} controller - UnitController component
     * @param {object} game - Game instance
     * @returns {boolean} True if action can execute
     */
    canExecute(entityId, controller, game) {
        return true;
    }

    /**
     * Override evaluateLeaf - called by BaseBehaviorNode for leaf nodes
     * Delegates to execute() which subclasses override
     */
    evaluateLeaf(entityId, game) {
        return this.execute(entityId, game);
    }

    /**
     * Execute one tick of the action
     * Override this in subclasses
     * @param {string} entityId - Entity ID
     * @param {object} game - Game instance
     * @returns {Object|null} Response with status (success/running) or null for failure
     */
    execute(entityId, game) {
        return this.failure();
    }
}

BuildBehaviorAction.script:
class BuildBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        // Skip if MoveBehaviorAction is already handling this order
        const aiState = game.getComponent(entityId, 'aiState');
   
        // Read from playerOrder component (like MoveBehaviorAction)
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        if (!playerOrder || !playerOrder.meta || !playerOrder.meta.buildingId) {
            return null;
        }

        const buildingId = playerOrder.meta.buildingId;
        const buildingPlacement = game.getComponent(buildingId, 'placement');

        // Check if building exists and is under construction
        if (!buildingPlacement || !buildingPlacement.isUnderConstruction) {
            return null;
        }

        const state = aiState.meta.buildState || 'traveling_to_building';

        // State machine - return state objects, don't modify aiState.meta
        switch (state) {
            case 'traveling_to_building':
                return this.travelToBuilding(entityId, aiState, game);
            case 'building':
                return this.doBuilding(entityId, aiState, game);
        }

        return null;
    }

    travelToBuilding(entityId, aiState, game) {
        // Get building info from playerOrder or aiState.meta
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        const buildingId = playerOrder.meta.buildingId || aiState.meta.buildingId;
        const buildingPos = game.getComponent(buildingId, 'position');

        if (!buildingPos) {
            return null;
        }        

        const pos = game.getComponent(entityId, 'position');
        const distance = this.distance(pos, buildingPos);

        if (distance < this.parameters.buildRange) {
            // Reached building
            const facing = game.getComponent(entityId, 'facing');
            if (facing) {
                const dx = buildingPos.x - pos.x;
                const dz = buildingPos.z - pos.z;
                const angleToBuilding = Math.atan2(dz, dx);
                facing.angle = angleToBuilding;
            }

            // Position at build range from building
            pos.x = buildingPos.x + this.parameters.buildRange;
            pos.z = buildingPos.z;

            // Clear playerOrder.targetPosition to stop movement
            const playerOrder = game.getComponent(entityId, 'playerOrder');
            if (playerOrder) {
                playerOrder.targetPosition = null;
            }

            // Anchor the unit so it doesn't move while building
            const vel = game.getComponent(entityId, 'velocity');
            if (vel) {
                vel.anchored = true;
                vel.vx = 0;
                vel.vz = 0;
            }

            // Return state object - transition to building state
            return {
                buildingId: buildingId,
                buildingPosition: buildingPos,
                buildState: 'building',
                constructionStartTime: game.state.round
            };
        }

        // Still traveling - return targetPosition so MovementSystem moves unit to building
        return {
            buildingId: buildingId,
            buildingPosition: buildingPos,
            buildState: 'traveling_to_building',
            targetPosition: { x: buildingPos.x, z: buildingPos.z }
        };
    }

    doBuilding(entityId, aiState, game) {
        const buildingId = aiState.meta.buildingId;
        const buildingPlacement = game.getComponent(buildingId, 'placement');
        const unitType = game.getComponent(buildingId, 'unitType');

        if (!buildingPlacement) {
            return null;
        }

        // Play building animation
        if (game.animationSystem) {
            const animState = game.animationSystem.entityAnimationStates.get(entityId);
            if (animState) {
                const finished = game.animationSystem.isAnimationFinished(entityId, animState.currentClip);
                if (finished || animState.currentClip !== 'attack') {
                    game.abilitySystem.startAbilityAnimation(entityId, { castTime: 1 });
                }
            }
        }

        const elapsed = game.state.round - aiState.meta.constructionStartTime;
        const buildTime = buildingPlacement.buildTime || this.parameters.defaultBuildTime;


        if (elapsed >= buildTime) {
            // Complete construction
            this.completeConstruction(entityId, buildingId, buildingPlacement, game);

            // Return null to end this action (building is complete)
            return null;
        }

        // Still building - return state to continue
        return {
            buildingId: buildingId,
            buildingPosition: aiState.meta.buildingPosition,
            buildState: 'building',
            constructionStartTime: aiState.meta.constructionStartTime
        };
    }

    completeConstruction(entityId, buildingId, buildingPlacement, game) {
        if (!buildingPlacement || !buildingPlacement.unitType) {
            console.error('[BuildBehaviorAction] Cannot complete construction - missing placement or unitType');
            return;
        }


        // Get the actual building unit type from placement
        const actualBuildingType = buildingPlacement.unitType;

        // 1. Restore renderable component - change from underConstruction to actual building
        const renderComponent = game.getComponent(buildingId, 'renderable');
        if (renderComponent) {
            renderComponent.spawnType = actualBuildingType.id;
            // Remove instance to trigger re-spawn with correct model
            game.gameManager.call('removeInstance', buildingId);
        }

        // 2. Restore health to full (health component is kept during construction)
        const maxHP = actualBuildingType.hp || 100;
        const health = game.getComponent(buildingId, 'health');
        if (health) {
            health.max = maxHP;
            health.current = maxHP;
        }

        // 3. Update unitType component to ensure it has all the actual building's data
        const unitTypeComponent = game.getComponent(buildingId, 'unitType');
        if (unitTypeComponent) {
            Object.assign(unitTypeComponent, actualBuildingType);
        }

        // 4. Update placement component - building is now complete
        buildingPlacement.isUnderConstruction = false;
        buildingPlacement.assignedBuilder = null;

        // 5. Register building with shop system
        if (game.shopSystem) {
            game.shopSystem.addBuilding(actualBuildingType.id, buildingId);
        }

        // 6. Change to idle animation
        if (game.animationSystem) {
            game.animationSystem.changeAnimation(buildingId, 'idle', 1.0, 0);
        }

    }

    onEnd(entityId, game) {
        const aiState = game.getComponent(entityId, 'aiState');
        if (!aiState || !aiState.meta) return;

        const buildingId = aiState.meta.buildingId;
        if (buildingId) {
            const buildingPlacement = game.getComponent(buildingId, 'placement');

            // Clean up if action was interrupted
            if (buildingPlacement && buildingPlacement.assignedBuilder === entityId) {
                buildingPlacement.assignedBuilder = null;
            }
        }

        // Unanchor the unit
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = false;
        }

        // Clear all meta data (like MineGoldBehaviorAction)
        aiState.meta = {};
        aiState.currentAction = null;
    }

    distance(pos, target) {
        const dx = target.x - pos.x;
        const dz = target.z - pos.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

CombatBehaviorAction.script:
class CombatBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        // Check if player order prevents combat (build orders, force move)
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        if (playerOrder?.meta?.preventCombat) {
            return this.failure();
        }

        const aiState = game.getComponent(entityId, 'aiState');
        const combat = game.getComponent(entityId, 'combat');
        const health = game.getComponent(entityId, 'health');

        // Skip if unit can't fight (no combat component or dead)
        if (!combat || !health || health.current <= 0) {
            return this.failure();
        }

        // Skip units with 0 damage and no abilities (non-combat units like peasants mining)
        const unitType = game.getComponent(entityId, 'unitType');
        if (combat.damage === 0 && (!unitType.abilities || unitType.abilities.length === 0)) {
            return this.failure();
        }

        const memory = this.getMemory(entityId);
        const state = memory.combatState || 'seeking_target';

        switch (state) {
            case 'seeking_target':
                return this.seekTarget(entityId, memory, game);
            case 'moving_to_target':
                return this.moveToTarget(entityId, memory, game);
            case 'attacking':
                return this.doAttacking(entityId, memory, game);
        }
    }

    onEnd(entityId, game) {
        // Unanchor the unit
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;

        // Clear per-entity memory (handled by base class)
        this.clearMemory(entityId);
    }

    seekTarget(entityId, memory, game) {
        const target = this.findNearestEnemy(entityId, game);

        if (!target) {
            // No enemies in range - don't take over behavior
            return this.failure();
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(target, 'position');
        const combat = game.getComponent(entityId, 'combat');

        const distance = this.distance(pos, targetPos);
        const attackRange = combat.range || 50;

        if (distance <= attackRange) {
            // Already in range, start attacking
            memory.combatState = 'attacking';
            memory.target = target;
            memory.targetPosition = { x: targetPos.x, z: targetPos.z };
            return this.running(memory);
        }

        // Need to move to target
        memory.combatState = 'moving_to_target';
        memory.target = target;
        memory.targetPosition = { x: targetPos.x, z: targetPos.z };
        return this.running(memory);
    }

    moveToTarget(entityId, memory, game) {
        const target = memory.target;

        // Check if target is still valid
        if (!target || !this.isValidTarget(target, game)) {
            // Target lost, seek new target
            memory.combatState = 'seeking_target';
            return this.seekTarget(entityId, memory, game);
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(target, 'position');
        const combat = game.getComponent(entityId, 'combat');

        const distance = this.distance(pos, targetPos);
        const attackRange = combat.range || 50;

        if (distance <= attackRange) {
            // In range, start attacking
            memory.combatState = 'attacking';
            memory.target = target;
            memory.targetPosition = { x: targetPos.x, z: targetPos.z };
            return this.running(memory);
        }

        // Keep moving toward target (MovementSystem reads targetPosition)
        memory.combatState = 'moving_to_target';
        memory.target = target;
        memory.targetPosition = { x: targetPos.x, z: targetPos.z };
        return this.running(memory);
    }

    doAttacking(entityId, memory, game) {
        const target = memory.target;

        // Check if target is still valid
        if (!target || !this.isValidTarget(target, game)) {
            // Target dead or lost, unanchor and seek new target
            const vel = game.getComponent(entityId, 'velocity');
            if (vel) vel.anchored = false;
            memory.combatState = 'seeking_target';
            return this.seekTarget(entityId, memory, game);
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(target, 'position');
        const combat = game.getComponent(entityId, 'combat');

        const distance = this.distance(pos, targetPos);
        const attackRange = combat.range || 50;

        if (distance > attackRange) {
            // Target moved out of range, unanchor and chase
            const vel = game.getComponent(entityId, 'velocity');
            if (vel) vel.anchored = false;
            memory.combatState = 'moving_to_target';
            memory.target = target;
            memory.targetPosition = { x: targetPos.x, z: targetPos.z };
            return this.running(memory);
        }

        // Anchor unit while attacking - stand still
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Perform attack
        this.performAttack(entityId, target, game);

        // Return running state with no targetPosition so MovementSystem doesn't move us
        memory.combatState = 'attacking';
        memory.target = target;
        delete memory.targetPosition;
        return this.running(memory);
    }

    performAttack(attackerId, targetId, game) {
        const combat = game.getComponent(attackerId, 'combat');

        if (!combat.lastAttack) combat.lastAttack = 0;

        const effectiveAttackSpeed = this.getEffectiveAttackSpeed(attackerId, game, combat.attackSpeed);
        const timeSinceLastAttack = game.state.now - combat.lastAttack;

        if (timeSinceLastAttack < 1 / effectiveAttackSpeed) {
            // Attack on cooldown
            return;
        }

        combat.lastAttack = game.state.now;

        // Face the target
        const attackerPos = game.getComponent(attackerId, 'position');
        const targetPos = game.getComponent(targetId, 'position');
        const facing = game.getComponent(attackerId, 'facing');

        if (attackerPos && targetPos && facing) {
            const dx = targetPos.x - attackerPos.x;
            const dz = targetPos.z - attackerPos.z;
            facing.angle = Math.atan2(dz, dx);
        }

        // Trigger attack animation
        if (game.gameManager && game.gameManager.has('triggerSinglePlayAnimation')) {
            const animationSpeed = this.calculateAnimationSpeed(attackerId, game, combat.attackSpeed);
            const minAnimationTime = 1 / combat.attackSpeed * 0.8;
            game.gameManager.call('triggerSinglePlayAnimation', attackerId, 'attack', animationSpeed, minAnimationTime);
        }

        // Handle projectile or melee damage
        if (combat.projectile) {
            this.fireProjectile(attackerId, targetId, game, combat);
        } else if (combat.damage > 0) {
            // Melee attack - schedule damage
            const damageDelay = (1 / combat.attackSpeed) * 0.5;
            const element = this.getDamageElement(attackerId, game, combat);

            game.gameManager.call('scheduleDamage',
                attackerId,
                targetId,
                combat.damage,
                element,
                damageDelay,
                {
                    isMelee: true,
                    weaponRange: (combat.range || 50) + 10
                }
            );
        } else if (game.abilitySystem) {
            // Ability-only units (damage = 0) - use abilities for combat
            this.useOffensiveAbility(attackerId, targetId, game);
        }
    }

    useOffensiveAbility(attackerId, targetId, game) {
        const abilities = game.gameManager.call('getEntityAbilities', attackerId);
        if (!abilities || abilities.length === 0) return;

        // Find available offensive abilities
        const availableAbilities = abilities
            .filter(ability => game.abilitySystem.isAbilityOffCooldown(attackerId, ability.id))
            .filter(ability => !ability.canExecute || ability.canExecute(attackerId))
            .sort((a, b) => (b.priority || 0) - (a.priority || 0) || a.id.localeCompare(b.id));

        if (availableAbilities.length > 0) {
            game.abilitySystem.useAbility(attackerId, availableAbilities[0].id, { target: targetId });
        }
    }

    fireProjectile(attackerId, targetId, game, combat) {
        const targetHealth = game.getComponent(targetId, 'health');
        if (!targetHealth || targetHealth.current <= 0) return;

        const projectileData = game.getCollections().projectiles[combat.projectile];
        if (projectileData) {
            game.gameManager.call('fireProjectile', attackerId, targetId, {
                id: combat.projectile,
                ...projectileData
            });
        }
    }

    findNearestEnemy(entityId, game) {
        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !team) return null;

        const visionRange = combat.visionRange || 300;

        // Get all potential targets
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');

        // Sort for deterministic iteration
        const sortedTargets = potentialTargets.sort((a, b) => String(a).localeCompare(String(b)));

        let nearestEnemy = null;
        let nearestDistance = Infinity;

        for (const targetId of sortedTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            const targetHealth = game.getComponent(targetId, 'health');
            const targetPos = game.getComponent(targetId, 'position');
            const targetDeathState = game.getComponent(targetId, 'deathState');

            // Skip allies
            if (targetTeam.team === team.team) continue;

            // Skip dead or dying units
            if (!targetHealth || targetHealth.current <= 0) continue;
            if (targetDeathState && targetDeathState.isDying) continue;

            const distance = this.distance(pos, targetPos);

            // Check if within vision range
            if (distance > visionRange) continue;

            if (distance < nearestDistance) {
                nearestDistance = distance;
                nearestEnemy = targetId;
            }
        }

        return nearestEnemy;
    }

    isValidTarget(targetId, game) {
        const targetHealth = game.getComponent(targetId, 'health');
        const targetDeathState = game.getComponent(targetId, 'deathState');

        if (!targetHealth || targetHealth.current <= 0) return false;
        if (targetDeathState && targetDeathState.isDying) return false;

        return true;
    }

    getEffectiveAttackSpeed(entityId, game, baseAttackSpeed) {
        if (game.equipmentSystem) {
            const equipment = game.getComponent(entityId, 'equipment');
            if (equipment && equipment.attackSpeed) {
                return baseAttackSpeed * equipment.attackSpeed;
            }
        }
        return baseAttackSpeed;
    }

    calculateAnimationSpeed(attackerId, game, baseAttackSpeed) {
        let animSpeed = baseAttackSpeed;
        if (game.equipmentSystem) {
            const equipment = game.getComponent(attackerId, 'equipment');
            if (equipment && equipment.attackSpeed) {
                animSpeed *= equipment.attackSpeed;
            }
        }
        return animSpeed;
    }

    getDamageElement(entityId, game, combat) {
        if (combat.element) {
            return combat.element;
        }

        const weaponElement = this.getWeaponElement(entityId, game);
        if (weaponElement) {
            return weaponElement;
        }

        return game.damageSystem?.ELEMENT_TYPES?.PHYSICAL || 'physical';
    }

    getWeaponElement(entityId, game) {
        if (!game.equipmentSystem) return null;

        const equipment = game.getComponent(entityId, 'equipment');
        if (!equipment) return null;

        const mainHandItem = equipment.slots?.mainHand;
        if (mainHandItem) {
            const itemData = game.gameManager.call('getItemData', mainHandItem);
            if (itemData && itemData.stats && itemData.stats.element) {
                return itemData.stats.element;
            }
        }

        const offHandItem = equipment.slots?.offHand;
        if (offHandItem) {
            const itemData = game.gameManager.call('getItemData', offHandItem);
            if (itemData && itemData.stats && itemData.stats.element) {
                return itemData.stats.element;
            }
        }

        return null;
    }

    distance(pos, target) {
        const dx = target.x - pos.x;
        const dz = target.z - pos.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

ConstructBuildingBehaviorAction.script:
/**
 * ConstructBuildingBehaviorAction - Performs construction over time
 *
 * Reads from shared state:
 * - shared.targetBuilding - Building entity ID
 * - shared.buildTime - Construction time required
 *
 * Returns RUNNING while constructing, SUCCESS when complete
 * Handles completion logic (restore model, health, register with systems)
 */
class ConstructBuildingBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const memory = this.getMemory(entityId);

        const buildingId = shared.targetBuilding;
        if (!buildingId) {
            return this.failure();
        }

        const buildingPlacement = game.getComponent(buildingId, 'placement');
        if (!buildingPlacement || !buildingPlacement.isUnderConstruction) {
            // Building no longer under construction
            return this.failure();
        }

        // Anchor the builder and face the building
        this.anchorBuilder(entityId, buildingId, game);

        // Initialize construction start time
        if (!memory.constructionStartTime) {
            memory.constructionStartTime = game.state.round;
        }

        // Play building animation
        this.playBuildAnimation(entityId, game);

        // Check progress
        const elapsed = game.state.round - memory.constructionStartTime;
        const buildTime = shared.buildTime || this.parameters.defaultBuildTime || 5;

        if (elapsed >= buildTime) {
            // Complete construction
            this.completeConstruction(entityId, buildingId, buildingPlacement, game);

            // Clear shared state
            shared.targetBuilding = null;
            shared.targetPosition = null;
            shared.buildTime = null;

            // Unanchor builder
            this.unanchorBuilder(entityId, game);

            return this.success();
        }

        // Still building
        return this.running({ progress: elapsed / buildTime });
    }

    anchorBuilder(entityId, buildingId, game) {
        const pos = game.getComponent(entityId, 'position');
        const buildingPos = game.getComponent(buildingId, 'position');
        const vel = game.getComponent(entityId, 'velocity');

        if (vel && !vel.anchored) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Face the building
        if (pos && buildingPos) {
            const facing = game.getComponent(entityId, 'facing');
            if (facing) {
                const dx = buildingPos.x - pos.x;
                const dz = buildingPos.z - pos.z;
                facing.angle = Math.atan2(dz, dx);
            }
        }

        // Clear movement target
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        if (playerOrder) {
            playerOrder.targetPosition = null;
        }
    }

    unanchorBuilder(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = false;
        }
    }

    playBuildAnimation(entityId, game) {
        if (!game.animationSystem) return;

        const animState = game.animationSystem.entityAnimationStates.get(entityId);
        if (animState) {
            const finished = game.animationSystem.isAnimationFinished(entityId, animState.currentClip);
            if (finished || animState.currentClip !== 'attack') {
                game.abilitySystem?.startAbilityAnimation(entityId, { castTime: 1 });
            }
        }
    }

    completeConstruction(entityId, buildingId, buildingPlacement, game) {
        if (!buildingPlacement || !buildingPlacement.unitType) {
            console.error('[ConstructBuildingBehaviorAction] Cannot complete construction - missing placement or unitType');
            return;
        }

        const actualBuildingType = buildingPlacement.unitType;

        // 1. Update renderable - change from underConstruction to actual building
        const renderComponent = game.getComponent(buildingId, 'renderable');
        if (renderComponent) {
            renderComponent.spawnType = actualBuildingType.id;
            game.gameManager.call('removeInstance', buildingId);
        }

        // 2. Restore health to full
        const maxHP = actualBuildingType.hp || 100;
        const health = game.getComponent(buildingId, 'health');
        if (health) {
            health.max = maxHP;
            health.current = maxHP;
        }

        // 3. Update unitType component
        const unitTypeComponent = game.getComponent(buildingId, 'unitType');
        if (unitTypeComponent) {
            Object.assign(unitTypeComponent, actualBuildingType);
        }

        // 4. Mark construction complete
        buildingPlacement.isUnderConstruction = false;
        buildingPlacement.assignedBuilder = null;

        // 5. Register with shop system
        if (game.shopSystem) {
            game.shopSystem.addBuilding(actualBuildingType.id, buildingId);
        }

        // 6. Change building to idle animation
        if (game.animationSystem) {
            game.animationSystem.changeAnimation(buildingId, 'idle', 1.0, 0);
        }
    }

    onEnd(entityId, game) {
        // Unanchor builder if action is interrupted
        this.unanchorBuilder(entityId, game);

        // Clean up assigned builder reference
        const shared = this.getShared(entityId, game);
        if (shared.targetBuilding) {
            const buildingPlacement = game.getComponent(shared.targetBuilding, 'placement');
            if (buildingPlacement && buildingPlacement.assignedBuilder === entityId) {
                buildingPlacement.assignedBuilder = null;
            }
        }

        super.onEnd(entityId, game);
    }
}

DefendBehaviorAction.script:
/**
 * DefendBehaviorAction - Combat action
 * Holds position and attacks enemies that come within range
 *
 * Parameters:
 *   defendRadius: number (default: 100) - Attack enemies within this radius
 *   anchorPosition: boolean (default: true) - Stay at current position
 *   positionKey: string (optional) - Key in shared for defend position
 *
 * Returns RUNNING while defending, SUCCESS if no enemies, FAILURE if can't defend
 */
class DefendBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const defendRadius = params.defendRadius || 100;
        const anchorPosition = params.anchorPosition !== false;
        const positionKey = params.positionKey;

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');
        const health = game.getComponent(entityId, 'health');
        const vel = game.getComponent(entityId, 'velocity');

        if (!pos || !team || !combat || !health || health.current <= 0) {
            return this.failure();
        }

        const memory = this.getMemory(entityId);
        const shared = this.getShared(entityId, game);

        // Set defend position
        if (!memory.defendPosition) {
            if (positionKey && shared[positionKey]) {
                memory.defendPosition = { ...shared[positionKey] };
            } else {
                memory.defendPosition = { x: pos.x, z: pos.z };
            }
        }

        // Anchor at defend position
        if (anchorPosition && vel) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Find enemies in defend radius
        const enemy = this.findNearestEnemyInRadius(entityId, game, memory.defendPosition, team, defendRadius);

        if (!enemy) {
            // No enemies - just defending
            return this.success({
                status: 'defending',
                position: memory.defendPosition,
                noEnemies: true
            });
        }

        // Check if we're in attack range
        const attackRange = combat.range || 50;
        const distanceToEnemy = this.distance(pos, game.getComponent(enemy.id, 'position'));

        if (distanceToEnemy <= attackRange) {
            // Attack the enemy
            this.performAttack(entityId, enemy.id, game, combat);

            return this.running({
                status: 'attacking',
                target: enemy.id,
                distance: distanceToEnemy,
                position: memory.defendPosition
            });
        }

        // Enemy in defend radius but not attack range - stay put and wait
        return this.running({
            status: 'watching',
            target: enemy.id,
            distance: distanceToEnemy,
            position: memory.defendPosition
        });
    }

    findNearestEnemyInRadius(entityId, game, centerPos, team, radius) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        let nearest = null;
        let nearestDistance = Infinity;

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetDeathState = game.getComponent(targetId, 'deathState');
            if (targetDeathState && targetDeathState.isDying) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(centerPos, targetPos);

            if (distance <= radius && distance < nearestDistance) {
                nearestDistance = distance;
                nearest = { id: targetId, distance };
            }
        }

        return nearest;
    }

    performAttack(attackerId, targetId, game, combat) {
        if (!combat.lastAttack) combat.lastAttack = 0;

        const timeSinceLastAttack = game.state.now - combat.lastAttack;
        if (timeSinceLastAttack < 1 / combat.attackSpeed) {
            return; // On cooldown
        }

        combat.lastAttack = game.state.now;

        // Face the target
        const attackerPos = game.getComponent(attackerId, 'position');
        const targetPos = game.getComponent(targetId, 'position');
        const facing = game.getComponent(attackerId, 'facing');

        if (attackerPos && targetPos && facing) {
            const dx = targetPos.x - attackerPos.x;
            const dz = targetPos.z - attackerPos.z;
            facing.angle = Math.atan2(dz, dx);
        }

        // Trigger attack
        if (game.gameManager?.has('triggerSinglePlayAnimation')) {
            game.gameManager.call('triggerSinglePlayAnimation', attackerId, 'attack', combat.attackSpeed);
        }

        if (combat.projectile) {
            const projectileData = game.getCollections().projectiles?.[combat.projectile];
            if (projectileData) {
                game.gameManager.call('fireProjectile', attackerId, targetId, {
                    id: combat.projectile,
                    ...projectileData
                });
            }
        } else if (combat.damage > 0) {
            const damageDelay = (1 / combat.attackSpeed) * 0.5;
            game.gameManager.call('scheduleDamage', attackerId, targetId, combat.damage, 'physical', damageDelay);
        }
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

DepositGoldBehaviorAction.script:
/**
 * Deposit Gold Action
 * Deposits carried gold at the depot, awarding it to the team
 *
 * Reads from shared state:
 *   - goldAmount: amount of gold being carried
 *   - hasGold: whether carrying gold
 *
 * Clears from shared state on success:
 *   - goldAmount
 *   - hasGold
 *   - targetDepot
 *   - targetDepotPosition
 *
 * Parameters:
 *   - duration: number - Time in seconds to deposit (default: 1)
 *
 * Returns:
 *   - SUCCESS when deposit is complete
 *   - RUNNING while depositing
 *   - FAILURE if not carrying gold
 */
class DepositGoldBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const memory = this.getMemory(entityId);

        // Check if carrying gold
        if (!shared.hasGold || !shared.goldAmount || shared.goldAmount <= 0) {
            return this.failure();
        }

        const duration = (this.parameters.duration || 1); // Convert to ms
        const goldAmount = shared.goldAmount;

        // Initialize deposit start time
        if (!memory.depositStartTime) {
            memory.depositStartTime = game.state.now;
        }

        const elapsed = game.state.now - memory.depositStartTime;
        const progress = Math.min(1, elapsed / duration);

        if (elapsed >= duration) {
            // Deposit complete - award gold to team
            this.awardGoldToTeam(entityId, game, goldAmount);

            // Clear gold-related shared state
            shared.goldAmount = 0;
            shared.hasGold = false;
            shared.targetDepot = null;
            shared.targetDepotPosition = null;

            // Reset memory
            memory.depositStartTime = null;

            return this.success({
                goldDeposited: goldAmount,
                depositComplete: true
            });
        }

        // Still depositing
        return this.running({
            progress: progress,
            goldAmount: goldAmount,
            timeRemaining: (duration - elapsed)
        });
    }

    /**
     * Award gold to the entity's team
     */
    awardGoldToTeam(entityId, game, amount) {
        const team = game.getComponent(entityId, 'team');
        if (!team) return;

        if (game.isServer) {
            // Server-side: update player stats
            const room = game.room;
            if (room && room.players) {
                for (const [playerId, player] of room.players) {
                    if (player.stats && player.stats.side === team.team) {
                        player.stats.gold = (player.stats.gold || 0) + amount;
                        break;
                    }
                }
            }
        } else {
            // Client-side: update local state
            if (team.team === game.state.mySide) {
                game.state.playerGold = (game.state.playerGold || 0) + amount;
            }
        }
    }

    onStart(entityId, game) {
        const memory = this.getMemory(entityId);
        memory.depositStartTime = game.state.now;
    }

    onEnd(entityId, game) {
        const memory = this.getMemory(entityId);
        memory.depositStartTime = null;
        super.onEnd(entityId, game);
    }
}

ExtractGoldBehaviorAction.script:
/**
 * Extract Gold Action
 * Performs the actual mining over a duration, extracting gold from the mine
 *
 * Reads from shared state:
 *   - targetMine: entityId of the gold mine
 *
 * Sets in shared state:
 *   - goldAmount: amount of gold being carried
 *   - hasGold: true when carrying gold
 *   - miningProgress: 0-1 progress of current mining
 *
 * Parameters:
 *   - duration: number - Time in seconds to mine (default: 2)
 *   - goldPerTrip: number - Gold extracted per mining cycle (default: 10)
 *
 * Returns:
 *   - SUCCESS when mining is complete
 *   - RUNNING while mining
 *   - FAILURE if no targetMine
 */
class ExtractGoldBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const memory = this.getMemory(entityId);
        const targetMine = shared.targetMine;

        if (!targetMine) {
            return this.failure();
        }

        const duration = (this.parameters.duration || 2); 
        const goldPerTrip = this.parameters.goldPerTrip || 10;

        // Initialize mining start time
        if (!memory.miningStartTime) {
            memory.miningStartTime = game.state.now;
        }

        const elapsed = game.state.now - memory.miningStartTime;
        const progress = Math.min(1, elapsed / duration);

        // Update shared state with progress
        shared.miningProgress = progress;
        shared.goldAmount = goldPerTrip * progress;
        shared.hasGold = progress > 0;

        if (elapsed >= duration) {
            // Mining complete!
            shared.goldAmount = goldPerTrip;
            shared.hasGold = true;
            shared.miningProgress = 1;

            // Process next miner in queue
            if (game.goldMineSystem) {
                game.goldMineSystem.processNextMinerInQueue(targetMine);
            }

            // Clear mine-related shared state
            shared.targetMine = null;
            shared.targetMinePosition = null;
            shared.inMineQueue = false;
            shared.canMine = false;

            // Reset memory
            memory.miningStartTime = null;

            return this.success({
                goldAmount: goldPerTrip,
                miningComplete: true
            });
        }

        // Still mining
        return this.running({
            progress: progress,
            goldAmount: shared.goldAmount,
            timeRemaining: (duration - elapsed)
        });
    }

    onStart(entityId, game) {
        const memory = this.getMemory(entityId);
        memory.miningStartTime = game.state.now;
    }

    onEnd(entityId, game) {
        const memory = this.getMemory(entityId);
        memory.miningStartTime = null;
        super.onEnd(entityId, game);
    }
}

FindAllyNeedingHelpBehaviorAction.script:
/**
 * FindAllyNeedingHelpBehaviorAction - Targeting action
 * Finds an allied unit that needs help (low health or in combat)
 *
 * Parameters:
 *   range: number (default: 500) - Search range
 *   targetKey: string (default: 'allyTarget') - Key to store target in shared state
 *   healthThreshold: number (default: 0.7) - Health % below which ally needs help
 *   prioritizeLowestHealth: boolean (default: true) - Target lowest health ally
 *   excludeSelf: boolean (default: true) - Don't target self
 *
 * Returns SUCCESS if ally needing help found, FAILURE otherwise
 */
class FindAllyNeedingHelpBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'allyTarget';
        const healthThreshold = params.healthThreshold !== undefined ? params.healthThreshold : 0.7;
        const prioritizeLowestHealth = params.prioritizeLowestHealth !== false;
        const excludeSelf = params.excludeSelf !== false;
        const range = params.range !== undefined ? params.range : 500;

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');

        if (!pos || !team) {
            return this.failure();
        }

        const ally = this.findAllyNeedingHelp(entityId, game, pos, team, range, healthThreshold, prioritizeLowestHealth, excludeSelf);

        if (ally) {
            const shared = this.getShared(entityId, game);
            shared[targetKey] = ally.id;

            return this.success({
                allyTarget: ally.id,
                distance: ally.distance,
                healthPercent: ally.healthPercent,
                inCombat: ally.inCombat
            });
        }

        return this.failure();
    }

    findAllyNeedingHelp(entityId, game, pos, team, range, healthThreshold, prioritizeLowestHealth, excludeSelf) {
        const potentialAllies = game.getEntitiesWith('position', 'team', 'health');
        const alliesNeedingHelp = [];

        for (const allyId of potentialAllies) {
            if (excludeSelf && allyId === entityId) continue;

            const allyTeam = game.getComponent(allyId, 'team');
            if (allyTeam.team !== team.team) continue;

            const allyHealth = game.getComponent(allyId, 'health');
            if (!allyHealth || allyHealth.current <= 0) continue;

            const allyDeathState = game.getComponent(allyId, 'deathState');
            if (allyDeathState && allyDeathState.isDying) continue;

            const allyPos = game.getComponent(allyId, 'position');
            const distance = this.distance(pos, allyPos);

            if (distance > range) continue;

            const healthPercent = allyHealth.current / allyHealth.max;

            // Check if ally needs help
            const needsHelp = healthPercent < healthThreshold;
            const inCombat = this.isInCombat(allyId, game);

            if (needsHelp || inCombat) {
                alliesNeedingHelp.push({
                    id: allyId,
                    distance,
                    healthPercent,
                    inCombat,
                    needsHelp
                });
            }
        }

        if (alliesNeedingHelp.length === 0) return null;

        // Sort by priority
        alliesNeedingHelp.sort((a, b) => {
            // Prioritize low health allies
            if (prioritizeLowestHealth) {
                const healthDiff = a.healthPercent - b.healthPercent;
                if (Math.abs(healthDiff) > 0.1) return healthDiff;
            }
            // Then by combat status
            if (a.inCombat !== b.inCombat) return a.inCombat ? -1 : 1;
            // Then by distance
            return a.distance - b.distance;
        });

        return alliesNeedingHelp[0];
    }

    isInCombat(entityId, game) {
        const aiState = game.getComponent(entityId, 'aiState');
        if (aiState && aiState.currentAction === 'CombatBehaviorAction') {
            return true;
        }
        return false;
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

FindNearestDepotBehaviorAction.script:
/**
 * Find Nearest Depot Action
 * Finds the nearest depot (town hall) belonging to the entity's team and stores it in shared state
 *
 * Sets in shared state:
 *   - targetDepot: entityId of the depot
 *   - targetDepotPosition: {x, z} position of the depot
 *   - targetPosition: same as targetDepotPosition (for movement)
 *
 * Parameters:
 *   - depotType: string - unitType.id to look for (default: 'townHall')
 *
 * Returns:
 *   - SUCCESS if a depot was found
 *   - FAILURE if no depot exists for this team
 */
class FindNearestDepotBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');

        if (!pos || !team) {
            return this.failure();
        }

        const depotType = this.parameters.depotType || 'townHall';

        // Get all entities that could be depots
        const depotEntities = game.getEntitiesWith('position', 'team', 'unitType');

        if (!depotEntities || depotEntities.size === 0) {
            return this.failure();
        }

        let closestDepot = null;
        let closestPosition = null;
        let closestDistance = Infinity;

        // Find nearest depot belonging to our team
        for (const depotId of depotEntities) {
            const depotTeam = game.getComponent(depotId, 'team');
            const depotUnitType = game.getComponent(depotId, 'unitType');
            const depotPos = game.getComponent(depotId, 'position');

            if (depotTeam &&
                depotTeam.team === team.team &&
                depotUnitType &&
                depotUnitType.id === depotType) {

                const distance = this.distance(pos, depotPos);

                if (distance < closestDistance) {
                    closestDistance = distance;
                    closestDepot = depotId;
                    closestPosition = { x: depotPos.x, z: depotPos.z };
                }
            }
        }

        if (!closestDepot) {
            return this.failure();
        }

        // Store in shared state
        const shared = this.getShared(entityId, game);
        shared.targetDepot = closestDepot;
        shared.targetDepotPosition = closestPosition;
        shared.targetPosition = closestPosition; // For movement

        return this.success({
            targetDepot: closestDepot,
            targetPosition: closestPosition,
            distance: closestDistance
        });
    }
}

FindNearestEnemyBehaviorAction.script:
/**
 * FindNearestEnemyBehaviorAction - Targeting action
 * Finds the nearest enemy and stores it in shared state
 *
 * Parameters:
 *   range: number (default: uses combat.visionRange or 300) - Search range
 *   targetKey: string (default: 'target') - Key to store target in shared state
 *
 * Returns SUCCESS if enemy found, FAILURE otherwise
 */
class FindNearestEnemyBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !team) {
            return this.failure();
        }

        const range = params.range !== undefined ? params.range : (combat?.visionRange || 300);

        const nearestEnemy = this.findNearestEnemy(entityId, game, pos, team, range);

        if (nearestEnemy) {
            const shared = this.getShared(entityId, game);
            shared[targetKey] = nearestEnemy.id;

            return this.success({
                target: nearestEnemy.id,
                distance: nearestEnemy.distance,
                range
            });
        }

        return this.failure();
    }

    findNearestEnemy(entityId, game, pos, team, range) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        const sortedTargets = potentialTargets.sort((a, b) => String(a).localeCompare(String(b)));

        let nearest = null;
        let nearestDistance = Infinity;

        for (const targetId of sortedTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetDeathState = game.getComponent(targetId, 'deathState');
            if (targetDeathState && targetDeathState.isDying) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance <= range && distance < nearestDistance) {
                nearestDistance = distance;
                nearest = { id: targetId, distance };
            }
        }

        return nearest;
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

FindNearestGoldMineBehaviorAction.script:
/**
 * Find Nearest Gold Mine Action
 * Finds the nearest gold mine belonging to the entity's team and stores it in shared state
 *
 * Sets in shared state:
 *   - targetMine: entityId of the gold mine
 *   - targetMinePosition: {x, z} position of the mine
 *
 * Returns:
 *   - SUCCESS if a gold mine was found
 *   - FAILURE if no gold mine exists for this team
 */
class FindNearestGoldMineBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');

        if (!pos || !team) {
            return this.failure();
        }

        // Get all gold mine entities
        const goldMineEntities = game.getEntitiesWith('goldMine', 'position', 'team');

        if (!goldMineEntities || goldMineEntities.size === 0) {
            return this.failure();
        }

        // Sort for deterministic iteration
        const sortedMineIds = Array.from(goldMineEntities).sort((a, b) =>
            String(a).localeCompare(String(b))
        );

        let closestMine = null;
        let closestPosition = null;
        let closestDistance = Infinity;

        // Find nearest mine belonging to our team
        for (const mineId of sortedMineIds) {
            const mineTeam = game.getComponent(mineId, 'team');
            const minePos = game.getComponent(mineId, 'position');

            if (mineTeam && mineTeam.team === team.team) {
                const distance = this.distance(pos, minePos);

                if (distance < closestDistance) {
                    closestDistance = distance;
                    closestMine = mineId;
                    closestPosition = { x: minePos.x, z: minePos.z };
                }
            }
        }

        if (!closestMine) {
            return this.failure();
        }

        // Store in shared state
        console.log('storing targetMine for', entityId );
        const shared = this.getShared(entityId, game);
        shared.targetMine = closestMine;
        shared.targetMinePosition = closestPosition;
        shared.targetPosition = closestPosition; // For movement

        return this.success({
            targetMine: closestMine,
            targetPosition: closestPosition,
            distance: closestDistance
        });
    }
}

FindWeakestEnemyBehaviorAction.script:
/**
 * FindWeakestEnemyBehaviorAction - Targeting action
 * Finds the enemy with lowest health and stores it in shared state
 *
 * Parameters:
 *   range: number (default: uses combat.visionRange or 300) - Search range
 *   targetKey: string (default: 'target') - Key to store target in shared state
 *   usePercentage: boolean (default: true) - Sort by health percentage vs absolute
 *   maxHealthPercent: number (default: 1.0) - Only target enemies below this health %
 *
 * Returns SUCCESS if enemy found, FAILURE otherwise
 */
class FindWeakestEnemyBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';
        const usePercentage = params.usePercentage !== false;
        const maxHealthPercent = params.maxHealthPercent !== undefined ? params.maxHealthPercent : 1.0;

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !team) {
            return this.failure();
        }

        const range = params.range !== undefined ? params.range : (combat?.visionRange || 300);

        const weakestEnemy = this.findWeakestEnemy(entityId, game, pos, team, range, usePercentage, maxHealthPercent);

        if (weakestEnemy) {
            const shared = this.getShared(entityId, game);
            shared[targetKey] = weakestEnemy.id;

            return this.success({
                target: weakestEnemy.id,
                distance: weakestEnemy.distance,
                healthPercent: weakestEnemy.healthPercent,
                range
            });
        }

        return this.failure();
    }

    findWeakestEnemy(entityId, game, pos, team, range, usePercentage, maxHealthPercent) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        const enemies = [];

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetDeathState = game.getComponent(targetId, 'deathState');
            if (targetDeathState && targetDeathState.isDying) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance > range) continue;

            const healthPercent = targetHealth.current / targetHealth.max;
            if (healthPercent > maxHealthPercent) continue;

            enemies.push({
                id: targetId,
                distance,
                healthPercent,
                healthCurrent: targetHealth.current
            });
        }

        if (enemies.length === 0) return null;

        // Sort by health (lowest first), then by distance
        enemies.sort((a, b) => {
            const healthDiff = usePercentage
                ? a.healthPercent - b.healthPercent
                : a.healthCurrent - b.healthCurrent;
            if (Math.abs(healthDiff) > 0.01) return healthDiff;
            return a.distance - b.distance;
        });

        return enemies[0];
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

FleeBehaviorAction.script:
/**
 * FleeBehaviorAction - Movement action
 * Runs away from the nearest threat
 *
 * Parameters:
 *   fleeDistance: number (default: 200) - How far to flee
 *   threatRange: number (default: 150) - Range to detect threats
 *   targetKey: string (default: 'threat') - Key in shared for specific threat to flee from
 *
 * Returns RUNNING while fleeing, SUCCESS when safe, FAILURE if no threats
 */
class FleeBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const fleeDistance = params.fleeDistance || 200;
        const threatRange = params.threatRange || 150;
        const targetKey = params.targetKey || 'threat';

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const vel = game.getComponent(entityId, 'velocity');

        if (!pos || !team) {
            return this.failure();
        }

        const memory = this.getMemory(entityId);
        const shared = this.getShared(entityId, game);

        // Find threat to flee from
        let threatId = shared[targetKey];
        let threatPos = null;

        if (threatId) {
            threatPos = game.getComponent(threatId, 'position');
            const threatHealth = game.getComponent(threatId, 'health');
            if (!threatPos || !threatHealth || threatHealth.current <= 0) {
                threatId = null;
            }
        }

        // If no specific threat, find nearest enemy
        if (!threatId) {
            const nearestEnemy = this.findNearestEnemy(entityId, game, pos, team, threatRange);
            if (nearestEnemy) {
                threatId = nearestEnemy.id;
                threatPos = game.getComponent(threatId, 'position');
            }
        }

        // No threats nearby - we're safe
        if (!threatId || !threatPos) {
            if (vel) vel.anchored = false;
            return this.success({ status: 'safe', noThreatsFound: true });
        }

        const distanceToThreat = this.distance(pos, threatPos);

        // Already far enough away
        if (distanceToThreat >= fleeDistance) {
            if (vel) vel.anchored = false;
            return this.success({ status: 'safe', distanceToThreat });
        }

        // Calculate flee direction (away from threat)
        const dx = pos.x - threatPos.x;
        const dz = pos.z - threatPos.z;
        const dist = Math.sqrt(dx * dx + dz * dz) || 1;

        // Target position is away from threat
        const targetX = pos.x + (dx / dist) * fleeDistance;
        const targetZ = pos.z + (dz / dist) * fleeDistance;

        memory.fleeState = 'fleeing';
        memory.targetPosition = { x: targetX, z: targetZ };
        memory.threat = threatId;

        return this.running({
            targetPosition: memory.targetPosition,
            threat: threatId,
            distanceToThreat,
            fleeDistance
        });
    }

    findNearestEnemy(entityId, game, pos, team, range) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        let nearest = null;
        let nearestDistance = Infinity;

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance <= range && distance < nearestDistance) {
                nearestDistance = distance;
                nearest = { id: targetId, distance };
            }
        }

        return nearest;
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

FollowBehaviorAction.script:
/**
 * FollowBehaviorAction - Movement action
 * Follows another entity, maintaining a certain distance
 *
 * Parameters:
 *   targetKey: string (default: 'followTarget') - Key in shared state for target
 *   followDistance: number (default: 50) - Ideal distance to maintain
 *   maxDistance: number (default: 200) - Start following if farther than this
 *   stopDistance: number (default: 30) - Stop following if closer than this
 *
 * Returns RUNNING while following, SUCCESS if at ideal distance, FAILURE if no target
 */
class FollowBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'followTarget';
        const followDistance = params.followDistance || 50;
        const maxDistance = params.maxDistance || 200;
        const stopDistance = params.stopDistance || 30;

        const pos = game.getComponent(entityId, 'position');
        const vel = game.getComponent(entityId, 'velocity');

        if (!pos) {
            return this.failure();
        }

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        const targetPos = game.getComponent(targetId, 'position');
        const targetHealth = game.getComponent(targetId, 'health');

        // Check if target is valid
        if (!targetPos) {
            return this.failure();
        }

        // Check if target is alive (if it has health)
        if (targetHealth && targetHealth.current <= 0) {
            shared[targetKey] = null; // Clear invalid target
            return this.failure();
        }

        const memory = this.getMemory(entityId);
        const distance = this.distance(pos, targetPos);

        // Already at ideal distance
        if (distance >= stopDistance && distance <= followDistance) {
            if (vel) {
                vel.anchored = false;
            }
            delete memory.targetPosition;
            return this.success({
                status: 'inPosition',
                distance,
                target: targetId
            });
        }

        // Too close, back up slightly (or just stop)
        if (distance < stopDistance) {
            if (vel) {
                vel.anchored = true;
                vel.vx = 0;
                vel.vz = 0;
            }
            delete memory.targetPosition;
            return this.success({
                status: 'tooClose',
                distance,
                target: targetId
            });
        }

        // Too far, need to follow
        if (distance > followDistance) {
            // Calculate position at followDistance from target
            const dx = pos.x - targetPos.x;
            const dz = pos.z - targetPos.z;
            const dist = Math.sqrt(dx * dx + dz * dz) || 1;

            // Target a point followDistance away from the target
            const targetX = targetPos.x + (dx / dist) * followDistance;
            const targetZ = targetPos.z + (dz / dist) * followDistance;

            memory.targetPosition = { x: targetX, z: targetZ };

            if (vel) {
                vel.anchored = false;
            }

            return this.running({
                status: 'following',
                targetPosition: memory.targetPosition,
                distance,
                target: targetId
            });
        }

        return this.success({ status: 'inPosition', distance, target: targetId });
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

HasBuildOrderBehaviorAction.script:
/**
 * HasBuildOrderBehaviorAction - Condition check for valid build order
 *
 * Checks if entity has a valid build order with:
 * - playerOrder.meta.buildingId exists
 * - Building exists and is under construction
 *
 * Returns SUCCESS if valid build order exists, FAILURE otherwise
 */
class HasBuildOrderBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const playerOrder = game.getComponent(entityId, 'playerOrder');

        // Check if we have a build order
        if (!playerOrder || !playerOrder.meta || !playerOrder.meta.buildingId) {
            return this.failure();
        }

        const buildingId = playerOrder.meta.buildingId;
        const buildingPlacement = game.getComponent(buildingId, 'placement');

        // Check if building exists and is under construction
        if (!buildingPlacement || !buildingPlacement.isUnderConstruction) {
            return this.failure();
        }

        return this.success();
    }
}

HasGoldBehaviorAction.script:
/**
 * Has Gold Action
 * Simple condition check - succeeds if entity is carrying gold
 *
 * Reads from shared state:
 *   - hasGold: boolean
 *   - goldAmount: number (optional, for threshold check)
 *
 * Parameters:
 *   - minAmount: number - Minimum gold required (default: 0, any gold)
 *
 * Returns:
 *   - SUCCESS if carrying gold (and meets minAmount if specified)
 *   - FAILURE if not carrying gold
 */
class HasGoldBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const minAmount = this.parameters.minAmount || 0;

        const hasGold = shared.hasGold === true;
        const goldAmount = shared.goldAmount || 0;

        if (hasGold && goldAmount > minAmount) {
            return this.success({
                hasGold: true,
                goldAmount: goldAmount
            });
        }

        return this.failure();
    }
}

HasTargetBehaviorAction.script:
/**
 * HasTargetBehaviorAction - Condition action
 * Checks if entity has a valid target stored in shared state
 *
 * Parameters:
 *   targetKey: string (default: 'target') - Key in shared state to check
 *   validateHealth: boolean (default: true) - Check if target is alive
 *
 * Returns SUCCESS if target exists and is valid, FAILURE otherwise
 */
class HasTargetBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';
        const validateHealth = params.validateHealth !== false;

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        // Optionally validate target is still alive
        if (validateHealth) {
            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) {
                // Clear invalid target
                shared[targetKey] = null;
                return this.failure();
            }

            const targetDeathState = game.getComponent(targetId, 'deathState');
            if (targetDeathState && targetDeathState.isDying) {
                shared[targetKey] = null;
                return this.failure();
            }
        }

        return this.success({ target: targetId });
    }
}

HealthCheckBehaviorAction.script:
/**
 * HealthCheckBehaviorAction - Condition action
 * Checks if entity's health is above or below a threshold
 *
 * Parameters:
 *   threshold: number (default: 0.5) - Health percentage threshold (0-1)
 *   comparison: string (default: 'below') - 'below', 'above', 'equals'
 *   usePercentage: boolean (default: true) - Use percentage or absolute value
 *
 * Returns SUCCESS if condition is met, FAILURE otherwise
 */
class HealthCheckBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const threshold = params.threshold !== undefined ? params.threshold : 0.5;
        const comparison = params.comparison || 'below';
        const usePercentage = params.usePercentage !== false;

        const health = game.getComponent(entityId, 'health');
        if (!health) {
            return this.failure();
        }

        let currentValue;
        if (usePercentage) {
            currentValue = health.current / health.max;
        } else {
            currentValue = health.current;
        }

        let conditionMet = false;
        switch (comparison) {
            case 'below':
                conditionMet = currentValue < threshold;
                break;
            case 'above':
                conditionMet = currentValue > threshold;
                break;
            case 'equals':
                conditionMet = Math.abs(currentValue - threshold) < 0.01;
                break;
            case 'atOrBelow':
                conditionMet = currentValue <= threshold;
                break;
            case 'atOrAbove':
                conditionMet = currentValue >= threshold;
                break;
        }

        if (conditionMet) {
            return this.success({
                healthPercent: health.current / health.max,
                healthCurrent: health.current,
                healthMax: health.max,
                threshold,
                comparison
            });
        }

        return this.failure();
    }
}

HoldPositionBehaviorAction.script:
class HoldPositionBehaviorAction extends GUTS.BaseBehaviorAction {
    /**
     * Action to hold position - prevents unit from moving
     * Activated when player issues a "hold position" order
     */
    execute(entityId, game) {
        const playerOrder = game.getComponent(entityId, 'playerOrder');

        // Check if this is a hold position order
        if (!playerOrder || !playerOrder.meta || playerOrder.meta.allowMovement !== false) {
            return this.failure();
        }

        // Stop movement
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Clear pathfinding
        const pathfinding = game.getComponent(entityId, 'pathfinding');
        if (pathfinding) {
            pathfinding.path = [];
        }

        // Return success - unit is holding position
        // This will prevent combat and other behaviors from taking over
        return this.success({
            holdingPosition: true,
            position: game.getComponent(entityId, 'position')
        });
    }
}

IdleBehaviorAction.script:
class IdleBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game, dt) {
        // Just idle
        return {};
    }

    onEnd(entityId, game) {
        // Nothing to clean up
    }
}

IsEnemyNearbyBehaviorAction.script:
/**
 * IsEnemyNearbyBehaviorAction - Condition action
 * Checks if there are enemies within range
 *
 * Parameters:
 *   range: number (default: uses combat.visionRange or 300) - Detection range
 *   minCount: number (default: 1) - Minimum enemies required
 *   storeNearest: boolean (default: false) - Store nearest enemy in shared state
 *   targetKey: string (default: 'target') - Key for storing nearest enemy
 *
 * Returns SUCCESS if enemies found, FAILURE otherwise
 */
class IsEnemyNearbyBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const minCount = params.minCount || 1;
        const storeNearest = params.storeNearest || false;
        const targetKey = params.targetKey || 'target';

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !team) {
            return this.failure();
        }

        const range = params.range !== undefined ? params.range : (combat?.visionRange || 300);

        const enemies = this.findEnemiesInRange(entityId, game, pos, team, range);

        if (enemies.length >= minCount) {
            const result = {
                enemyCount: enemies.length,
                range
            };

            if (storeNearest && enemies.length > 0) {
                const shared = this.getShared(entityId, game);
                shared[targetKey] = enemies[0].id;
                result.nearestEnemy = enemies[0].id;
                result.nearestDistance = enemies[0].distance;
            }

            return this.success(result);
        }

        return this.failure();
    }

    findEnemiesInRange(entityId, game, pos, team, range) {
        const enemies = [];
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetDeathState = game.getComponent(targetId, 'deathState');
            if (targetDeathState && targetDeathState.isDying) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance <= range) {
                enemies.push({ id: targetId, distance });
            }
        }

        // Sort by distance
        enemies.sort((a, b) => a.distance - b.distance);

        return enemies;
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

IsInAttackRangeBehaviorAction.script:
/**
 * IsInAttackRangeBehaviorAction - Condition action
 * Checks if target is within attack range (uses combat.range)
 *
 * Parameters:
 *   targetKey: string (default: 'target') - Key in shared state for target
 *
 * Returns SUCCESS if in attack range, FAILURE otherwise
 */
class IsInAttackRangeBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(targetId, 'position');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !targetPos || !combat) {
            return this.failure();
        }

        const attackRange = combat.range || 50;
        const distance = this.distance(pos, targetPos);

        if (distance <= attackRange) {
            return this.success({
                distance,
                range: attackRange,
                target: targetId
            });
        }

        return this.failure();
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

IsInRangeBehaviorAction.script:
/**
 * IsInRangeBehaviorAction - Condition action
 * Checks if entity is within range of a target
 *
 * Parameters:
 *   range: number (default: 100) - Distance threshold
 *   comparison: string (default: 'within') - 'within', 'outside', 'tooClose'
 *   targetKey: string (default: 'target') - Key in shared state for target
 *   minRange: number (default: 0) - Minimum range for 'tooClose' check
 *
 * Returns SUCCESS if condition is met, FAILURE otherwise
 */
class IsInRangeBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const range = params.range !== undefined ? params.range : 100;
        const comparison = params.comparison || 'within';
        const targetKey = params.targetKey || 'target';
        const minRange = params.minRange || 0;

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(targetId, 'position');

        if (!pos || !targetPos) {
            return this.failure();
        }

        const distance = this.distance(pos, targetPos);

        let conditionMet = false;
        switch (comparison) {
            case 'within':
                conditionMet = distance <= range;
                break;
            case 'outside':
                conditionMet = distance > range;
                break;
            case 'tooClose':
                conditionMet = distance < minRange;
                break;
            case 'between':
                conditionMet = distance >= minRange && distance <= range;
                break;
        }

        if (conditionMet) {
            return this.success({
                distance,
                range,
                minRange,
                comparison,
                target: targetId
            });
        }

        return this.failure();
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

JoinMineQueueBehaviorAction.script:
/**
 * Join Mine Queue Action
 * Adds the entity to the mining queue for the target gold mine
 *
 * Reads from shared state:
 *   - targetMine: entityId of the gold mine to queue at
 *
 * Sets in shared state:
 *   - inMineQueue: true
 *
 * Returns:
 *   - SUCCESS if successfully joined queue (or already in queue)
 *   - FAILURE if no targetMine in shared state
 */
class JoinMineQueueBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const targetMine = shared.targetMine;

        if (!targetMine) {
            return this.failure();
        }


        // Check if already in queue
        if (shared.inMineQueue) {
            return this.success({ alreadyInQueue: true });
        }

        // Add to queue
        game.gameManager.call('addMinerToQueue', targetMine, entityId);
        shared.inMineQueue = true;

        return this.success({
            targetMine: targetMine,
            joinedQueue: true
        });
    }

    onEnd(entityId, game) {
        // Clean up shared state
        const shared = this.getShared(entityId, game);
        shared.inMineQueue = false;
        super.onEnd(entityId, game);
    }
}

KiteBehaviorAction.script:
/**
 * KiteBehaviorAction - Combat action
 * Attack-and-retreat pattern for ranged units
 * Maintains optimal range while attacking
 *
 * Parameters:
 *   targetKey: string (default: 'target') - Key in shared state for target
 *   optimalRange: number (default: uses combat.range * 0.9) - Ideal attack distance
 *   minRange: number (default: 60) - Minimum distance to maintain
 *   backoffDistance: number (default: 80) - How far to back off when too close
 *
 * Returns RUNNING while kiting, FAILURE if no target or can't attack
 */
class KiteBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const combat = game.getComponent(entityId, 'combat');
        const health = game.getComponent(entityId, 'health');
        const vel = game.getComponent(entityId, 'velocity');

        if (!pos || !team || !combat || !health || health.current <= 0) {
            return this.failure();
        }

        // Must have ranged attack for kiting
        if (!combat.range || combat.range < 60) {
            return this.failure();
        }

        const optimalRange = params.optimalRange || combat.range * 0.9;
        const minRange = params.minRange || 60;
        const backoffDistance = params.backoffDistance || 80;

        const shared = this.getShared(entityId, game);
        const memory = this.getMemory(entityId);

        // Get or find target
        let targetId = shared[targetKey];

        if (!targetId || !this.isValidTarget(targetId, game)) {
            // Try to find a new target
            const newTarget = this.findNearestEnemy(entityId, game, pos, team, combat.visionRange || 300);
            if (newTarget) {
                targetId = newTarget.id;
                shared[targetKey] = targetId;
            } else {
                return this.failure();
            }
        }

        const targetPos = game.getComponent(targetId, 'position');
        if (!targetPos) {
            return this.failure();
        }

        const distance = this.distance(pos, targetPos);
        memory.kiteState = memory.kiteState || 'attacking';

        // Face the target
        const facing = game.getComponent(entityId, 'facing');
        if (facing) {
            const dx = targetPos.x - pos.x;
            const dz = targetPos.z - pos.z;
            facing.angle = Math.atan2(dz, dx);
        }

        // Too close - back off
        if (distance < minRange) {
            memory.kiteState = 'backing_off';

            // Calculate backoff position (away from target)
            const dx = pos.x - targetPos.x;
            const dz = pos.z - targetPos.z;
            const dist = Math.sqrt(dx * dx + dz * dz) || 1;

            memory.targetPosition = {
                x: pos.x + (dx / dist) * backoffDistance,
                z: pos.z + (dz / dist) * backoffDistance
            };

            if (vel) vel.anchored = false;

            return this.running({
                status: 'backing_off',
                targetPosition: memory.targetPosition,
                target: targetId,
                distance,
                minRange
            });
        }

        // Too far - move closer
        if (distance > combat.range) {
            memory.kiteState = 'approaching';

            // Move towards target but stop at optimal range
            const dx = targetPos.x - pos.x;
            const dz = targetPos.z - pos.z;
            const dist = Math.sqrt(dx * dx + dz * dz) || 1;

            memory.targetPosition = {
                x: targetPos.x - (dx / dist) * optimalRange,
                z: targetPos.z - (dz / dist) * optimalRange
            };

            if (vel) vel.anchored = false;

            return this.running({
                status: 'approaching',
                targetPosition: memory.targetPosition,
                target: targetId,
                distance,
                optimalRange
            });
        }

        // In optimal range - attack and hold position
        memory.kiteState = 'attacking';

        if (vel) {
            vel.anchored = true;
            vel.vx = 0;
            vel.vz = 0;
        }

        // Perform attack
        this.performAttack(entityId, targetId, game, combat);

        delete memory.targetPosition;

        return this.running({
            status: 'attacking',
            target: targetId,
            distance,
            optimalRange,
            inRange: true
        });
    }

    performAttack(attackerId, targetId, game, combat) {
        if (!combat.lastAttack) combat.lastAttack = 0;

        const timeSinceLastAttack = game.state.now - combat.lastAttack;
        if (timeSinceLastAttack < 1 / combat.attackSpeed) {
            return;
        }

        combat.lastAttack = game.state.now;

        if (game.gameManager?.has('triggerSinglePlayAnimation')) {
            game.gameManager.call('triggerSinglePlayAnimation', attackerId, 'attack', combat.attackSpeed);
        }

        if (combat.projectile) {
            const projectileData = game.getCollections().projectiles?.[combat.projectile];
            if (projectileData) {
                game.gameManager.call('fireProjectile', attackerId, targetId, {
                    id: combat.projectile,
                    ...projectileData
                });
            }
        } else if (combat.damage > 0) {
            const damageDelay = (1 / combat.attackSpeed) * 0.5;
            game.gameManager.call('scheduleDamage', attackerId, targetId, combat.damage, 'physical', damageDelay);
        }
    }

    isValidTarget(targetId, game) {
        const targetHealth = game.getComponent(targetId, 'health');
        if (!targetHealth || targetHealth.current <= 0) return false;

        const targetDeathState = game.getComponent(targetId, 'deathState');
        if (targetDeathState && targetDeathState.isDying) return false;

        return true;
    }

    findNearestEnemy(entityId, game, pos, team, range) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        let nearest = null;
        let nearestDistance = Infinity;

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            if (!this.isValidTarget(targetId, game)) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance <= range && distance < nearestDistance) {
                nearestDistance = distance;
                nearest = { id: targetId, distance };
            }
        }

        return nearest;
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

MineGoldBehaviorAction.script:
class MineGoldBehaviorAction extends GUTS.BaseBehaviorAction {


    execute(entityId, game) {
        console.log(entityId, 'mining gold');
        const aiState = game.getComponent(entityId, 'aiState');
        const state = aiState.meta.mineState || 'traveling_to_mine';
        switch (state) {
            case 'traveling_to_mine':
                return this.travelToMine(entityId, aiState, game);
            case 'waiting_at_mine':
                return this.waitAtMine(entityId, aiState, game);
            case 'mining':
                return this.doMining(entityId, aiState, game);
            case 'traveling_to_depot':
                return this.travelToDepot(entityId, aiState, game);
            case 'depositing':
                return this.doDepositing(entityId, aiState, game);
        }
    }

    onEnd(entityId, game) {
        const aiState = game.getComponent(entityId, 'aiState');

        aiState.meta = {};
        aiState.currentAction = null;
    }

    travelToMine(entityId, aiState, game) {
        let targetMine = aiState.meta.targetMine;
        let targetPosition = aiState.meta.targetPosition;
        let targetMinePosition = aiState.meta.targetMinePosition;
        if(!targetMine){
            let nearestMineData = this.findNearesetGoldMine(entityId, game);
            targetMine = nearestMineData.targetMine;
            targetPosition = nearestMineData.targetPosition;
            targetMinePosition = nearestMineData.targetPosition;
        }
        const pos = game.getComponent(entityId, 'position');
        
        const distance = this.distance(pos, targetMinePosition);
        if (distance < this.parameters.miningRange) {
            this.game.goldMineSystem.addMinerToQueue(targetMine, entityId); 
            return { 
                targetPosition: targetMinePosition,
                targetMinePosition: targetMinePosition,
                targetMine: targetMine,
                mineState: 'waiting_at_mine',
                miningStartTime: game.state.now
             };
        }

        // Set target for MovementSystem to handle
        return {
            targetPosition: targetMinePosition,
            targetMinePosition: targetMinePosition,
            targetMine: targetMine,
            mineState: 'traveling_to_mine',
        };
    }
    waitAtMine(entityId, aiState, game) {
        let targetMine = aiState.meta.targetMine;
        let targetPosition = aiState.meta.targetPosition;
        let targetMinePosition = aiState.meta.targetMinePosition;

        const goldMine = game.getComponent(targetMine, 'goldMine');
        const isCurrentMiner = goldMine && goldMine.currentMiner === entityId;
        const isNextInQueue = this.game.goldMineSystem.isNextInQueue(
            targetMine,
            entityId
        );
        const isMineOccupied = this.game.goldMineSystem.isMineOccupied(targetMine);

        // Start mining if we're the current miner OR if we're next and mine is free
        if (isCurrentMiner || (isNextInQueue && !isMineOccupied)) {            
            if (!isCurrentMiner) {
                game.gameManager.call('processNextMinerInQueue', targetMine);
            }
            const pos = game.getComponent(entityId, 'position');
            const vel = game.getComponent(entityId, 'velocity');
            pos.x = targetMinePosition.x;
            pos.z = targetMinePosition.z;
            vel.vx = 0;
            vel.vz = 0;
            return {
                targetPosition: targetMinePosition,
                targetMinePosition: targetMinePosition,
                targetMine: targetMine,
                mineState: 'mining',
                miningStartTime: this.game.state.now
            }
        }
        return {
            targetPosition: targetPosition,
            targetMinePosition: targetMinePosition,
            targetMine: targetMine,
            mineState: 'waiting_at_mine',
            miningStartTime: aiState.meta.miningStartTime
        }
    }

    doMining(entityId, aiState, game) {
        const elapsed = game.state.now - aiState.meta.miningStartTime;
        let targetMine = aiState.meta.targetMine;

        if (elapsed >= this.parameters.miningDuration) {
            // Mining complete - process next miner in queue
            game.gameManager.call('processNextMinerInQueue', targetMine);

            return {
                hasGold: true,
                goldAmt: this.parameters.goldPerTrip,
                mineState: 'traveling_to_depot'
            };
        }

        return {
            hasGold: elapsed > 0,
            goldAmt: this.parameters.goldPerTrip * elapsed / this.parameters.miningDuration,
            mineState: 'mining',
            miningStartTime: aiState.meta.miningStartTime,
            targetMine: aiState.meta.targetMine
        };
    }

    travelToDepot(entityId, aiState, game) {        
        const pos = game.getComponent(entityId, 'position');
        const depot = this.findNearestDepot(entityId, game);

        if (!depot) {
            return null;
        }

        const depotPos = game.getComponent(depot, 'position');
        const distance = this.distance(pos, depotPos);

        if (distance < this.parameters.depositRange) {
            // Reached depot - clear movement target
            let depositStartTime = aiState.meta.depositStartTime;
            if(!depositStartTime){
                depositStartTime = game.state.now;
            }
            return { 
                mineState: 'depositing',
                goldAmt: aiState.meta.goldAmt,
                hasGold: aiState.meta.goldAmt > 0,
                depositStartTime  
            };
        }

        // Set target for MovementSystem to handle        
        return { 
            mineState: 'traveling_to_depot',
            goldAmt: aiState.meta.goldAmt,
            hasGold: aiState.meta.goldAmt > 0,
            targetPosition: { x: depotPos.x, z: depotPos.z }    
        };
    }

    doDepositing(entityId, aiState, game) {
        const elapsed = game.state.now - aiState.meta.depositStartTime;

        if (elapsed >= this.parameters.depositDuration) {
            const team = game.getComponent(entityId, 'team');

            // Award gold
            if (game.isServer) {
                const room = game.room;
                for (const [playerId, player] of room.players) {
                    if (player.stats.side === team.team) {
                        player.stats.gold += aiState.meta.goldAmt;
                        break;
                    }
                }
            } else {
                if (team.team === game.state.mySide) {
                    game.state.playerGold += aiState.meta.goldAmt;
                }
            }

            return { 
                mineState: 'traveling_to_mine'
            };
        }

        return { 
            mineState: 'depositing',
            goldAmt: aiState.meta.goldAmt,
            hasGold: aiState.meta.goldAmt > 0,
            targetPosition: aiState.meta.targetPosition,
            depositStartTime: aiState.meta.depositStartTime    
        };
    }

    findNearestDepot(entityId, game) {
        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const townHalls = game.getEntitiesWith(
            'position',
            'team',
            'unitType'
        );

        let nearest = null;
        let minDist = Infinity;

        for (const thId of townHalls) {
            const thTeam = game.getComponent(thId, 'team');
            const thType = game.getComponent(thId, 'unitType');
            const thPos = game.getComponent(thId, 'position');

            if (thTeam.team === team.team && thType.id === 'townHall') {
                const dist = this.distance(pos, thPos);
                if (dist < minDist) {
                    minDist = dist;
                    nearest = thId;
                }
            }
        }

        return nearest;
    }
    findNearesetGoldMine(entityId, game) {
        let closestMinePos = null;
        let closestDistance = Infinity;
        let closestMineEntityId = null;

        const pos = game.getComponent(entityId, "position");

        if (!pos) return;

        // Get all entities with goldMine component
        const goldMineEntities = game.getEntitiesWith("goldMine", "position", "team");

        // Sort for deterministic iteration
        const sortedMineIds = goldMineEntities.sort((a, b) =>
            String(a).localeCompare(String(b))
        );

        // Search through all gold mines in deterministic order
        for (const mineEntityId of sortedMineIds) {
            const mineTeam = game.getComponent(mineEntityId, "team");
            const entityTeam = game.getComponent(entityId, "team");
            const minePos = game.getComponent(mineEntityId, "position");

            // Check if this mine belongs to our team
            if (mineTeam && mineTeam.team === entityTeam.team) {
                // Calculate distance to this mine
                const dx = minePos.x - pos.x;
                const dz = minePos.z - pos.z;
                const distance = Math.sqrt(dx * dx + dz * dz);

                if (distance < closestDistance) {
                    closestDistance = distance;
                    closestMinePos = minePos;
                    closestMineEntityId = mineEntityId;
                }
            }
        }

        if (!closestMinePos) {
            return null;
        }

        return {
            targetMine: closestMineEntityId,
            targetPosition: {
                x: closestMinePos.x,
                y: closestMinePos.y || 0,
                z: closestMinePos.z
            }
        }

    }

    distance(pos, target) {
        const dx = target.x - pos.x;
        const dz = target.z - pos.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

MoveBehaviorAction.script:
class MoveBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        if (!playerOrder || !playerOrder.meta || !playerOrder.meta.isMoveOrder) {
            return this.failure();
        }

        const targetPosition = playerOrder.targetPosition;

        if (targetPosition) {
            const isForceMove = playerOrder.meta.preventEnemiesInRangeCheck || false;
            const pos = game.getComponent(entityId, 'position');
            const distanceToTarget = this.distance(pos, targetPosition);

            // Check if we've reached the target
            if (distanceToTarget <= this.parameters.arrivalThreshold) {
                // Movement complete
                return this.success({
                    targetPosition: targetPosition,
                    reachedTarget: true,
                    distanceToTarget,
                    preventEnemiesInRangeCheck: isForceMove,
                    handledByMove: true
                });
            }

            // Still moving toward target
            // MovementSystem will handle movement to target
            return this.running({
                targetPosition: targetPosition,
                reachedTarget: false,
                distanceToTarget,
                preventEnemiesInRangeCheck: isForceMove,
                handledByMove: true
            });
        }
        return this.failure();
    }

    distance(pos, target) {
        const dx = target.x - pos.x;
        const dz = target.z - pos.z;
        return Math.round(Math.sqrt(dx * dx + dz * dz));
    }
}

MoveToEnemyBehaviorAction.script:
/**
 * MoveToEnemyBehaviorAction - Movement action
 * Moves toward the target enemy stored in shared state
 *
 * Parameters:
 *   targetKey: string (default: 'target') - Key in shared state for target entity ID
 *   arrivalRange: number (default: uses combat.range) - Stop when this close to target
 *
 * Returns SUCCESS when in range, RUNNING while moving, FAILURE if no target
 */
class MoveToEnemyBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const targetKey = params.targetKey || 'target';

        const shared = this.getShared(entityId, game);
        const targetId = shared[targetKey];

        if (!targetId) {
            return this.failure();
        }

        const pos = game.getComponent(entityId, 'position');
        const targetPos = game.getComponent(targetId, 'position');
        const combat = game.getComponent(entityId, 'combat');

        if (!pos || !targetPos || !combat) {
            return this.failure();
        }

        const attackRange = combat.range || 50;
        const arrivalRange = params.arrivalRange || attackRange;
        const distance = this.distance(pos, targetPos);

        // Check if in range
        if (distance <= arrivalRange) {
            return this.success({
                arrived: true,
                distance,
                target: targetId
            });
        }

        // Still moving - return running with targetPosition for MovementSystem
        return this.running({
            targetPosition: { x: targetPos.x, z: targetPos.z },
            distance,
            target: targetId,
            moving: true
        });
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

MoveToSharedTargetBehaviorAction.script:
/**
 * Move To Shared Target Action
 * Moves entity toward a target position stored in shared state
 *
 * Reads from shared state:
 *   - targetPosition: {x, z} position to move to
 *   OR uses targetKey parameter to read from a different shared key
 *
 * Parameters:
 *   - targetKey: string - Shared state key for target position (default: 'targetPosition')
 *   - arrivalRange: number - Distance considered "arrived" (default: 25)
 *   - stopOnArrival: boolean - Stop velocity when arrived (default: true)
 *
 * Returns:
 *   - SUCCESS when within arrivalRange of target
 *   - RUNNING while moving
 *   - FAILURE if no target position
 */
class MoveToSharedTargetBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const targetKey = this.parameters.targetKey || 'targetPosition';
        const arrivalRange = this.parameters.arrivalRange || 25;
        const stopOnArrival = this.parameters.stopOnArrival !== false;

        const targetPosition = shared[targetKey];

        if (!targetPosition) {
            return this.failure();
        }

        const pos = game.getComponent(entityId, 'position');
        if (!pos) {
            return this.failure();
        }

        const distance = this.distance(pos, targetPosition);

        if (distance <= arrivalRange) {
            // Arrived at target
            if (stopOnArrival) {
                const vel = game.getComponent(entityId, 'velocity');
                if (vel) {
                    vel.vx = 0;
                    vel.vz = 0;
                }
            }

            return this.success({
                arrived: true,
                distance: distance,
                targetPosition: targetPosition
            });
        }

        // Still moving - return running with target for MovementSystem
        return this.running({
            targetPosition: targetPosition,
            distance: distance,
            moving: true
        });
    }
}

PatrolBehaviorAction.script:
/**
 * PatrolBehaviorAction - Movement action
 * Moves between waypoints in a patrol pattern
 *
 * Parameters:
 *   waypoints: array (default: []) - Array of {x, z} positions to patrol between
 *   waypointsKey: string (optional) - Key in shared state containing waypoints
 *   waitTime: number (default: 1000) - Time to wait at each waypoint (ms)
 *   loop: boolean (default: true) - Loop back to start or reverse
 *   arrivalDistance: number (default: 20) - Distance to consider "arrived"
 *
 * Returns RUNNING while patrolling, SUCCESS never (continuous), FAILURE if no waypoints
 */
class PatrolBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const waypointsKey = params.waypointsKey;
        const waitTime = params.waitTime !== undefined ? params.waitTime : 1000;
        const loop = params.loop !== false;
        const arrivalDistance = params.arrivalDistance || 20;

        const pos = game.getComponent(entityId, 'position');
        if (!pos) {
            return this.failure();
        }

        // Get waypoints from params or shared state
        let waypoints = params.waypoints || [];
        if (waypointsKey) {
            const shared = this.getShared(entityId, game);
            waypoints = shared[waypointsKey] || waypoints;
        }

        if (!waypoints || waypoints.length === 0) {
            return this.failure();
        }

        const memory = this.getMemory(entityId);

        // Initialize patrol state
        if (memory.waypointIndex === undefined) {
            memory.waypointIndex = 0;
            memory.direction = 1; // 1 = forward, -1 = backward
            memory.waitingUntil = 0;
            memory.patrolState = 'moving';
        }

        const now = game.state?.now || Date.now();

        // Check if we're waiting at a waypoint
        if (memory.patrolState === 'waiting') {
            if (now < memory.waitingUntil) {
                return this.running({
                    state: 'waiting',
                    waypointIndex: memory.waypointIndex,
                    waitingFor: memory.waitingUntil - now
                });
            }
            // Done waiting, move to next waypoint
            memory.patrolState = 'moving';
            this.advanceWaypoint(memory, waypoints.length, loop);
        }

        // Get current target waypoint
        const targetWaypoint = waypoints[memory.waypointIndex];
        if (!targetWaypoint) {
            return this.failure();
        }

        const distance = this.distance(pos, targetWaypoint);

        // Check if arrived at waypoint
        if (distance <= arrivalDistance) {
            if (waitTime > 0) {
                memory.patrolState = 'waiting';
                memory.waitingUntil = now + waitTime;
                return this.running({
                    state: 'arrived',
                    waypointIndex: memory.waypointIndex,
                    waitTime
                });
            } else {
                // No wait time, immediately advance
                this.advanceWaypoint(memory, waypoints.length, loop);
            }
        }

        // Set target position for movement system
        memory.targetPosition = { x: targetWaypoint.x, z: targetWaypoint.z };

        return this.running({
            state: 'moving',
            targetPosition: memory.targetPosition,
            waypointIndex: memory.waypointIndex,
            distance
        });
    }

    advanceWaypoint(memory, waypointCount, loop) {
        if (loop) {
            // Loop back to start
            memory.waypointIndex = (memory.waypointIndex + 1) % waypointCount;
        } else {
            // Ping-pong pattern
            memory.waypointIndex += memory.direction;
            if (memory.waypointIndex >= waypointCount - 1) {
                memory.direction = -1;
                memory.waypointIndex = waypointCount - 1;
            } else if (memory.waypointIndex <= 0) {
                memory.direction = 1;
                memory.waypointIndex = 0;
            }
        }
    }

    onEnd(entityId, game) {
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

RetreatBehaviorAction.script:
/**
 * RetreatBehaviorAction - Combat action
 * Falls back when health is low, combining fleeing with counter-attacks
 *
 * Parameters:
 *   healthThreshold: number (default: 0.3) - Health % to trigger retreat
 *   retreatDistance: number (default: 150) - How far to retreat
 *   counterAttack: boolean (default: true) - Attack while retreating if possible
 *   safeDistance: number (default: 200) - Distance to consider safe
 *
 * Returns RUNNING while retreating, SUCCESS when safe, FAILURE if healthy
 */
class RetreatBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const healthThreshold = params.healthThreshold !== undefined ? params.healthThreshold : 0.3;
        const retreatDistance = params.retreatDistance || 150;
        const counterAttack = params.counterAttack !== false;
        const safeDistance = params.safeDistance || 200;

        const pos = game.getComponent(entityId, 'position');
        const team = game.getComponent(entityId, 'team');
        const health = game.getComponent(entityId, 'health');
        const combat = game.getComponent(entityId, 'combat');
        const vel = game.getComponent(entityId, 'velocity');

        if (!pos || !team || !health) {
            return this.failure();
        }

        const healthPercent = health.current / health.max;

        // Only retreat if health is low
        if (healthPercent >= healthThreshold) {
            return this.failure();
        }

        const memory = this.getMemory(entityId);

        // Find nearest threat
        const threat = this.findNearestEnemy(entityId, game, pos, team, safeDistance * 2);

        if (!threat) {
            // No threats, we're safe
            if (vel) vel.anchored = false;
            return this.success({ status: 'safe', noThreats: true, healthPercent });
        }

        const threatPos = game.getComponent(threat.id, 'position');
        const distanceToThreat = this.distance(pos, threatPos);

        // Already at safe distance
        if (distanceToThreat >= safeDistance) {
            if (vel) vel.anchored = false;
            return this.success({ status: 'safe', distanceToThreat, healthPercent });
        }

        // Counter-attack if in range and able
        if (counterAttack && combat && combat.damage > 0) {
            const attackRange = combat.range || 50;
            if (distanceToThreat <= attackRange) {
                this.performCounterAttack(entityId, threat.id, game, combat);
            }
        }

        // Calculate retreat direction (away from threat)
        const dx = pos.x - threatPos.x;
        const dz = pos.z - threatPos.z;
        const dist = Math.sqrt(dx * dx + dz * dz) || 1;

        const targetX = pos.x + (dx / dist) * retreatDistance;
        const targetZ = pos.z + (dz / dist) * retreatDistance;

        memory.targetPosition = { x: targetX, z: targetZ };
        memory.retreatState = 'retreating';

        if (vel) vel.anchored = false;

        return this.running({
            status: 'retreating',
            targetPosition: memory.targetPosition,
            threat: threat.id,
            distanceToThreat,
            healthPercent
        });
    }

    performCounterAttack(attackerId, targetId, game, combat) {
        if (!combat.lastAttack) combat.lastAttack = 0;

        const timeSinceLastAttack = game.state.now - combat.lastAttack;
        if (timeSinceLastAttack < 1 / combat.attackSpeed) {
            return;
        }

        combat.lastAttack = game.state.now;

        if (game.gameManager?.has('triggerSinglePlayAnimation')) {
            game.gameManager.call('triggerSinglePlayAnimation', attackerId, 'attack', combat.attackSpeed);
        }

        if (combat.projectile) {
            const projectileData = game.getCollections().projectiles?.[combat.projectile];
            if (projectileData) {
                game.gameManager.call('fireProjectile', attackerId, targetId, {
                    id: combat.projectile,
                    ...projectileData
                });
            }
        } else if (combat.damage > 0) {
            const damageDelay = (1 / combat.attackSpeed) * 0.5;
            game.gameManager.call('scheduleDamage', attackerId, targetId, combat.damage, 'physical', damageDelay);
        }
    }

    findNearestEnemy(entityId, game, pos, team, range) {
        const potentialTargets = game.getEntitiesWith('position', 'team', 'health');
        let nearest = null;
        let nearestDistance = Infinity;

        for (const targetId of potentialTargets) {
            if (targetId === entityId) continue;

            const targetTeam = game.getComponent(targetId, 'team');
            if (targetTeam.team === team.team) continue;

            const targetHealth = game.getComponent(targetId, 'health');
            if (!targetHealth || targetHealth.current <= 0) continue;

            const targetPos = game.getComponent(targetId, 'position');
            const distance = this.distance(pos, targetPos);

            if (distance <= range && distance < nearestDistance) {
                nearestDistance = distance;
                nearest = { id: targetId, distance };
            }
        }

        return nearest;
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }

    distance(pos1, pos2) {
        const dx = pos2.x - pos1.x;
        const dz = pos2.z - pos1.z;
        return Math.sqrt(dx * dx + dz * dz);
    }
}

SetBuildTargetBehaviorAction.script:
/**
 * SetBuildTargetBehaviorAction - Gets building info from playerOrder and stores in shared state
 *
 * Reads playerOrder.meta.buildingId and stores:
 * - shared.targetBuilding - Building entity ID
 * - shared.targetPosition - Building position (for MoveToSharedTargetBehaviorAction)
 * - shared.buildTime - Construction time required
 *
 * Returns SUCCESS if target was set, FAILURE if no valid building
 */
class SetBuildTargetBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const playerOrder = game.getComponent(entityId, 'playerOrder');

        if (!playerOrder || !playerOrder.meta || !playerOrder.meta.buildingId) {
            return this.failure();
        }

        const buildingId = playerOrder.meta.buildingId;
        const buildingPos = game.getComponent(buildingId, 'position');
        const buildingPlacement = game.getComponent(buildingId, 'placement');

        if (!buildingPos || !buildingPlacement) {
            return this.failure();
        }

        // Store in shared state
        const shared = this.getShared(entityId, game);
        shared.targetBuilding = buildingId;
        shared.targetPosition = { x: buildingPos.x, z: buildingPos.z };
        shared.buildTime = buildingPlacement.buildTime || this.parameters.defaultBuildTime || 5;

        return this.success();
    }
}

SetSharedValueBehaviorAction.script:
/**
 * SetSharedValueBehaviorAction - Utility action
 * Sets a value in the shared state (aiState.shared)
 *
 * Parameters:
 *   key: string (required) - Key to set in shared state
 *   value: any (optional) - Value to set (can be any type)
 *   valueFromKey: string (optional) - Copy value from another shared key
 *   clear: boolean (default: false) - Clear (delete) the key instead of setting
 *   increment: number (optional) - Add this number to existing value
 *   toggle: boolean (default: false) - Toggle boolean value
 *
 * Always returns SUCCESS after setting the value
 */
class SetSharedValueBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const key = params.key;

        if (!key) {
            console.warn('SetSharedValueBehaviorAction: key parameter is required');
            return this.failure();
        }

        const shared = this.getShared(entityId, game);

        // Clear the key
        if (params.clear) {
            const previousValue = shared[key];
            delete shared[key];
            return this.success({
                key,
                action: 'clear',
                previousValue
            });
        }

        // Toggle boolean
        if (params.toggle) {
            const previousValue = shared[key];
            shared[key] = !previousValue;
            return this.success({
                key,
                action: 'toggle',
                previousValue,
                newValue: shared[key]
            });
        }

        // Increment number
        if (params.increment !== undefined) {
            const previousValue = shared[key] || 0;
            shared[key] = previousValue + params.increment;
            return this.success({
                key,
                action: 'increment',
                increment: params.increment,
                previousValue,
                newValue: shared[key]
            });
        }

        // Copy from another key
        if (params.valueFromKey) {
            const copiedValue = shared[params.valueFromKey];
            shared[key] = copiedValue;
            return this.success({
                key,
                action: 'copy',
                sourceKey: params.valueFromKey,
                newValue: copiedValue
            });
        }

        // Set explicit value
        const previousValue = shared[key];
        shared[key] = params.value;

        return this.success({
            key,
            action: 'set',
            previousValue,
            newValue: params.value
        });
    }
}

WaitBehaviorAction.script:
/**
 * WaitBehaviorAction - Utility action
 * Waits for a specified duration before succeeding
 *
 * Parameters:
 *   duration: number (default: 1000) - Wait time in milliseconds
 *   durationKey: string (optional) - Key in shared state for dynamic duration
 *   anchor: boolean (default: true) - Anchor entity while waiting
 *
 * Returns RUNNING while waiting, SUCCESS when done
 */
class WaitBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const params = this.parameters || {};
        const anchor = params.anchor !== false;
        const durationKey = params.durationKey;

        // Get duration from params or shared state
        let duration = params.duration !== undefined ? params.duration : 1000;
        if (durationKey) {
            const shared = this.getShared(entityId, game);
            if (shared[durationKey] !== undefined) {
                duration = shared[durationKey];
            }
        }

        const memory = this.getMemory(entityId);
        const now = game.state?.now || Date.now();

        // Initialize wait start time
        if (memory.waitStartTime === undefined) {
            memory.waitStartTime = now;
            memory.waitDuration = duration;
        }

        const elapsed = now - memory.waitStartTime;
        const remaining = memory.waitDuration - elapsed;

        // Anchor entity while waiting
        if (anchor) {
            const vel = game.getComponent(entityId, 'velocity');
            if (vel) {
                vel.anchored = true;
                vel.vx = 0;
                vel.vz = 0;
            }
        }

        // Still waiting
        if (remaining > 0) {
            return this.running({
                status: 'waiting',
                elapsed,
                remaining,
                duration: memory.waitDuration,
                progress: elapsed / memory.waitDuration
            });
        }

        // Wait complete
        return this.success({
            status: 'complete',
            duration: memory.waitDuration,
            actualElapsed: elapsed
        });
    }

    onEnd(entityId, game) {
        const vel = game.getComponent(entityId, 'velocity');
        if (vel) vel.anchored = false;
        this.clearMemory(entityId);
    }
}

WaitForMineBehaviorAction.script:
/**
 * Wait For Mine Action
 * Waits until it's the entity's turn to mine at the gold mine
 *
 * Reads from shared state:
 *   - targetMine: entityId of the gold mine
 *
 * Sets in shared state:
 *   - canMine: true when it's our turn
 *
 * Returns:
 *   - SUCCESS if it's our turn to mine (we're current miner or next in queue and mine is free)
 *   - RUNNING if still waiting in queue
 *   - FAILURE if no targetMine or goldMineSystem unavailable
 */
class WaitForMineBehaviorAction extends GUTS.BaseBehaviorAction {

    execute(entityId, game) {
        const shared = this.getShared(entityId, game);
        const targetMine = shared.targetMine;

        if (!targetMine) {
            return this.failure();
        }

        const goldMine = game.getComponent(targetMine, 'goldMine');
        if (!goldMine) {
            return this.failure();
        }

        // Check if we're the current miner
        const isCurrentMiner = goldMine.currentMiner === entityId;

        // Check if we're next in queue and mine is free
        const isNextInQueue = game.gameManager.call('isNextInMinerQueue', targetMine, entityId);
        const isMineOccupied = game.gameManager.call('isMineOccupied', targetMine);

        if (isCurrentMiner || (isNextInQueue && !isMineOccupied)) {
            // It's our turn!
            if (!isCurrentMiner) {
                // Process queue to become current miner
                game.gameManager.call('processNextMinerInQueue', targetMine);
            }

            shared.canMine = true;

            // Snap to mine position for precise mining
            const minePos = shared.targetMinePosition;
            if (minePos) {
                const pos = game.getComponent(entityId, 'position');
                const vel = game.getComponent(entityId, 'velocity');
                if (pos) {
                    pos.x = minePos.x;
                    pos.z = minePos.z;
                }
                if (vel) {
                    vel.vx = 0;
                    vel.vz = 0;
                }
            }

            return this.success({
                isCurrentMiner: true,
                targetMine: targetMine
            });
        }

        // Still waiting
        shared.canMine = false;

        return this.running({
            waiting: true,
            isNextInQueue: isNextInQueue,
            isMineOccupied: isMineOccupied
        });
    }
}

BaseBehaviorDecorator.script:
/**
 * Base class for behavior tree decorators
 * Decorators wrap a single child node and modify its behavior
 */
class BaseBehaviorDecorator extends GUTS.BaseBehaviorNode {

    constructor(game, config = {}) {
        super(game, config);
    }

    /**
     * Override evaluateDecorator - called by BaseBehaviorNode for decorator nodes
     * Delegates to execute() for decorator-specific logic
     */
    evaluateDecorator(entityId, game) {
        return this.execute(entityId, game);
    }

    /**
     * Execute the decorator - override in subclasses
     * Default behavior: pass through to child
     * @param {string} entityId - Entity ID
     * @param {object} game - Game instance
     * @returns {Object|null} Modified result
     */
    execute(entityId, game) {
        return this.evaluateChild(entityId, game, this.child);
    }
}

ConditionDecorator.script:
/**
 * Condition Decorator
 * Only executes child if a condition is met
 *
 * Parameters:
 *   conditionType: string - Type of condition to check
 *     - 'hasComponent': Entity has specified component
 *     - 'componentValue': Component property matches value
 *     - 'sharedValue': Shared state value matches
 *     - 'custom': Uses custom condition function name
 *
 *   component: string - Component name (for hasComponent, componentValue)
 *   property: string - Property path (for componentValue, sharedValue)
 *   operator: string - Comparison operator: '==', '!=', '>', '<', '>=', '<=', 'exists', 'notExists'
 *   value: any - Value to compare against
 *   invert: boolean - Invert the condition result (default: false)
 *
 * Usage:
 *   {
 *     "fileName": "ConditionDecorator",
 *     "childAction": "AttackAction",
 *     "parameters": {
 *       "conditionType": "componentValue",
 *       "component": "health",
 *       "property": "current",
 *       "operator": ">",
 *       "value": 50
 *     }
 *   }
 */
class ConditionDecorator extends GUTS.BaseBehaviorDecorator {

    constructor(game, config) {
        super(game, config);

        this.conditionType = this.parameters.conditionType || 'sharedValue';
        this.component = this.parameters.component || null;
        this.property = this.parameters.property || null;
        this.operator = this.parameters.operator || '==';
        this.value = this.parameters.value;
        this.invert = this.parameters.invert || false;
        this.sharedKey = this.parameters.sharedKey || this.property;
    }

    /**
     * Execute - only run child if condition passes
     */
    execute(entityId, game) {
        const conditionMet = this.evaluateCondition(entityId, game);
        const finalResult = this.invert ? !conditionMet : conditionMet;

        if (finalResult) {
            // Condition passed - execute child
            const childResult = this.evaluateChild(entityId, game, this.child);
            if (childResult) {
                return {
                    ...childResult,
                    meta: {
                        ...childResult.meta,
                        conditionPassed: true,
                        conditionType: this.conditionType
                    }
                };
            }
            return childResult;
        }

        // Condition failed - return failure
        return this.failure();
    }

    /**
     * Evaluate the condition
     */
    evaluateCondition(entityId, game) {
        switch (this.conditionType) {
            case 'hasComponent':
                return this.checkHasComponent(entityId, game);

            case 'componentValue':
                return this.checkComponentValue(entityId, game);

            case 'sharedValue':
                return this.checkSharedValue(entityId, game);

            case 'custom':
                return this.checkCustomCondition(entityId, game);

            default:
                console.warn(`Unknown condition type: ${this.conditionType}`);
                return false;
        }
    }

    /**
     * Check if entity has a component
     */
    checkHasComponent(entityId, game) {
        const component = game.getComponent(entityId, this.component);
        return component !== null && component !== undefined;
    }

    /**
     * Check a component property value
     */
    checkComponentValue(entityId, game) {
        const component = game.getComponent(entityId, this.component);
        if (!component) return false;

        const actualValue = this.getNestedValue(component, this.property);
        return this.compare(actualValue, this.value);
    }

    /**
     * Check a shared state value
     */
    checkSharedValue(entityId, game) {
        const shared = this.getShared(entityId, game);
        const actualValue = this.getNestedValue(shared, this.sharedKey);
        return this.compare(actualValue, this.value);
    }

    /**
     * Check using a custom condition function registered on gameManager
     */
    checkCustomCondition(entityId, game) {
        const conditionName = this.parameters.conditionName;
        if (!conditionName) {
            console.warn('ConditionDecorator: custom condition requires conditionName parameter');
            return false;
        }

        try {
            return game.gameManager.call(conditionName, entityId, this.parameters);
        } catch (e) {
            console.warn(`ConditionDecorator: custom condition '${conditionName}' failed:`, e);
            return false;
        }
    }

    /**
     * Get a nested value from an object using dot notation
     */
    getNestedValue(obj, path) {
        if (!obj || !path) return undefined;

        const parts = path.split('.');
        let current = obj;

        for (const part of parts) {
            if (current === null || current === undefined) return undefined;
            current = current[part];
        }

        return current;
    }

    /**
     * Compare two values using the configured operator
     */
    compare(actual, expected) {
        switch (this.operator) {
            case '==':
            case 'equals':
                return actual == expected;

            case '===':
            case 'strictEquals':
                return actual === expected;

            case '!=':
            case 'notEquals':
                return actual != expected;

            case '!==':
            case 'strictNotEquals':
                return actual !== expected;

            case '>':
            case 'greaterThan':
                return actual > expected;

            case '<':
            case 'lessThan':
                return actual < expected;

            case '>=':
            case 'greaterOrEqual':
                return actual >= expected;

            case '<=':
            case 'lessOrEqual':
                return actual <= expected;

            case 'exists':
                return actual !== null && actual !== undefined;

            case 'notExists':
                return actual === null || actual === undefined;

            case 'contains':
                if (Array.isArray(actual)) {
                    return actual.includes(expected);
                }
                if (typeof actual === 'string') {
                    return actual.includes(expected);
                }
                return false;

            case 'notContains':
                if (Array.isArray(actual)) {
                    return !actual.includes(expected);
                }
                if (typeof actual === 'string') {
                    return !actual.includes(expected);
                }
                return true;

            default:
                console.warn(`Unknown operator: ${this.operator}`);
                return false;
        }
    }
}

CooldownDecorator.script:
/**
 * Cooldown Decorator
 * Prevents the child action from running more frequently than specified
 * Parameters:
 *   - cooldownTime: Time in seconds between allowed executions
 *   - failOnCooldown: If true, returns failure when on cooldown; otherwise passes through
 */
class CooldownDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const memory = this.getMemory(entityId);
        const cooldownTime = this.parameters.cooldownTime || 1;
        const failOnCooldown = this.parameters.failOnCooldown !== false;

        const now = game.state?.now || Date.now() / 1000;

        // Initialize last execution time if needed
        if (memory.lastExecution === undefined) {
            memory.lastExecution = 0;
        }

        const timeSinceLastExecution = now - memory.lastExecution;

        // Check if on cooldown
        if (timeSinceLastExecution < cooldownTime) {
            if (failOnCooldown) {
                return this.failure();
            }
            // Not failing on cooldown, return running to indicate waiting
            return this.running({
                onCooldown: true,
                remainingCooldown: cooldownTime - timeSinceLastExecution
            });
        }

        // Execute child
        const result = this.evaluateChild(entityId, game, this.child);

        // If child succeeded or is running, update last execution time
        if (result !== null) {
            if (result.status !== 'running') {
                // Only update cooldown on completion, not while running
                memory.lastExecution = now;
            }
            return result;
        }

        // Child failed
        return this.failure();
    }
}

ForceFailureDecorator.script:
/**
 * ForceFailure Decorator
 * Always returns failure regardless of child result
 * Running status passes through unchanged
 */
class ForceFailureDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const result = this.evaluateChild(entityId, game, this.child);

        if (result && result.status === 'running') {
            // Pass through running status
            return this.running(result.meta);
        }

        // Always return failure
        return this.failure();
    }
}

ForceSuccessDecorator.script:
/**
 * ForceSuccess Decorator
 * Always returns success regardless of child result
 * Running status passes through unchanged
 */
class ForceSuccessDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const result = this.evaluateChild(entityId, game, this.child);

        if (result && result.status === 'running') {
            // Pass through running status
            return this.running(result.meta);
        }

        // Always return success
        return this.success(result?.meta || {});
    }
}

InverterDecorator.script:
/**
 * Inverter Decorator
 * Flips success to failure and failure to success
 * Running status passes through unchanged
 */
class InverterDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const result = this.evaluateChild(entityId, game, this.child);

        if (result === null) {
            // Child failed, return success
            return this.success({});
        }

        if (result.status === 'running') {
            // Pass through running status
            return this.running(result.meta);
        }

        // Child succeeded, return failure
        return this.failure();
    }
}

LoopDecorator.script:
/**
 * Loop Decorator
 * Executes child node multiple times or until a condition is met
 *
 * Parameters:
 *   loopType: string - Type of loop:
 *     - 'count': Run exactly N times
 *     - 'whileSuccess': Run while child succeeds
 *     - 'whileFailure': Run while child fails
 *     - 'untilSuccess': Run until child succeeds
 *     - 'untilFailure': Run until child fails
 *     - 'forever': Run indefinitely (returns running)
 *
 *   count: number - Number of iterations (for 'count' type, default: 3)
 *   maxIterations: number - Safety limit for non-count loops (default: 100)
 *   breakOnRunning: boolean - Stop loop if child returns running (default: true)
 *   returnLastResult: boolean - Return last child result vs loop status (default: true)
 *
 * Usage:
 *   {
 *     "fileName": "LoopDecorator",
 *     "childAction": "ProcessItemAction",
 *     "parameters": {
 *       "loopType": "count",
 *       "count": 5
 *     }
 *   }
 */
class LoopDecorator extends GUTS.BaseBehaviorDecorator {

    constructor(game, config) {
        super(game, config);

        this.loopType = this.parameters.loopType || 'count';
        this.count = this.parameters.count || 3;
        this.maxIterations = this.parameters.maxIterations || 100;
        this.breakOnRunning = this.parameters.breakOnRunning !== false;
        this.returnLastResult = this.parameters.returnLastResult !== false;

        // Memory defaults for tracking loop state
        this.memoryDefaults = {
            currentIteration: 0,
            completed: false,
            lastResult: null
        };
    }

    /**
     * Execute the loop
     */
    execute(entityId, game) {
        const memory = this.getMemory(entityId);

        // Reset if previously completed
        if (memory.completed) {
            memory.currentIteration = 0;
            memory.completed = false;
            memory.lastResult = null;
        }

        let result = null;
        let iterations = 0;

        while (iterations < this.maxIterations) {
            // Check loop termination conditions before executing
            if (this.shouldTerminate(memory)) {
                break;
            }

            // Execute child
            result = this.evaluateChild(entityId, game, this.child);
            memory.lastResult = result;
            memory.currentIteration++;
            iterations++;

            // Handle running state
            if (result && result.status === 'running') {
                if (this.breakOnRunning) {
                    return this.createLoopResult(memory, result, 'running', false);
                }
                // Continue loop even with running (unusual but supported)
            }

            // Check if loop should continue based on result
            if (!this.shouldContinue(result)) {
                break;
            }
        }

        // Loop completed or terminated
        memory.completed = true;
        const status = this.determineLoopStatus(memory, result);

        return this.createLoopResult(memory, result, status, true);
    }

    /**
     * Check if loop should terminate before next iteration
     */
    shouldTerminate(memory) {
        switch (this.loopType) {
            case 'count':
                return memory.currentIteration >= this.count;

            case 'forever':
                return false;

            default:
                return false;
        }
    }

    /**
     * Check if loop should continue after child execution
     */
    shouldContinue(result) {
        const childSucceeded = result !== null && result.status !== 'failure';
        const childFailed = result === null || result.status === 'failure';

        switch (this.loopType) {
            case 'count':
                // Count-based continues regardless of result
                return true;

            case 'whileSuccess':
                return childSucceeded;

            case 'whileFailure':
                return childFailed;

            case 'untilSuccess':
                return !childSucceeded;

            case 'untilFailure':
                return !childFailed;

            case 'forever':
                return true;

            default:
                return false;
        }
    }

    /**
     * Determine the final loop status
     */
    determineLoopStatus(memory, lastResult) {
        switch (this.loopType) {
            case 'count':
                // Count loops succeed if they completed all iterations
                return memory.currentIteration >= this.count ? 'success' : 'failure';

            case 'whileSuccess':
                // Terminated because child failed - return success (loop completed its purpose)
                return 'success';

            case 'whileFailure':
                // Terminated because child succeeded - return success
                return 'success';

            case 'untilSuccess':
                // Return success if child eventually succeeded
                return lastResult && lastResult.status !== 'failure' ? 'success' : 'failure';

            case 'untilFailure':
                // Return success if child eventually failed
                return lastResult === null || lastResult.status === 'failure' ? 'success' : 'failure';

            case 'forever':
                // Forever loops always return running
                return 'running';

            default:
                return lastResult ? lastResult.status : 'failure';
        }
    }

    /**
     * Create the loop result with metadata
     */
    createLoopResult(memory, childResult, status, completed) {
        const meta = {
            loopType: this.loopType,
            iterations: memory.currentIteration,
            maxIterations: this.loopType === 'count' ? this.count : this.maxIterations,
            completed: completed,
            childResult: childResult ? {
                action: childResult.action,
                status: childResult.status
            } : null
        };

        // Return based on configuration
        if (this.returnLastResult && childResult) {
            return {
                ...childResult,
                meta: {
                    ...childResult.meta,
                    loop: meta
                }
            };
        }

        // Return loop-level result
        switch (status) {
            case 'success':
                return this.success(meta);
            case 'running':
                return this.running(meta);
            default:
                return this.failure();
        }
    }

    /**
     * Reset the loop state for an entity
     */
    resetLoop(entityId) {
        const memory = this.getMemory(entityId);
        memory.currentIteration = 0;
        memory.completed = false;
        memory.lastResult = null;
    }

    /**
     * Get current iteration count
     */
    getCurrentIteration(entityId) {
        return this.getMemory(entityId).currentIteration;
    }

    /**
     * Check if loop is completed
     */
    isCompleted(entityId) {
        return this.getMemory(entityId).completed;
    }

    /**
     * Called when decorator ends - reset loop state
     */
    onEnd(entityId, game) {
        super.onEnd(entityId, game);
    }
}

RateLimitDecorator.script:
/**
 * Rate Limit Decorator
 * Limits how often the child node can be executed
 *
 * Parameters:
 *   interval: number - Minimum time (ms) between executions (default: 1000)
 *   maxExecutions: number - Maximum executions per interval (default: 1)
 *   resetOnSuccess: boolean - Reset timer on successful execution (default: false)
 *   resetOnFailure: boolean - Reset timer on failed execution (default: false)
 *   failureResult: string - What to return when rate limited: 'failure', 'success', 'running' (default: 'failure')
 *
 * Usage:
 *   {
 *     "fileName": "RateLimitDecorator",
 *     "childAction": "ExpensiveCalculation",
 *     "parameters": {
 *       "interval": 500,
 *       "maxExecutions": 1,
 *       "failureResult": "running"
 *     }
 *   }
 */
class RateLimitDecorator extends GUTS.BaseBehaviorDecorator {

    constructor(game, config) {
        super(game, config);

        this.interval = this.parameters.interval || 1000;
        this.maxExecutions = this.parameters.maxExecutions || 1;
        this.resetOnSuccess = this.parameters.resetOnSuccess || false;
        this.resetOnFailure = this.parameters.resetOnFailure || false;
        this.failureResult = this.parameters.failureResult || 'failure';

        // Memory defaults for tracking execution times
        this.memoryDefaults = {
            lastExecutionTime: 0,
            executionsInWindow: 0,
            windowStartTime: 0
        };
    }

    /**
     * Execute - only run child if rate limit allows
     */
    execute(entityId, game) {
        const memory = this.getMemory(entityId);
        const now = performance.now();

        // Check if we're in a new time window
        if (now - memory.windowStartTime >= this.interval) {
            // Reset the window
            memory.windowStartTime = now;
            memory.executionsInWindow = 0;
        }

        // Check if we've exceeded the rate limit
        if (memory.executionsInWindow >= this.maxExecutions) {
            return this.getRateLimitedResult(memory);
        }

        // Execute the child
        const childResult = this.evaluateChild(entityId, game, this.child);

        // Track execution
        memory.executionsInWindow++;
        memory.lastExecutionTime = now;

        // Handle reset conditions
        if (childResult) {
            if (this.resetOnSuccess && childResult.status === 'success') {
                memory.executionsInWindow = 0;
                memory.windowStartTime = now;
            }
        } else {
            if (this.resetOnFailure) {
                memory.executionsInWindow = 0;
                memory.windowStartTime = now;
            }
        }

        // Add rate limit info to result
        if (childResult) {
            return {
                ...childResult,
                meta: {
                    ...childResult.meta,
                    rateLimited: false,
                    executionsInWindow: memory.executionsInWindow,
                    maxExecutions: this.maxExecutions,
                    timeUntilReset: Math.max(0, this.interval - (now - memory.windowStartTime))
                }
            };
        }

        return childResult;
    }

    /**
     * Get the result to return when rate limited
     */
    getRateLimitedResult(memory) {
        const now = performance.now();
        const timeUntilReset = Math.max(0, this.interval - (now - memory.windowStartTime));

        const meta = {
            rateLimited: true,
            executionsInWindow: memory.executionsInWindow,
            maxExecutions: this.maxExecutions,
            timeUntilReset: timeUntilReset
        };

        switch (this.failureResult) {
            case 'success':
                return this.success(meta);

            case 'running':
                return this.running(meta);

            case 'failure':
            default:
                return this.failure();
        }
    }

    /**
     * Get time until rate limit resets
     */
    getTimeUntilReset(entityId) {
        const memory = this.getMemory(entityId);
        const now = performance.now();
        return Math.max(0, this.interval - (now - memory.windowStartTime));
    }

    /**
     * Check if currently rate limited
     */
    isRateLimited(entityId) {
        const memory = this.getMemory(entityId);
        const now = performance.now();

        // Check if we're in a new time window
        if (now - memory.windowStartTime >= this.interval) {
            return false;
        }

        return memory.executionsInWindow >= this.maxExecutions;
    }

    /**
     * Manually reset the rate limit for an entity
     */
    resetRateLimit(entityId) {
        const memory = this.getMemory(entityId);
        memory.executionsInWindow = 0;
        memory.windowStartTime = performance.now();
    }
}

RepeaterDecorator.script:
/**
 * Repeater Decorator
 * Runs the child action multiple times
 * Parameters:
 *   - times: Number of times to repeat (0 = infinite)
 *   - untilFail: If true, repeat until child fails
 *   - untilSuccess: If true, repeat until child succeeds
 */
class RepeaterDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const memory = this.getMemory(entityId);
        const times = this.parameters.times || 0;
        const untilFail = this.parameters.untilFail || false;
        const untilSuccess = this.parameters.untilSuccess || false;

        // Initialize count if needed
        if (memory.count === undefined) {
            memory.count = 0;
        }

        const result = this.evaluateChild(entityId, game, this.child);

        // Handle running status
        if (result && result.status === 'running') {
            return this.running({ ...result.meta, repeatCount: memory.count });
        }

        // Child completed (success or failure)
        const childSucceeded = result !== null;

        // Check termination conditions
        if (untilFail && !childSucceeded) {
            // Wanted to run until fail, child failed - we're done with success
            memory.count = 0;
            return this.success({ repeatCount: memory.count });
        }

        if (untilSuccess && childSucceeded) {
            // Wanted to run until success, child succeeded - we're done
            memory.count = 0;
            return this.success({ ...result.meta, repeatCount: memory.count });
        }

        // Increment count
        memory.count++;

        // Check if we've reached the repeat limit
        if (times > 0 && memory.count >= times) {
            // Reached limit
            const finalCount = memory.count;
            memory.count = 0;
            return childSucceeded ? this.success({ repeatCount: finalCount }) : this.failure();
        }

        // Continue repeating
        return this.running({ repeatCount: memory.count });
    }

    onEnd(entityId, game) {
        super.onEnd(entityId, game);
    }
}

RetryDecorator.script:
/**
 * Retry Decorator
 * Retries the child action on failure up to a maximum number of attempts
 * Parameters:
 *   - maxAttempts: Maximum number of retry attempts (default: 3)
 *   - retryDelay: Optional delay between retries in seconds (default: 0)
 */
class RetryDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const memory = this.getMemory(entityId);
        const maxAttempts = this.parameters.maxAttempts || 3;
        const retryDelay = this.parameters.retryDelay || 0;

        const now = game.state?.now || Date.now() / 1000;

        // Initialize attempts if needed
        if (memory.attempts === undefined) {
            memory.attempts = 0;
            memory.lastAttemptTime = 0;
        }

        // Check retry delay
        if (retryDelay > 0 && memory.attempts > 0) {
            const timeSinceLastAttempt = now - memory.lastAttemptTime;
            if (timeSinceLastAttempt < retryDelay) {
                return this.running({
                    waiting: true,
                    attempts: memory.attempts,
                    remainingDelay: retryDelay - timeSinceLastAttempt
                });
            }
        }

        // Execute child
        const result = this.evaluateChild(entityId, game, this.child);

        if (result && result.status === 'running') {
            // Child is running
            return this.running({ ...result.meta, attempts: memory.attempts });
        }

        if (result !== null) {
            // Child succeeded
            memory.attempts = 0;
            return this.success({ ...result.meta, attempts: memory.attempts });
        }

        // Child failed
        memory.attempts++;
        memory.lastAttemptTime = now;

        if (memory.attempts >= maxAttempts) {
            // Exhausted all attempts
            const finalAttempts = memory.attempts;
            memory.attempts = 0;
            return this.failure();
        }

        // Will retry next tick
        return this.running({
            retrying: true,
            attempts: memory.attempts,
            maxAttempts: maxAttempts
        });
    }

    onEnd(entityId, game) {
        super.onEnd(entityId, game);
    }
}

TimeoutDecorator.script:
/**
 * Timeout Decorator
 * Fails if the child action takes longer than specified time
 * Parameters:
 *   - timeout: Maximum time in seconds for child to complete
 */
class TimeoutDecorator extends GUTS.BaseBehaviorDecorator {

    execute(entityId, game) {
        const memory = this.getMemory(entityId);
        const timeout = this.parameters.timeout || 5;

        const now = game.state?.now || Date.now() / 1000;

        // Initialize start time if this is a new execution
        if (memory.startTime === undefined) {
            memory.startTime = now;
        }

        const elapsed = now - memory.startTime;

        // Check if we've exceeded timeout
        if (elapsed >= timeout) {
            // Timeout exceeded, fail
            memory.startTime = undefined;
            return this.failure();
        }

        // Execute child
        const result = this.evaluateChild(entityId, game, this.child);

        if (result === null) {
            // Child failed
            memory.startTime = undefined;
            return this.failure();
        }

        if (result.status === 'running') {
            // Child still running, check timeout next tick
            return this.running({
                ...result.meta,
                elapsed: elapsed,
                remaining: timeout - elapsed
            });
        }

        // Child succeeded
        memory.startTime = undefined;
        return this.success(result.meta);
    }

    onEnd(entityId, game) {
        const memory = this.getMemory(entityId);
        memory.startTime = undefined;
        super.onEnd(entityId, game);
    }
}

AbilitiesBehaviorTree.script:
class AbilitiesBehaviorTree extends GUTS.BaseBehaviorTree {
    evaluate(entityId, game) {
        if (!game.abilitySystem) return null;

        const unitType = game.getComponent(entityId, 'unitType');
        const abilities = unitType?.abilities;
        if (!abilities || abilities.length === 0) return null;        
        // Evaluate abilities using selector pattern (first success wins)
        for (const abilityType of abilities) {
            const ability = game.getCollections().abilities[abilityType];
            if (!ability?.behaviorAction) continue;

            const abilityAction = game.gameManager.call('getNodeByType', ability.behaviorAction);
            if (!abilityAction) continue;

            const result = abilityAction.evaluate(entityId, game);
            if (result !== null) {
                return result;
            }
        }

        return null;
    }
}

BaseBehaviorTree.script:
class BaseBehaviorTree extends GUTS.BaseBehaviorNode {
    constructor(game, config = {}) {
        super(game, config);
    }

    onBattleStart() {
    }

    onBattleEnd(entityId, game) {
        this.runningState.delete(entityId);
    }

    onPlacementPhaseStart(entityId, game) {
    }

    /**
     * Override evaluateComposite - called by BaseBehaviorNode for composite nodes
     * Uses selector pattern (try each child until one succeeds)
     */
    evaluateComposite(entityId, game) {
        return super.evaluateSelector(entityId, game);
    }

    /**
     * Override evaluate to set pathSize before delegation
     */
    evaluate(entityId, game) {
        this.pathSize = game.gameManager?.call('getPlacementGridSize');
        return super.evaluate(entityId, game);
    }
}

MineGoldBehaviorTree.script:
/**
 * Mine Gold Behavior Tree
 * Main selector for gold mining behavior:
 * 1. Try to deposit gold (if carrying any)
 * 2. Otherwise, gather gold from a mine
 *
 * This replaces the monolithic MineGoldBehaviorAction with a proper
 * tree structure of smaller, reusable nodes.
 *
 * Tree structure:
 * Selector
 *  DepositGoldSequence (succeeds if has gold and can deposit)
 *     HasGoldBehaviorAction
 *     FindNearestDepotBehaviorAction
 *     MoveToSharedTargetBehaviorAction
 *     DepositGoldBehaviorAction
 * 
 *  GatherGoldSequence (fallback - mine more gold)
 *      FindNearestGoldMineBehaviorAction
 *      MoveToSharedTargetBehaviorAction
 *      JoinMineQueueBehaviorAction
 *      WaitForMineBehaviorAction
 *      ExtractGoldBehaviorAction
 */
class MineGoldBehaviorTree extends GUTS.BaseBehaviorTree {

    constructor(game, config = {}) {
        super(game, config);

        // Selector behavior - try deposit first, then gather
        this.config.behaviorActions = [
            'DepositGoldSequence',
            'GatherGoldSequence'
        ];
    }

    /**
     * Evaluate as a selector (first success wins)
     * Default BaseBehaviorTree behavior is selector, so we can just call super
     */
    evaluate(entityId, game) {
        return super.evaluate(entityId, game);
    }

    /**
     * Clean up shared state when mining behavior ends
     */
    onEnd(entityId, game) {
        const shared = this.getShared(entityId, game);

        // Clear all mining-related shared state
        shared.targetMine = null;
        shared.targetMinePosition = null;
        shared.targetDepot = null;
        shared.targetDepotPosition = null;
        shared.targetPosition = null;
        shared.inMineQueue = false;
        shared.canMine = false;
        shared.hasGold = false;
        shared.goldAmount = 0;
        shared.miningProgress = 0;

        super.onEnd(entityId, game);
    }

    /**
     * Get shared state (delegates to BaseBehaviorTree helper)
     */
    getShared(entityId, game) {
        const aiState = game.getComponent(entityId, 'aiState');
        if (aiState) {
            if (!aiState.shared) {
                aiState.shared = {};
            }
            return aiState.shared;
        }
        return {};
    }
}

NewCombatBehaviorTree.script:
class NewCombatBehaviorTree extends GUTS.BaseBehaviorTree {
    evaluate(entityId, game) {
        // Check if player order prevents combat
        const playerOrder = game.getComponent(entityId, 'playerOrder');
        if (playerOrder?.meta?.preventCombat) {
            return null;
        }

        const combat = game.getComponent(entityId, 'combat');
        const health = game.getComponent(entityId, 'health');

        // Skip if unit can't fight
        if (!combat || !health || health.current <= 0) {
            return null;
        }

        // Skip non-combat units (peasants mining, etc.)
        const unitType = game.getComponent(entityId, 'unitType');
        if (combat.damage === 0 && (!unitType.abilities || unitType.abilities.length === 0)) {
            return null;
        }

        // Use base class evaluate which handles the selector pattern
        return super.evaluate(entityId, game);
    }
}

ParallelBehaviorTree.script:
/**
 * Parallel Behavior Tree
 * Evaluates all children simultaneously on each tick
 *
 * Policies:
 *   successPolicy: 'all' | 'any' (default: 'all')
 *     - 'all': Succeed when ALL children succeed
 *     - 'any': Succeed when ANY child succeeds
 *
 *   failurePolicy: 'all' | 'any' (default: 'any')
 *     - 'all': Fail when ALL children fail
 *     - 'any': Fail when ANY child fails
 *
 * Usage in behaviorActions:
 *   "behaviorActions": [
 *     "CombatParallelTree",  // A ParallelBehaviorTree instance
 *     "IdleBehaviorAction"
 *   ]
 *
 * The parallel tree config:
 *   {
 *     "fileName": "CombatParallelTree",
 *     "behaviorActions": ["AttackAction", "DefendBehaviorAction"],
 *     "parameters": {
 *       "successPolicy": "any",
 *       "failurePolicy": "any"
 *     }
 *   }
 */
class ParallelBehaviorTree extends GUTS.BaseBehaviorTree {

    constructor(game, config = {}) {
        super(game, config);

        // Parse parameters
        const params = config.parameters || {};
        this.successPolicy = params.successPolicy || 'all';
        this.failurePolicy = params.failurePolicy || 'any';

        // Track running children per entity
        // Key: entityId, Value: Set of running child names
        this.runningChildren = new Map();
    }

    /**
     * Evaluate all children in parallel
     * @param {string} entityId - Entity ID
     * @param {object} game - Game instance
     * @returns {Object|null} Combined result based on policies
     */
    evaluate(entityId, game) {
        const behaviorActions = this.config.behaviorActions;
        if (!behaviorActions || behaviorActions.length === 0) {
            return null;
        }

        // Get or create running children set for this entity
        if (!this.runningChildren.has(entityId)) {
            this.runningChildren.set(entityId, new Set());
        }
        const running = this.runningChildren.get(entityId);

        // Evaluate all children
        const results = [];
        let successCount = 0;
        let failureCount = 0;
        let runningCount = 0;
        let primaryResult = null;

        for (const childName of behaviorActions) {
            const result = this.evaluateChild(entityId, game, childName);
            results.push({ name: childName, result });

            if (result === null) {
                // Child failed
                failureCount++;
                running.delete(childName);
            } else if (result.status === 'running') {
                // Child is running
                runningCount++;
                running.add(childName);
                if (!primaryResult) primaryResult = result;
            } else {
                // Child succeeded
                successCount++;
                running.delete(childName);
                if (!primaryResult) primaryResult = result;
            }
        }

        const totalChildren = behaviorActions.length;

        // Apply failure policy
        if (this.failurePolicy === 'any' && failureCount > 0) {
            this.runningChildren.delete(entityId);
            return null; // Fail immediately on any failure
        }
        if (this.failurePolicy === 'all' && failureCount === totalChildren) {
            this.runningChildren.delete(entityId);
            return null; // Fail only when all fail
        }

        // Apply success policy
        if (this.successPolicy === 'all' && successCount === totalChildren) {
            // All succeeded
            this.runningChildren.delete(entityId);
            return this.createSuccessResult(primaryResult);
        }
        if (this.successPolicy === 'any' && successCount > 0) {
            // At least one succeeded
            this.runningChildren.delete(entityId);
            return this.createSuccessResult(primaryResult);
        }

        // Still running if we have running children
        if (runningCount > 0) {
            return this.createRunningResult(primaryResult, {
                parallelRunning: Array.from(running),
                successCount,
                failureCount,
                runningCount
            });
        }

        // Default: use primary result or null
        return primaryResult;
    }

    /**
     * Evaluate a single child (unified node lookup)
     */
    evaluateChild(entityId, game, childName) {
        const node = game.gameManager.call('getNodeByType', childName);
        if (!node) {
            console.warn(`Parallel child not found: ${childName}`);
            return null;
        }

        // Trees have evaluate() method
        if (node.evaluate) {
            return node.evaluate(entityId, game);
        }

        // Actions and decorators have execute() - use processAction for normalization
        if (node.execute) {
            return this.processAction(entityId, game, childName, node);
        }

        console.warn(`Node has no evaluate or execute method: ${childName}`);
        return null;
    }

    /**
     * Create a success result
     */
    createSuccessResult(primaryResult) {
        return {
            action: primaryResult?.action || 'ParallelBehaviorTree',
            status: 'success',
            meta: primaryResult?.meta || {}
        };
    }

    /**
     * Create a running result with parallel metadata
     */
    createRunningResult(primaryResult, parallelMeta) {
        return {
            action: primaryResult?.action || 'ParallelBehaviorTree',
            status: 'running',
            meta: {
                ...(primaryResult?.meta || {}),
                ...parallelMeta
            }
        };
    }

    /**
     * Clear running state when battle ends
     */
    onBattleEnd(entityId, game) {
        super.onBattleEnd(entityId, game);
        this.runningChildren.delete(entityId);
    }
}

PlayerOrderBehaviorTree.script:
class PlayerOrderBehaviorTree extends GUTS.BaseBehaviorTree {
    /**
     * Behavior tree for player-issued orders.
     * Checks for different types of player orders and executes them in priority:
     * 1. Build orders (peasants constructing buildings)
     * 2. Hold position orders (unit stays in place)
     * 3. Move orders (unit moves to target position)
     *
     * For normal move orders, checks if enemies are nearby first.
     * If enemies present, fails to let CombatBehaviorAction take over.
     * Force moves ignore enemies and proceed regardless.
     *
     * If no player order exists or order is completed, returns null to allow
     * other behaviors (like combat) to take over.
     */
    evaluate(entityId, game) {
        const playerOrder = game.getComponent(entityId, 'playerOrder');

        // No player order component - skip this tree
        if (!playerOrder) {
            return null;
        }

        // For normal move orders (not force moves), check for nearby enemies first
        if (playerOrder.meta?.isMoveOrder && !playerOrder.meta?.preventEnemiesInRangeCheck) {
            const isEnemyNearby = game.gameManager.call('getNodeByType', 'IsEnemyNearbyBehaviorAction');
            if (isEnemyNearby) {
                const enemyCheckResult = isEnemyNearby.execute(entityId, game);
                if (enemyCheckResult) {
                    // Enemy nearby for normal move - fail to allow combat to take over
                    return null;
                }
            }
        }

        // Use base class evaluate which handles the selector pattern
        // This evaluates: BuildSequence -> HoldPositionBehaviorAction -> MoveBehaviorAction
        return super.evaluate(entityId, game);
    }
}

RandomSelectorBehaviorTree.script:
/**
 * Random Selector Behavior Tree
 * Randomly picks and evaluates children instead of priority order
 *
 * Behavior:
 *   - Shuffles children randomly each evaluation (or uses sticky selection)
 *   - Evaluates in random order until one succeeds
 *   - If all children fail, the selector fails
 *
 * Parameters:
 *   sticky: boolean (default: false)
 *     - If true, remembers the last successful child and tries it first
 *     - If false, completely random each evaluation
 *
 *   seed: number (optional)
 *     - Random seed for reproducible behavior (useful for testing)
 *
 * Usage:
 *   {
 *     "fileName": "RandomPatrolSelector",
 *     "behaviorActions": ["PatrolNorth", "PatrolSouth", "PatrolEast"],
 *     "parameters": {
 *       "sticky": true
 *     }
 *   }
 */
class RandomSelectorBehaviorTree extends GUTS.BaseBehaviorTree {

    constructor(game, config = {}) {
        super(game, config);

        // Parse parameters
        const params = config.parameters || {};
        this.sticky = params.sticky || false;
        this.seed = params.seed || null;

        // Track last successful child per entity (for sticky mode)
        // Key: entityId, Value: childName
        this.lastSuccess = new Map();

        // Track running state per entity
        // Key: entityId, Value: { shuffledOrder: [], currentIndex: number }
        this.randomState = new Map();

        // Simple seeded random if seed is provided
        if (this.seed !== null) {
            this.rng = this.createSeededRandom(this.seed);
        } else {
            this.rng = Math.random;
        }
    }

    /**
     * Create a seeded random number generator
     */
    createSeededRandom(seed) {
        let s = seed;
        return () => {
            s = (s * 9301 + 49297) % 233280;
            return s / 233280;
        };
    }

    /**
     * Shuffle array using Fisher-Yates algorithm
     */
    shuffleArray(array) {
        const shuffled = [...array];
        for (let i = shuffled.length - 1; i > 0; i--) {
            const j = Math.floor(this.rng() * (i + 1));
            [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
        }
        return shuffled;
    }

    /**
     * Evaluate children in random order
     * @param {string} entityId - Entity ID
     * @param {object} game - Game instance
     * @returns {Object|null} First successful result or null
     */
    evaluate(entityId, game) {
        const behaviorActions = this.config.behaviorActions;
        if (!behaviorActions || behaviorActions.length === 0) {
            return null;
        }

        // Get debugger if available
        const debugger_ = game.gameManager.call('getDebugger');
        const treeId = this.config.id || this.config.fileName || 'RandomSelectorBehaviorTree';
        const trace = debugger_?.beginEvaluation(entityId, treeId);

        let evaluationOrder;
        let startIndex = 0;

        // Check for existing running state
        const state = this.randomState.get(entityId);

        if (state) {
            // Resume from running state
            evaluationOrder = state.shuffledOrder;
            startIndex = state.currentIndex;
        } else {
            // Create new random order
            evaluationOrder = this.shuffleArray(behaviorActions);

            // If sticky mode and we have a last success, move it to front
            if (this.sticky) {
                const lastSuccessChild = this.lastSuccess.get(entityId);
                if (lastSuccessChild && evaluationOrder.includes(lastSuccessChild)) {
                    evaluationOrder = evaluationOrder.filter(c => c !== lastSuccessChild);
                    evaluationOrder.unshift(lastSuccessChild);
                }
            }
        }

        // Evaluate in random order
        for (let i = startIndex; i < evaluationOrder.length; i++) {
            const childName = evaluationOrder[i];
            const nodeStartTime = performance.now();
            const result = this.evaluateChild(entityId, game, childName);

            if (result === null) {
                // Child failed - try next
                this.recordNodeToTrace(trace, debugger_, {
                    name: childName,
                    type: 'random-child',
                    index: i,
                    status: 'failure',
                    duration: performance.now() - nodeStartTime
                });
                continue;
            }

            if (result.status === 'running') {
                // Child is running - save state
                this.recordNodeToTrace(trace, debugger_, {
                    name: childName,
                    type: 'random-child',
                    index: i,
                    status: 'running',
                    duration: performance.now() - nodeStartTime,
                    meta: result.meta
                });

                this.randomState.set(entityId, {
                    shuffledOrder: evaluationOrder,
                    currentIndex: i
                });

                const runningResult = {
                    action: result.action || childName,
                    status: 'running',
                    meta: {
                        ...result.meta,
                        randomIndex: i,
                        randomOrder: evaluationOrder
                    }
                };

                this.endTrace(debugger_, trace, runningResult, entityId, game);
                return runningResult;
            }

            // Child succeeded
            this.recordNodeToTrace(trace, debugger_, {
                name: childName,
                type: 'random-child',
                index: i,
                status: 'success',
                duration: performance.now() - nodeStartTime,
                meta: result.meta
            });

            this.randomState.delete(entityId);

            if (this.sticky) {
                this.lastSuccess.set(entityId, childName);
            }

            const successResult = {
                action: result.action || childName,
                status: 'success',
                meta: {
                    ...result.meta,
                    selectedRandomly: true,
                    randomIndex: i
                }
            };

            this.endTrace(debugger_, trace, successResult, entityId, game);
            return successResult;
        }

        // All children failed
        this.randomState.delete(entityId);
        this.endTrace(debugger_, trace, null, entityId, game);
        return null;
    }

    /**
     * Evaluate a single child (unified node lookup)
     */
    evaluateChild(entityId, game, childName) {
        const node = game.gameManager.call('getNodeByType', childName);
        if (!node) {
            console.warn(`Random selector child not found: ${childName}`);
            return null;
        }

        // Trees have evaluate() method
        if (node.evaluate) {
            return node.evaluate(entityId, game);
        }

        // Actions and decorators have execute() - use processAction for normalization
        if (node.execute) {
            return this.processAction(entityId, game, childName, node);
        }

        console.warn(`Node has no evaluate or execute method: ${childName}`);
        return null;
    }

    /**
     * Record a node to the debug trace
     */
    recordNodeToTrace(trace, debugger_, nodeInfo) {
        if (debugger_ && trace) {
            debugger_.recordNode(trace, nodeInfo);
        }
    }

    /**
     * End the debug trace
     */
    endTrace(debugger_, trace, result, entityId, game) {
        if (debugger_ && trace) {
            const aiState = game.getComponent?.(entityId, 'aiState');
            const stateSnapshot = aiState?.shared ? { shared: { ...aiState.shared } } : null;
            debugger_.endEvaluation(trace, result, stateSnapshot);
        }
    }

    /**
     * Clear state when battle ends
     */
    onBattleEnd(entityId, game) {
        super.onBattleEnd(entityId, game);
        this.lastSuccess.delete(entityId);
        this.randomState.delete(entityId);
    }
}

WeightedSelectorBehaviorTree.script:
/**
 * Weighted Selector Behavior Tree
 * Selects children based on weighted probabilities
 *
 * Behavior:
 *   - Each child has a weight determining selection probability
 *   - Higher weight = higher chance of being selected
 *   - Selected child is evaluated; if it fails, falls back to priority order
 *
 * Parameters:
 *   weights: object (required)
 *     - Map of childName to weight (number)
 *     - Children not in weights get default weight of 1
 *
 *   fallbackOnFail: boolean (default: true)
 *     - If true, tries other children in weight order when selected fails
 *     - If false, immediately fails if selected child fails
 *
 *   normalizeWeights: boolean (default: true)
 *     - If true, weights are normalized to probabilities
 *     - If false, weights are used as raw values
 *
 * Usage:
 *   {
 *     "fileName": "WeightedAttackSelector",
 *     "behaviorActions": ["MeleeAttack", "RangedAttack", "SpecialAttack"],
 *     "parameters": {
 *       "weights": {
 *         "MeleeAttack": 5,
 *         "RangedAttack": 3,
 *         "SpecialAttack": 1
 *       },
 *       "fallbackOnFail": true
 *     }
 *   }
 */
class WeightedSelectorBehaviorTree extends GUTS.BaseBehaviorTree {

    constructor(game, config = {}) {
        super(game, config);

        // Parse parameters
        const params = config.parameters || {};
        this.weights = params.weights || {};
        this.fallbackOnFail = params.fallbackOnFail !== false;
        this.normalizeWeights = params.normalizeWeights !== false;

        // Track running state per entity
        // Key: entityId, Value: { selectedOrder: [], currentIndex: number }
        this.weightedState = new Map();
    }

    /**
     * Get weight for a child
     */
    getWeight(childName) {
        return this.weights[childName] !== undefined ? this.weights[childName] : 1;
    }

    /**
     * Select a child based on weighted probability
     * Returns the selected index
     */
    weightedSelect(children) {
        const weights = children.map(c => this.getWeight(c));
        const totalWeight = weights.reduce((sum, w) => sum + w, 0);

        if (totalWeight <= 0) {
            return 0; // Fallback to first
        }

        let random = Math.random() * totalWeight;

        for (let i = 0; i < weights.length; i++) {
            random -= weights[i];
            if (random <= 0) {
                return i;
            }
        }

        return weights.length - 1; // Fallback to last
    }

    /**
     * Sort children by weight (descending) for fallback order
     */
    sortByWeight(children) {
        return [...children].sort((a, b) => this.getWeight(b) - this.getWeight(a));
    }

    /**
     * Evaluate using weighted selection
     * @param {string} entityId - Entity ID
     * @param {object} game - Game instance
     * @returns {Object|null} Selected child result or null
     */
    evaluate(entityId, game) {
        const behaviorActions = this.config.behaviorActions;
        if (!behaviorActions || behaviorActions.length === 0) {
            return null;
        }

        // Get debugger if available
        const debugger_ = game.gameManager.call('getDebugger');
        const treeId = this.config.id || this.config.fileName || 'WeightedSelectorBehaviorTree';
        const trace = debugger_?.beginEvaluation(entityId, treeId);

        let evaluationOrder;
        let startIndex = 0;

        // Check for existing running state
        const state = this.weightedState.get(entityId);

        if (state) {
            // Resume from running state
            evaluationOrder = state.selectedOrder;
            startIndex = state.currentIndex;
        } else {
            // Create new weighted selection order
            const selectedIndex = this.weightedSelect(behaviorActions);
            const selectedChild = behaviorActions[selectedIndex];

            if (this.fallbackOnFail) {
                // Create fallback order: selected first, then rest by weight
                const remaining = behaviorActions.filter((_, i) => i !== selectedIndex);
                const sortedRemaining = this.sortByWeight(remaining);
                evaluationOrder = [selectedChild, ...sortedRemaining];
            } else {
                // Only try the selected child
                evaluationOrder = [selectedChild];
            }
        }

        // Record selection weights for debugging
        const selectionInfo = {
            weights: {},
            probabilities: {}
        };
        const totalWeight = behaviorActions.reduce((sum, c) => sum + this.getWeight(c), 0);
        behaviorActions.forEach(c => {
            const w = this.getWeight(c);
            selectionInfo.weights[c] = w;
            selectionInfo.probabilities[c] = totalWeight > 0 ? (w / totalWeight * 100).toFixed(1) + '%' : '0%';
        });

        // Evaluate in weighted order
        for (let i = startIndex; i < evaluationOrder.length; i++) {
            const childName = evaluationOrder[i];
            const nodeStartTime = performance.now();
            const result = this.evaluateChild(entityId, game, childName);

            const weight = this.getWeight(childName);
            const probability = totalWeight > 0 ? (weight / totalWeight * 100).toFixed(1) + '%' : '0%';

            if (result === null) {
                // Child failed
                this.recordNodeToTrace(trace, debugger_, {
                    name: childName,
                    type: 'weighted-child',
                    index: i,
                    status: 'failure',
                    duration: performance.now() - nodeStartTime,
                    meta: { weight, probability, isSelected: i === 0 }
                });

                if (!this.fallbackOnFail) {
                    // No fallback - fail immediately
                    this.weightedState.delete(entityId);
                    this.endTrace(debugger_, trace, null, entityId, game);
                    return null;
                }

                continue; // Try next in fallback order
            }

            if (result.status === 'running') {
                // Child is running - save state
                this.recordNodeToTrace(trace, debugger_, {
                    name: childName,
                    type: 'weighted-child',
                    index: i,
                    status: 'running',
                    duration: performance.now() - nodeStartTime,
                    meta: { weight, probability, isSelected: i === 0, ...result.meta }
                });

                this.weightedState.set(entityId, {
                    selectedOrder: evaluationOrder,
                    currentIndex: i
                });

                const runningResult = {
                    action: result.action || childName,
                    status: 'running',
                    meta: {
                        ...result.meta,
                        weight,
                        probability,
                        wasSelectedChild: i === 0,
                        selectionInfo
                    }
                };

                this.endTrace(debugger_, trace, runningResult, entityId, game);
                return runningResult;
            }

            // Child succeeded
            this.recordNodeToTrace(trace, debugger_, {
                name: childName,
                type: 'weighted-child',
                index: i,
                status: 'success',
                duration: performance.now() - nodeStartTime,
                meta: { weight, probability, isSelected: i === 0, ...result.meta }
            });

            this.weightedState.delete(entityId);

            const successResult = {
                action: result.action || childName,
                status: 'success',
                meta: {
                    ...result.meta,
                    weight,
                    probability,
                    wasSelectedChild: i === 0,
                    usedFallback: i > 0,
                    selectionInfo
                }
            };

            this.endTrace(debugger_, trace, successResult, entityId, game);
            return successResult;
        }

        // All children failed
        this.weightedState.delete(entityId);
        this.endTrace(debugger_, trace, null, entityId, game);
        return null;
    }

    /**
     * Evaluate a single child (unified node lookup)
     */
    evaluateChild(entityId, game, childName) {
        const node = game.gameManager.call('getNodeByType', childName);
        if (!node) {
            console.warn(`Weighted selector child not found: ${childName}`);
            return null;
        }

        // Trees have evaluate() method
        if (node.evaluate) {
            return node.evaluate(entityId, game);
        }

        // Actions and decorators have execute() - use processAction for normalization
        if (node.execute) {
            return this.processAction(entityId, game, childName, node);
        }

        console.warn(`Node has no evaluate or execute method: ${childName}`);
        return null;
    }

    /**
     * Record a node to the debug trace
     */
    recordNodeToTrace(trace, debugger_, nodeInfo) {
        if (debugger_ && trace) {
            debugger_.recordNode(trace, nodeInfo);
        }
    }

    /**
     * End the debug trace
     */
    endTrace(debugger_, trace, result, entityId, game) {
        if (debugger_ && trace) {
            const aiState = game.getComponent?.(entityId, 'aiState');
            const stateSnapshot = aiState?.shared ? { shared: { ...aiState.shared } } : null;
            debugger_.endEvaluation(trace, result, stateSnapshot);
        }
    }

    /**
     * Clear state when battle ends
     */
    onBattleEnd(entityId, game) {
        super.onBattleEnd(entityId, game);
        this.weightedState.delete(entityId);
    }
}

TextureEditor.css:
/* =============================================================================
   TEXTURE EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Canvas specific styling */
#texture-canvas {
    background-color: transparent;
    image-rendering: pixelated;
    border: 1px solid var(--editor-border-color);
    box-shadow: var(--editor-shadow-md);
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
}

/* No texture message */
.texture-editor__no-texture-message {
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    color: var(--editor-text-muted);
    font-size: 14px;
    pointer-events: none;
}

/* Color palette grid */
.texture-editor__color-palette {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: var(--editor-spacing-sm);
    margin-bottom: var(--editor-spacing-md);
}

.texture-editor__color-btn {
    width: 100%;
    aspect-ratio: 1;
    border: 2px solid var(--editor-border-color);
    border-radius: var(--editor-border-radius-sm);
    cursor: pointer;
    transition: var(--editor-transition);
}

.texture-editor__color-btn:hover {
    border-color: var(--editor-border-color-hover);
    transform: scale(1.1);
}

.texture-editor__color-btn--active {
    border-color: var(--editor-primary);
    box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.3);
}

audioEditor.css:
/* =============================================================================
   AUDIO EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Value display for sliders */
.audio-editor__value-display {
    float: right;
    font-weight: 600;
    color: var(--editor-primary);
    font-size: 12px;
}

/* Waveform display area */
.audio-editor__waveform-display {
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--editor-bg-dark);
    position: relative;
}

.audio-editor__waveform-display::before {
    content: ' Waveform Visualization';
    color: var(--editor-text-muted);
    font-size: 24px;
}

/* Status container */
#status-container {
    background-color: var(--editor-bg-light);
    padding: var(--editor-spacing-sm) var(--editor-spacing-md);
    text-align: center;
    font-size: 12px;
    color: var(--editor-text-secondary);
}

behaviorTreeEditor.css:
/* Behavior Tree Editor Styles */

.bt-action-list {
    display: flex;
    flex-direction: column;
    gap: var(--editor-spacing-sm, 8px);
}

.bt-action-item {
    padding: var(--editor-spacing-sm, 8px);
    border: 1px solid var(--editor-border-color, #333);
    border-radius: var(--editor-border-radius-sm, 4px);
    background: var(--editor-bg-secondary, #1a1a1a);
    cursor: grab;
    transition: all 0.2s;
}

.bt-action-item:hover {
    background: var(--editor-bg-hover, #252525);
    border-color: var(--editor-primary, #6366f1);
}

.bt-action-item__title {
    font-weight: 600;
    color: var(--editor-text-primary, #fff);
    margin-bottom: 4px;
}

.bt-action-item__priority {
    font-size: 12px;
    color: var(--editor-text-muted, #888);
}

.bt-node-types {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: var(--editor-spacing-sm, 8px);
}

.bt-node-btn {
    padding: var(--editor-spacing-sm, 8px);
}

.bt-canvas-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: var(--editor-spacing-md, 16px);
    border-bottom: 1px solid var(--editor-border-color, #333);
}

.bt-canvas-header h2 {
    margin: 0;
    font-size: 18px;
    color: var(--editor-text-primary, #fff);
}

.bt-canvas-controls {
    display: flex;
    gap: var(--editor-spacing-xs, 4px);
}

.bt-tree-canvas {
    flex: 1;
    position: relative;
    overflow: auto;
    background: var(--editor-bg-primary, #0a0a0a);
    background-image: 
        linear-gradient(rgba(255,255,255,0.05) 1px, transparent 1px),
        linear-gradient(90deg, rgba(255,255,255,0.05) 1px, transparent 1px);
    background-size: 20px 20px;
}

.bt-node {
    position: absolute;
    min-width: 150px;
    padding: var(--editor-spacing-md, 16px);
    border: 2px solid var(--editor-border-color, #333);
    border-radius: var(--editor-border-radius-md, 8px);
    background: var(--editor-bg-secondary, #1a1a1a);
    cursor: move;
    transition: all 0.2s;
}

.bt-node:hover {
    border-color: var(--editor-primary, #6366f1);
    box-shadow: 0 4px 12px rgba(99, 102, 241, 0.3);
}

.bt-node--selected {
    border-color: var(--editor-primary, #6366f1);
    box-shadow: 0 0 0 3px rgba(99, 102, 241, 0.2);
}

.bt-node--selector {
    border-color: #10b981;
}

.bt-node--sequence {
    border-color: #f59e0b;
}

.bt-node--condition {
    border-color: #3b82f6;
}

.bt-node--action {
    border-color: #ef4444;
}

.bt-node__header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--editor-spacing-sm, 8px);
}

.bt-node__type {
    font-size: 10px;
    text-transform: uppercase;
    letter-spacing: 0.5px;
    color: var(--editor-text-muted, #888);
}

.bt-node__title {
    font-weight: 600;
    color: var(--editor-text-primary, #fff);
}

.bt-node__actions {
    display: flex;
    gap: 4px;
}

.bt-node__btn {
    width: 20px;
    height: 20px;
    padding: 0;
    font-size: 14px;
    border: none;
    background: var(--editor-bg-hover, #252525);
    color: var(--editor-text-primary, #fff);
    border-radius: 3px;
    cursor: pointer;
}

.bt-node__btn:hover {
    background: var(--editor-primary, #6366f1);
}

.bt-connection {
    position: absolute;
    pointer-events: none;
}

.bt-connection-line {
    stroke: var(--editor-border-color, #333);
    stroke-width: 2;
    fill: none;
}

.bt-connection-line--active {
    stroke: var(--editor-primary, #6366f1);
}

.bt-node-properties {
    display: flex;
    flex-direction: column;
    gap: var(--editor-spacing-sm, 8px);
}

.bt-property {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.bt-property__label {
    font-size: 12px;
    font-weight: 600;
    color: var(--editor-text-muted, #888);
}

.bt-property__input {
    padding: var(--editor-spacing-sm, 8px);
    background: var(--editor-bg-primary, #0a0a0a);
    border: 1px solid var(--editor-border-color, #333);
    border-radius: var(--editor-border-radius-sm, 4px);
    color: var(--editor-text-primary, #fff);
    font-family: inherit;
}

.bt-json-textarea {
    min-height: 300px;
    font-family: 'Courier New', monospace;
    font-size: 12px;
    resize: vertical;
}

.bt-validation-output {
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    max-height: 150px;
    overflow-y: auto;
    background: var(--editor-bg-secondary, #1a1a1a);
    border-top: 1px solid var(--editor-border-color, #333);
    padding: var(--editor-spacing-md, 16px);
    display: none;
}

.bt-validation-output--visible {
    display: block;
}

.bt-validation-error {
    color: #ef4444;
    margin: 4px 0;
}

.bt-validation-success {
    color: #10b981;
    margin: 4px 0;
}

/* Simulation Styles */
.bt-simulation-vars {
    display: flex;
    flex-direction: column;
    gap: 8px;
}

.bt-sim-var {
    display: flex;
    flex-direction: column;
    gap: 4px;
}

.bt-sim-var__label {
    font-size: 11px;
    color: #aaa;
    font-weight: 500;
}

.bt-sim-var input[type="checkbox"] {
    width: 16px;
    height: 16px;
    cursor: pointer;
}

.bt-sim-var input[type="text"],
.bt-sim-var input[type="number"] {
    padding: 4px 8px;
    background: #0a0a0a;
    border: 1px solid #333;
    border-radius: 4px;
    color: #fff;
    font-size: 12px;
}

.bt-sim-result {
    color: #fff;
}

.bt-sim-result__action {
    font-weight: 600;
    color: #6366f1;
}

/* Active/Inactive node highlighting */
.bt-node-active {
    background: rgba(230, 226, 7, 0.747) !important;
    font-weight: 700;
}

.bt-node-inactive {
    opacity: 0.4;
}

graphicsEditor.css:
/* =============================================================================
   GRAPHICS EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Canvas specific styling */
#graphics-editor-canvas {
