}

.graphics-editor__group-item.drag-over {
    border-color: var(--editor-success);
    box-shadow: 0 0 8px rgba(34, 197, 94, 0.3);
}

/* Mode-specific visibility */
#graphics-editor-container.equipment-mode #graphics-toolbar,
#graphics-editor-container.equipment-mode #graphics-lists,
#graphics-editor-container.equipment-mode #inspector {
