.result-content {
    max-width: 600px;
    width: 90%;
    background: 
        linear-gradient(145deg, rgba(26, 13, 26, 0.9), rgba(13, 10, 26, 0.9));
    border-radius: 15px;
    padding: 3rem;
    border: 3px solid var(--primary-gold);
