    color: var(--forest-green);
    animation: victoryPulse 2s ease-in-out infinite;
}

.defeat-title {
    color: var(--blood-red);
    animation: defeatShake 0.5s ease-in-out infinite;
}

@keyframes victoryPulse {
    0%, 100% { transform: scale(1); }
    50% { transform: scale(1.05); }
}

@keyframes defeatShake {
    0%, 100% { transform: translateX(0); }
    25% { transform: translateX(-3px); }
    75% { transform: translateX(3px); }
}

.stats-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;
    margin: 2rem 0;
    background: rgba(0, 0, 0, 0.3);
    padding: 1.5rem;
    border-radius: 10px;
}

.victory-stats .stat, .defeat-stats .stat {
    display: flex;
    justify-content: space-between;
    padding: 0.5rem 0;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}

.stat-card {
    background: rgba(0, 0, 0, 0.4);
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    padding: 1rem;
}

.stat-label {
    font-size: 0.9rem;
    color: var(--stone-gray);
    margin-bottom: 0.5rem;
    font-family: var(--font-title);
}

.stat-value {
    font-size: 1.5rem;
    font-weight: bold;
    color: var(--primary-gold);
    font-family: var(--font-title);
}

.victory-controls, .defeat-controls {
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin-top: 2rem;
}

.victory-controls .btn, .defeat-controls .btn {
    min-width: 150px;
}

/* Loading Screen */
#loadingScreen {
    background: var(--shadow-gradient);
}

.loading-content {
    text-align: center;
}

.loading-spinner {
    width: 60px;
    height: 60px;
    border: 4px solid var(--dark-bronze);
    border-top: 4px solid var(--primary-gold);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin: 0 auto 2rem;
    box-shadow: 0 0 20px rgba(212, 175, 55, 0.3);
}

@keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
}

.loading-text {
    font-family: var(--font-decorative);
    color: var(--primary-gold);
    font-size: 1.5rem;
    margin-bottom: 1rem;
}

.loading-tip {
    color: var(--stone-gray);
    font-style: italic;
    max-width: 400px;
    margin: 0 auto;
    line-height: 1.4;
}

/* Pause Menu */
.pause-overlay {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.85);
    z-index: 2000;
    justify-content: center;
    align-items: center;
    backdrop-filter: blur(5px);
}

.pause-overlay.active {
    display: flex;
}

.pause-content {
    background: 
        linear-gradient(145deg, rgba(26, 13, 26, 0.95), rgba(13, 10, 26, 0.95));
    border: 3px solid var(--primary-gold);
    border-radius: 15px;
    padding: 3rem;
    text-align: center;
    box-shadow: 0 0 50px rgba(212, 175, 55, 0.3);
}

.pause-content h2 {
    font-family: var(--font-decorative);
    color: var(--primary-gold);
    font-size: 2.5rem;
    margin-bottom: 2rem;
}

/* Battle log styles */
.battle-log {
    background: rgba(0, 0, 0, 0.6);
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    padding: 1rem;
    height: 150px;
    overflow-y: auto;
    font-size: 0.8rem;
    margin-top: 1.5rem;
}

.log-entry {
    margin: 0.3rem 0;
    padding: 0.3rem 0.5rem;
    border-radius: 3px;
    line-height: 1.3;
}

.log-damage {
    color: #ff6666;
    background: rgba(255, 102, 102, 0.1);
}

.log-death {
    color: var(--blood-red);
    font-weight: bold;
    background: rgba(183, 28, 28, 0.1);
}

.log-victory {
    color: var(--forest-green);
    font-weight: bold;
    background: rgba(27, 94, 32, 0.1);
}

.log-placement {
    color: var(--primary-gold);
    background: rgba(212, 175, 55, 0.1);
}

/* Experience Panel Styles */
.experience-panel {
    background: linear-gradient(135deg, rgba(255, 140, 0, 0.15), rgba(212, 175, 55, 0.15));
    border-radius: 10px;
    border: 2px solid var(--accent-amber);
    animation: experienceGlow 2s ease-in-out infinite alternate;
    position: relative;
    overflow: hidden;
    padding: 0.5em;
    margin-bottom: 1em;
}

.experience-panel .experience-squad-info {
    display: flex; 
    justify-content: space-between; 
    align-items: center; margin-bottom: 6px; position: relative; z-index: 2;
}

.experience-panel .experience-squad-name {
    color: var(--parchment); font-size: 13px; font-weight: bold; font-family: var(--font-title);
}

.experience-panel .experience-levelUpCost {
    display: flex; 
    justify-content: space-between; 
    font-size: 11px; 
    margin-bottom: 6px; 
    position: relative; 
    z-index: 2;
}

.experience-panel .level-up-button {
    position: relative;
    z-index: 2;   
}

.experience-panel .level-up-button.level-up-button-spec {
    background: 'linear-gradient(135deg, #cc6600, #ff8800)';
    border-color: '#ffaa00';
    box-shadow: '0 0 15px rgba(255, 170, 0, 0.4)';
}

.experience-nextLevelSpec {
    color: '#ffaa00'; 
    font-size: 12px; 
    font-weight: bold;
}

.experience-nextLevel {    
    color: '#44ff44';
    font-size: 12px; 
    font-weight: bold;
}

.unit-card .shimmer {
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 215, 0, 0.3), transparent);
    transition: left 0.5s ease;
    pointer-events: none;
    z-index: 1;
}

.insufficientGoldEffect  {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(183, 28, 28, 0.3);
    border-radius: 8px;
    animation: insufficientGoldFlash 0.6s ease-out;
    pointer-events: none;
    z-index: 5;
}

@keyframes experienceGlow {
    from { box-shadow: 0 0 10px rgba(255, 140, 0, 0.3); }
    to { box-shadow: 0 0 25px rgba(255, 140, 0, 0.6); }
}
.selection-ripple {
    position: absolute;
    width: 20px;
    height: 20px;
    background: radial-gradient(circle, rgba(255, 140, 0, 0.8), transparent);
    border-radius: 50%;
    transform: scale(0);
    animation: selectionRipple 0.6s ease-out;
    pointer-events: none;
    z-index: 10;
}

.level-up-burst {
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    background: radial-gradient(circle, 
        rgba(255, 215, 0, 0.9) 0%, 
        rgba(255, 140, 0, 0.6) 50%, 
        transparent 100%);
    border-radius: 50%;
    transform: translate(-50%, -50%) scale(0);
    animation: levelUpBurst 1.2s ease-out;
    pointer-events: none;
    z-index: 10;
}

.level-up-sparkle {
    position: absolute;
    width: 4px;
    height: 4px;
    background: var(--primary-gold);
    transform: scale(0);
    animation: sparkle 0.8s ease-out;
    pointer-events: none;
    z-index: 10;
}
.experience-bar {
    width: 100%;
    height: 8px;
    background: rgba(0, 0, 0, 0.5);
    border-radius: 4px;
    overflow: hidden;
    margin: 0.5rem 0;
    border: 1px solid var(--dark-bronze);

    position: relative; 
    z-index: 2;
}

.experience-fill {
    height: 100%;
    width: 100%;
    background: linear-gradient(90deg, var(--accent-amber), var(--primary-gold));
    transition: width 0.3s ease;
    box-shadow: 0 0 10px rgba(255, 140, 0, 0.5);
}

.level-up-button {
    background: linear-gradient(135deg, var(--forest-green), #2e7d32);
    color: var(--parchment);
    border: 2px solid #388e3c;
    padding: 0.5rem 1rem;
    margin: 0.3rem;
    border-radius: 6px;
    cursor: pointer;
    font-family: var(--font-title);
    font-size: 0.8rem;
    font-weight: 600;
    transition: all 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.level-up-button:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(27, 94, 32, 0.4);
}

.level-up-button:disabled {
    background: var(--stone-gray);
    border-color: #616161;
    cursor: not-allowed;
    opacity: 0.6;
}

/* Undo Container */
.undo-container {
    margin-bottom: 1.5rem;
    padding: 1rem;
    background: rgba(0, 0, 0, 0.4);
    border-radius: 8px;
    border: 1px solid var(--dark-bronze);
}

.undo-button {
    width: 100%;
    padding: 0.8rem;
    background: linear-gradient(135deg, var(--stone-gray), #616161);
    color: var(--parchment);
    border: 1px solid #757575;
    border-radius: 6px;
    cursor: pointer;
    font-family: var(--font-title);
    font-size: 0.9rem;
    transition: all 0.3s ease;
}

.undo-button:hover {
    background: linear-gradient(135deg, #616161, #757575);
    transform: translateY(-1px);
}

.undo-button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
    transform: none;
}

/* Multiplayer Setup Dialog */
.multiplayer-setup-dialog {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.9);
    display: flex;
    justify-content: center;
    align-items: center;
    z-index: 3000;
    backdrop-filter: blur(5px);
}

.setup-content {
    background: 
        linear-gradient(145deg, rgba(26, 13, 26, 0.95), rgba(13, 10, 26, 0.95));
    border: 3px solid var(--primary-gold);
    border-radius: 15px;
    padding: 2.5rem;
    max-width: 500px;
    width: 90%;
    text-align: center;
    color: var(--parchment);
    box-shadow: 0 0 50px rgba(212, 175, 55, 0.3);
}

.setup-content h2 {
    font-family: var(--font-decorative);
    color: var(--primary-gold);
    font-size: 2rem;
    margin-bottom: 1.5rem;
}

.player-name-input {
    margin: 2rem 0;
}

.player-name-input label {
    display: block;
    margin-bottom: 0.8rem;
    color: var(--stone-gray);
    font-family: var(--font-title);
}

.player-name-input input {
    width: 100%;
    padding: 1rem;
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    background: rgba(0, 0, 0, 0.5);
    color: var(--parchment);
    font-size: 1rem;
    font-family: var(--font-title);
    transition: border-color 0.3s ease;
}

.player-name-input input:focus {
    border-color: var(--primary-gold);
    outline: none;
    box-shadow: 0 0 10px rgba(212, 175, 55, 0.3);
}

.room-join-section {
    margin-top: 1.5rem;
    display: flex;
    gap: 0.8rem;
}

.room-join-section input {
    flex: 1;
    padding: 1rem;
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    background: rgba(0, 0, 0, 0.5);
    color: var(--parchment);
    text-transform: uppercase;
    font-family: var(--font-title);
    font-weight: bold;
    letter-spacing: 1px;
}

.opponent-info {
    background: rgba(0,0,0,0.8);
    border: 2px solid var(--blood-red);
    border-radius: 5px;
    padding: 1rem;
    color: var(--parchment);
    min-width: 200px;
}

.opponent-info h4 {
    color: var(--blood-red);
    margin-bottom: 0.5rem;
    font-family: var(--font-decorative);
}

.opponent-stats div {
    margin: 0.25rem 0;
    font-size: 0.9rem;
}

.multiplayer-notification {
    background: rgba(0,0,0,0.9);
    border: 2px solid;
    border-radius: 5px;
    padding: 1rem 2rem;
    margin: 0.5rem;
    font-weight: bold;
    animation: notificationSlideIn 0.3s ease-out;
}

.modal {
    position: fixed; 
    top: 0; 
    left: 0; 
    width: 100%; 
    height: 100%;
    background: rgba(0,0,0,0.9); 
    display: flex; 
    justify-content: center;
    align-items: center; 
    z-index: 10000;
}

.team-health-bar {
    background: linear-gradient(135deg, rgba(0, 0, 0, 0.6), rgba(26, 13, 26, 0.4));
    border: 1px solid var(--dark-bronze);
    border-radius: 10px;
    padding: 0.8rem;
    min-width: 200px;
    text-align: center;
    font-family: 'Courier New', monospace;
    margin-bottom: 1rem;
}

.player-health {
    border-color: #00ff00;
}

.opponent-health {
    border-color: #ff4444;
}

.team-label {
    font-size: 0.9rem;
    font-weight: bold;
    margin-bottom: 0.5rem;
    text-shadow: 0 0 5px currentColor;
}

.health-bar-container {
    display: flex;
    flex-direction: column;
    gap: 0.3rem;
}

.health-bar {
    width: 100%;
    height: 20px;
    background: #222;
    border: 1px solid #444;
    border-radius: 10px;
    overflow: hidden;
    position: relative;
}

.health-fill {
    height: 100%;
    transition: width 0.5s ease;
    border-radius: 10px;
    position: relative;
}

.player-fill {
    background: linear-gradient(90deg, #004400 0%, #00aa00 50%, #00ff00 100%);
    box-shadow: 0 0 10px rgba(0, 255, 0, 0.5);
}

.opponent-fill {
    background: linear-gradient(90deg, #440000 0%, #aa0000 50%, #ff4444 100%);
    box-shadow: 0 0 10px rgba(255, 68, 68, 0.5);
}

.health-text {
    font-size: 0.8rem;
    color: #fff;
    font-weight: bold;
    text-shadow: 1px 1px 2px #000;
}

.damage-popup {
    font-size: 1.5rem;
    font-weight: bold;
    text-shadow: 2px 2px 4px #000;
    animation: damageFloat 2s ease-out forwards;
    pointer-events: none;
    z-index: 1001;
    color: #ff6666;
}

@keyframes damageFloat {
    0% {
        opacity: 1;
        transform: translateY(0);
        font-size: 1.5rem;
    }
    50% {
        opacity: 1;
        transform: translateY(-30px);
        font-size: 2rem;
    }
    100% {
        opacity: 0;
        transform: translateY(-60px);
        font-size: 1rem;
    }
}
@keyframes notificationSlideIn {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

/* Scrollbar Styling */
::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-track {
    background: rgba(0, 0, 0, 0.3);
    border-radius: 4px;
}

::-webkit-scrollbar-thumb {
    background: var(--dark-bronze);
    border-radius: 4px;
    transition: background 0.3s ease;
}

::-webkit-scrollbar-thumb:hover {
    background: var(--primary-gold);
}

/* Responsive Design */
@media (max-width: 768px) {
    .game-title {
        font-size: 2.5rem;
    }
    
    .mode-grid {
        grid-template-columns: 1fr;
    }
    
    #canvasContainer {
        width: 100%;
    }
    
    #uiContainer {
        position: relative;
        width: 100%;
        border-left: none;
        border-top: 3px solid var(--primary-gold);
    }
    
    .phase-info {
        position: relative;
        bottom: auto;
        left: auto;
        margin: 1rem;
    }

    .unit-shop {
        grid-template-columns: 1fr;
    }

    #multiplayerCanvasContainer {
        width: 100%;
    }
    
    #multiplayerUIContainer {
        position: relative;
        width: 100%;
        height: auto;
        border-left: none;
        border-top: 3px solid var(--primary-gold);
    }
}



/* Ripple Animation */
@keyframes ripple {
    to {
        transform: scale(4);
        opacity: 0;
    }
}

@keyframes particleFloat {
    0% { transform: translate(0, 0) rotate(0deg); opacity: 0; }
    10% { opacity: 1; }
    90% { opacity: 1; }
    100% { transform: translate(-100px, -100vh) rotate(360deg); opacity: 0; }
}

@keyframes shimmer {
    0% { left: -100%; }
    50% { left: 100%; }
    100% { left: 100%; }
}

@keyframes selectionPulse {
    0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
    100% { transform: translate(-50%, -50%) scale(10); opacity: 0; }
}

@keyframes selectionBurst {
    0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
    100% { transform: translate(-50%, -50%) scale(8); opacity: 0; }
}

@keyframes levelUpBurst {
    0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
    50% { transform: translate(-50%, -50%) scale(6); opacity: 0.8; }
    100% { transform: translate(-50%, -50%) scale(12); opacity: 0; }
}

@keyframes cardSelect {
    0% { transform: scale(1); }
    50% { transform: scale(1.05); }
    100% { transform: scale(1.02); }
}

@keyframes cardDeselect {
    0% { transform: scale(1.02); }
    100% { transform: scale(1); }
}

@keyframes copyPulse {
    0%, 100% { transform: scale(1); color: var(--accent-amber); }
    50% { transform: scale(1.1); color: var(--primary-gold); }
}

@keyframes notificationSlideOut {
    from { transform: translateX(0); opacity: 1; }
    to { transform: translateX(100%); opacity: 0; }
}



 @keyframes selectionRipple {
    0% { transform: scale(0); opacity: 1; }
    100% { transform: scale(6); opacity: 0; }
}

@keyframes insufficientGoldFlash {
    0%, 100% { opacity: 0; }
    50% { opacity: 1; }
}

@keyframes sparkle {
    0% { transform: scale(0) rotate(0deg); opacity: 1; }
    50% { transform: scale(1) rotate(180deg); opacity: 1; }
    100% { transform: scale(0) rotate(360deg); opacity: 0; }
}

@keyframes legendaryBorder {
    0%, 100% { opacity: 0.6; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.02); }
}

@keyframes levelUpBurst {
    0% { transform: translate(-50%, -50%) scale(0); opacity: 1; }
    50% { transform: translate(-50%, -50%) scale(8); opacity: 0.8; }
    100% { transform: translate(-50%, -50%) scale(15); opacity: 0; }
}

.unit-card.selected {
    border-color: var(--accent-amber) !important;
    background: linear-gradient(145deg, rgba(74, 20, 140, 0.3), rgba(114, 47, 55, 0.3)) !important;
    box-shadow: 0 0 20px rgba(255, 140, 0, 0.4), inset 0 0 10px rgba(255, 140, 0, 0.1) !important;
    transform: translateY(-3px) scale(1.02) !important;
}

.unit-card.disabled:hover {
    transform: none !important;
    border-color: var(--dark-bronze) !important;
    box-shadow: none !important;
}


@keyframes readyPulse {
    0%, 100% { 
        box-shadow: 
            0 0 20px rgba(27, 94, 32, 0.3),
            0 4px 15px rgba(27, 94, 32, 0.2);
    }
    50% { 
        box-shadow: 
            0 0 35px rgba(27, 94, 32, 0.5),
            0 4px 15px rgba(27, 94, 32, 0.2);
    }
}

@keyframes readyGlow {
    from { 
        box-shadow: 
            0 0 20px rgba(255, 140, 0, 0.4),
            0 4px 15px rgba(255, 140, 0, 0.3);
    }
    to { 
        box-shadow: 
            0 0 40px rgba(255, 140, 0, 0.7),
            0 4px 15px rgba(255, 140, 0, 0.3);
    }
}

@keyframes startGamePulse {
    0%, 100% { 
        box-shadow: 0 0 25px rgba(74, 20, 140, 0.4);
    }
    50% { 
        box-shadow: 0 0 40px rgba(74, 20, 140, 0.7);
    }
}





/* Building Shop Container */
#buildingShop {
    display: flex;
    flex-direction: column;
    gap: 8px;
    padding: 10px;
    overflow-y: auto;
    max-height: calc(100vh - 200px);
}

/* Building Section */
.building-section {
    margin-bottom: 20px;
}

.building-section-header {
    color: var(--primary-gold);
    font-family: var(--font-title);
    margin-bottom: 10px;
    font-size: 1rem;
}

/* Building Card */
.building-card {
    background: linear-gradient(145deg, rgba(13, 10, 26, 0.8), rgba(26, 13, 26, 0.8));
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    padding: 12px;
    margin-bottom: 8px;
    cursor: pointer;
    transition: all 0.3s ease;
    font-size: 0.85rem;
    position: relative;
}

.building-card.owned {
    border-color: var(--primary-gold);
}

.building-card.locked {
    opacity: 0.5;
    cursor: not-allowed;
    border-color: var(--blood-red);
}

.building-card.phase-disabled {
    opacity: 0.6;
    filter: grayscale(0.5);
}

.building-card.hover {
    transform: scale(1.02) translateY(-2px);
    box-shadow: 0 4px 12px rgba(212, 175, 55, 0.3);
}

.building-card.shake {
    animation: shake 0.5s;
}

/* Building Card Header */
.building-card-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 6px;
}

.building-card-title {
    font-family: var(--font-title);
    color: var(--primary-gold);
    font-weight: 600;
    font-size: 0.9rem;
}

.building-card-progress {
    color: var(--accent-amber);
    font-size: 0.7rem;
}

/* Building Card Details */
.building-card-cost {
    color: var(--accent-amber);
    font-size: 0.8rem;
    margin-bottom: 6px;
    font-weight: bold;
}

.building-card-locked {
    color: var(--blood-red);
    font-size: 0.75rem;
    margin-bottom: 6px;
}

.building-card-description {
    font-size: 0.75rem;
    line-height: 1.2;
    color: var(--stone-gray);
}

.building-card-owned {
    color: var(--forest-green);
    font-size: 0.7rem;
    margin-top: 6px;
}

/* Upgrade Modal */
.upgrade-modal {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    z-index: 10000;
    display: flex;
    justify-content: center;
    align-items: center;
    animation: fadeIn 0.2s;
}

.upgrade-panel {
    background: linear-gradient(145deg, #1a1a2e, #16213e);
    border: 2px solid var(--primary-gold);
    border-radius: 10px;
    padding: 20px;
    max-width: 500px;
    max-height: 80vh;
    overflow-y: auto;
    width: 90%;
}

.upgrade-panel-header {
    color: var(--primary-gold);
    margin-bottom: 15px;
    font-family: var(--font-title);
}

/* Upgrade List */
.upgrade-list {
    display: flex;
    flex-direction: column;
    gap: 10px;
    margin-bottom: 15px;
}

/* Upgrade Card */
.upgrade-card {
    background: rgba(0, 0, 0, 0.3);
    border: 2px solid var(--dark-bronze);
    border-radius: 8px;
    padding: 12px;
    transition: all 0.2s;
}

.upgrade-card.purchased {
    border-color: var(--forest-green);
}

.upgrade-card.locked {
    border-color: var(--blood-red);
}

.upgrade-card.available {
    cursor: pointer;
}

.upgrade-card.available:hover {
    transform: translateX(5px);
    border-color: var(--primary-gold);
}

/* Upgrade Card Header */
.upgrade-card-header {
    display: flex;
    justify-content: space-between;
    align-items: start;
    margin-bottom: 6px;
}

.upgrade-card-name {
    color: var(--primary-gold);
    font-weight: 600;
}

.upgrade-card-status {
    color: var(--accent-amber);
}

.upgrade-card.purchased .upgrade-card-status {
    color: var(--forest-green);
}

.upgrade-card.locked .upgrade-card-status {
    color: var(--blood-red);
}

/* Upgrade Card Details */
.upgrade-card-description {
    color: var(--stone-gray);
    font-size: 0.85rem;
    margin-bottom: 6px;
}

.upgrade-card-requirement {
    color: var(--blood-red);
    font-size: 0.75rem;
}

/* Upgrade Close Button */
.upgrade-close-button {
    width: 100%;
}

/* Animations */
@keyframes fadeIn {
    from {
        opacity: 0;
    }
    to {
        opacity: 1;
    }
}

@keyframes shake {
    0%, 100% {
        transform: translateX(0);
    }
    10%, 30%, 50%, 70%, 90% {
        transform: translateX(-5px);
    }
    20%, 40%, 60%, 80% {
        transform: translateX(5px);
    }
}













.shop-container {
    display: grid;
    grid-template-columns: 200px 1fr;
    gap: 1rem;
    margin-top: 1rem;
}


.action-panel-header {
    display: flex;
    align-items: center;
    gap: 0.6rem;
    font-size: 1.1rem;
    font-weight: bold;
    color: #ffaa00;
    margin-bottom: 1rem;
    padding-bottom: 0.6rem;
    border-bottom: 2px solid rgba(255, 170, 0, 0.3);
    text-transform: uppercase;
    letter-spacing: 1px;
}

.deselect-btn {
    background: rgba(255, 170, 0, 0.2);
    border: 1px solid rgba(255, 170, 0, 0.5);
    border-radius: 4px;
    padding: 0.3rem 0.6rem;
    color: #ffaa00;
    cursor: pointer;
    font-weight: bold;
    transition: all 0.2s ease;
}

.deselect-btn:hover {
    background: rgba(255, 170, 0, 0.3);
    border-color: rgba(255, 170, 0, 0.8);
}

.action-section {
    margin-bottom: 1.5rem;
}

.action-section:last-child {
    margin-bottom: 0;
}

.action-section-header {
    font-size: 0.85rem;
    font-weight: bold;
    color: #ffaa00;
    margin-bottom: 0.8rem;
    text-transform: uppercase;
    letter-spacing: 0.5px;
}

.action-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(50px, 1fr));
    gap: 0.6rem;
    width: 100%;
}

.action-btn {
    position: relative;
    background: linear-gradient(145deg, #2a2a3e, #1a1a2e);
    border: 2px solid rgba(255, 170, 0, 0.4);
    border-radius: 6px;
    cursor: pointer;
}

.action-btn:hover:not(.locked):not(.disabled):not(.purchased) {
    background: linear-gradient(145deg, #3a3a4e, #2a2a3e);
    border-color: rgba(255, 170, 0, 0.8);
    transform: translateY(-2px);
    box-shadow: 0 4px 12px rgba(255, 170, 0, 0.3);
}

.action-btn.locked {
    opacity: 0.5;
    cursor: not-allowed;
    border-color: rgba(100, 100, 100, 0.4);
}

.action-btn.disabled {
    opacity: 0.6;
    cursor: not-allowed;
}

.action-btn.purchased {
    background: linear-gradient(145deg, #2a3a2e, #1a2a1e);
    border-color: rgba(74, 222, 128, 0.6);
    opacity: 0.8;
    cursor: default;
}

.action-btn-icon {
    font-size: 2rem;
}

.action-btn-icon > img {
    width: 100%;
    display: block;
}

.action-btn-title {
    font-size: 0.75rem;
    font-weight: 600;
    color: #fff;
    text-align: center;
    line-height: 1.2;
}

.action-btn-cost {
    font-size: 0.75rem;
    color: #ffaa00;
    font-weight: bold;
}

.action-btn-check {
    color: #4ade80;
    font-size: 1.5rem;
    font-weight: bold;
}

.action-btn-lock {
    position: absolute;
    top: 4px;
    right: 4px;
    font-size: 0.9rem;
}

.action-btn-tooltip {
    position: absolute;
    bottom: 100%;
    left: 50%;
    transform: translateX(-50%);
    background: rgba(0, 0, 0, 0.95);
    color: #fff;
    padding: 0.4rem 0.6rem;
    border-radius: 4px;
    font-size: 0.7rem;
    white-space: nowrap;
    opacity: 0;
    pointer-events: none;
    transition: opacity 0.2s ease;
    margin-bottom: 4px;
    z-index: 1000;
}

.action-btn.locked:hover .action-btn-tooltip {
    opacity: 1;
}

.action-empty {
    text-align: center;
    color: rgba(255, 255, 255, 0.4);
    font-style: italic;
    padding: 3rem 1rem;
}

@media (max-width: 768px) {
    .shop-container {
        grid-template-columns: 1fr;
    }
    
    .building-list {
        max-height: 200px;
        display: flex;
        flex-wrap: nowrap;
        overflow-x: auto;
        overflow-y: hidden;
        gap: 0.5rem;
        padding: 0.6rem;
    }
    
    .building-list-item {
        flex-direction: column;
        min-width: 80px;
        margin-bottom: 0;
        padding: 0.5rem;
    }
    
    .building-list-info {
        text-align: center;
    }
    
    .action-grid {
        grid-template-columns: repeat(auto-fill, minmax(90px, 1fr));
    }
}

sceneEditor.css:
/* =============================================================================
   SCENE EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Scene view */
.scene-editor__scene-view {
    flex: 1;
    position: relative;
    overflow: hidden;
}

#scene-canvas-container {
    width: 100%;
    height: 100%;
    position: relative;
}

#scene-editor-canvas {
    cursor: crosshair;
    outline: none;
}

/* No selection message */
.scene-editor__no-selection {
    text-align: center;
    padding: var(--editor-spacing-xl);
    color: var(--editor-text-muted);
    font-style: italic;
}

/* Hierarchy item */
.scene-editor__hierarchy-item {
    padding: var(--editor-spacing-sm) var(--editor-spacing-md);
    cursor: pointer;
    border-left: 2px solid transparent;
    transition: var(--editor-transition);
}

.scene-editor__hierarchy-item:hover {
    background-color: rgba(255, 255, 255, 0.05);
}

.scene-editor__hierarchy-item--selected {
    background-color: var(--editor-primary);
    border-left-color: var(--editor-primary-hover);
    color: white;
}

/* Component styling */
.scene-editor__component {
    background-color: var(--editor-bg-light);
    border-radius: var(--editor-border-radius-sm);
    margin-bottom: var(--editor-spacing-md);
    padding: var(--editor-spacing-md);
}

.scene-editor__component-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: var(--editor-spacing-sm);
    font-weight: 600;
    color: var(--editor-text-secondary);
}

scriptEditor.css:
/* =============================================================================
   SCRIPT EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Code container */
.script-editor__code-container {
    position: relative;
    height: 100%;
    width: 100%;
    display: flex;
    flex-direction: column;
}

.script-editor__code {
    flex: 1;
    height: 100%;
    font-family: 'Courier New', 'Monaco', 'Menlo', monospace;
    font-size: 14px;
    line-height: 1.5;
}

/* Override global scrollbar suppression for CodeMirror */
#script-editor-container .CodeMirror {
    overflow-y: auto !important;
    -ms-overflow-style: auto !important;
    scrollbar-width: auto !important;
    height: 100%;
    font-size: 14px;
}

terrainEditor.css:
/* =============================================================================
   TERRAIN EDITOR - MODULE-SPECIFIC STYLES
   ============================================================================= */

/* Canvas specific styling */
#grid {
    display: block;
    image-rendering: pixelated;
}

/* Save button styling */
.terrain-editor__save-btn {
    width: 100%;
    font-weight: 600;
}

/* Terrain list */
.terrain-editor__terrain-list {
    display: flex;
    flex-direction: column;
    gap: var(--editor-spacing-md);
}

.terrain-editor__terrain-item {
    display: flex;
    align-items: center;
    gap: var(--editor-spacing-md);
    padding: var(--editor-spacing-md);
    border-radius: var(--editor-border-radius-sm);
    background-color: rgba(255, 255, 255, 0.03);
    border: 1px solid var(--editor-border-color);
    cursor: move;
    user-select: none;
    transition: var(--editor-transition);
}

.terrain-editor__terrain-item:hover {
    background-color: rgba(255, 255, 255, 0.05);
    transform: translateY(-2px);
}

.terrain-editor__color-option {
    width: 36px;
    height: 36px;
    min-width: 36px;
    border-radius: var(--editor-border-radius-sm);
    cursor: pointer;
    border: 2px solid transparent;
    flex-shrink: 0;
    transition: var(--editor-transition);
}

.terrain-editor__color-option--active {
    border-color: var(--editor-primary);
    box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.3);
}

.terrain-editor__terrain-label {
    flex-grow: 1;
    font-weight: 500;
}

.terrain-editor__terrain-buttons {
    display: flex;
    gap: var(--editor-spacing-sm);
}

/* Object controls */
.terrain-editor__object-controls {
    display: flex;
    justify-content: space-between;
    gap: var(--editor-spacing-sm);
    margin-bottom: var(--editor-spacing-md);
}

.terrain-editor__object-controls button {
    flex: 1;
}

/* Environment objects container */
.terrain-editor__environment-objects {
    display: flex;
    flex-direction: column;
    gap: var(--editor-spacing-md);
}

.terrain-editor__environment-type {
    background-color: var(--editor-bg-light);
    border-radius: var(--editor-border-radius-sm);
    overflow: hidden;
    box-shadow: var(--editor-shadow-sm);
    transition: var(--editor-transition);
}

.terrain-editor__environment-type:hover {
    box-shadow: var(--editor-shadow-md);
}

.terrain-editor__environment-type-header {
    padding: var(--editor-spacing-md);
    font-weight: 500;
    cursor: pointer;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: var(--editor-bg-medium);
    color: var(--editor-text-secondary);
    text-transform: capitalize;
}

.terrain-editor__environment-type-header::after {
    content: '';
    font-size: 10px;
    transition: transform 0.2s ease;
}

.terrain-editor__environment-type-header.open::after {
    transform: rotate(180deg);
}

.terrain-editor__environment-items {
    padding: var(--editor-spacing-md);
    display: flex;
    flex-wrap: wrap;
    gap: var(--editor-spacing-xs);
}

.terrain-editor__environment-item {
    width: 128px;
    height: 128px;
    border: 2px solid var(--editor-border-color);
    border-radius: var(--editor-border-radius-sm);
    cursor: pointer;
    padding: 2px;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: var(--editor-bg-light);
    transition: var(--editor-transition);
    position: relative;
}

.terrain-editor__environment-item:hover {
    border-color: var(--editor-border-color-hover);
    transform: translateY(-2px);
}

.terrain-editor__environment-item--active {
    border-color: var(--editor-primary);
    box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.3);
}

.terrain-editor__environment-item canvas {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
}

/* Tooltip for environment items */
.terrain-editor__environment-item::before {
    content: attr(data-name);
    position: absolute;
    bottom: calc(100% + 5px);
    left: 50%;
    transform: translateX(-50%);
    background-color: var(--editor-bg-dark);
    color: var(--editor-text-primary);
    text-align: center;
    border-radius: var(--editor-border-radius-sm);
    padding: var(--editor-spacing-xs) var(--editor-spacing-sm);
    font-size: 12px;
    white-space: nowrap;
    visibility: hidden;
    opacity: 0;
    transition: var(--editor-transition);
    z-index: 10;
    pointer-events: none;
    box-shadow: var(--editor-shadow-md);
}

.terrain-editor__environment-item:hover::before {
    visibility: visible;
    opacity: 1;
}

/* Object preview canvas (dragging) */
#object-preview-canvas {
    transition: transform 0.05s ease-out;
    will-change: transform;
    image-rendering: pixelated;
    position: absolute;
    pointer-events: none;
    z-index: 9999;
    opacity: 0.7;
}

/* Delete mode indicator */
.terrain-editor--delete-mode .editor-module__canvas-area {
    cursor: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="red" stroke-width="2"><path d="M3 6h18M19 6v14a2 2 0 01-2 2H7a2 2 0 01-2-2V6m3 0V4a2 2 0 012-2h4a2 2 0 012 2v2"/><path d="M10 11v6M14 11v6"/></svg>'), auto;
}

/* Height level selector */
.terrain-editor__height-levels {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: var(--editor-spacing-sm);
    margin-top: var(--editor-spacing-md);
}

.terrain-editor__height-btn {
    padding: var(--editor-spacing-sm) var(--editor-spacing-md);
    background-color: var(--editor-bg-medium);
    border: 1px solid var(--editor-border-color);
    border-radius: var(--editor-border-radius-sm);
    color: var(--editor-text-secondary);
    cursor: pointer;
    transition: var(--editor-transition);
    font-size: 12px;
    font-weight: 500;
}

.terrain-editor__height-btn:hover {
    background-color: var(--editor-bg-light);
    border-color: var(--editor-border-color-hover);
    transform: translateY(-1px);
}

.terrain-editor__height-btn.active {
    background-color: var(--editor-primary);
    border-color: var(--editor-primary);
    color: white;
    box-shadow: 0 0 0 2px rgba(99, 102, 241, 0.3);
}

/* Tool buttons */
.terrain-editor__tool-buttons {
    display: flex;
    gap: var(--editor-spacing-sm);
    margin-top: var(--editor-spacing-xs);
}

.terrain-editor__tool-buttons button {
    flex: 1;
}

/* Slider styling */
.editor-module__slider {
    flex: 1;
    height: 4px;
    border-radius: 2px;
    background: var(--editor-bg-medium);
    outline: none;
    -webkit-appearance: none;
    margin: 0 var(--editor-spacing-sm);
}

.editor-module__slider::-webkit-slider-thumb {
    -webkit-appearance: none;
    appearance: none;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background: var(--editor-primary);
    cursor: pointer;
    transition: var(--editor-transition);
}

.editor-module__slider::-moz-range-thumb {
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background: var(--editor-primary);
    cursor: pointer;
    border: none;
    transition: var(--editor-transition);
}

.editor-module__slider::-webkit-slider-thumb:hover {
    background: var(--editor-primary-hover);
    transform: scale(1.2);
}

.editor-module__slider::-moz-range-thumb:hover {
    background: var(--editor-primary-hover);
    transform: scale(1.2);
}

.editor-module__value-display {
    min-width: 20px;
    text-align: center;
    font-weight: 500;
    color: var(--editor-text-primary);
}

/* Responsive adjustments */
@media (max-width: 768px) {
    .terrain-editor__environment-items {
        justify-content: center;
    }

    .terrain-editor__environment-item {
        width: 80px;
        height: 80px;
    }

    .terrain-editor__height-levels {
        grid-template-columns: repeat(2, 1fr);
    }
}

grass.fragmentScript:
varying vec2 vUv;
uniform sampler2D map;
uniform vec3 fogColor;
uniform float fogDensity;
uniform vec3 directionalLightColor; // DirectionalLight color
uniform float directionalLightIntensity; // DirectionalLight intensity
uniform vec3 directionalLightDirection; // DirectionalLight direction (world space)
uniform vec3 ambientLightColor; // AmbientLight color
uniform float ambientLightIntensity; // AmbientLight intensity
uniform vec3 skyColor; // HemisphereLight sky color
uniform vec3 groundColor; // HemisphereLight ground color
uniform float hemisphereIntensity; // HemisphereLight intensity
varying vec3 vWorldPosition;
varying vec3 vNormal;

void main() {
    vec4 texColor = texture2D(map, vUv);
    vec3 color = texColor.rgb;

    // Directional light (Lambertian diffuse)
    vec3 lightDir = normalize(directionalLightDirection); // Ensure direction is normalized
    float diff = max(dot(vNormal, lightDir), 0.0);
    vec3 diffuse = directionalLightColor * directionalLightIntensity * diff * color;

    // Ambient light
    vec3 ambient = ambientLightColor * ambientLightIntensity * color;

    // Hemisphere light
    vec3 hemiDir = vec3(0.0, 1.0, 0.0); // Up direction for hemisphere
    float hemiDot = dot(vNormal, hemiDir) * 0.5 + 0.5; // Remap to 0-1
    vec3 hemi = mix(groundColor, skyColor, hemiDot) * hemisphereIntensity * color;

    // Combine lighting contributions
    vec3 litColor = diffuse + ambient + hemi;

    // Apply fog
    float fogDistance = length(vWorldPosition - cameraPosition);
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695);
    fogFactor = clamp(fogFactor, 0.0, 1.0);

    vec3 finalColor = mix(fogColor, litColor, fogFactor);

    gl_FragColor = vec4(finalColor, texColor.a);
}

grass.vertexScript:
varying vec2 vUv;
uniform float time;
uniform float windSpeed;
uniform float windStrength;
uniform vec2 windDirection;
attribute float instancePhase;
varying vec3 vWorldPosition;
varying vec3 vNormal; // Add for lighting

void main() {
    vUv = uv;
    vec2 dir = normalize(windDirection);
    float wave = sin(time * windSpeed + instancePhase) * windStrength;
    wave *= uv.y;

    vec3 displacement = vec3(
        dir.x * wave,
        0.0,
        dir.y * wave
    );

    // Compute world position in world space
    vec4 worldPosition = modelMatrix * instanceMatrix * vec4(position + displacement, 1.0);
    vWorldPosition = worldPosition.xyz; // Store correct world position

    vec3 normal = normalize(normal); // Assuming grass geometry has normals
    vNormal = normalize((modelMatrix * instanceMatrix * vec4(normal, 0.0)).xyz);

    // Compute view position for gl_Position
    vec4 mvPosition = viewMatrix * worldPosition;
    gl_Position = projectionMatrix * mvPosition;
    vUv = uv;
}

water.fragmentScript:
uniform float time;
uniform float waveHeight;
uniform vec3 liquidColor;
uniform vec3 foamColor;
uniform float waveFrequency;
uniform float fresnelPower;
uniform vec3 lightDirection;
uniform float ambientIntensity;
uniform float specularIntensity;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight; // New varying for normalized height
varying vec3 vWorldPosition;
uniform vec3 fogColor;
uniform float fogDensity;

void main() {
    vec2 uv = vUv;
    vec3 normal = normalize(vNormal);
    vec3 viewDir = normalize(vViewPosition);
    vec3 lightDir = normalize(lightDirection);

    // Fresnel effect for edge transparency
    float fresnel = pow(1.0 - max(dot(normal, viewDir), 0.0), fresnelPower);

    // Diffuse lighting
    float diffuse = max(dot(normal, lightDir), 0.0) * 0.25;

    // Specular (Blinn-Phong)
    vec3 halfwayDir = normalize(lightDir + viewDir);
    float specular = pow(max(dot(normal, halfwayDir), 0.0), 32.0) * specularIntensity;

    // Base color with subtle wave height modulation
    vec3 baseColor = liquidColor * (0.8 + 0.2 * vNormalizedWaveHeight); // Slight tint variation

    // Foam effect based on normalized wave height
    float foamFactor = smoothstep(0.8, 1.0, vNormalizedWaveHeight); // Tighter range for foam at peaks
    vec3 color = mix(baseColor, foamColor, foamFactor);

    float fogDistance = length(vWorldPosition - cameraPosition); // Distance from fragment to camera
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695); // 1.442695 = ln(2)
    fogFactor = clamp(fogFactor, 0.0, 1.0);
    // Combine lighting components
    vec3 lightColor = color * (ambientIntensity + diffuse) + vec3(specular);
		vec3 finalColor = mix(fogColor, lightColor, fogFactor);
    // Apply fresnel for transparency at edges
    float alpha = mix(0.6, 1.0, fresnel);

    gl_FragColor = vec4(finalColor, alpha);
}

water.vertexScript:
 uniform float time;
uniform float waveHeight;
uniform float waveFrequency;
uniform float waveSpeed;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight; // New varying for normalized height
varying vec3 vWorldPosition;
// Simple noise function for wave variation
float snoise(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void main() {
    vUv = uv;

    // Combine multiple waves for more natural movement
    float wave1 = sin(uv.x * waveFrequency + time * waveSpeed) * waveHeight;
    float wave2 = sin(uv.y * waveFrequency * 0.7 + time * waveSpeed * 0.8) * waveHeight * 0.5;
    float wave3 = cos((uv.x + uv.y) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * waveHeight * 0.3;
    float displacementY = (wave1 + wave2 + wave3) * 0.5;

    // Store wave height for fragment shader
    vWaveHeight = displacementY;

    // Normalize wave height based on maximum possible displacement
    float maxWaveHeight = waveHeight * (1.0 + 0.5 + 0.3) * 0.5; // Sum of wave amplitudes
    vNormalizedWaveHeight = displacementY / maxWaveHeight;

    // Update position with displacement
    vec3 newPosition = vec3(position.x, position.y + displacementY, position.z);

    // Compute normal for lighting
    float offset = 0.01;
    float waveX = (sin((uv.x + offset) * waveFrequency + time * waveSpeed) +
                   sin((uv.y + offset) * waveFrequency * 0.7 + time * waveSpeed * 0.8) * 0.5 +
                   cos((uv.x + uv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.3) * waveHeight * 0.5;
    float waveZ = (sin((uv.x) * waveFrequency + time * waveSpeed) +
                   sin((uv.y + offset) * waveFrequency * 0.7 + time * waveSpeed * 0.8) * 0.5 +
                   cos((uv.x + uv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.3) * waveHeight * 0.5;
    vec3 tangent = normalize(vec3(1.0, (waveX - displacementY) / offset, 0.0));
    vec3 bitangent = normalize(vec3(0.0, (waveZ - displacementY) / offset, 1.0));
    vNormal = normalize(cross(tangent, bitangent));

    // Pass view position for fresnel and lighting
    vec4 worldPosition = modelMatrix * vec4(newPosition, 1.0);
    vViewPosition = (cameraPosition - worldPosition.xyz);
    vWorldPosition = worldPosition.xyz;
    // Apply projection and model-view transforms
    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
}

