fragmentScript:
uniform float time;
uniform sampler2D portalTexture;
uniform float rotationSpeed;
uniform float textureScale;
uniform vec3 portalColor;
uniform float colorMix;
varying vec2 vUv;

void main() {
    // Rotate UVs around center
    float angle = time * rotationSpeed;
    vec2 center = vec2(0.5, 0.5);
    vec2 uv = vUv - center;

    float s = sin(angle);
    float c = cos(angle);
    vec2 rotatedUV = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);
    rotatedUV = (rotatedUV / textureScale) + center;

    vec4 texColor = texture2D(portalTexture, rotatedUV);

    // Mix texture with portal color
    vec3 finalColor = mix(texColor.rgb, portalColor * texColor.rgb, colorMix);

    gl_FragColor = vec4(finalColor, texColor.a);
}

vertexScript:
varying vec2 vUv;

void main() {
    vUv = uv;
    gl_Position = projectionMatrix * modelViewMatrix * vec4(position, 1.0);
}
