fragmentScript:
uniform float time;
uniform sampler2D overlayTexture;
uniform float textureScale;
uniform float textureSpeed;
uniform float textureAlpha;
uniform vec3 ambientLightColor;
varying vec2 vUv;

void main() {
    // Two texture layers moving in opposite diagonal directions
    vec2 texOffset1 = vec2(time * textureSpeed, time * textureSpeed);
    vec2 texOffset2 = vec2(-time * textureSpeed, -time * textureSpeed) + vec2(0.37, 0.23);
    vec2 texUv1 = (vUv / textureScale) + texOffset1;
    vec2 texUv2 = (vUv / textureScale) + texOffset2;
    vec4 texColor1 = texture2D(overlayTexture, texUv1);
    vec4 texColor2 = texture2D(overlayTexture, texUv2);

    // Blend two texture layers
    vec3 color = (texColor1.rgb + texColor2.rgb) * 0.5;

    // Apply ambient lighting
    color *= ambientLightColor;

    gl_FragColor = vec4(color, textureAlpha);
}

vertexScript:
uniform float time;
uniform float waveHeight;
uniform float waveFrequency;
uniform float waveSpeed;
varying vec2 vUv;

void main() {
    vUv = uv;

    float wave1 = sin(uv.x * waveFrequency + time * waveSpeed) * waveHeight;
    float wave2 = sin(uv.y * waveFrequency * 0.7 + time * waveSpeed * 0.8) * waveHeight * 0.5;
    float wave3 = cos((uv.x + uv.y) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * waveHeight * 0.3;
    float displacementY = (wave1 + wave2 + wave3) * 0.5;

    vec3 newPosition = vec3(position.x, position.y + displacementY, position.z);

    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
}
