water.fragmentScript:
uniform float time;
uniform float waveHeight;
uniform vec3 liquidColor;
uniform vec3 foamColor;
uniform float waveFrequency;
uniform float fresnelPower;
uniform vec3 lightDirection;
uniform float ambientIntensity;
uniform float specularIntensity;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight; // New varying for normalized height
varying vec3 vWorldPosition;
uniform vec3 fogColor;
uniform float fogDensity;

void main() {
    vec2 uv = vUv;
    vec3 normal = normalize(vNormal);
    vec3 viewDir = normalize(vViewPosition);
    vec3 lightDir = normalize(lightDirection);

    // Fresnel effect for edge transparency
    float fresnel = pow(1.0 - max(dot(normal, viewDir), 0.0), fresnelPower);

    // Diffuse lighting
    float diffuse = max(dot(normal, lightDir), 0.0) * 0.25;

    // Specular (Blinn-Phong)
    vec3 halfwayDir = normalize(lightDir + viewDir);
    float specular = pow(max(dot(normal, halfwayDir), 0.0), 32.0) * specularIntensity;

    // Base color with subtle wave height modulation
    vec3 baseColor = liquidColor * (0.8 + 0.2 * vNormalizedWaveHeight); // Slight tint variation

    // Foam effect based on normalized wave height
    float foamFactor = smoothstep(0.8, 1.0, vNormalizedWaveHeight); // Tighter range for foam at peaks
    vec3 color = mix(baseColor, foamColor, foamFactor);

    float fogDistance = length(vWorldPosition - cameraPosition); // Distance from fragment to camera
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695); // 1.442695 = ln(2)
    fogFactor = clamp(fogFactor, 0.0, 1.0);
    // Combine lighting components
    vec3 lightColor = color * (ambientIntensity + diffuse) + vec3(specular);
		vec3 finalColor = mix(fogColor, lightColor, fogFactor);
    // Apply fresnel for transparency at edges
    float alpha = mix(0.6, 1.0, fresnel);

    gl_FragColor = vec4(finalColor, alpha);
}

water.vertexScript:
 uniform float time;
uniform float waveHeight;
uniform float waveFrequency;
uniform float waveSpeed;
varying vec2 vUv;
varying vec3 vNormal;
varying vec3 vViewPosition;
varying float vWaveHeight;
varying float vNormalizedWaveHeight; // New varying for normalized height
varying vec3 vWorldPosition;
// Simple noise function for wave variation
float snoise(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

void main() {
    vUv = uv;

    // Combine multiple waves for more natural movement
    float wave1 = sin(uv.x * waveFrequency + time * waveSpeed) * waveHeight;
    float wave2 = sin(uv.y * waveFrequency * 0.7 + time * waveSpeed * 0.8) * waveHeight * 0.5;
    float wave3 = cos((uv.x + uv.y) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * waveHeight * 0.3;
    float displacementY = (wave1 + wave2 + wave3) * 0.5;

    // Store wave height for fragment shader
    vWaveHeight = displacementY;

    // Normalize wave height based on maximum possible displacement
    float maxWaveHeight = waveHeight * (1.0 + 0.5 + 0.3) * 0.5; // Sum of wave amplitudes
    vNormalizedWaveHeight = displacementY / maxWaveHeight;

    // Update position with displacement
    vec3 newPosition = vec3(position.x, position.y + displacementY, position.z);

    // Compute normal for lighting
    float offset = 0.01;
    float waveX = (sin((uv.x + offset) * waveFrequency + time * waveSpeed) +
                   sin((uv.y + offset) * waveFrequency * 0.7 + time * waveSpeed * 0.8) * 0.5 +
                   cos((uv.x + uv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.3) * waveHeight * 0.5;
    float waveZ = (sin((uv.x) * waveFrequency + time * waveSpeed) +
                   sin((uv.y + offset) * waveFrequency * 0.7 + time * waveSpeed * 0.8) * 0.5 +
                   cos((uv.x + uv.y + offset) * waveFrequency * 0.5 + time * waveSpeed * 1.2) * 0.3) * waveHeight * 0.5;
    vec3 tangent = normalize(vec3(1.0, (waveX - displacementY) / offset, 0.0));
    vec3 bitangent = normalize(vec3(0.0, (waveZ - displacementY) / offset, 1.0));
    vNormal = normalize(cross(tangent, bitangent));

    // Pass view position for fresnel and lighting
    vec4 worldPosition = modelMatrix * vec4(newPosition, 1.0);
    vViewPosition = (cameraPosition - worldPosition.xyz);
    vWorldPosition = worldPosition.xyz;
    // Apply projection and model-view transforms
    gl_Position = projectionMatrix * modelViewMatrix * vec4(newPosition, 1.0);
}

grass.fragmentScript:
varying vec2 vUv;
uniform sampler2D map;
uniform vec3 fogColor;
uniform float fogDensity;
uniform vec3 directionalLightColor; // DirectionalLight color
uniform float directionalLightIntensity; // DirectionalLight intensity
uniform vec3 directionalLightDirection; // DirectionalLight direction (world space)
uniform vec3 ambientLightColor; // AmbientLight color
uniform float ambientLightIntensity; // AmbientLight intensity
uniform vec3 skyColor; // HemisphereLight sky color
uniform vec3 groundColor; // HemisphereLight ground color
uniform float hemisphereIntensity; // HemisphereLight intensity
varying vec3 vWorldPosition;
varying vec3 vNormal;

void main() {
    vec4 texColor = texture2D(map, vUv);
    vec3 color = texColor.rgb;

    // Directional light (Lambertian diffuse)
    vec3 lightDir = normalize(directionalLightDirection); // Ensure direction is normalized
    float diff = max(dot(vNormal, lightDir), 0.0);
    vec3 diffuse = directionalLightColor * directionalLightIntensity * diff * color;

    // Ambient light
    vec3 ambient = ambientLightColor * ambientLightIntensity * color;

    // Hemisphere light
    vec3 hemiDir = vec3(0.0, 1.0, 0.0); // Up direction for hemisphere
    float hemiDot = dot(vNormal, hemiDir) * 0.5 + 0.5; // Remap to 0-1
    vec3 hemi = mix(groundColor, skyColor, hemiDot) * hemisphereIntensity * color;

    // Combine lighting contributions
    vec3 litColor = diffuse + ambient + hemi;

    // Apply fog
    float fogDistance = length(vWorldPosition - cameraPosition);
    float fogFactor = exp2(-fogDensity * fogDensity * fogDistance * fogDistance * 1.442695);
    fogFactor = clamp(fogFactor, 0.0, 1.0);

    vec3 finalColor = mix(fogColor, litColor, fogFactor);

    gl_FragColor = vec4(finalColor, texColor.a);
}

grass.vertexScript:
varying vec2 vUv;
uniform float time;
uniform float windSpeed;
uniform float windStrength;
uniform vec2 windDirection;
attribute float instancePhase;
varying vec3 vWorldPosition;
varying vec3 vNormal; // Add for lighting

void main() {
    vUv = uv;
    vec2 dir = normalize(windDirection);
    float wave = sin(time * windSpeed + instancePhase) * windStrength;
    wave *= uv.y;

    vec3 displacement = vec3(
        dir.x * wave,
        0.0,
        dir.y * wave
    );

    // Compute world position in world space
    vec4 worldPosition = modelMatrix * instanceMatrix * vec4(position + displacement, 1.0);
    vWorldPosition = worldPosition.xyz; // Store correct world position

    vec3 normal = normalize(normal); // Assuming grass geometry has normals
    vNormal = normalize((modelMatrix * instanceMatrix * vec4(normal, 0.0)).xyz);

    // Compute view position for gl_Position
    vec4 mvPosition = viewMatrix * worldPosition;
    gl_Position = projectionMatrix * mvPosition;
    vUv = uv;
}

Transform.script:
class Transform extends engine.Component {
    init({x, y, z, scaleX, scaleY, scaleZ, rotationX, rotationY, rotationZ}) {

        this.position = new THREE.Vector3(x, y, z);
        this.lastPosition = new THREE.Vector3(x, y, z);
        this.drawPosition = new THREE.Vector2(x, y);
        this.scale = new THREE.Vector3(scaleX, scaleY, scaleZ);
        this.quaternion = new THREE.Quaternion();
        this.quaternion.setFromAxisAngle( new THREE.Vector3( rotationX, rotationY, rotationZ ), Math.PI / 2 );
        
        this.parent.transform = this;
        this.setGridPosition(); 
    }

    update() {
    }

    postUpdate() {
        this.lastPosition.x = this.position.x;
        this.lastPosition.y = this.position.y;
        this.lastPosition.z = this.position.z;
        this.setGridPosition(); 
    }
    draw() {

    }
    setGridPosition() {
        if(this.game.translator){
            let gridPosition = this.game.translator.pixelToGrid( this.position.x, this.position.y ); 
            this.gridPosition = this.game.translator.snapToGrid(gridPosition.x, gridPosition.y);   
            return;
        }
        this.gridPosition = { x: 0, y: 0 };
    }
}

ArrayTracker.script:
class ArrayTracker extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
    init( {objectType}) {
        this.arr = objectType;
        if(!this.game.state[this.arr]){
            this.game.state[this.arr] = [];
        }
        this.game.state[this.arr].push(this.parent);
    }

    destroy(){
        let index = this.game.state[this.arr].indexOf(this.parent);
        this.game.state[this.arr].splice(index, 1);        
    }
}

Animator.script:
class Animator extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
        
    }
    
    
    init({ objectType, spawnType}) {

        this.animations = this.game.imageManager.getImages(objectType, spawnType); // { "idle": [...], "walk": [...] }
        this.currentAnimation = "idle";
        if(this.animations.walk) this.currentAnimation = "walk";
        this.currentFrame = 0;
        this.frameDuration = .166; // 10 frames per animation frame (~0.166s at 60 FPS)
        this.frameTimer = 0;

        this.baseSpeed = this.parent.getComponent("stats")?.stats.speed || 1;
    }

    update() {
        
        this.frameTimer += this.game.deltaTime;
        let currentSpeedPercent = this.parent.getComponent("stats")?.stats.speed / this.baseSpeed || 1;

        if (this.frameTimer >= this.frameDuration / currentSpeedPercent) {
            this.frameTimer = 0;
            const animFrames = this.animations[this.currentAnimation];
            this.currentFrame = (this.currentFrame + 1) % animFrames.length;
        }
        // Sync direction with Renderer (if separate)
        const renderer = this.parent.getComponent("Renderer");
        if (renderer) {
            renderer.images = this.animations[this.currentAnimation][this.currentFrame];
        }
    }

    setAnimation(animationType) {
        if (this.animations[animationType] && this.currentAnimation !== animationType) {
            this.currentAnimation = animationType;
            this.currentFrame = 0;
            this.frameTimer = 0;
        }
    }
    
}

Attacker.script:
class Attacker extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
 init() {        
        this.stats = this.getComponent('stats').stats;    
        this.level = 1;
        this.target = null;
        this.projectiles = [];  
        this.cooldown = 0;
    }

    update() {
        if (this.cooldown > 0) this.cooldown -= this.game.deltaTime;

        // Validate current target
        if (this.target) {
            const distance = Math.hypot(
                this.target.transform.position.x - this.parent.transform.position.x,
                this.target.transform.position.y - this.parent.transform.position.y
            );
            if (distance > this.stats.range || this.target.getComponent('health').hp <= 0) {
                this.target = null;
            }
        }

        // Find target if none
        if (!this.target) {
            this.findTarget();
        }

        // Attack if ready and has target
        if (this.cooldown <= 0 && this.target) {
            this.attack();
        }

        if (this.cooldown <= 0 && this.stats.mineAmt > 0) {
            this.gather();
        }
        return true;
    }
    
    findTarget() {
        this.target = null;
        let furthestEnemy = null;
        let furthestDistance = -1;
        const nearbyEntities = this.game.spatialGrid.getNearbyEntities(
            this.parent.transform.gridPosition.x, 
            this.parent.transform.gridPosition.y, 
            this.stats.range,
            "enemy"
        );
        for (let enemy of nearbyEntities) {
            let enemyHP = enemy.getComponent("health").hp;
            let followPath = enemy.getComponent('followPath');
            if (enemyHP <= 0) continue;          

            let distanceToEnd = this.game.state.paths[followPath.pathIndex].length - followPath.indexInPath;
            if (distanceToEnd > furthestDistance) {
                furthestDistance = followPath.indexInPath;
                furthestEnemy = enemy;
            }  
        }
        
        this.target = furthestEnemy;
    }

    gather() {
        this.game.state.bloodShards += this.stats.mineAmt;
        this.cooldown = this.stats.attackSpeed;
    }

    attack() {
        if (!this.target) return; 
        this.launchProjectile();
        this.cooldown = this.stats.attackSpeed;
    }
    
    launchProjectile() {
        this.stats = this.getComponent('stats').stats;    
        let projectileType = this.stats.projectile;
        let projectileDef = this.game.getCollections().projectiles[projectileType];
        

        let projStats = { ...projectileDef };
        delete projStats.render;
        projStats.baseDamage = this.stats.damage || 1; 
        projStats.speed = this.stats.speed || 5;     
        projStats.piercing = this.stats.piercing || 0;
        projStats.splashRadius = this.stats.splashRadius || 0;
        projStats.critChance = this.stats.critChance || 0.05;
        projStats.critMultiplier = this.stats.critMultiplier || 2;
    
       	if(projectileDef.customRenderer == "lightning") {
         	this.game.spawn('lightningProjectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else if(projectileDef.isBallistic) {
         	this.game.spawn('ballisticProjectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else if( this.stats.projectileCount > 0 ) {
            this.game.spawn('multiShotProjectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        } else {
            this.game.spawn('projectile', { objectType: "projectiles", spawnType: projectileType, target: this.target, owner: this.parent, stats: projStats }, this.parent.transform.position);
        }
    }
}

AudioManager.script:
class AudioManager extends engine.Component {
    constructor(game, parent, params) {
        super(game, parent, params);
    }

    init() {
        this.isInitialized = false;
        this.bindInitialization();
    }

    bindInitialization() {
        const initHandler = () => {
            if (!this.isInitialized) {
                this.initialize();
            }
        };

        document.addEventListener('click', initHandler, { once: true });
        document.addEventListener('keydown', initHandler, { once: true });
    }

    async initialize() {
        class SoundPool {
            constructor(audioContext, destination, poolSize = 8) {
                this.audioContext = audioContext;
                this.destination = destination;
                this.poolSize = poolSize;
                this.sources = [];
                this.initializePool();
            }

            initializePool() {
                for (let i = 0; i < this.poolSize; i++) {
                    this.sources.push(this.createSource());
                }
            }

            createSource() {
                const source = {
                    active: false,
                    id: null,
                    envelopeGain: this.audioContext.createGain(),
                    gainNode: this.audioContext.createGain(),
                    filter: this.audioContext.createBiquadFilter(),
                    distortion: this.audioContext.createWaveShaper(),
                    compressor: this.audioContext.createDynamicsCompressor(),
                    pannerNode: this.audioContext.createStereoPanner(),
                    delay: this.audioContext.createDelay(5.0),
                    delayGain: this.audioContext.createGain(),
                    convolver: this.audioContext.createConvolver(),
                    reverbGain: this.audioContext.createGain(),
                    noiseFilter: this.audioContext.createBiquadFilter(),
                    noiseGain: this.audioContext.createGain(),
                    destination: this.destination
                };

                source.filter.type = 'lowpass';
                source.filter.frequency.setValueAtTime(20000, this.audioContext.currentTime);
                source.filter.Q.setValueAtTime(0.5, this.audioContext.currentTime);

                source.noiseFilter.type = 'lowpass';
                source.noiseFilter.frequency.setValueAtTime(2000, this.audioContext.currentTime);
                source.noiseFilter.Q.setValueAtTime(1, this.audioContext.currentTime);

                source.noiseGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                source.distortion.curve = null;
                source.distortion.oversample = '4x';

                source.compressor.threshold.setValueAtTime(-24, this.audioContext.currentTime);
                source.compressor.knee.setValueAtTime(30, this.audioContext.currentTime);
                source.compressor.ratio.setValueAtTime(12, this.audioContext.currentTime);
                source.compressor.attack.setValueAtTime(0.003, this.audioContext.currentTime);
                source.compressor.release.setValueAtTime(0.25, this.audioContext.currentTime);

                source.pannerNode.pan.setValueAtTime(0, this.audioContext.currentTime);

                source.delay.delayTime.setValueAtTime(0.3, this.audioContext.currentTime);
                source.delayGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                source.reverbGain.gain.setValueAtTime(0, this.audioContext.currentTime);

                const chain = [
                    source.envelopeGain,
                    source.gainNode,
                    source.filter,
                    source.distortion,
                    source.compressor,
                    source.pannerNode
                ];

                for (let i = 0; i < chain.length - 1; i++) {
                    chain[i].connect(chain[i + 1]);
                }

                // Route delay and reverb after filter
                source.filter.connect(source.delay);
                source.delay.connect(source.delayGain);
                source.delayGain.connect(source.delay);
                source.delayGain.connect(source.pannerNode);

                source.filter.connect(source.convolver);
                source.convolver.connect(source.reverbGain);
                source.reverbGain.connect(source.pannerNode);

                source.pannerNode.connect(source.destination);

                return source;
            }

            getSource() {
                let source = this.sources.find(src => !src.active && (!src.lastUsed || this.audioContext.currentTime - src.lastUsed > 1));
                if (!source && this.sources.length < this.poolSize * 2) {
                    source = this.createSource();
                    this.sources.push(source);
                }
                if (source) {
                    source.active = true;
                    source.gainNode.gain.setValueAtTime(1, this.audioContext.currentTime);
                    source.envelopeGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.delayGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.reverbGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                    source.noiseGain.gain.setValueAtTime(0, this.audioContext.currentTime);
                }
                return source;
            }

            releaseSource(source) {
                if (source) {
                    source.active = false;
                    source.id = null;
                    const now = this.audioContext.currentTime;

                    // Reset all nodes
                    source.filter.frequency.cancelScheduledValues(now);
                    source.filter.frequency.setValueAtTime(20000, now);
                    source.distortion.curve = null;
                    source.pannerNode.pan.setValueAtTime(0, now);
                    source.envelopeGain.gain.setValueAtTime(0, now);
                    source.gainNode.gain.setValueAtTime(1, now);
                    source.delayGain.gain.setValueAtTime(0, now);
                    source.reverbGain.gain.setValueAtTime(0, now);
                    source.noiseGain.gain.setValueAtTime(0, now);

                    // Disconnect and clear sources if they exist
                    if (source.source) {
                        try {
                            source.source.stop(now);
                            source.source.disconnect();
                        } catch (e) {
                            console.warn('Error stopping source:', e);
                        }
                        source.source = null;
                    }

                    if (source.noiseSource) {
                        try {
                            source.noiseSource.stop(now);
                            source.noiseSource.disconnect();
                        } catch (e) {
                            console.warn('Error stopping noise source:', e);
                        }
                        source.noiseSource = null;
                    }

                    source.lastUsed = now;
                }
            }

            destroy() {
                this.sources.forEach(source => {
                    source.envelopeGain.disconnect();
                    source.gainNode.disconnect();
                    source.filter.disconnect();
                    source.distortion.disconnect();
                    source.compressor.disconnect();
                    source.pannerNode.disconnect();
                    source.delay.disconnect();
                    source.delayGain.disconnect();
                    source.convolver.disconnect();
                    source.reverbGain.disconnect();
                    source.noiseFilter.disconnect();
                    source.noiseGain.disconnect();
                });
                this.sources = [];
            }
        }
        this.audioContext = new (window.AudioContext || window.webkitAudioContext)();
        this.masterBus = this.createAudioBus('master');
        this.musicBus = this.createAudioBus('music');
        this.sfxBus = this.createAudioBus('sfx');
        this.uiBus = this.createAudioBus('ui');
        this.musicBus.connect(this.masterBus);
        this.sfxBus.connect(this.masterBus);
        this.uiBus.connect(this.masterBus);
        this.masterBus.connect(this.audioContext.destination);
        this.soundPools = {
            sfx: new SoundPool(this.audioContext, this.sfxBus.input, 6),
            music: new SoundPool(this.audioContext, this.musicBus.input, 2),
            ui: new SoundPool(this.audioContext, this.uiBus.input, 3)
        };
        this.buffers = {};
        this.activeSounds = new Set();
        if (this.audioContext.state === 'suspended') {
            await this.audioContext.resume();
        }
        this.isInitialized = true;
    }

    createAudioBus(name) {
        const bus = {
            name,
            input: this.audioContext.createGain(),
            compressor: this.audioContext.createDynamicsCompressor(),
            output: this.audioContext.createGain()
        };

        bus.input.connect(bus.compressor);
        bus.compressor.connect(bus.output);

        bus.connect = (destination) => {
            bus.output.connect(destination.input || destination);
        };

        bus.setVolume = (value) => {
            const now = this.audioContext.currentTime;
            bus.output.gain.cancelScheduledValues(now);
            bus.output.gain.setTargetAtTime(
                value <= 0 ? 0.000001 : value,
                now,
                0.02
            );
        };

        return bus;
    }

    getSynthSound(soundCollectionName, soundName) {
        if (this.game.getCollections()[soundCollectionName]?.[soundName]) {
            return this.game.getCollections()[soundCollectionName][soundName].audio;
        }
        console.warn('sound not found', soundCollectionName, soundName);
        return null;
    }

    playSound(soundCollectionName, soundName) {
        let data = this.getSynthSound(soundCollectionName, soundName);
        if (data) {
            this.playSynthSound(`${soundCollectionName}_${soundName}`, data);
        }
    }

    playSynthSound(soundId, soundConfig, options = {}) {
        if (!this.isInitialized) {
            this.initialize();
            return this.playSynthSound(soundId, soundConfig, options);
        }

        const category = options.category || 'sfx';
        const pool = this.soundPools[category];

        if (!pool) {
            console.warn(`No sound pool for category ${category}`);
            return null;
        }

        let instanceCount = 0;
        this.activeSounds.forEach(sound => {
            if (sound.id === soundId) instanceCount++;
        });

        if (instanceCount >= (options.maxInstances || 3)) {
            console.warn(`Max instances reached for sound ${soundId}`);
            return null;
        }

        const sound = pool.getSource();
        if (!sound) {
            console.warn(`No available source for sound ${soundId}`);
            return null;
        }

        sound.id = soundId;
        sound.category = category;

        // Create main oscillator
        const oscillator = this.createSynthSource(soundConfig);
        sound.source = oscillator;
        oscillator.connect(sound.envelopeGain);

        // Add noise generator if configured
        if (soundConfig.noise && soundConfig.noise.amount > 0) {
            this.applyNoiseToSound(sound, soundConfig.noise);
        }

        this.createEnvelopeFromConfig(soundConfig.envelope, sound.envelopeGain, soundConfig.duration);

        if (soundConfig.effects) {
            this.applySynthEffects(sound, soundConfig.effects, soundConfig);
        } 

        this.configureSoundInstance(sound, options);

        const now = this.audioContext.currentTime;
        oscillator.start(now);

        // Start noise generator if it exists
        if (sound.noiseSource) {
            sound.noiseSource.start(now);
        }

        const duration = soundConfig.duration || 1;
        const release = (soundConfig.envelope?.release) || 0.3;
        let totalDuration = duration + release;

        if (soundConfig.effects?.delay?.time) {
            const delayTail = soundConfig.effects.delay.time * 3;
            if (totalDuration < delayTail) {
                totalDuration = delayTail;
            }
        }

        oscillator.stop(now + totalDuration + 0.02);
        if (sound.noiseSource) {
            sound.noiseSource.stop(now + totalDuration + 0.02);
        }

        this.activeSounds.add(sound);

        oscillator.onended = () => {
            setTimeout(() => {
                sound.active = false;
                this.activeSounds.delete(sound);
                pool.releaseSource(sound);
            }, (soundConfig.effects?.delay?.time || 0) * 1000 * 3);
        };

        return sound;
    }

    createSynthSource(config) {
        // Default to oscillator unless waveform is 'noise'
        const oscillator = config.waveform === 'noise'
            ? this.createNoiseSource('white') 
            : this.audioContext.createOscillator();

        if (config.waveform !== 'noise') {
            oscillator.type = config.waveform || 'sine';
            const baseFreq = config.frequency || 440;
            const now = this.audioContext.currentTime;

            // Initialize frequency
            oscillator.frequency.cancelScheduledValues(now);
            oscillator.frequency.setValueAtTime(baseFreq, now);

            // Apply pitch envelope if defined
            if (config.pitchEnvelope) {
                const startMultiplier = config.pitchEnvelope.start ?? 1;
                const endMultiplier = config.pitchEnvelope.end ?? 1;
                const envelopeTime = config.pitchEnvelope.time ?? config.duration ?? 1;

                if (startMultiplier !== 1 || endMultiplier !== 1) {
                    const startFreq = baseFreq * startMultiplier;
                    const endFreq = baseFreq * endMultiplier;

                    oscillator.frequency.setValueAtTime(startFreq, now);
                    oscillator.frequency.exponentialRampToValueAtTime(
                        Math.max(endFreq, 0.01),
                        now + envelopeTime
                    );
                }
            }
        }

        return oscillator;
    }

    applyNoiseToSound(sound, noiseConfig) {
        if (!noiseConfig || noiseConfig.amount <= 0) return;

        const noiseType = noiseConfig.type || 'white';
        const noiseAmount = Math.min(1, Math.max(0, noiseConfig.amount));

        // Create the noise source
        sound.noiseSource = this.createNoiseSource(noiseType);

        // Configure noise filter if specified
        if (noiseConfig.filter && noiseConfig.filter.type !== 'none') {
            sound.noiseFilter.type = noiseConfig.filter.type || 'lowpass';
            sound.noiseFilter.frequency.setValueAtTime(
                noiseConfig.filter.frequency || 2000, 
                this.audioContext.currentTime
            );
        }

        // Set noise gain based on the amount
        sound.noiseGain.gain.setValueAtTime(noiseAmount, this.audioContext.currentTime);

        // Connect noise through the filter and envelope
        sound.noiseSource.connect(sound.noiseFilter);
        sound.noiseFilter.connect(sound.noiseGain);
        sound.noiseGain.connect(sound.envelopeGain);
    }

    createNoiseSource(noiseType = 'white') {
        const bufferSize = this.audioContext.sampleRate * 2;
        const noiseBuffer = this.audioContext.createBuffer(1, bufferSize, this.audioContext.sampleRate);
        const output = noiseBuffer.getChannelData(0);

        // Generate different types of noise
        switch (noiseType) {
            case 'pink':
                this.generatePinkNoise(output);
                break;
            case 'brown':
                this.generateBrownNoise(output);
                break;
            case 'white':
            default:
                this.generateWhiteNoise(output);
                break;
        }

        const source = this.audioContext.createBufferSource();
        source.buffer = noiseBuffer;
        source.loop = true;

        return source;
    }

    generateWhiteNoise(output) {
        for (let i = 0; i < output.length; i++) {
            output[i] = Math.random() * 2 - 1;
        }
    }

    generatePinkNoise(output) {
        // Pink noise algorithm (approximation using Paul Kellet's method)
        let b0 = 0, b1 = 0, b2 = 0, b3 = 0, b4 = 0, b5 = 0, b6 = 0;

        for (let i = 0; i < output.length; i++) {
            const white = Math.random() * 2 - 1;

            // Filter white noise to create pink noise
            b0 = 0.99886 * b0 + white * 0.0555179;
            b1 = 0.99332 * b1 + white * 0.0750759;
            b2 = 0.96900 * b2 + white * 0.1538520;
            b3 = 0.86650 * b3 + white * 0.3104856;
            b4 = 0.55000 * b4 + white * 0.5329522;
            b5 = -0.7616 * b5 - white * 0.0168980;

            output[i] = (b0 + b1 + b2 + b3 + b4 + b5 + b6 + white * 0.5362) * 0.11;
            b6 = white * 0.115926;
        }
    }

    generateBrownNoise(output) {
        // Brown noise algorithm (random walk)
        let lastValue = 0;

        for (let i = 0; i < output.length; i++) {
            // Small random change from previous value
            const white = Math.random() * 2 - 1;
            lastValue = (lastValue + (0.02 * white)) / 1.02;

            // Keep within range
            output[i] = lastValue * 3.5; // Amplify to make it audible
        }

        // Normalize to prevent clipping
        const max = Math.max(...Array.from(output).map(Math.abs));
        if (max > 0) {
            for (let i = 0; i < output.length; i++) {
                output[i] /= max;
            }
        }
    }

    createEnvelopeFromConfig(envelope, gainNode, duration = 1) {
        if (!envelope) {
            console.warn('No envelope provided, using default');
            envelope = { attack: 0.01, decay: 0.1, sustain: 0.7, release: 0.3 };
        }

        const now = this.audioContext.currentTime;

        const attack = Math.max(0.01, envelope.attack || 0.01);
        const decay = Math.max(0, envelope.decay || 0.1);
        const sustain = Math.max(0, Math.min(1, envelope.sustain || 0.7));
        const release = Math.max(0.02, envelope.release || 0.3);

        gainNode.gain.cancelScheduledValues(now);
        gainNode.gain.setValueAtTime(0, now);
        gainNode.gain.linearRampToValueAtTime(1, now + attack);
        gainNode.gain.linearRampToValueAtTime(sustain, now + attack + decay);
        gainNode.gain.setValueAtTime(sustain, now + duration);
        gainNode.gain.setTargetAtTime(0.0001, now + duration, release / 3);
        gainNode.gain.setValueAtTime(0, now + duration + release); // Absolute zero
    }

    applySynthEffects(sound, effectsConfig, soundConfig) {
        const now = this.audioContext.currentTime;

        // Filter
        if (effectsConfig.filter) {
            sound.filter.type = effectsConfig.filter.type || 'lowpass';
            sound.filter.frequency.setValueAtTime(effectsConfig.filter.frequency || 20000, now);
            sound.filter.Q.setValueAtTime(effectsConfig.filter.Q || 0.5, now);

            // Apply pitch envelope to filter for noise waveforms
            if (sound.source.buffer && soundConfig.pitchEnvelope && (soundConfig.pitchEnvelope.start !== 1 || soundConfig.pitchEnvelope.end !== 1)) {
                const baseFreq = soundConfig.frequency || 100;
                const startFreq = baseFreq * (soundConfig.pitchEnvelope.start + 1);
                const endFreq = baseFreq * soundConfig.pitchEnvelope.end;
                const duration = soundConfig.pitchEnvelope.time || soundConfig.duration || 1;

                sound.filter.frequency.cancelScheduledValues(now);
                sound.filter.frequency.setValueAtTime(startFreq, now);
                sound.filter.frequency.exponentialRampToValueAtTime(
                    Math.max(endFreq, 0.01),
                    now + duration
                );
                sound.filter.frequency.exponentialRampToValueAtTime(
                    Math.max(endFreq * 0.5, 0.01),
                    now + soundConfig.duration + (soundConfig.envelope?.release || 0.3)
                );
            }
        }

        // Distortion
        if (effectsConfig.distortion && effectsConfig.distortion > 0) {
            sound.distortion.curve = this.makeDistortionCurve(effectsConfig.distortion);
        } else {
            sound.distortion.curve = null;
        }

        // Delay
        if (effectsConfig.delay && effectsConfig.delay.feedback > 0) {
            sound.delay.delayTime.setValueAtTime(effectsConfig.delay.time || 0.3, now);
            sound.delayGain.gain.setValueAtTime(Math.min(effectsConfig.delay.feedback || 0, 0.8), now);
            sound.delayGain.gain.setTargetAtTime(0, now + soundConfig.duration + (soundConfig.envelope?.release || 0.3), 0.1);
        } else {
            sound.delayGain.gain.setValueAtTime(0, now);
        }

        // Reverb
        if (effectsConfig.reverb && effectsConfig.reverb > 0) {
            this.generateImpulseResponse(sound.convolver);
            sound.reverbGain.gain.setValueAtTime(Math.min(effectsConfig.reverb, 1), now);
            sound.reverbGain.gain.setTargetAtTime(0, now + soundConfig.duration + (soundConfig.envelope?.release || 0.3), 0.2);
        } else {
            sound.reverbGain.gain.setValueAtTime(0, now);
        }

        // Panning
        if (effectsConfig.pan !== undefined) {
            sound.pannerNode.pan.setValueAtTime(Math.max(-1, Math.min(1, effectsConfig.pan)), now);
        }
    }

    configureSoundInstance(sound, options) {
        const now = this.audioContext.currentTime;

        if (options.volume !== undefined) {
            sound.gainNode.gain.cancelScheduledValues(now);
            sound.gainNode.gain.setTargetAtTime(
                options.volume <= 0 ? 0.000001 : Math.max(0, Math.min(options.volume, 1)),
                now,
                0.02
            );
        }

        if (options.position && sound.pannerNode.positionX) {
            const pos = options.position;
            sound.pannerNode.positionX.setValueAtTime(pos.x || 0, now);
            sound.pannerNode.positionY.setValueAtTime(pos.y || 0, now);
            sound.pannerNode.positionZ.setValueAtTime(pos.z || 0, now);
        }

        if (options.pitch && sound.source && sound.source.playbackRate) {
            sound.source.playbackRate.setValueAtTime(options.pitch, now);
        }
    }

    generateImpulseResponse(convolver) {
        const length = this.audioContext.sampleRate * 1.5; // Shorter impulse
        const impulse = this.audioContext.createBuffer(2, length, this.audioContext.sampleRate);

        for (let channel = 0; channel < 2; channel++) {
            const impulseData = impulse.getChannelData(channel);
            for (let i = 0; i < length; i++) {
                const decay = Math.pow(1 - i / length, 2);
                impulseData[i] = (Math.random() * 2 - 1) * decay;
            }
        }

        convolver.buffer = impulse;
    }

    makeDistortionCurve(amount) {
        const k = Math.max(amount, 0) * 10;
        const n_samples = 44100;
        const curve = new Float32Array(n_samples);
        const deg = Math.PI / 180;
        for (let i = 0; i < n_samples; i++) {
            const x = (i * 2) / n_samples - 1;
            curve[i] = (3 + k) * x * 20 * deg / (Math.PI + k * Math.abs(x));
        }
        return curve;
    }

    stopSound(sound) {
        if (!sound) return;

        const now = this.audioContext.currentTime;

        if (sound.gainNode) {
            sound.gainNode.gain.cancelScheduledValues(now);
            sound.gainNode.gain.setValueAtTime(sound.gainNode.gain.value || 0, now);
            sound.gainNode.gain.linearRampToValueAtTime(0, now + 0.05);
        }

        if (sound.source) {
            try {
                sound.source.stop(now + 0.06);
            } catch (e) {
                console.warn('Error stopping source:', e);
            }
        }

        if (sound.noiseSource) {
            try {
                sound.noiseSource.stop(now + 0.06);
            } catch (e) {
                console.warn('Error stopping noise source:', e);
            }
        }

        sound.active = false;
        this.activeSounds.delete(sound);
    }

    stopAllSounds() {
        this.activeSounds.forEach(sound => {
            this.stopSound(sound);
        });
        this.activeSounds.clear();
    }
}

BallisticProjectile.script:
class BallisticProjectile extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
init({ spawnType, owner, target, stats }) {
    this.type = spawnType;
    this.def = this.game.getCollections().projectiles[this.type];
    this.owner = owner;
    this.target = target;
    this.stats = stats;
    this.piercedEnemies = [];
    this.ownerStats = this.owner.getComponent("stats").stats;
    this.distanceTraveled = 0;
    this.distanceToSpawnParticle = 1;
    
    // Ballistic trajectory variables
    this.startPosition = { ...this.parent.transform.position, z: 10 }; // Start 10 units above ground
    this.targetPosition = { ...this.target.transform.position, z: 0 };
    this.time = 0;
    
    this.totalDist = Math.sqrt(
        (this.targetPosition.x - this.startPosition.x) ** 2 + 
        (this.targetPosition.y - this.startPosition.y) ** 2
    );
    this.maxHeight = this.totalDist / Math.PI;
    
    // Initialize position with Z component
    this.parent.transform.position.z = this.startPosition.z;
    this.positionZ = this.startPosition.z;
    
    // Animation state variables
    this.lastZPosition = this.positionZ;

    
    this.peakThreshold = this.maxHeight * 0.1; // 10% threshold for idle at peak
    this.currentAnimState = 'ascend';
    if(this.stats.attackSound){
        this.game.audioManager.playSound('attackSounds', this.stats.attackSound);
    }
}

setAnimation(anim) {

    if(this.game.getCollections().configs.game.is3D) {
        this.modelRenderer = this.parent.getComponent('modelRenderer');
        this.modelRenderer?.setAnimation(anim);
    } else {
        this.animator = this.parent.getComponent('animator');
        this.animator?.setAnimation(anim);
    }
}

update() {
    this.parent.transform.position.z = this.positionZ;
    
    // Save previous z position to detect direction change
    this.lastZPosition = this.positionZ;

    // Calculate progress (0 to 1)
    this.time += this.game.deltaTime;
    const dx = this.targetPosition.x - this.parent.transform.position.x;
    const dy = this.targetPosition.y - this.parent.transform.position.y;
    const distSq = dx * dx + dy * dy;
    let dist = Math.sqrt(distSq);
    const speed = this.stats.speed;
    this.parent.transform.position.x += (dx / dist) * speed / (Math.PI);
    this.parent.transform.position.y += (dy / dist) * speed / (Math.PI);


    const currentDist = Math.sqrt(
        (this.parent.transform.position.x - this.startPosition.x) ** 2 + 
        (this.parent.transform.position.y - this.startPosition.y) ** 2
    );
    const xyprogressToTarget = Math.min(1, currentDist / this.totalDist);

    // Parabolic trajectory calculation (2:1 isometric adjusted)
    this.parent.transform.position.z = this.maxHeight * (1 - Math.pow(2 * xyprogressToTarget - 1, 2));

    this.positionZ = this.parent.transform.position.z;
    
    // Calculate distance from peak height to determine if we're near the top
    const distanceFromPeak = Math.abs(this.positionZ - this.maxHeight);
    
    // Check if animation state needs to change based on z movement and proximity to peak
    if( xyprogressToTarget < .075 ) {
        if (this.currentAnimState !== 'launch') {
            this.setAnimation('launch');
            this.currentAnimState = 'launch';
        }
    // } else if( xyprogressToTarget > .95 ) {
    //     if (this.currentAnimState !== 'land') {
    //         this.animator.setAnimation('land');
    //         this.currentAnimState = 'land';
    //     }
    } else if (distanceFromPeak <= this.peakThreshold) {
        // We're near the peak of the trajectory
        if (this.currentAnimState !== 'idle') {
            this.setAnimation('idle');
            this.currentAnimState = 'idle';
        }
    } else if (this.positionZ < this.lastZPosition) {
        // We're descending and not near the peak
        if (this.currentAnimState !== 'descend') {
            this.setAnimation('descend');
            this.currentAnimState = 'descend';
        }
    } else if (this.positionZ > this.lastZPosition) {
        // We're ascending and not near the peak
        if (this.currentAnimState !== 'ascend') {
            this.setAnimation('ascend');
            this.currentAnimState = 'ascend';
        }
    }
    
    // Check if we've hit the ground (Z <= 0)
    if (this.parent.transform.position.z <= 0) {
        this.parent.transform.position.z = 0; // Snap to ground

        // Hit detection - same as before but at current position
        const targetDistSq = (this.parent.transform.position.x - this.target.transform.position.x) ** 2 + 
                            (this.parent.transform.position.y - this.target.transform.position.y) ** 2;



            // We missed the target but hit the ground - maybe still do splash damage
        if (this.stats.splashRadius > 0) {
            this.processSplashDamage();
        }
        this.parent.destroy();
        if(this.stats.hitSound){
            this.game.audioManager.playSound('hitSounds', this.stats.hitSound);
        }

        return;
    }
    
    // Update distance traveled for particles
    const tDx = this.parent.transform.lastPosition.x - this.parent.transform.position.x;
    const tDy = this.parent.transform.lastPosition.y - this.parent.transform.position.y;
    const tDist = Math.sqrt(tDx * tDx + tDy * tDy);
    this.distanceTraveled += tDist;
    
    if (this.def.particle && this.distanceTraveled > this.distanceToSpawnParticle) {
        let position = {
            x: this.parent.transform.lastPosition.x + Math.random() * 4 - 2, 
            y: this.parent.transform.lastPosition.y + Math.random() * 4 - 2, 
            z: this.parent.transform.lastPosition.z + Math.random() * 4 - 2
        }
        this.game.spawn("particle", { objectType: "particles", spawnType: this.def.particle }, position);
        this.distanceTraveled = 0;
        this.distanceToSpawnParticle += Math.random() * 2;
    }
}


processSplashDamage() {
    const nearbyEnemies = this.game.spatialGrid.getNearbyEntities(
        this.parent.transform.gridPosition.x, 
        this.parent.transform.gridPosition.y, 
        this.stats.splashRadius,
        "enemy"
    );
    this.game.spawn("explosion", { radius: this.stats.splashRadius}, this.parent.transform.position);
    for (const enemy of nearbyEnemies) {
        if (enemy.isDead) continue;
        
        const dx = enemy.transform.position.x - this.parent.transform.position.x;
        const dy = enemy.transform.position.y - this.parent.transform.position.y;
        const distSq = dx * dx + dy * dy;
        
        let gridSize = this.game.getCollections().configs.game.gridSize;
        const splashRadiusSq = this.stats.splashRadius * this.stats.splashRadius * gridSize * gridSize;
        
        if (distSq <= splashRadiusSq) {
            let enemyHealth = enemy.getComponent("health");
            let enemyEnergyShield = enemy.getComponent("energyshield");
            let enemyStats = enemy.getComponent("stats");
            let enemyStatClone = { ...enemyStats.stats };
            enemyStatClone.energyShield = enemyEnergyShield.energyShield;
            
            let damageResult = engine.getFunction("calculateDamage")(this.stats, enemyStatClone);
            if (!damageResult.wasEvaded) {
                enemyHealth.hp -= damageResult.damageDealt;
                enemyEnergyShield.absorbDamage(damageResult.damageAbsorbed);
                this.game.spawn("hitEffect", { damageType: this.stats.damageType , lifeSpan: .3}, enemy.transform.position);
                if (this.ownerStats.slowAmount) {
                    enemyStats.addEffect(this.game.getCollections().effects.slow, this.game.effects.slow, this.ownerStats.slowAmount);
                }
            }
        }
    }
}
}

