GameLoader.script:
class GameLoader extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }

    init() {}
    
    async load(){
        this.collections = this.game.getCollections();        
        this.collections.configs.game.canvasWidth = window.outerWidth;
        this.collections.configs.game.canvasHeight = window.outerHeight;
        this.game.palette = this.collections.palettes && this.collections.configs.game.palette ? this.collections.palettes[this.collections.configs.game.palette] : null;
        this.isometric = this.collections.configs.game.isIsometric;
        this.game.state.tileMapData = this.collections.levels[this.game.state.level].tileMap;
        this.game.state.isometric = this.collections.configs.game.isIsometric;
        if (this.game.state.modifierSet && this.collections.modifierSets) {
            this.game.state.stats = this.collections.modifierSets[this.game.state.modifierSet];
            this.game.state.defaultStats = { ...this.game.state.stats };
        }   

        this.setupCanvas(this.collections.configs.game.canvasWidth, this.collections.configs.game.canvasHeight);
        await this.loadAssets();

        this.game.translator = new (this.game.libraryClasses.CoordinateTranslator)(this.collections.configs.game, this.collections.levels[this.game.state.level].tileMap.terrainMap.length, this.isometric);
        this.game.spatialGrid = new (this.game.libraryClasses.SpatialGrid)(this.collections.levels[this.game.state.level].tileMap.terrainMap.length, this.collections.configs.game.gridSize);
        const terrainImages = this.game.imageManager.getImages("levels", this.game.state.level);

        // Use ModuleManager's script environment
        this.game.terrainTileMapper = new (this.game.libraryClasses.TileMap)(this, {}, {CanvasUtility: this.game.libraryClasses.CanvasUtility});
        this.game.terrainTileMapper.init(this.terrainCanvasBuffer, this.collections.configs.game.gridSize, terrainImages, this.isometric);

        this.game.gameEntity = this.game.createEntityFromCollections('game', { gameConfig: this.collections.configs.game, canvas: this.canvas, canvasBuffer: this.canvasBuffer, terrainCanvasBuffer: this.terrainCanvasBuffer, worldObjects: this.collections.worldObjects, imageManager: this.game.imageManager, levelName: this.game.state.level, level: this.collections.levels[this.game.state.level], palette: this.game.palette }, new THREE.Vector3());
        this.game.imageManager.dispose();    
        this.game.audioManager = this.game.gameEntity.getComponent('AudioManager');  
    }

    getProject() {
        return this.game.gameEntity;
    }
    setupCanvas(canvasWidth, canvasHeight) {
        this.canvas = document.getElementById("gameCanvas");
        if(this.game.getCollections().configs.game.is3D){
            this.finalCtx = this.canvas.getContext("webgl2");
        } else {
            this.finalCtx = this.canvas.getContext("2d");
        }
        this.canvasBuffer = document.createElement("canvas");
        this.ctx = this.canvasBuffer.getContext("2d");
        this.canvasBuffer.setAttribute('width', canvasWidth);
        this.canvasBuffer.setAttribute('height', canvasHeight);
        this.canvas.setAttribute('width', canvasWidth);
        this.canvas.setAttribute('height', canvasHeight);  
        
        this.terrainCanvasBuffer = document.createElement('canvas');
        this.terrainCanvasBuffer.width = this.collections.configs.game.gridSize * this.collections.levels[this.game.state.level].tileMap.terrainMap[0].length;
        this.terrainCanvasBuffer.height = this.collections.configs.game.gridSize * this.collections.levels[this.game.state.level].tileMap.terrainMap.length;

        this.game.canvas = this.canvas;
        this.game.finalCtx = this.finalCtx;
        this.game.canvasBuffer = this.canvasBuffer;
        this.game.ctx = this.ctx;
        this.game.terrainCanvasBuffer = this.terrainCanvasBuffer;
    }
    async loadAssets() {
        this.game.imageManager = new (this.game.libraryClasses.ImageManager)(this, { imageSize: this.collections.configs.game.imageSize, palette: this.game.palette, textures:  this.game.getCollections().textures}, {ShapeFactory: this.game.libraryClasses.ShapeFactory});    
        // Load all images
        for(let objectType in this.collections) {
            await this.game.imageManager.loadImages(objectType, this.collections[objectType]);
        }  
        this.game.modelManager = new (this.game.libraryClasses.ModelManager)(this, {}, { ShapeFactory: this.game.libraryClasses.ShapeFactory, palette: this.game.palette, textures: this.game.getCollections().textures});
        
        for(let objectType in this.collections) {
            await this.game.modelManager.loadModels(objectType, this.collections[objectType]);
        }  
 
    }
}

HitEffectParticle.script:
class HitEffectParticle extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
  init( {damageType}) {
        this.damageType = damageType || "default";
        this.particles = [];
        
        // Spawn particles based on damage type
        const particleCount = 5;
        for (let i = 0; i < particleCount; i++) {
            this.particles.push({
                x: 0, // Relative to parent
                y: 0,
                vx: (Math.random() - 0.5) * 4, // Velocity (-2 to 2)
                vy: (Math.random() - 0.5) * 4,
                size: Math.random() * 3 + 1, // 1-4 pixels
                alpha: 1, // Fades from 1 to 0
                color: this.getColor()
            });
        }
    }

    getColor() {
        switch (this.damageType) {
            case "electric": return "rgba(0, 255, 255, "; // Cyan for electric
            case "fire": return "rgba(255, 165, 0, "; // Orange for fire
            case "ice": return "rgba(128, 128, 255, "; // Blue for ice
            case "plasma": return "rgba(255, 0, 255, "; // Purple for plasma
            default: return "rgba(255, 255, 255, "; // White fallback
        }
    }

    update() {
        const lifespanComp = this.parent.getComponent("LifeSpan");
        if (!lifespanComp) return; // Safety check

        const fadeFactor = lifespanComp.lifeSpan / 30; // Normalize to initial lifespan (30 frames)
        for (let particle of this.particles) {
            particle.x += particle.vx * this.game.state.timeScale;
            particle.y += particle.vy * this.game.state.timeScale;
            particle.alpha = fadeFactor; // Fade based on remaining lifespan
            particle.size *= 0.98; // Slight shrink per frame
        }
    }
}

MapManager.script:
class MapManager extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
init({level}) {
  this.nodeClass = class Node {
    constructor(x, y, tileType, parent = null) {
        this.x = x;
        this.y = y;
        this.tileType = tileType;
        this.parent = parent;
        
        this.g = 0; // Cost from start to current node
        this.h = 0; // Heuristic (estimated cost from current to goal)
        this.f = 0; // Total cost (g + h)
    }

    equals(other) {
        return this.x === other.x && this.y === other.y;
    }

    // Unique key for node based on coordinates
    key() {
        return `${this.x},${this.y}`;
    }
};
  this.tileMap = level.tileMap;
   
   const {tileMap, paths} = this.generateMap();
   this.game.state.tileMap = tileMap;
   this.game.state.paths = paths;
}

    generateMap() {
        // Extract values from the data object
        const { size, terrainTypes, terrainMap } = this.tileMap;
        
        let paths = [];
        let starts = [];
        let endPoint = {x: 0, y: 0};

        // Find START and END tile types by their IDs
        const startTypeId = terrainTypes.findIndex(t => t.type === "start");
        const endTypeId = terrainTypes.findIndex(t => t.type === "end");
        // Create the tile map using the provided terrainMap
        const tileMap = terrainMap.map((row, y) => 
            row.map((terrainId, x) => {
                // Find the terrain object to get color information
                const terrain = terrainTypes[terrainId]; 
                // Check for start/end points using IDs
                if(terrainId === startTypeId) {
                    starts.push({x: x, y: y});
                } else if(terrainId === endTypeId) {
                    endPoint = {x: x, y: y};
                }
                
                return { 
                    type: terrain ? terrain.type : 'unknown',
                    typeId: terrainId,
                    color: terrain ? terrain.color : '#8bc34a', // Default to grass color if not found
                    buildable: terrain ? terrain.buildable : false
                };
            })
        );        
        
        starts.forEach((startPoint) => {
            paths.push(this.findPath(startPoint, endPoint, terrainMap));
        });

        return { tileMap, paths };
    }

    findPath(startPoint, endPoint, tileMap) {
        return this.aStar(startPoint, endPoint, tileMap);
    }
    
    /**
     * A* pathfinding algorithm
     * @param {Object} start - Starting position {x, y}
     * @param {Object} end - Ending position {x, y}
     * @param {Array} tileMap - 2D array representing the map
     * @returns {Array} - Array of positions forming the path, or empty array if no path found
     */
    aStar(start, end, tileMap) {
       
        const rows = tileMap.length;
        const cols = tileMap[0].length;
        
        // Validate inputs
        if (start.x < 0 || start.x >= cols || start.y < 0 || start.y >= rows) {
            throw new Error('Start position is outside the map bounds');
        }
        if (end.x < 0 || end.x >= cols || end.y < 0 || end.y >= rows) {
            throw new Error('End position is outside the map bounds');
        }
        
        // Create start and end nodes
        const startNode = new this.nodeClass(start.x, start.y, tileMap[start.y][start.x]);
        const endNode = new this.nodeClass(end.x, end.y, tileMap[end.y][end.x]);
        
        // Initialize open and closed lists
        const openList = [];
        const closedList = new Set();
        const openSet = new Set(); // For faster lookups
        
        // Add start node to open list
        openList.push(startNode);
        openSet.add(startNode.key());
        
        // Define movement directions (4-directional: up, right, down, left)
        const directions = [
            {x: 0, y: -1}, // Up
            {x: 1, y: 0},  // Right
            {x: 0, y: 1},  // Down
            {x: -1, y: 0}  // Left
            
            // {x: 1, y: 1}, // Up
            // {x: 1, y: -1},  // Right
            // {x: -1, y: 1},  // Down
            // {x: -1, y: -1}  // Left
        ];
        
        // Main loop
        while (openList.length > 0) {
            // Sort by f value and take the lowest
            openList.sort((a, b) => a.f - b.f);
            const currentNode = openList.shift();
            openSet.delete(currentNode.key());
            
            // Add current node to closed list
            closedList.add(currentNode.key());
            
            // Check if we've reached the end
            if (currentNode.equals(endNode)) {
                // Reconstruct the path
                return this.reconstructPath(currentNode);
            }
            
            // Generate neighbors
            for (const dir of directions) {
                const neighborX = currentNode.x + dir.x;
                const neighborY = currentNode.y + dir.y;
                
                // Check if neighbor is inside the map
                if (neighborX < 0 || neighborX >= cols || neighborY < 0 || neighborY >= rows) {
                    continue;
                }
                
                const tileType = tileMap[neighborY][neighborX];
                const neighbor = new this.nodeClass(neighborX, neighborY, tileType, currentNode);
                const neighborKey = neighbor.key();
                
                // Skip if neighbor is in closed list
                if (closedList.has(neighborKey)) {
                    continue;
                }
                
                // Calculate g score for this neighbor
                // Path tiles will have a much lower cost than other tile types
                let movementCost = this.calculateMovementCost(tileType);
                const tentativeG = currentNode.g + movementCost;
                
                // Check if this is a better path to neighbor
                if (!openSet.has(neighborKey) || tentativeG < neighbor.g) {
                    // Update neighbor values
                    neighbor.g = tentativeG;
                    neighbor.h = this.calculateHeuristic(neighbor, endNode);
                    neighbor.f = neighbor.g + neighbor.h;
                    
                    // Add neighbor to open list if not there already
                    if (!openSet.has(neighborKey)) {
                        openList.push(neighbor);
                        openSet.add(neighborKey);
                    }
                }
            }
        }
        
        // No path found
        return [];
    }
    
    /**
     * Calculate movement cost based on tile type
     * Path tiles are heavily favored
     * @param {number} tileTypeId - ID of the tile type
     * @returns {number} - Movement cost
     */
    calculateMovementCost(tileTypeId) {
        // Find path and end type IDs
        const { terrainTypes } = this.tileMap;
        const pathTypeId = terrainTypes.findIndex(t => t.type === "path");
        const endTypeId = terrainTypes.findIndex(t => t.type === "end");
        
        // Heavily favor "path" and "end" type tiles
        if (tileTypeId === pathTypeId || tileTypeId === endTypeId) {
            return 1;
        } else {
            // Make non-path tiles much less desirable
            return 100000;
        }
    }
    
    /**
     * Calculate heuristic (Manhattan distance)
     * @param {Node} nodeA 
     * @param {Node} nodeB 
     * @returns {number} - Heuristic value
     */
    calculateHeuristic(nodeA, nodeB) {
        return Math.abs(nodeA.x - nodeB.x) + Math.abs(nodeA.y - nodeB.y);
    }
    
    /**
     * Reconstruct the path from the end node to the start
     * @param {Node} endNode - The end node
     * @returns {Array} - Array of positions {x, y} from start to end
     */
    reconstructPath(endNode) {
        const path = [];
        let currentNode = endNode;
        
        while (currentNode !== null) {
            path.unshift({
                x: currentNode.x,
                y: currentNode.y,
                tileType: currentNode.tileType
            });
            currentNode = currentNode.parent;
        }
        
        return path;
    }
}

Game.script:
class Game extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
        
    init() {
        this.initEffectsAndUpgrades();
        this.gridSize = this.game.getCollections().configs.game.gridSize;
        let endPath = this.game.state.paths[0][this.game.state.paths[0].length - 1];
        let endY = endPath.y;
        let endX = endPath.x;
        let keepPosition = {
            x: endX * this.gridSize + this.gridSize / 2,
            y: endY * this.gridSize + this.gridSize / 2,
            z: 0
        }
        this.keep = this.game.spawn("tower",{ spawnType: 'keep', objectType: 'towers', setDirection: 1}, keepPosition);
        this.keep.placed = true;
        
        this.keep.transform.position.z = this.getTerrainHeight(this.keep.transform.gridPosition);
    }

    update() {
        if(this.game.getCollections().configs.game.is3D){
            this.getComponent("MapRenderer")?.render(this.game.state.tileMapData, this.game.state.paths);
        } else {
            this.getComponent("MapRenderer")?.renderBG(this.game.state.tileMapData, this.game.state.paths);
        }
        if (!this.game.state.isPaused) {
            this.currentTime = Date.now();

            // Only update if a reasonable amount of time has passed
            const timeSinceLastUpdate = this.currentTime - this.lastTime;

            // Skip update if more than 1 second has passed (tab was inactive)
            if (timeSinceLastUpdate > 1000) {
                this.lastTime = this.currentTime; // Reset timer without updating
                return;
            }

            this.game.deltaTime = Math.min(1/30, timeSinceLastUpdate / 1000); // Cap at 1/30th of a second        
            this.lastTime = this.currentTime;

            // Sort entities by y position for proper drawing order
            this.game.state.entities.sort((a, b) => {
                return (a.transform.position.y * this.game.state.tileMap.length + a.transform.position.x) - (b.transform.position.y * this.game.state.tileMap.length + b.transform.position.x)
            });

            this.game.state.stats = {...this.game.state.defaultStats};//reset stats to recalculate upgrades from base stats.

            let entitiesToRemove = [];
            for (let i = 0; i < this.game.state.entities.length; i++) {
                let e = this.game.state.entities[i];
                e.update();
                if(!e.destroyed){
                    e.draw();
                    e.postUpdate();  
                } else {
                    entitiesToRemove.push(i);
                }     
            }
            for(let i = entitiesToRemove.length - 1; i >= 0; i--){
                this.game.state.entities.splice(entitiesToRemove[i], 1);
            }
            
            this.postUpdate();
        }     
        if(!this.game.getCollections().configs.game.is3D){
            this.getComponent("MapRenderer")?.renderFG(this.game.state.tileMapData, this.game.state.paths);
        }   
        
    }

    getTerrainHeight(gridPosition) {
        if(this.game.state.tileMap.length > gridPosition.y && gridPosition.y > 0 && this.game.state.tileMap[Math.floor(gridPosition.y)] && this.game.state.tileMap[Math.floor(gridPosition.y)].length > gridPosition.x && gridPosition.x > 0){
            const tile = this.game.state.tileMap[Math.floor(gridPosition.y)][Math.floor(gridPosition.x)];
            if (!tile) {
                return 0;
            }
            const terrainHeight = tile.typeId * this.game.heightMapConfig.heightStep;
            return terrainHeight;
        }
        return 0;
    }
    postUpdate() {
        
            if (this.game.state.gameOver || this.game.state.victory || this.game.state.isLevelingUp) return;
                    
            // Game over check
            if (this.game.state.bloodCoreHP <= 0 && !this.game.state.gameOver) {
                this.gameOver();
            }
    }


    // Game-over and victory functions
    gameOver() {
        this.game.state.gameOver = true;
        this.game.state.isPaused = true;
        gameOverWave.textContent = this.game.state.round + 1;
        gameOverMenu.style.display = 'block';
        overlay.style.display = 'block';
    }

    gameVictory() {
        this.game.state.victory = true;
        this.game.state.isPaused = true;
        victoryMenu.style.display = 'block';
        overlay.style.display = 'block';
    }


    // Tower placement system
  

    initEffectsAndUpgrades() {

        const Upgrade = class { 
            constructor(id, title, desc, icon, appliesTo, condition, apply, onAcquire) {
                this.id = id;
                this.title = title;
                this.desc = desc;
                this.icon = icon;
                this.appliesTo = appliesTo;
                this.conditionFn = condition;
                this.applyFn = apply;
                this.onAcquire = onAcquire;        
            }

            canApply(gameState) {
                return this.conditionFn(gameState);
            }

            apply(s, add, mul) {
                this.applyFn(s, add, mul);
            }
        }


        this.game.effects = {
            slow: (stats, additiveStats, multiplicitiveStats, slowAmount) => {
                stats[this.game.getCollections().effects.slow.stat] *= slowAmount;
            }
        }
        this.game.upgrades = [
            // Bat Swarm Upgrades
            new Upgrade(
                'sentryFrenzy',
                'Sentry Frenzy',
                'Sentry Swarm: ' + this.game.getCollections().upgrades.sentryFrenzy.desc,
                '',
                'sentry',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    multiplicitiveStats['attackSpeed'].push(this.game.getCollections().upgrades.sentryFrenzy.value);
                }
            ),
            new Upgrade(
                'sentryIntelligence',
                'Sentry Intelligence',
                'Sentry Swarm: ' + this.game.getCollections().upgrades.sentryIntelligence.desc,
                '',
                'sentry',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {            
                    multiplicitiveStats['damage'].push(this.game.getCollections().upgrades.sentryIntelligence.damage);
                    multiplicitiveStats['range'].push(this.game.getCollections().upgrades.sentryIntelligence.range);        
                }
            ),

            // Necromancer Upgrades
            new Upgrade(
                'necroSummon',
                'Raise Dead',
                'Necromancer: ' + this.game.getCollections().upgrades.necroSummon.desc,
                '',
                'fabricator',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    stats.summonChance = 1;
                    if(!additiveStats.summonChance) additiveStats.summonChance = [];
                    additiveStats['summonChance'].push(this.game.getCollections().upgrades.necroSummon.summonChance);
                }
            ),

            // Shadow Turret Upgrades
            new Upgrade(
                'overCharge',
                'Overcharge',
                'Tesla Coil: ' + this.game.getCollections().upgrades.overCharge.desc,
                '',
                'teslaCoil',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    additiveStats['range'].push(this.game.getCollections().upgrades.overCharge.range);
                }
            ),

            // Soul Pyre Upgrades
            new Upgrade(
                'pyreSoul',
                'Radiant Soul',
                'Soul Pyre: ' + this.game.getCollections().upgrades.pyreSoul.desc,
                '',
                'soulPyre',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    additiveStats['splashRadius'].push(this.game.getCollections().upgrades.pyreSoul.splashRadius);
                }
            ),

            // Mist Shrine Upgrades
            new Upgrade(
                'mistSlow',
                'Chilling Mist',
                'Mist Shrine: ' + this.game.getCollections().upgrades.mistSlow.desc,
                '',
                'mistShrine',
                (state) => true,
                (stats, additiveStats, multiplicitiveStats) => {
                    multiplicitiveStats['slowAmount'].push(this.game.getCollections().upgrades.mistSlow.slowAmount);
                }
            ),
            // Global Upgrades
            new Upgrade(
                'homeReinforcement',
                'Reinforcement',
                this.game.getCollections().upgrades.bloodCore.desc,
                '',
                'global',
                (state) => true,
                (stats) => {
                    stats.maxBloodCoreHP *= this.game.getCollections().upgrades.bloodCore.maxHpMultiplier;
                },
                (state) => {
                    state.bloodCoreHP = Math.min(state.stats.maxBloodCoreHP, state.bloodCoreHP + this.game.getCollections().upgrades.bloodCore.healAmount);
                }
            ),
            new Upgrade(
                'essenceExtraction',
                'Essence Extraction',
                this.game.getCollections().upgrades.essenceExtraction.desc,
                '',
                'global',
                (state) => true,
                (stats) => {
                    stats.essenceMultiplier *= this.game.getCollections().upgrades.essenceExtraction.value;
                }
            ),
            new Upgrade(
                'essenceOverflow',
                'Essence Overflow',
                this.game.getCollections().upgrades.essenceOverflow.desc,
                '',
                'global',
                (state) => state.bloodCoreHP > state.stats.maxBloodCoreHP / 2,
                (stats) => {
                    stats.essenceMultiplier *= this.game.getCollections().upgrades.essenceOverflow.value;
                }
            ),

        ];

    }
}

