class GameState {
    constructor(gameConfig = {}) {
        let state = gameConfig.configs.state;
     
        // Clear all existing properties
        for (let prop in this) {
            if (Object.prototype.hasOwnProperty.call(this, prop)) {
                delete this[prop];
            }
        }

        // Set only the properties from params
        for (let key in state) {
            if (Object.prototype.hasOwnProperty.call(state, key)) {
                this[key] = state[key];
            }
        }
        this.entities = [];
        // If stats is present, create defaultStats as a copy
  
    }

    addEntity(entity) {
        this.entities.push(entity);
    }
    removeEntity(entity) {
    
        let index = this.entities.indexOf(entity);
        if( index >= 0 ) {
            this.entities.splice(index, 1);
        }
    }
}

ModelManager.script:
class ModelManager {
    constructor(app, config, { ShapeFactory, palette, textures }) {
        this.app = app;
        this.config = config;
        this.models = {};
        this.shapeFactory = new ShapeFactory(palette, textures, null);
        this.textureAtlases = new Map();
        this.uvMappings = new Map();
        this.mergedGeometries = new Map();
    }

    clear() {
        this.models = {};
        this.uvMappings.clear();
        this.mergedGeometries.clear();
        this.textureAtlases.clear();

    }

    dispose() {
        for (const [key, model] of Object.entries(this.models)) {
            this.disposeModel(model);
        }
        this.models = {};
        this.uvMappings.clear();
        this.mergedGeometries.clear();
        this.textureAtlases.clear();

    }

    disposeModel(model) {
        if (!model) return;
        if (model.animations) {
            for (const [animType, frames] of Object.entries(model.animations)) {
                for (const frame of frames) {
                    if (frame.group) {
                        this.shapeFactory.disposeObject(frame.group);
                        frame.group = null;
                    }
                }
            }
        }
    }

    async loadModels(prefix, config) {
        if (!prefix || !config || typeof config !== 'object') {
            throw new Error('Invalid prefix or config provided to loadModels');
        }

        const textures = [];
        const textureInfo = [];

        // First pass: Load temporary models to collect textures
        const tempModels = [];
        for (const [type, cfg] of Object.entries(config)) {
            if (cfg.render && cfg.render.model) {
                const modelGroupName = Object.keys(cfg.render.model)[0];
                const modelGroup = cfg.render.model[modelGroupName];
                const isGLTF = modelGroup.shapes.length > 0 && modelGroup.shapes[0].type === "gltf";
                if (isGLTF) {
                    const modelKey = `${prefix}_${type}`;
                    const model = await this.createModel(prefix, type, cfg.render.model, false);
                    tempModels.push({ modelKey, model, spawnType: type });
                }
            }
        }

        // Collect textures
        tempModels.forEach(({ modelKey, model, spawnType }) => {
            let meshIndex = 0;
            model.traverse(child => {
                if (child.isMesh && child.material.map) {
                    textures.push(child.material.map);                    
                    textureInfo.push({ modelKey, spawnType, meshIndex });
                    meshIndex++;
                }
            });
        });

        // Generate texture atlas if textures exist
        if (textures.length > 0) {
            await this.generateTextureAtlas(prefix, textures, textureInfo);
        }

        // Second pass: Create final models with atlas and remapped UVs
        for (const [type, cfg] of Object.entries(config)) {
            if (cfg.render && cfg.render.model) {
                const modelGroupName = Object.keys(cfg.render.model)[0];
                const modelGroup = cfg.render.model[modelGroupName];
                const isGLTF = modelGroup.shapes.length > 0 && modelGroup.shapes[0].type === "gltf";
                if (isGLTF) {
                    const modelKey = `${prefix}_${type}`;
                    this.models[modelKey] = await this.createModel(prefix, type, cfg.render.model, true);
                    const animations = cfg.render.animations;
                    await Promise.all(Object.keys(animations).map(async (animationName) => {
                        const anim = animations[animationName][0];
                        const animMainGroup = anim[Object.keys(anim)[0]];
                        let mergedModel = JSON.parse(JSON.stringify(cfg.render.model));
                        if (animMainGroup) {
                            mergedModel[modelGroupName].shapes[0].url = animMainGroup.shapes[0].url;
                        }
                        const modelKey = `${prefix}_${type}_${animationName}`;
                        this.models[modelKey] = await this.createModel(prefix, type, mergedModel, true);
                    }));
                } else {
                    this.models[`${prefix}_${type}`] = await this.createModel(prefix, type, cfg.render.model, false);
                }
            }
        }  
        // if (textures.length > 0) {
        //     this.debugTextureAtlas(config, prefix);
        // }
        // Dispose temporary models
        tempModels.forEach(({ model }) => this.shapeFactory.disposeObject(model));
    }
    debugTextureAtlas(config, prefix) {
        if (!this.textureAtlases[prefix] || this.uvMappings.size === 0) {
            console.warn('No texture atlas or UV mappings available to debug.');
            return;
        }

        // Create a new canvas
        const canvas = document.createElement('canvas');
        canvas.width = this.textureAtlases[prefix].image.width;
        canvas.height = this.textureAtlases[prefix].image.height;
        const ctx = canvas.getContext('2d');

        // Draw the texture atlas
        ctx.drawImage(this.textureAtlases[prefix].image, 0, 0);

        // Draw UV mapping regions (atlas regions per spawnType)
        ctx.lineWidth = 2;
        const colors = ['#FF0000', '#00FF00', '#0000FF', '#FFFF00', '#FF00FF', '#00FFFF'];
        let index = 0;
        this.uvMappings.forEach((uv, spawnType) => {
            const [uMin, vMin, uMax, vMax] = uv;
            const x = uMin * canvas.width;
            const y = vMin * canvas.height;
            const width = (uMax - uMin) * canvas.width;
            const height = (vMax - vMin) * canvas.height;

            // Draw rectangle for atlas region
            ctx.strokeStyle = colors[index % colors.length];
            ctx.strokeRect(x, y, width, height);

            // Draw label
            ctx.fillStyle = colors[index % colors.length];
            ctx.font = '16px Arial';
            ctx.fillText(spawnType, x + 5, y + 20);

            index++;
        });
        index = 0;
        for (const [type] of Object.entries(config)) {

            const model = this.getModel(prefix, type);
            if (!model) continue;

            model.traverse((child) => {

                if (child.isMesh && child.geometry && child.geometry.attributes.uv && child.material.map) {
                    const geometry = child.geometry;
                    const uvAttribute = geometry.attributes.uv;
                    const indexAttribute = geometry.index;
                    const textureWidth = canvas.width;
                    const textureHeight = canvas.height;
                    // Draw UVs as a wireframe (using remapped UVs from createModel)
                    ctx.fillStyle = colors[(index) % colors.length];
                    ctx.strokeStyle = colors[(index) % colors.length];
                    ctx.beginPath();
                    if (indexAttribute) {
                        // Draw lines for each triangle
                        for (let i = 0; i < indexAttribute.count; i += 3) {
                            const a = indexAttribute.getX(i);
                            const b = indexAttribute.getX(i + 1);
                            const c = indexAttribute.getX(i + 2);

                            const uvA = [uvAttribute.getX(a), uvAttribute.getY(a)];
                            const uvB = [uvAttribute.getX(b), uvAttribute.getY(b)];
                            const uvC = [uvAttribute.getX(c), uvAttribute.getY(c)];

                            // Scale UVs to canvas coordinates
                            const xA = uvA[0] * textureWidth;
                            const yA = uvA[1] * textureHeight;
                            const xB = uvB[0] * textureWidth;
                            const yB = uvB[1] * textureHeight;
                            const xC = uvC[0] * textureWidth;
                            const yC = uvC[1] * textureHeight;

                            // Draw triangle edges
                           ctx.moveTo(xA, yA);
                           ctx.lineTo(xB, yB);
                           ctx.lineTo(xC, yC);
                           ctx.lineTo(xA, yA);
                        }
                    } 
                    ctx.stroke();
            index++;
                }
            });

        }
        // Style the canvas for visibility
        canvas.style.border = '1px solid black';
        canvas.style.margin = '10px';
        canvas.title = `Texture Atlas with UV Mappings and Geometry UVs (Prefix: ${prefix})`;

        // Append to document body
        document.body.appendChild(canvas);
    }
    async generateTextureAtlas(objectType, textures, textureInfo) {
        const textureSizes = textures.map((texture) => {
            const img = texture.image;
            return { width: img.width, height: img.height };
        });

        const gridSize = Math.ceil(Math.sqrt(textures.length));
        let maxWidth = 0;
        let maxHeight = 0;
        const gridPositions = [];

        textureSizes.forEach((size, i) => {
            const row = Math.floor(i / gridSize);
            const col = i % gridSize;
            const x = col * Math.max(...textureSizes.map(s => s.width));
            const y = row * Math.max(...textureSizes.map(s => s.height));
            gridPositions.push({ x, y });
            maxWidth = Math.max(maxWidth, x + size.width);
            maxHeight = Math.max(maxHeight, y + size.height);
        });

        let atlasWidth = Math.pow(2, Math.ceil(Math.log2(maxWidth)));
        let atlasHeight = Math.pow(2, Math.ceil(Math.log2(maxHeight)));

        const maxTextureSize = 4096;
        let scale = 1;
        if (atlasWidth > maxTextureSize || atlasHeight > maxTextureSize) {
            console.warn('Atlas size exceeds GPU limit. Scaling down textures.');
            scale = Math.min(maxTextureSize / atlasWidth, maxTextureSize / atlasHeight);
            maxWidth = Math.floor(maxWidth * scale);
            maxHeight = Math.floor(maxHeight * scale);
            atlasWidth = Math.pow(2, Math.ceil(Math.log2(maxWidth)));
            atlasHeight = Math.pow(2, Math.ceil(Math.log2(maxHeight)));
            textureSizes.forEach(size => {
                size.width = Math.floor(size.width * scale);
                size.height = Math.floor(size.height * scale);
            });
            gridPositions.forEach(pos => {
                pos.x = Math.floor(pos.x * scale);
                pos.y = Math.floor(pos.y * scale);
            });
        }

        const canvas = document.createElement('canvas');
        canvas.width = atlasWidth;
        canvas.height = atlasHeight;
        const ctx = canvas.getContext('2d');

        this.uvMappings.clear();
        textures.forEach((texture, i) => {
            const img = texture.image;
            const size = textureSizes[i];
            const pos = gridPositions[i];
            ctx.drawImage(img, pos.x, pos.y, size.width, size.height);
            this.uvMappings.set(`${textureInfo[i].spawnType}_${textureInfo[i].meshIndex}`, [
                pos.x / atlasWidth,
                pos.y / atlasHeight,
                (pos.x + size.width) / atlasWidth,
                (pos.y + size.height) / atlasHeight
            ]);
        });
        this.textureAtlases[objectType] = new THREE.CanvasTexture(canvas);
        this.textureAtlases[objectType].flipY = false;
        this.textureAtlases[objectType].colorSpace = THREE.SRGBColorSpace;
        this.textureAtlases[objectType].needsUpdate = true;
    }

    async createModel(objectType, spawnType, modelData, useAtlas = true) {
        const modelGroup = await this.createObjectsFromJSON(modelData, {}, objectType, spawnType);
        if (modelGroup) {
            let meshIndex = 0;
            modelGroup.traverse((child) => {
                if (child.isMesh) {
                    child.castShadow = true;
                    child.receiveShadow = true;
                    const geometry = child.geometry;
                    if (useAtlas && this.textureAtlases[objectType] && this.uvMappings.has(spawnType) && !geometry.userData.uvsRemapped) {
                        geometry.userData.uvsRemapped = true;
                        // Apply texture atlas material
                        child.material = new THREE.MeshStandardMaterial({
                            map: this.textureAtlases[objectType],
                            metalness: child.material.metalness || 0.5,
                            roughness: child.material.roughness || 0.5
                        });
                        child.material.needsUpdate = true;
                        // Remap UVs
                        const uvMapping = this.uvMappings.get(`${spawnType}_${meshIndex}`);
                        meshIndex++;
                        const [uMin, vMin, uMax, vMax] = uvMapping;
                        const uvAttribute = geometry.attributes.uv;
                        if (uvAttribute) {                   
                            for (let i = 0; i < uvAttribute.count; i++) {

                                let u = uvAttribute.getX(i);
                                let v = uvAttribute.getY(i);
                                // Map UVs to atlas region
                                const uNew = uMin + u * (uMax - uMin);
                                // Flip v to match GLTF (bottom-left) if atlas is top-left
                                const vNew = vMin + v * (vMax - vMin); // Flip vertically                                 
                                uvAttribute.setXY(i,uNew, vNew);
                            }
                            uvAttribute.needsUpdate = true;
                        }
                    }
                }
            });
        }
        return modelGroup;
    }

    getModel(prefix, type) {
        return this.models[`${prefix}_${type}`];
    }

    getAnimation(prefix, type, anim) {
        return this.models[`${prefix}_${type}_${anim}`];
    }

    async createObjectsFromJSON(model, frameData, objectType, spawnType) {
        const rootGroup = new THREE.Group();
        for (const groupName in model) {
            const group = await this.shapeFactory.createMergedGroupFromJSON(model, frameData, groupName, objectType, spawnType);
            if (group) {
                rootGroup.add(group);
            }
        }
        return rootGroup;
    }
}

EnergyShield.script:
class EnergyShield extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
 init(){
        // Get stats component for reference
        let statsComp = this.parent.getComponent('stats');
        
        // Shield configuration with defaults
        this.maxEnergyShield = statsComp.stats.energyShield || 0;
        this.energyShield = this.maxEnergyShield || 0;
        this.rechargeRate = statsComp.stats.rechargeRate || 10; // Shield points per second
        this.rechargeDelay = statsComp.stats.rechargeDelay || 2000; // Milliseconds before recharge starts
        this.lastDamageTime = 0; // Timestamp of last damage taken
        
        // Add relevant stats to the stats component
        statsComp.addStat('maxEnergyShield', this.maxEnergyShield);
        statsComp.addStat('shieldRechargeRate', this.rechargeRate);
        statsComp.addStat('shieldRechargeDelay', this.rechargeDelay);
        
        // Visual settings
        this.shieldColor = 'rgba(77, 166, 255, 0.6)'; // Light blue default
        this.shieldEmptyColor = 'rgba(77, 166, 255, 0.2)';
        this.barWidth = 30;
        this.barHeight = 5;
        this.barOffset = 8; // Distance above health bar
    }
    
    /**
     * Handle incoming damage
     * @param {number} damage - Amount of incoming damage
     * @returns {Object} - Damage breakdown
     */
    absorbDamage(damage) {
        const result = {
            absorbedByShield: 0,
            remainingDamage: 0
        };
        
        // Reset recharge timer when taking damage
        this.lastDamageTime = this.game.currentTime;
        
        if (this.energyShield > 0) {
            if (damage <= this.energyShield) {
                // Shield absorbs all damage
                result.absorbedByShield = damage;
                this.energyShield -= damage;
            } else {
                // Shield is depleted, remaining damage goes through
                result.absorbedByShield = this.energyShield;
                result.remainingDamage = damage - this.energyShield;
                this.energyShield = 0;
            }
        } else {
            // No shield available
            result.remainingDamage = damage;
        }
        
        return result;
    }
    
    /**
     * Update shield state (recharging)
     */
    update() {
        let statsComp = this.parent.getComponent('stats');
        this.maxEnergyShield = statsComp.stats.energyShield || 0;
        // Check if enough time has passed since last damage
        const timeSinceLastDamage = this.game.currentTime - this.lastDamageTime;
        
        if (timeSinceLastDamage >= this.rechargeDelay && this.energyShield < this.maxEnergyShield) {
            // Calculate recharge amount based on time elapsed and rate
            const deltaTime = this.game.deltaTime || 16; // Fallback to 60fps if deltaTime not available
            const rechargeAmount = (this.rechargeRate * deltaTime) / 1000; // Convert to per-frame amount
            
            // Apply recharge
            this.energyShield = Math.min(this.maxEnergyShield, this.energyShield + rechargeAmount);
        }
    }
    
    /**
     * Draw shield bar
     */
    draw() {
        if (this.maxEnergyShield <= 0) return; // Don't draw if no shield capacity
        
        const shieldPercentage = this.energyShield / this.maxEnergyShield;        
        
        // Shield bar will be slightly above that
        const barY = this.parent.drawPosition.y - this.game.getCollections().configs.game.imageSize * .3 - this.barOffset;
        
        // Draw shield background/empty bar
        this.game.ctx.fillStyle = this.shieldEmptyColor;
        this.game.ctx.fillRect(this.parent.drawPosition.x - this.barWidth/2, barY, this.barWidth, this.barHeight);
        
        // Draw current shield level
        if (shieldPercentage > 0) {
            this.game.ctx.fillStyle = this.shieldColor;
            this.game.ctx.fillRect(
                this.parent.drawPosition.x - this.barWidth/2, 
                barY, 
                this.barWidth * shieldPercentage, 
                this.barHeight
            );
        }
        
        // Draw recharge indicator if currently recharging
        const timeSinceLastDamage = this.game.currentTime - this.lastDamageTime;
        if (timeSinceLastDamage < this.rechargeDelay && this.energyShield < this.maxEnergyShield) {
            const rechargePercentage = timeSinceLastDamage / this.rechargeDelay;
            
            // Draw a small indicator below the shield bar
            this.game.ctx.fillStyle = 'rgba(255, 255, 255, 0.5)';
            this.game.ctx.fillRect(
                this.parent.drawPosition.x - this.barWidth/2, 
                barY + this.barHeight + 1, 
                this.barWidth * rechargePercentage, 
                2
            );
        }
    }
    
    /**
     * Get current shield value
     * @returns {number} Current shield amount
     */
    getShieldValue() {
        return this.energyShield;
    }
    
    /**
     * Reset shield to full
     */
    resetShield() {
        this.energyShield = this.maxEnergyShield;
    }
    
    /**
     * Temporarily boost shield capacity
     * @param {number} amount - Amount to boost by
     * @param {number} duration - Duration in milliseconds
     */
    boostShield(amount, duration) {
        const originalMax = this.maxEnergyShield;
        this.maxEnergyShield += amount;
        this.energyShield += amount;
        
        // Reset after duration
        setTimeout(() => {
            this.maxEnergyShield = originalMax;
            this.energyShield = Math.min(this.energyShield, this.maxEnergyShield);
        }, duration);
    }
}

Explosion.script:
class Explosion extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
   init( {radius}) {
        this.maxRadius = radius * this.game.getCollections().configs.game.gridSize / 2;
        this.currentRadius = 0;
        this.alpha = 1;
    }

    update() {
        this.currentRadius += this.maxRadius * 0.1;
        this.alpha -= 0.05;
        if( this.alpha < 0 ) {
            this.parent.destroy();
        }
    }

    draw() {
        const pixelX = this.parent.transform.position.x;
        const pixelY = this.parent.transform.position.y;

        // Convert pixel to isometric
        const isoPos = this.game.translator.pixelToIso(pixelX, pixelY);
        const isoRadiusX = this.currentRadius;         // X-axis range remains roughly the same in isometric space
        const isoRadiusY = this.currentRadius * 0.5;   // Y-axis range is halved due to isometric compression

        this.game.ctx.beginPath();
        this.game.ctx.ellipse(
                isoPos.x,           // Center x in isometric coords
                isoPos.y,           // Center y in isometric coords
                isoRadiusX,          // X radius (wider due to isometric projection)
                isoRadiusY,          // Y radius (shorter due to flattening)
                0,                  // Rotation (none needed for isometric)
                0,                  // Start angle
                2 * Math.PI         // End angle (full circle)
            );
        this.game.ctx.fillStyle = `rgba(255, 200, 0, ${this.alpha * 0.5})`;
        this.game.ctx.fill();

        this.game.ctx.beginPath();
        this.game.ctx.ellipse(
                isoPos.x,           // Center x in isometric coords
                isoPos.y,           // Center y in isometric coords
                isoRadiusX * .7,          // X radius (wider due to isometric projection)
                isoRadiusY * .7,          // Y radius (shorter due to flattening)
                0,                  // Rotation (none needed for isometric)
                0,                  // Start angle
                2 * Math.PI         // End angle (full circle)
            );
        this.game.ctx.fillStyle = `rgba(255, 100, 0, ${this.alpha})`;
        this.game.ctx.fill();
    
    }
}

Health.script:
class Health extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
  init(){
        let statsComp = this.parent.getComponent('stats');
        this.hp = statsComp.stats.hp;
        
        statsComp.addStat('maxHp', this.hp);
    }
    update() {        
        if (this.hp <= 0){            
            this.parent.destroy();
        }
    }

    draw() {
        let imageSize = this.game.getCollections().configs.game.imageSize;
        let statsComp = this.parent.getComponent('stats');
        const healthPercentage = this.hp / statsComp.stats.maxHp;
        const barWidth = 30;
        this.game.ctx.fillStyle = 'rgba(0, 0, 0, 0.2)';

        const isoPos = this.game.translator.pixelToIso(this.parent.transform.position.x, this.parent.transform.position.y)

        this.game.ctx.fillRect(isoPos.x - barWidth/2, isoPos.y - imageSize * .3, barWidth, 5);
        if( healthPercentage >= 0 ) {
            this.game.ctx.fillStyle = healthPercentage > 0.5 ? 'rgba(0, 255, 0, 0.5)' : healthPercentage > 0.25 ? 'rgba(255, 255, 0, 0.5)' : 'rgba(255, 0, 0, 0.5)';
            this.game.ctx.fillRect(isoPos.x - barWidth/2, isoPos.y - imageSize * .3, barWidth * healthPercentage, 5);
        }
    }
}

HitEffectRenderer.script:
class HitEffectRenderer extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
 init( ) {
        this.ctx = this.game.ctx;
    }

    draw() {
        const particleComp = this.parent.getComponent("HitEffectParticle");
        if (!particleComp || !particleComp.particles.length) return;

        const basePos = this.game.translator.pixelToIso(this.parent.transform.position.x, this.parent.transform.position.y);

        for (let particle of particleComp.particles) {
            this.ctx.fillStyle = `${particle.color}${particle.alpha})`;
            this.ctx.beginPath();
            const drawX = basePos.x + particle.x;
            const drawY = basePos.y + particle.y;
            this.ctx.arc(drawX, drawY, particle.size, 0, Math.PI * 2);
            this.ctx.fill();
        }
    }
}

LightningRenderer.script:
class LightningRenderer extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
  init( {owner}) {
        this.ctx = this.game.ctx;
        const ownerStats = owner.getComponent("stats").stats;
        this.startOffsetY = ownerStats.projectileStartOffsetY || this.game.getCollections().configs.game.gridSize / 2;
    }

    draw() {
        const projectile = this.parent.getComponent("ChainProjectile");
        if (!projectile || !projectile.chainTargets.length) return;

        this.ctx.strokeStyle = "rgba(0, 255, 255, 0.8)"; // Cyan lightning
        this.ctx.lineWidth = 2;
        let stats = this.parent.getComponent('stats').stats;
        // Start from Tesla Coil (parent position)
        let startPos = this.game.translator.pixelToIso(this.parent.transform.position.x, this.parent.transform.position.y);
        startPos.y -= this.startOffsetY;//dont shoot off the ground
        for (let i = 0; i < projectile.chainTargets.length; i++) {
            const target = projectile.chainTargets[i];
            if (!target || target.destroyed) continue;

            const endPos = this.game.translator.pixelToIso(target.transform.position.x, target.transform.position.y);
            this.drawLightning(startPos, endPos);

            // Next arc starts from this target
            startPos = endPos;
        }
    }

    drawLightning(startPos, endPos) {
        this.ctx.beginPath();            
        this.ctx.moveTo(startPos.x, startPos.y);

        const segments = 8; // Number of zigzag points
        const dx = (endPos.x - startPos.x) / segments;
        const dy = (endPos.y - startPos.y) / segments;

        let currentX = startPos.x;
        let currentY = startPos.y;

        for (let i = 1; i < segments; i++) {
            currentX += dx;
            currentY += dy;

            // Add random offset for jagged effect
            const offsetX = (Math.random() - 0.5) * 10; // Max 10px jitter
            const offsetY = (Math.random() - 0.5) * 10;

            this.ctx.lineTo(currentX + offsetX, currentY + offsetY);
        }

        this.ctx.lineTo(endPos.x, endPos.y);
        this.ctx.stroke();
    }
}

MapRenderer.script:
class MapRenderer extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
    init({canvasBuffer, terrainCanvasBuffer, worldObjects, imageManager, levelName, gameConfig, level, isEditor, palette}) {   

        this.config = gameConfig;
        this.imageManager = imageManager;
        this.worldObjects = worldObjects;
        this.selectedTowerType = null;
        this.hoverCell = { x: -1, y: -1 };
        this.showRange = false;
        this.isMapCached = false; // Flag to track if map needs redrawing
        this.currentLevel = levelName;
        this.level = level;
        this.tileMap = level.tileMap;
        this.palette = palette;

        // Create off-screen canvas for caching
        if(!this.config.is3D || isEditor) {
            this.ctx = canvasBuffer.getContext('2d');
            this.mapCacheCanvas = document.createElement('canvas');
            this.mapCacheCanvas.width = this.config.canvasWidth;
            this.mapCacheCanvas.height = this.config.canvasHeight;
            this.mapCacheCtx = this.mapCacheCanvas.getContext('2d');

            
            this.envCacheCanvasBG = document.createElement('canvas');
            this.envCacheCanvasBG.width = this.config.canvasWidth;
            this.envCacheCanvasBG.height = this.config.canvasHeight / 2;
            this.envCacheCtxBG = this.envCacheCanvasBG.getContext('2d');

            this.envCacheCanvasFG = document.createElement('canvas');
            this.envCacheCanvasFG.width = this.config.canvasWidth;
            this.envCacheCanvasFG.height = this.config.canvasHeight / 2;
            this.envCacheCtxFG = this.envCacheCanvasFG.getContext('2d');
        }
        this.terrainCanvas = terrainCanvasBuffer;
        this.terrainCanvas.width = this.tileMap.size * this.config.gridSize;
        this.terrainCanvas.height = this.tileMap.size * this.config.gridSize;
        this.terrainCtx = this.terrainCanvas.getContext('2d');
        
        this.terrainTileMapper = this.game.terrainTileMapper;
        this.game.mapRenderer = this;
        this.isometric = this.game.getCollections().configs.game.isIsometric;
    }

    draw(){}
    render(tileMapData) {
        if(!this.isMapCached){
            this.terrainCtx.clearRect(0, 0, this.terrainCanvas.width, this.terrainCanvas.height);
                
            // Draw the map onto the cache canvas
            this.drawTileMap(tileMapData);
            this.renderEnvironment();
            
            //this.drawPaths(this.terrainCacheCtx, paths);
        }
        // Mark cache as valid
        this.isMapCached = true;
    }
    renderEnvironment() {
        if (this.tileMap.environmentObjects.length === 0) return;
    
        // Group environment objects by type
        const objectsByType = {};
        this.tileMap.environmentObjects.forEach(obj => {
            if (!objectsByType[obj.type]) {
                objectsByType[obj.type] = [];
            }
            objectsByType[obj.type].push(obj);
        });
    
        // Process each type separately
        Object.entries(objectsByType).forEach(([type, objects]) => {
            // Load the model for this type
            const referenceModel = this.game.modelManager.getModel("worldObjects", type);
            if (!referenceModel) return;
    
            // Find all meshes and store their complete transform data
            const meshData = [];
            referenceModel.updateMatrixWorld(true); // Ensure world matrices are up to date
    
            referenceModel.traverse(node => {
                if (node.isMesh) {
                    const parent = node.parent;
                    const parentWorldMatrix = parent.matrixWorld.clone();
                    const localMatrix = node.matrix.clone();
    
                    // Get transform relative to model root
                    const relativeMatrix = new THREE.Matrix4();
                    relativeMatrix.copy(parentWorldMatrix);
                    relativeMatrix.multiply(localMatrix);
    
                    meshData.push({
                        mesh: node,
                        relativeMatrix: relativeMatrix
                    });
                }
            });
            if (meshData.length === 0) return;
    
            // Create instanced meshes for this type
            const instancedMeshes = meshData.map(({ mesh, relativeMatrix }) => {
                const instancedMesh = new THREE.InstancedMesh(
                    mesh.geometry,
                    mesh.material,
                    objects.length // Number of instances for this type
                );
                instancedMesh.userData.relativeMatrix = relativeMatrix;
                instancedMesh.castShadow = true;
                instancedMesh.receiveShadow = true;
                return instancedMesh;
            });
    
            // Set positions for all instances of this type
            const matrix = new THREE.Matrix4();
            const dummy = new THREE.Object3D();
    
            objects.forEach((obj, index) => {
                // Get pixel color at object position
                let hexColor = this.tileMap.terrainTypes[this.tileMap.extensionTerrainType].color.slice(1);
                let numColor = parseInt(hexColor, 16); // Convert hex string to number, e.g., 0xaa3311

                let r = (numColor >> 16) & 0xff; // Red channel
                let g = (numColor >> 8) & 0xff;  // Green channel   
                let b = numColor & 0xff;         // Blue channel
                if (g > r && g > b) {
                    dummy.position.set(
                        obj.x, 
                        this.game.getCollections().heightMaps[this.game.getCollections().worlds[this.level.world].heightMap].heightStep * this.tileMap.extensionTerrainType, 
                        obj.y
                    );
                    dummy.rotation.y = Math.random() * Math.PI * 2;
                    const scale = 0.8 + Math.random() * 0.4;
                    dummy.scale.set(scale, scale, scale);
                    dummy.updateMatrix();
            
                    // Apply transforms while preserving mesh relationships
                    instancedMeshes.forEach(instancedMesh => {
                        matrix.copy(dummy.matrix);
                        matrix.multiply(instancedMesh.userData.relativeMatrix);
                        instancedMesh.setMatrixAt(index, matrix);
                    });
                }
            });
    
            // Add instanced meshes to the scene
            instancedMeshes.forEach(instancedMesh => {
                instancedMesh.instanceMatrix.needsUpdate = true;
                this.game.scene.add(instancedMesh);
            });
        });
    }
    renderBG(tileMapData, paths) {
        this.clearMap(tileMapData);
        // Generate cache if not already done
        if (!this.isMapCached) {
            this.cacheMap(tileMapData, paths, this.isometric);            
        }        
  
        // Draw cached map image to main canvas
      
       this.ctx.drawImage( this.terrainCanvas, ( this.config.canvasWidth - this.terrainCanvas.width) / 2, ( this.config.canvasHeight - this.terrainCanvas.height) / 2 );

       //this.ctx.drawImage(this.terrainCanvas, 0, 0);
        //this.ctx.drawImage(this.mapCacheCanvas, 0,  -this.config.canvasHeight / 2);
        this.ctx.drawImage(this.envCacheCanvasBG, 0, 0);
    }
    renderFG() {  
        this.ctx.drawImage(this.envCacheCanvasFG, 0, this.config.canvasHeight / 2);
    }    
    setLevel(level) {
        this.currentLevel = level;
        this.terrainImages = this.imageManager.getImages("levels", level);

    }

    drawTileMap(tileMapData) {
        this.terrainTileMapper.draw(tileMapData.terrainMap);
    }
    clearMap(tileMapData) {
        this.ctx.clearRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
        this.ctx.fillStyle = tileMapData.terrainTypes[tileMapData.extensionTerrainType].color;       
        this.ctx.fillRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
    }
    // Call this when map data changes or on initialization
    cacheMap(tileMapData, paths, isometric) {
        this.terrainCtx.clearRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
                   
        // Clear the cache canvas
        this.mapCacheCtx.clearRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
        
        // Draw the map onto the cache canvas
        this.drawTileMap(tileMapData, isometric);
        this.drawPaths(this.mapCacheCtx, paths);
        this.drawEnvironment(tileMapData.terrainMap.length, tileMapData.terrainMap.length * tileMapData.terrainMap.length);
        
        // Mark cache as valid
        this.isMapCached = true;
    }

    drawMap(tileMap) {

        this.mapCacheCtx.fillStyle = '#4a7c59';
        this.mapCacheCtx.fillRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
        const tileWidth = this.config.gridSize;
        const tileHeight = this.config.gridSize * 0.5;
        
        for (let y = 0; y < tileMap.length; y++) {
            for (let x = 0; x < tileMap[y].length; x++) {
                const tile = tileMap[y][x];
                
                // Use translator to get iso coordinates
                const isoCoords = this.game.translator.gridToIso(x, y);
                const isoX = isoCoords.x;
                const isoY = isoCoords.y;
                
                this.mapCacheCtx.fillStyle = tile.color;
                this.mapCacheCtx.beginPath();
                this.mapCacheCtx.moveTo(isoX, isoY);
                this.mapCacheCtx.lineTo(isoX + tileWidth / 2, isoY + tileHeight / 2);
                this.mapCacheCtx.lineTo(isoX, isoY + tileHeight);
                this.mapCacheCtx.lineTo(isoX - tileWidth / 2, isoY + tileHeight / 2);
                this.mapCacheCtx.closePath();
                this.mapCacheCtx.fill();
                this.mapCacheCtx.strokeStyle = 'rgba(0, 0, 0, 0.1)';
                this.mapCacheCtx.stroke();
            }
        }
        
       
        this.drawEnvironment(tileMap.length);
    }

    drawPaths(ctx, paths) {
        const tileHeight = this.config.gridSize * 0.5;
        ctx.strokeStyle = '#ffd166';
        ctx.lineWidth = 2;
        ctx.beginPath();
        
        paths.forEach(path => {
            // First point in path
            const firstIsoCoords = this.game.translator.gridToIso(path[0].x, path[0].y);
            const firstIsoX = firstIsoCoords.x;
            const firstIsoY = firstIsoCoords.y + tileHeight / 2; // Add half tile height for center of tile
            
            ctx.moveTo(firstIsoX, firstIsoY);
            
            // Remaining points in path
            path.forEach(location => {
                const isoCoords = this.game.translator.gridToIso(location.x, location.y);
                const isoX = isoCoords.x;
                const isoY = isoCoords.y + tileHeight / 2; // Add half tile height for center of tile
                
                ctx.lineTo(isoX, isoY);
            });
        });
        
        ctx.stroke();
    }

    drawEnvironment(boardSize, amountToDraw) {

        let itemAmt = amountToDraw;
        let environmentTypes = [];
        for(let envType in this.worldObjects){
            environmentTypes.push(envType);
        }
        this.envCacheCtxBG.clearRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
        this.envCacheCtxFG.clearRect(0, 0, this.config.canvasWidth, this.config.canvasHeight);
        
        let items = [];
        
        // Check if we have stored environment objects
        if (this.tileMap.environmentObjects && this.tileMap.environmentObjects.length > 0) {
            // Use the stored objects
            this.tileMap.environmentObjects.forEach(obj => {
                const images = this.imageManager.getImages("environment", obj.type);
                if (images && images.idle && images.idle[0] && images.idle[0][obj.imageIndex]) {
                    items.push({
                        img: images.idle[0][obj.imageIndex],
                        x: obj.x,
                        y: obj.y
                    });
                }
            });
        } else {       
            // for(let i = 0; i < itemAmt; i++) {
            //     // Define the game board boundaries
            //     const boardMinX = 0;
            //     const boardMaxX = boardSize * this.config.gridSize;
            //     const boardMinY = 0;
            //     const boardMaxY = boardSize * this.config.gridSize;
                
            //     // Generate a random position that's outside the board but within a reasonable distance
            //     let x, y;
                
            //     // Expand the area where we can place objects
            //     const expandAmount = boardSize * this.config.gridSize; // Adjust this value as needed
                
            //     // Randomly choose whether to place on x-axis or y-axis outside the board
            //     if (Math.random() < 0.5) {
            //         // Place on the left or right of the board
            //         x = Math.random() < 0.5 ?
            //             boardMinX - Math.random() * expandAmount : // Left side
            //             boardMaxX + Math.random() * expandAmount;  // Right side
                    
            //         // Allow y to be anywhere, including outside the board
            //         y = boardMinY - expandAmount + Math.random() * (boardMaxY - boardMinY + 2 * expandAmount);
            //     } else {
            //         // Place on the top or bottom of the board
            //         y = Math.random() < 0.5 ?
            //             boardMinY - Math.random() * expandAmount : // Top side
            //             boardMaxY + Math.random() * expandAmount;  // Bottom side
                    
            //         // Allow x to be anywhere, including outside the board
            //         x = boardMinX - expandAmount + Math.random() * (boardMaxX - boardMinX + 2 * expandAmount);
            //     }
                
            //     // Double-check that the position is actually outside the board
            //     if (x < boardMinX || x > boardMaxX || y < boardMinY || y > boardMaxY) {
            //         const type = environmentTypes[Math.floor(Math.random() * environmentTypes.length)];
            //         const images = this.imageManager.getImages("environment", type);
            //         if(images){
            //             items.push( { img: images.idle[0][parseInt(Math.random()*images.idle[0].length)], x: x, y: y});
            //         }
            //     } else {
            //         i--; // Position inside board, try again
            //     }
            // }
        }

        items.sort((a, b) => {
            return (a.y * boardSize + a.x) - (b.y * boardSize + b.x)
        });

        items.forEach((item) => {            
            // Convert pixel to isometric
            const isoPos = this.game.translator.pixelToIso(item.x, item.y);
            const image = item.img;
            const imgWidth = image.width;
            const imgHeight = image.height;
            
            const drawX = isoPos.x;
            const drawY = isoPos.y;
            if( drawY < this.config.canvasHeight / 2 ) {
                this.envCacheCtxBG.drawImage(image, drawX - imgWidth / 2, drawY - imgHeight / 2);
            } else if(drawY - this.config.canvasHeight / 2 - imgHeight / 2 > 0) {//prevent trees in FG from getting chopped off at the top if they are too close to the middle of the frame.
  
                this.envCacheCtxFG.drawImage(image, drawX - imgWidth / 2, drawY - this.config.canvasHeight / 2 - imgHeight / 2);
            }
        });
    }

}

ModelRenderer.script:
class ModelRenderer extends engine.Component {
    init({ objectType, spawnType, frameDuration }) {
        if(!this.game.getCollections().configs.game.is3D) {
            return;
        }
        this.animationState = 'idle';
        this.currentFrameIndex = 0;
        this.frameTime = 0;
        this.frameDuration = frameDuration || 0.17;
        this.lastDirection = -1;

        // Load animation and model data
        this.animationData = this.game.getCollections()[objectType]?.[spawnType]?.render?.animations;
        this.modelData = this.game.getCollections()[objectType]?.[spawnType]?.render?.model;

        // Get the model
        this.model = this.game.modelManager.getModel(objectType, spawnType);

        if (!this.model || !this.animationData || !this.modelData) {
            console.error(`No model or data found for ${objectType}_${spawnType}`);
            return;
        }

        // Create the initial model instance
        this.modelGroup = this.model.clone();  
        // Add the model group to the scene
        this.game.scene.add(this.modelGroup);

        this.modelGroup.position.set(0, -10000, 0);

        // Set initial animation
        this.setAnimation('idle');
    }

    setAnimation(animationName) {
        if (!this.animationData[animationName]) {
            console.warn(`Animation '${animationName}' not found, defaulting to 'idle'`);
            animationName = 'idle';

            if (!this.animationData[animationName]) {
                const availableAnims = Object.keys(this.animationData);
                animationName = availableAnims.length > 0 ? availableAnims[0] : null;

                if (!animationName) {
                    console.error('No animations available for this model');
                    return;
                }
            }
        }

        if (this.animationState !== animationName) {
            this.animationState = animationName;
            this.currentFrameIndex = 0;
            this.frameTime = 0;
            this.updateModelFrame();
        }
    }

    draw() {
        if(!this.game.getCollections().configs.game.is3D) {
            return;
        }
        // Update animation frames
        this.frameTime += this.game.deltaTime;
        if (this.frameTime >= this.frameDuration) {
            this.frameTime -= this.frameDuration;
            this.advanceFrame();
        }
        // Update position of model to match entity position
        if (this.parent && this.parent.transform.position) {
            this.modelGroup.position.set(
                this.parent.transform.position.x,
                this.parent.transform.position.z,
                this.parent.transform.position.y
            );

            // Handle rotation based on movement direction
            this.updateDirection();
        }


    }

    updateDirection() {
        // Calculate direction based on movement
        if (this.parent && this.parent.transform.lastPosition) {
            const dx = this.parent.transform.position.x - this.parent.transform.lastPosition.x;
            const dy = this.parent.transform.position.y - this.parent.transform.lastPosition.y;

            // Only update direction if there's significant movement
            if (Math.abs(dx) > 0.001 || Math.abs(dy) > 0.001) {
                // Calculate angle in radians
                let angle = Math.atan2(dy, dx);

                // Rotate model to face movement direction
                this.modelGroup.rotation.y = -angle + Math.PI / 2;

                // Set walking animation when moving
                if (this.animationData['walk']) {
                    this.setAnimation('walk');
                    return;
                }
            } else {
                // Entity is stationary, use idle animation
                this.setAnimation('idle');
            }
        }
    }

    advanceFrame() {
        const frames = this.animationData[this.animationState];
        if (!frames || frames.length === 0) return;

        this.currentFrameIndex = (this.currentFrameIndex + 1) % frames.length;
        this.updateModelFrame();
    }

    updateModelFrame() {
        // Get current animation and frame
        const frames = this.animationData[this.animationState];
        if (!frames?.length) return;

        const frameData = frames[this.currentFrameIndex] || {};

        // Traverse the modelGroup to apply transformations
        this.modelGroup.traverse((obj) => {
          // Handle group-level transformations (apply to group objects)
            if (!obj.isMesh && obj.name && this.modelData[obj.name]) {
                const groupName = obj.name;
                const groupData = frameData[groupName];
                const modelGroupData = this.modelData[groupName];
                this.updateObjectTransforms(obj, groupData, modelGroupData);
            }

            // Handle shape-level transformations (apply to meshes)
            if (obj.isMesh && obj.userData?.index >= 0 && obj.parent?.name) {
                const groupName = obj.parent.name;
                const index = obj.userData.index;
                const groupData = frameData[groupName];
                const modelGroupData = this.modelData[groupName];
                // Find shape in animationData by id (id: 1 maps to index 0)
                let shape;
                if (groupData?.shapes) {
                    shape = groupData.shapes.find(s => s.id === index);
                }
                const modelShape = modelGroupData?.shapes?.[index];

                if (shape || modelShape) {
                    this.updateShapeTransforms(obj, shape, modelShape);
                }
            }
        });
    }

    updateObjectTransforms(obj, groupData, modelGroupData) {
        if (!modelGroupData) return;

        // Position
        const pos = groupData?.position || modelGroupData.position || { x: 0, y: 0, z: 0 };
        obj.position.set(
            pos.x ?? modelGroupData.position?.x ?? 0,
            pos.y ?? modelGroupData.position?.y ?? 0,
            pos.z ?? modelGroupData.position?.z ?? 0
        );

        // Rotation
        const rot = groupData?.rotation || modelGroupData.rotation || { x: 0, y: 0, z: 0 };
        obj.rotation.set(
            rot.x ?? modelGroupData.rotation?.x ?? 0,
            rot.y ?? modelGroupData.rotation?.y ?? 0,
            rot.z ?? modelGroupData.rotation?.z ?? 0
        );

        // Scale
        const scale = groupData?.scale || modelGroupData.scale || { x: 1, y: 1, z: 1 };
        obj.scale.set(
            scale.x ?? modelGroupData.scale?.x ?? 1,
            scale.y ?? modelGroupData.scale?.y ?? 1,
            scale.z ?? modelGroupData.scale?.z ?? 1
        );
    }
    updateShapeTransforms(obj, shape, modelShape) {
        if (!modelShape) return;

        // Position (local to group)
        obj.position.set(
            shape?.x ?? modelShape.x ?? 0,
            shape?.y ?? modelShape.y ?? 0,
            shape?.z ?? modelShape.z ?? 0
        );

        // Rotation (local to group, convert degrees to radians)
        obj.rotation.set(
            ((shape?.rotationX ?? modelShape.rotationX) || 0) * Math.PI / 180,
            ((shape?.rotationY ?? modelShape.rotationY) || 0) * Math.PI / 180,
            ((shape?.rotationZ ?? modelShape.rotationZ) || 0) * Math.PI / 180
        );

        // Scale (local to group)
        obj.scale.set(
            shape?.scaleX ?? modelShape.scaleX ?? 1,
            shape?.scaleY ?? modelShape.scaleY ?? 1,
            shape?.scaleZ ?? modelShape.scaleZ ?? 1
        );

    }
    destroy() {
        if(this.modelGroup){
            // Remove model from scene
            if (this.modelGroup && this.game.scene) {
                this.game.scene.remove(this.modelGroup);
            }

            // Clear all children
            while (this.modelGroup.children.length > 0) {
                const child = this.modelGroup.children[0];
                this.modelGroup.remove(child);
            }

            this.modelGroup = null;
        }
    }
}

RangeIndicator.script:
class RangeIndicator extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
    init() {
        this.ctx = this.game.ctx;
        this.translator = this.game.translator; // Access translator for tileWidth
    }

    draw() {
        let statsComp = this.getComponent('stats');
        if (!statsComp || !statsComp.stats || !statsComp.stats.range) {
            return;
        }
        this.drawRangeIndicator(statsComp.stats.range);
    }

    drawRangeIndicator(range) {    
        const drawRage = range;        
        const pixelX = this.parent.transform.position.x;
        const pixelY = this.parent.transform.position.y;
        let gridPos = this.translator.pixelToGrid(pixelX, pixelY);
        gridPos = this.translator.snapToGrid(gridPos.x, gridPos.y);
        const isoPos = this.translator.pixelToIso(pixelX, pixelY);
    
        let isoRangeX = drawRage * this.game.getCollections().configs.game.gridSize;  // Matches gridToIso X scaling
        let isoRangeY = drawRage * this.game.getCollections().configs.game.gridSize * (this.game.state.isometric ? 0.5 : 1); // Matches gridToIso Y scaling
    
        if( this.game.state.isometric ) {
            isoRangeX *= .56;
            isoRangeY *= .56;
        }

        if (gridPos.x === this.game.state.mousePosition.gridX && gridPos.y === this.game.state.mousePosition.gridY) {
            this.ctx.save();
            this.ctx.beginPath();
            this.ctx.strokeStyle = 'rgba(255, 0, 0, 0.5)';
            this.ctx.lineWidth = 2;
    
            this.ctx.ellipse(
                isoPos.x,
                isoPos.y,
                isoRangeX,
                isoRangeY,
                0,
                0,
                2 * Math.PI
            );
    
            this.ctx.stroke();
            this.ctx.closePath();
            this.ctx.restore();
        }    
      
    }
}

Renderer.script:
class Renderer extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
  init( { objectType, spawnType, setDirection = -2}) {
        if(this.game.getCollections().configs.game.is3D) {
            return;
        }
        this.images = this.game.imageManager.getImages(objectType, spawnType);  
        this.setDirection = setDirection;
        this.currentDirection = 0; // Default direction (will be 0-7 now)
        // Rotated direction zones 45 degrees counter-clockwise
        // Each zone covers a 45-degree arc (/4 radians)
        this.directionZones = [
            { min: Math.PI/8, max: 3*Math.PI/8 },     // 0: Down-Right (Southeast)
            { min: 3*Math.PI/8, max: 5*Math.PI/8 },   // 1: Down
            { min: 5*Math.PI/8, max: 7*Math.PI/8 },   // 2: Down-Left (Southwest)
            { min: 7*Math.PI/8, max: -7*Math.PI/8 },  // 3: Left (West)
            { min: -7*Math.PI/8, max: -5*Math.PI/8 }, // 4: Up-Left (Northwest)
            { min: -5*Math.PI/8, max: -3*Math.PI/8 }, // 5: Up
            { min: -3*Math.PI/8, max: -Math.PI/8 },   // 6: Up-Right (Northeast)
            { min: -Math.PI/8, max: Math.PI/8 }       // 7: Right (East)
        ];
        this.isometric = this.game.getCollections().configs.game.isIsometric;
    }

    draw() {
        if(this.game.getCollections().configs.game.is3D) {
            return;
        }
        if( this.setDirection < 0 ) {
            const dx = this.parent.transform.position.x - this.parent.transform.lastPosition.x; // Change in x
            const dy = this.parent.transform.position.y - this.parent.transform.lastPosition.y; // Change in y
            
            // Only update direction if there's movement
            if (Math.abs(dx) > 0.001 || Math.abs(dy) > 0.001) {
                // Calculate angle of movement in radians
                const angle = Math.atan2(dy, dx);
                
                // Determine which of the 8 directional zones the angle falls into
                for (let i = 0; i < this.directionZones.length; i++) {
                    const zone = this.directionZones[i];
                    
                    // Special handling for the West zone which wraps around from PI to -PI
                    if (i === 3) {
                        if (angle >= zone.min || angle <= zone.max) {
                            this.currentDirection = i;
                            break;
                        }
                    } 
                    // Normal zone check
                    else if (angle >= zone.min && angle < zone.max) {
                        this.currentDirection = i;
                        break;
                    }
                }
            }
        } else {
            this.currentDirection = this.setDirection;
        }
        let direction = this.currentDirection + (this.isometric ? 0 : -1);
        if(direction == -1 ) direction = this.images.length - 1;//wrap around
        // Draw the image for the current direction
        if (this.images && this.images.length > direction) {
            const image = this.images[direction];
            if (image) {
                const imgWidth = image.width;
                const imgHeight = image.height;
                
                const drawX = this.parent.drawPosition.x - imgWidth / 2;
                let drawY = this.parent.drawPosition.y - imgHeight / 2;
                if( this.parent.transform.position.z ) {
                   drawY -= this.parent.transform.position.z;                   
                }
                this.game.ctx.drawImage(image, drawX, drawY);
            }
        }
    }
}

UiManager.script:
class UiManager extends engine.Component {
    
    constructor(game, parent, params) {
        super(game, parent, params);
    }
    
    
    init({ canvas, canvasBuffer, terrainCanvasBuffer }) {
        this.canvas = canvas;
        this.canvasBuffer = canvasBuffer;
        if(this.game.getCollections().configs.game.is3D) {
            this.finalCtx = this.canvas.getContext("webgl2");
            this.ctx = this.canvasBuffer.getContext("webgl2");
        } else {
            this.finalCtx = this.canvas.getContext("2d");
            this.ctx = this.canvasBuffer.getContext("2d");
        }
        this.terrainCanvasBuffer = terrainCanvasBuffer;
        this.projectConfig = this.game.getCollections().configs.game;
        this.gridSize = this.projectConfig.gridSize;
        this.isometric = this.projectConfig.isIsometric || false;
        this.upgradeMenu = document.getElementById('upgradeMenu');
        this.upgradeOptionsDiv = document.getElementById('upgradeOptions');
        this.overlay = document.getElementById('overlay');
        this.tooltip = document.getElementById('tooltip');
        this.gameOverMenu = document.getElementById('gameOverMenu');
        this.victoryMenu = document.getElementById('victoryMenu');

        // Stats displays
        this.shardsDisplay = document.getElementById('shardsDisplay');
        this.essenceDisplay = document.getElementById('essenceDisplay');
        this.essenceNeededDisplay = document.getElementById('essenceNeededDisplay');
        this.populationDisplay = document.getElementById('populationDisplay');
        this.maxPopulationDisplay = document.getElementById('maxPopulationDisplay');
        this.hpDisplay = document.getElementById('hpDisplay');
        this.waveDisplay = document.getElementById('waveDisplay');
        this.waveProgress = document.getElementById('waveProgress');
        this.gameOverWave = document.getElementById('gameOverWave');
        this.towerMenu = document.getElementById('towerMenu');
        let towerMenuOptions = '';
        for(let type in this.game.getCollections().towers) {
            if(this.game.getCollections().towers[type].cost > 0){
                towerMenuOptions += `<button class="tower-option" data-type="${type}">${this.game.getCollections().towers[type].title} (${this.game.getCollections().towers[type].cost})</button>`;
            }
        }
        this.towerMenu.innerHTML = towerMenuOptions;
        this.setupTowerPlacement();
        this.setupEventListeners();
        this.game.uiManager = this;
       
    }
    clearCanvas() {
        this.ctx.clearRect(0, 0, this.canvasBuffer.width, this.canvasBuffer.height);
        this.finalCtx.clearRect(0, 0, this.canvas.width, this.canvas.height);    
    }
    renderCanvas() {      
        this.finalCtx.drawImage(this.canvasBuffer, 0, 0);
    }

    setMousePosition(clientX, clientY) {
        if(!this.game.getCollections().configs.game.is3D) {
            const rect = this.canvas.getBoundingClientRect();
                
            // Account for canvas scaling and offset
            const scaleX = this.canvas.width / rect.width;   // Ratio of canvas pixel width to CSS width
            const scaleY = this.canvas.height / rect.height; // Ratio of canvas pixel height to CSS height
            
            const mapGridWidth = this.game.state.tileMap.length;
            // Calculate mouse position relative to canvas with scaling
            const mouseX = (clientX - rect.left) * scaleX + (this.isometric ? 0 : -( this.canvas.width - mapGridWidth * this.game.getCollections().configs.game.gridSize) / 2);
            const mouseY = (clientY - rect.top) * scaleY + (this.isometric ? 0 : -( this.canvas.height - mapGridWidth * this.game.getCollections().configs.game.gridSize) / 2);

            // Convert to isometric and grid coordinates
            const gridPos = this.game.translator.isoToGrid(mouseX, mouseY);
            const snappedGrid = this.game.translator.snapToGrid(gridPos.x, gridPos.y);
            const pixelIsoPos = this.game.translator.pixelToIso(mouseX, mouseY);

            // Update state with corrected coordinates
            this.game.state.mousePosition = { 
                x: mouseX, 
                y: mouseY, 
                isoX: pixelIsoPos.x, 
                isoY: pixelIsoPos.y, 
                gridX: snappedGrid.x, 
                gridY: snappedGrid.y 
            };
        } else {
            const rect = this.canvas.getBoundingClientRect();
            
            // Calculate normalized device coordinates (-1 to +1)
            const mouseX = ((clientX - rect.left) / rect.width) * 2 - 1;
            const mouseY = -((clientY - rect.top) / rect.height) * 2 + 1;
            
            // Setup the raycaster
            const raycaster = new THREE.Raycaster();
            const mouse = new THREE.Vector2(mouseX, mouseY);
            
            // Update the picking ray with the camera and mouse position
            raycaster.setFromCamera(mouse, this.game.camera);
            
            // Create a plane at y=0 to represent our terrain
            const groundPlane = new THREE.Plane(new THREE.Vector3(0, 1, 0), 0);
            
            // Calculate the point where the ray intersects the ground plane
            const intersectPoint = new THREE.Vector3();
            raycaster.ray.intersectPlane(groundPlane, intersectPoint);
            
            if (intersectPoint) {
                // Convert to grid coordinates
                const gridX = Math.floor(intersectPoint.x / this.game.getCollections().configs.game.gridSize);
                const gridY = Math.floor(intersectPoint.z / this.game.getCollections().configs.game.gridSize);
                
                // Update state with world and grid coordinates
                this.game.state.mousePosition = {
                    x: intersectPoint.x,                    
                    y: intersectPoint.z,
                    gridX: gridX,
                    gridY: gridY,
                    worldX: intersectPoint.x,
                    worldY: intersectPoint.z
                };
            }

        }
    }

    setupEventListeners() {
        document.getElementById('startGameBtn').removeAttribute('style');
        document.getElementById('startGameBtn').addEventListener('click', (e) => {    
            this.game.state.isPaused = false;
            e.target.setAttribute('style','display:none;');
        });
        document.querySelector("#gameOverMenu .menu-button").addEventListener("click", (e) => {
            this.game.reset();
        });
        
        document.querySelector("#victoryMenu .menu-button").addEventListener("click", (e) => {
            this.game.reset();
        });
        this.canvas.addEventListener('mousemove', (e) => {
            this.setMousePosition(e.clientX, e.clientY);

            if (!this.game.state.selectedTowerType && !this.game.state.towers.length) return;

            if (this.game.state.selectedTowerType && this.game.state.previewTower) {
                this.game.state.previewTower.transform.position.x = this.game.state.mousePosition.gridX * this.gridSize + this.gridSize / 2;
                this.game.state.previewTower.transform.position.y = this.game.state.mousePosition.gridY * this.gridSize + this.gridSize / 2;
                this.game.state.previewTower.transform.position.z = this.game.gameEntity.getComponent('game').getTerrainHeight(this.game.state.previewTower.transform.gridPosition);
                const isValidPosition = this.checkValidTowerPosition(this.game.state.mousePosition.gridX, this.game.state.mousePosition.gridY);
                this.canvas.style.cursor = isValidPosition ? 'pointer' : 'not-allowed';
            }

            let hoveredTower = null;
            for (const tower of this.game.state.towers) {
                const dist = Math.hypot(tower.transform.gridPosition.x - this.game.state.mousePosition.gridX, tower.transform.gridPosition.y + this.game.translator.tileHeight / 2 - this.game.state.mousePosition.gridY);
                if (dist < 20) {
                    hoveredTower = tower;
                    break;
                }
            }

            if (hoveredTower && hoveredTower.stats) {
                let info = `${hoveredTower.type} (Level ${hoveredTower.level})\n`;
                info += `Damage: ${Math.round(hoveredTower.stats.damage * this.game.state.stats.damageMultiplier * 10) / 10}\n`;
                info += `Attack Speed: ${Math.round(1000 / hoveredTower.stats.attackSpeed)} per sec\n`;
                info += `Range: ${hoveredTower.stats.range}\n`;
                info += `Crit Chance: ${Math.round(hoveredTower.stats.critChance * 100)}%\n`;
                if (hoveredTower.stats.leech > 0) {
                    info += `Life Leech: ${Math.round(hoveredTower.stats.leech * 100 * this.game.state.stats.healingMultiplier) / 100} HP per hit\n`;
                }
                if (hoveredTower.stats.piercing > 0) {
                    info += `Piercing: ${hoveredTower.stats.piercing} enemies\n`;
                }
                if (hoveredTower.stats.summonChance > 0) {
                    info += `Summon Chance: ${Math.round(hoveredTower.stats.summonChance * 100)}%\n`;
                }

                this.showTooltip(e.clientX, e.clientY, info);
                hoveredTower.showRange = true;
            } else {
                this.hideTooltip();
                this.game.state.towers.forEach(t => t.showRange = false);
            }
        });

        this.canvas.addEventListener('mouseout', () => {
            this.hideTooltip();
        });
        
        this.canvas.addEventListener('click', (e) => {
            if (!this.game.state.selectedTowerType) return;
            

            if (this.checkValidTowerPosition(this.game.state.mousePosition.gridX, this.game.state.mousePosition.gridY)) {
                // Create the tower
                let cost = this.game.getCollections().towers[this.game.state.selectedTowerType].cost;
                let populationCost = this.game.getCollections().towers[this.game.state.selectedTowerType].population || 0;
                
                const finalCost = Math.floor(cost * this.game.state.stats.towerCostMod);
                
                if (this.game.state.bloodShards >= finalCost && this.game.state.stats.population + populationCost <= this.game.state.stats.maxPopulation) {
                    let position = new THREE.Vector3(
                        this.game.state.mousePosition.gridX * this.gridSize + this.gridSize / 2, 
                        this.game.state.mousePosition.gridY * this.gridSize + this.gridSize / 2,
                        0
                    );
                    const tower = this.game.spawn("tower", { objectType: "towers", spawnType: this.game.state.selectedTowerType, setDirection: 1}, position);
                    tower.placed = true;
                    tower.transform.position.z = this.game.gameEntity.getComponent('game').getTerrainHeight(tower.transform.gridPosition);
                    this.game.state.tileMap[this.game.state.mousePosition.gridY][this.game.state.mousePosition.gridX].buildable = false;
                    this.game.state.tileMap[this.game.state.mousePosition.gridY][this.game.state.mousePosition.gridX].tower = tower;
                    this.game.state.bloodShards -= finalCost;
                    this.game.state.previewTower.destroy();
                    this.game.state.previewTower = null;
                    const gameEventData = this.game.getCollections().gameEvents['placeTower'];
                    const audioData = this.game.getCollections().sounds[gameEventData.sound].audio;
                    this.game.audioManager.playSynthSound('placeTower', audioData);
                    // Clear selection
                    this.game.state.selectedTowerType = null;
                    this.canvas.style.cursor = 'default';
                }
            }
        });
        
        // Cancel tower placement with right click
        this.canvas.addEventListener('contextmenu', (e) => {
            e.preventDefault();
            if (this.game.state.selectedTowerType) {
                this.game.state.selectedTowerType = null;
                this.canvas.style.cursor = 'default';
            }
        });
    }

    setupTowerPlacement() {

        
        const towerButtons = document.querySelectorAll('.tower-option');
        towerButtons.forEach(button => {
            button.addEventListener('click', () => {

                if(this.game.state.isPaused) return;
                
                const type = button.getAttribute('data-type');
                let cost = this.game.getCollections().towers[type].cost;
                const finalCost = Math.floor(cost * this.game.state.stats.towerCostMod);
                
                let populationCost = this.game.getCollections().towers[type].population || 0;
                if (this.game.state.bloodShards >= finalCost && this.game.state.stats.population + populationCost <= this.game.state.stats.maxPopulation) {
                    this.game.state.selectedTowerType = type;
                    if(this.game.state.previewTower) {
                        this.game.state.previewTower.destroy();
                    }
                    this.game.state.previewTower = this.game.spawn('previewTower', { objectType: "towers", spawnType: this.game.state.selectedTowerType}, new THREE.Vector3());
                }
            });
            
            // Show tooltip with info
            button.addEventListener('mouseover', (e) => {
                const type = button.getAttribute('data-type');
                let info = this.game.getCollections().towers[type].info;
                
                this.showTooltip(e.clientX, e.clientY, info);
            });
            
            button.addEventListener('mouseout', () => {
                this.hideTooltip();
            });
        });
        
        
    }

    checkValidTowerPosition(posX, posY) {
        if(posX >= 0 && posY >= 0 && this.game.state.tileMap.length > posY && this.game.state.tileMap[posY].length > posX){
            return this.game.state.tileMap[posY][posX].buildable;            
        }
        return false;
    }

    reset() {
        this.gameOverMenu.style.display = 'none';
        this.victoryMenu.style.display = 'none';
        this.overlay.style.display = 'none';
        this.waveDisplay.textContent = '1';
        this.waveProgress.style.width = '0%';        


    }


    draw() {
        this.shardsDisplay.textContent = Math.floor(this.game.state.bloodShards);
        this.essenceDisplay.textContent = Math.floor(this.game.state.essence);
        this.essenceNeededDisplay.textContent = Math.floor(this.game.state.essenceToNextLevel);
        this.hpDisplay.textContent = Math.floor(this.game.state.bloodCoreHP);
        this.populationDisplay.textContent = Math.floor(this.game.state.stats.population);
        this.maxPopulationDisplay.textContent = Math.floor(this.game.state.stats.maxPopulation);  
        if (this.game.state.enemies.length === 0 && this.game.state.enemiesSpawned >= this.game.state.numEnemiesInWave && !this.game.state.victory) {
            const countdown = Math.ceil((this.game.state.waveDelay - this.game.state.waveTimer) / 60);
            this.ctx.fillStyle = 'white';
            this.ctx.font = '20px Arial';
            this.ctx.textAlign = 'center';
            this.ctx.fillText(`Next Wave in ${countdown}...`, this.canvas.width / 2, 50);
        }  
        if(!this.game.getCollections().configs.game.is3D) {
            this.renderCanvas();
        }
    }

    updateWaveDisplay(waveNumber) {
        this.waveDisplay.textContent = waveNumber;
    }


    showTooltip(x, y, text) {
        const tooltip = document.getElementById('tooltip');
        tooltip.style.display = 'block';
        tooltip.style.left = (x + 10) + 'px';
        tooltip.style.top = (y + 10) + 'px';
        tooltip.textContent = text;
    }

    hideTooltip() {
        const tooltip = document.getElementById('tooltip');
        tooltip.style.display = 'none';
    }
}

