/* retro-theme.css - Pixel Art Retro Video Game Theme */

/* Import pixel font from Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

/* Base Styles */
body {
    background-color: #1a1a1a; /* Dark retro background */
    font-family: 'Press Start 2P', cursive; /* Pixelated font */
    image-rendering: pixelated; /* Ensures crisp pixel art rendering */
    color: #fff; /* Default text color */
}

/* Container */
.container {
    background: #2b2b2b; /* Retro gray */
    border: 4px solid #000; /* Thick black border */
    padding: .5em;
}

/* Sidebar */
.sidebar {
    background: #3c3c3c; /* Mid-gray retro */
    border: 4px solid #000;
}

.sidebar img {
    image-rendering: pixelated;
    border-bottom: 2px solid #000;
}

/* Object List */
.object-item {
    background: #5a5a5a; /* Darker gray */
    border: 2px solid #000;
    color: #fff;
    font-size: 10px; /* Smaller for pixel readability */
}

.object-item:hover, .object-item.selected {
    background: #ffcc00; /* Bright retro yellow */
    color: #000;
}
.object-type-item.selected {
    color: #ffcc00;
}

/* Buttons */
button {
    background: #ff3333; /* Retro red */
    color: #fff;
    border: 2px solid #000;
    font-family: 'Press Start 2P', cursive;
    font-size: 10px;
    text-transform: uppercase; /* Retro game style */
    transition: background 0.1s ease;
}

button:hover {
    background: #33cc33; /* Retro green */
}

button.primary {
    background: #33cc33; /* Green for primary actions */
}

button.primary:hover {
    background: #ffcc00; /* Yellow on hover */
}

/* Main Content */
.main-content {
    background: #2b2b2b;
}

/* Editor */
.editor {
    background: #3c3c3c;
    border: 4px solid #000;
    color: #fff;
    font-size: 10px;
}

/* Canvas and Grid Containers */
#canvas-container, .grid-container {
    background: #000; /* Black canvas background */
    border: 4px solid #fff;
}

#canvas, #grid {
    image-rendering: pixelated;
    background: repeating-linear-gradient(
        45deg,
        #333 0px,
        #333 10px,
        #000 10px,
        #000 20px
    ); /* Retro checkerboard grid */
}

.scene-info {
    background: rgba(0, 0, 0, 0.8);
    border: 2px solid #fff;
    color: #00ff00; /* Neon green */
    font-size: 8px;
}

/* Toolbar */
.toolbar {
    background: #5a5a5a;
    border-bottom: 2px solid #000;
}

.btn {
    background: #ff3333; /* Retro red */
    color: #fff;
    border: 2px solid #000;
    font-family: 'Press Start 2P', cursive;
    font-size: 10px;
    transition: background 0.1s ease;
}

.btn:hover {
    background: #33cc33; /* Retro green */
}

.btn.btn-danger {
    background: #ff0000; /* Bright red */
}

.btn.btn-danger:hover {
    background: #cc0000; /* Darker red */
}

.btn.btn-secondary {
    background: #6666ff; /* Retro blue */
}

.btn.btn-secondary:hover {
    background: #9999ff; /* Lighter blue */
}

.btn.btn-special {
    background: #ff00ff; /* Retro magenta */
}

.btn.btn-special:hover {
    background: #cc00cc; /* Darker magenta */
}

/* Modals */
.modal {
    background: rgba(0, 0, 0, 0.8);
}

.modal-content {
    background: #3c3c3c;
    border: 4px solid #fff;
    color: #fff;
    font-size: 10px;
}

/* Form Elements */
.form-group label {
    color: #00ff00; /* Neon green */
    font-size: 10px;
}

input, textarea, select {
    background: #000;
    border: 2px solid #fff;
    color: #ffcc00; /* Retro yellow */
    font-family: 'Press Start 2P', cursive;
    font-size: 10px;
}

input[type="color"] {
    padding: 0; /* Fix color picker padding */
    height: 20px; /* Consistent height */
}

/* Tabs */
.tab-navigation {
    border-bottom: 2px solid #000;
}

.tab {
    background: #5a5a5a;
    border: 2px solid #000;
    color: #fff;
    font-size: 10px;
}

.tab.active {
    background: #ffcc00; /* Yellow for active tab */
    color: #000;
}

/* Inspector and JSON Editor */
#inspector, #json-editor {
    background: #3c3c3c;
    border: 2px solid #000;
}

#json-content {
    background: #000;
    color: #00ff00; /* Green for code-like text */
}

/* Translation Controls */
.translation-controls button {
    background: #6666ff; /* Retro blue */
}

.translation-controls button:hover {
    background: #9999ff;
}

/* Shape List */
.shape-item {
    background: #5a5a5a;
    border: 2px solid #000;
    color: #fff;
    font-size: 10px;
}

.shape-item:hover {
    background: #ffcc00;
    color: #000;
}

.type-selector .object-item {
  font-size: .6em;
}

/* codemirror-retro.css - Retro Pixel Art Theme for CodeMirror */

/* Import pixel font from Google Fonts */
@import url('https://fonts.googleapis.com/css2?family=Press+Start+2P&display=swap');

/* Base CodeMirror Styles */
.CodeMirror {
    height: 100% !important;
    font-family: 'Press Start 2P', cursive !important;
    font-size: 10px !important; /* Small size for pixel readability */
    background: #000 !important; /* Black background like a CRT screen */
    color: #00ff00 !important; /* Neon green default text */
    border: 4px solid #fff !important; /* Thick white border */
    image-rendering: pixelated; /* Crisp rendering */
}
.CodeMirror-gutters {
 background-color: #000;
}

/* Retro Theme Definition */
.cm-s-retro .CodeMirror-gutters {
    background: #1a1a1a !important; /* Dark gray gutter */
    border-right: 2px solid #fff !important; /* White separator */
    color: #ffcc00 !important; /* Yellow line numbers */
}

.cm-s-retro .CodeMirror-linenumber {
    color: #ffcc00 !important; /* Yellow line numbers */
    padding: 0 5px !important;
}

.cm-s-retro .CodeMirror-cursor {
    border-left: 2px solid #ff3333 !important; /* Red blinking cursor */
    animation: blink 1s step-end infinite; /* Retro blink effect */
}

.cm-s-retro .CodeMirror-selected {
    background: #ff3333 !important; /* Red selection highlight */
    opacity: 0.5; /* Slightly transparent for retro effect */
}

.cm-s-retro .CodeMirror-focused .CodeMirror-selected {
    background: #ff3333 !important;
}

/* Syntax Highlighting */
.cm-s-retro .cm-comment {
    color: #6666ff !important; /* Blue for comments */
    font-style: italic; /* Slight retro flair */
}

.cm-s-retro .cm-string {
    color: #ffcc00 !important; /* Yellow for strings */
}

.cm-s-retro .cm-number {
    color: #33cc33 !important; /* Green for numbers */
}

.cm-s-retro .cm-keyword {
    color: #ff00ff !important; /* Magenta for keywords */
    font-weight: bold; /* Bold for emphasis */
}

.cm-s-retro .cm-operator {
    color: #fff !important; /* White for operators */
}

.cm-s-retro .cm-variable {
    color: #00ff00 !important; /* Green for variables */
}

.cm-s-retro .cm-variable-2 {
    color: #33cc33 !important; /* Lighter green for secondary variables */
}

.cm-s-retro .cm-def {
    color: #ff00ff !important; /* Magenta for definitions */
}

.cm-s-retro .cm-property {
    color: #ffcc00 !important; /* Yellow for properties */
}

.cm-s-retro .cm-function {
    color: #ff00ff !important; /* Magenta for functions */
}

.cm-s-retro .cm-builtin {
    color: #33cc33 !important; /* Green for built-ins */
}

.cm-s-retro .cm-tag {
    color: #ff3333 !important; /* Red for tags (if HTML mode is used) */
}

.cm-s-retro .cm-attribute {
    color: #ffcc00 !important; /* Yellow for attributes */
}

/* Matching Brackets */
.cm-s-retro .CodeMirror-matchingbracket {
    color: #33cc33 !important; /* Green for matching brackets */
    border-bottom: 2px solid #33cc33 !important;
    background: none !important;
}

.cm-s-retro .CodeMirror-nonmatchingbracket {
    color: #ff3333 !important; /* Red for non-matching brackets */
    border-bottom: 2px solid #ff3333 !important;
}

/* Autocomplete Hints */
.CodeMirror-hints {
    background: #2b2b2b !important; /* Dark retro gray */
    border: 2px solid #fff !important; /* White border */
    color: #fff !important;
    font-family: 'Press Start 2P', cursive !important;
    font-size: 10px !important;
    box-shadow: 4px 4px 0 #000; /* Retro shadow effect */
}

.CodeMirror-hints .CodeMirror-hint {
    color: #ffcc00 !important; /* Yellow for hint items */
    padding: 5px !important;
}

.CodeMirror-hints .CodeMirror-hint-active {
    background: #ff3333 !important; /* Red for selected hint */
    color: #000 !important;
}

/* Scrollbars (minimal retro style) */
.CodeMirror-vscrollbar, .CodeMirror-hscrollbar {
    background: #3c3c3c !important;
}

.CodeMirror-scrollbar-filler {
    background: #000 !important;
}

/* Animations */
@keyframes blink {
    50% { opacity: 0; }
}
