css:
body { background-color: #1a0f05; color: #ffffff; font-family: 'Arial', sans-serif; }

h1, h2, h3 { color: #00ffbf; }

.button-primary { background-color: #00ffbf; border: none; padding: 10px 20px; border-radius: 5px; cursor: pointer; transition: all 0.3s ease; }
.button-primary:hover { transform: scale(1.05); box-shadow: 0 0 15px #00ffbf; }

.link { color: #00ffbf; text-decoration: none; }
.link:hover { text-decoration: underline; }

.card { background-color: #2d1e13; border-radius: 10px; padding: 20px; margin: 10px; box-shadow: 0 4px 6px rgba(0,0,0,0.1); }

input, textarea { background-color: #2d1e13; color: #ffffff; border: 1px solid #00ffbf; padding: 8px; border-radius: 5px; }

header { background-color: #00ffbf; padding: 20px; text-align: center; box-shadow: 0 4px 6px rgba(0,255,191,0.3); }

footer { background-color: #2d1e13; color: #ffffff; padding: 20px; text-align: center; border-top: 1px solid #00ffbf; }
