css:
/* Eye-friendly dark theme overriding default CodeMirror blues and purples */
.CodeMirror {
    background: #1e1e1e; /* Dark gray background */
    color: #d4d4d4; /* Light gray text */
    font-size: 14px; /* Increased font size for better readability */
}

.CodeMirror-gutters {
    background: #252525; /* Slightly lighter gutter */
    border-right: 1px solid #444; /* Soft border */
}

.CodeMirror-linenumber {
    color: #888; /* Muted line numbers */
}

.CodeMirror-cursor {
    border-left: 1px solid #d4d4d4; /* Light cursor */
}

.CodeMirror-selected {
    background: #3a3a3a; /* Dark selection */
}

.CodeMirror-focused .CodeMirror-selected {
    background: #404040; /* Slightly lighter when focused */
}

.CodeMirror-activeline-background {
    background: #2a2a2a; /* Subtle active line highlight */
}

.CodeMirror-matchingbracket {
    color: #b5cea8; /* Muted green for matching brackets */
}

.CodeMirror-nonmatchingbracket {
    color: #d16969; /* Soft red for non-matching */
}

/* Override default theme's syntax highlighting */
.cm-s-default .cm-header { color: #dcdcaa; } /* Soft yellow (overrides #00f blue) */
.cm-s-default .cm-quote { color: #b5cea8; } /* Muted green (overrides #090) */
.cm-negative { color: #d16969; } /* Soft red */
.cm-positive { color: #6a9955; } /* Muted green */
.cm-header, .cm-strong { font-weight: 700; }
.cm-em { font-style: italic; }
.cm-s-default .cm-link { color: #d7ba7d; text-decoration: underline; } /* Muted yellow (overrides #00c blue) */
.cm-strikethrough { text-decoration: line-through; }

.cm-s-default .cm-keyword { color: #ce9178; } /* Soft orange (overrides #708 purple) */
.cm-s-default .cm-atom { color: #d4d4d4; } /* Light gray (overrides #219 blue) */
.cm-s-default .cm-number { color: #b5cea8; } /* Muted green (overrides #164) */
.cm-s-default .cm-def { color: #dcdcaa; } /* Soft yellow (overrides #00f blue) */
.cm-s-default .cm-variable-2 { color: #d4d4d4; } /* Light gray (overrides #05a blue) */
.cm-s-default .cm-variable-3, 
.cm-s-default .cm-type { color: #dcdcaa; } /* Soft yellow (overrides #085 blue) */
.cm-s-default .cm-comment { color: #6a9955; } /* Muted green (overrides #a50) */
.cm-s-default .cm-string { color: #ce9178; } /* Soft orange (overrides #a11) */
.cm-s-default .cm-string-2 { color: #d7ba7d; } /* Muted yellow (overrides #f50) */
.cm-s-default .cm-meta { color: #d4d4d4; } /* Light gray (overrides #555) */
.cm-s-default .cm-qualifier { color: #d4d4d4; } /* Light gray (overrides #555) */
.cm-s-default .cm-builtin { color: #dcdcaa; } /* Soft yellow (overrides #30a) */
.cm-s-default .cm-bracket { color: #d4d4d4; } /* Light gray (overrides #997) */
.cm-s-default .cm-tag { color: #dcdcaa; } /* Soft yellow (overrides #170) */
.cm-s-default .cm-attribute { color: #d4d4d4; } /* Light gray (overrides #00c blue) */
.cm-s-default .cm-hr { color: #888; } /* Muted gray (overrides #999) */
.cm-s-default .cm-link { color: #d7ba7d; } /* Muted yellow (overrides #00c blue) */
.cm-s-default .cm-error { color: #d16969; } /* Soft red (overrides red) */
.cm-invalidchar { color: #d16969; } /* Soft red */

/* Search highlight */
.cm-searching {
    background-color: rgba(255, 255, 0, 0.2); /* Subtle yellow */
}
